private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response, FilterChain filterChain)throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k-> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

    public static int startArthasClient(String arthasHomeDir, List < String > telnetArgs, OutputStream out) throws Throwable {
        // start java telnet client
        // find arthas-client.jar
        URLClassLoader classLoader = new URLClassLoader(new URL [] {
                                                            new File(arthasHomeDir, "arthas-client.jar").toURI().toURL()});
        Class < ?> telnetConsoleClass = classLoader.loadClass("com.taobao.arthas.client.TelnetConsole");
        Method processMethod = telnetConsoleClass.getMethod("process", String[].class);

        //redirect System.out/System.err
        PrintStream originSysOut = System.out;
        PrintStream originSysErr = System.err;
        PrintStream newOut = new PrintStream(out);
        PrintStream newErr = new PrintStream(out);

        // call TelnetConsole.process()
        // fix https://github.com/alibaba/arthas/issues/833
        ClassLoader tccl = Thread.currentThread().getContextClassLoader();
        try {
            System.setOut(newOut);
            System.setErr(newErr);
            Thread.currentThread().setContextClassLoader(classLoader);
            return (Integer) processMethod.invoke(null, new Object [] {
                                                            telnetArgs.toArray(new String[0])});
        } catch (Throwable e) {
            //java.lang.reflect.InvocationTargetException : java.net.ConnectException
            e = e.getCause();

            if (e instanceof IOException || e instanceof InterruptedException) {
                // ignore connection error and interrupted error
                return STATUS_ERROR;
            } else {
                // process error
                AnsiLog.error("process error: {}", e.toString());
                AnsiLog.error(e);
                return STATUS_EXEC_ERROR;
            }
        }

finally {
            Thread.currentThread().setContextClassLoader(tccl);

            //reset System.out/System.err
            System.setOut(originSysOut);
            System.setErr(originSysErr);
            //flush output
            newOut.flush();
            newErr.flush();
        }
    }

private static boolean isValidJakartaMailMultipart(Object multipart) {

if (multipart == null) return false;
try {
        // Check if the object implements jakarta.mail.Multipart interface
    Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
    if (!multipartInterface.isInstance(multipart)) {
    return false;
    }

        // Additional check for MimeMultipart

    try {
    Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
    if (mimeMultipartClass.isInstance(multipart)) {
        log.debug("Found MimeMultipart instance for enhanced processing");
        return true;
    }
    } catch (ClassNotFoundException e) {
      log.debug("MimeMultipart not available, using base Multipart interface");
    }
    return true;
} catch (ClassNotFoundException e) {
  log.debug("Jakarta Mail Multipart interface not available for validation");
  return false;
}
}

public static String generateMachineFingerprint() {
   try {
        // Get the MAC address
    StringBuilder sb = new StringBuilder();
    InetAddress ip = InetAddress.getLocalHost();
    NetworkInterface network = NetworkInterface.getByInetAddress(ip);
    if (network == null) {
       Enumeration<NetworkInterface> networks = NetworkInterface.getNetworkInterfaces();
       while (networks.hasMoreElements()) {
        NetworkInterface net = networks.nextElement();
        byte[] mac = net.getHardwareAddress();
        if (mac != null) {
                                              for (int i = 0; i < mac.length; i++) {
                             sb.append(String.format("%02X", mac[i]));
                                              }

                                              break; // Use the first network interface with a MAC address
        }
       }
    } else {
      byte[] mac = network.getHardwareAddress();
      if (mac != null) {
               for (int i = 0; i < mac.length; i++) {
                           sb.append(String.format("%02X", mac[i]));
               }
      }
    }

        // Hash the MAC address for privacy and consistency
    MessageDigest md = MessageDigest.getInstance("SHA-256");
    byte[] hash = md.digest(sb.toString().getBytes(StandardCharsets.UTF_8));
    StringBuilder fingerprint = new StringBuilder();

    for (byte b : hash) {
       fingerprint.append(String.format("%02x", b));
    }
    return fingerprint.toString();
   } catch (Exception e) {
     return "GenericID";
   }
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();
    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }
    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            return;// let the download thread handle this error
        default:
            if (statusCode < 200
                    || statusCode > 299) {
                throw new HttpError(statusCode);
            }
    }
}

@Override
public void startElement(XlsxReadContext xlsxReadContext, String name, Attributes attributes) {
    String ref = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_REF);
    if (StringUtils.isEmpty(ref)) {
        return;
    }
    // Hyperlink has 2 case:
    // case 1In the 'location' tag
    String location = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_LOCATION);
    if (location != null) {
        CellExtra cellExtra = new CellExtra(CellExtraTypeEnum.HYPERLINK, location, ref);
        xlsxReadContext.readSheetHolder().setCellExtra(cellExtra);
        xlsxReadContext.analysisEventProcessor().extra(xlsxReadContext);
        return;
    }
    // case 2, In the 'r:id' tag, Then go to 'PackageRelationshipCollection' to get inside
    String rId = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_RID);
    PackageRelationshipCollection packageRelationshipCollection = xlsxReadContext.xlsxReadSheetHolder().getPackageRelationshipCollection();
    if (rId == null
            || packageRelationshipCollection == null) {
        return;
    }
    Optional.ofNullable(packageRelationshipCollection.getRelationshipByID(rId)).map(PackageRelationship::getTargetURI).ifPresent(uri -> {
        CellExtra cellExtra = new CellExtra(CellExtraTypeEnum.HYPERLINK, uri.toString(), ref);
        xlsxReadContext.readSheetHolder().setCellExtra(cellExtra);
        xlsxReadContext.analysisEventProcessor().extra(xlsxReadContext);
    });
}

