@Bean(name = "machineType" )
public String determineMachineType() {
   try {
    boolean isDocker = runningInDocker();
    boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;
    boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));
    if (isKubernetes) {
       return "Kubernetes";
    } else if (isDocker) {
      return "Docker";
    } else if (isBrowserOpen) {
      String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);
      if (os.contains("win")) {
               return "Client-windows";
      } else if (os.contains("mac")) {
        return "Client-mac";
      } else {
        return "Client-unix";
      }
           } else {
             return "Server-jar";
           }
   } catch (Exception e) {
     return "Unknown";
   }
}

private Row createRowIfNecessary(Sheet sheet, Sheet cachedSheet, Integer lastRowIndex, FillConfig fillConfig, AnalysisCell analysisCell, boolean isOriginalCell, RowWriteHandlerContext rowWriteHandlerContext) {
    rowWriteHandlerContext.setRowIndex(lastRowIndex);
    Row row = sheet.getRow(lastRowIndex);
    if (row != null) {
        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);
        rowWriteHandlerContext.setRow(row);
        return row;
    }
    row = cachedSheet.getRow(lastRowIndex);
    if (row == null) {
        rowWriteHandlerContext.setRowIndex(lastRowIndex);
        WriteHandlerUtils.beforeRowCreate(rowWriteHandlerContext);
        if (fillConfig.getForceNewRow()) {
            row = cachedSheet.createRow(lastRowIndex);
        } else {
            // The last row of the middle disk inside empty rows, resulting in cachedSheet can not get inside.
            // Will throw Attempting to write a row[" + rownum + "] " + "in the range [0," + this._sh
            // .getLastRowNum() + "] that is already written to disk.
            try {
                row = sheet.createRow(lastRowIndex);
            }

                catch(IllegalArgumentException ignore ) {
                row = cachedSheet.createRow(lastRowIndex);
            }
        }
        rowWriteHandlerContext.setRow(row);
        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);
        WriteHandlerUtils.afterRowCreate(rowWriteHandlerContext);
    } else {
        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);
        rowWriteHandlerContext.setRow(row);
    }
    return row;
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List<EmailAttachment> attachments) throws IOException {

if (document.getNumberOfPages() == 0 || attachments == null|| attachments.isEmpty()) {
    return;
}

    // 1. Find the screen position of all attachment markers
AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
finder.setSortByPosition(true); // Process pages in order
finder.getText(document);
List<MarkerPosition> markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match
if (markerPositions.size() != attachments.size()) {
    log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.",
        markerPositions.size(),
        attachments.size());
}

    // 3. Create an invisible annotation over each found marker
int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());

for (int i = 0; i < annotationsToAdd; i++) {
    MarkerPosition position = markerPositions.get(i);
    EmailAttachment attachment = attachments.get(i);
    if (attachment.getEmbeddedFilename() != null) {
                                               PDPage page = document.getPage(position.getPageIndex());
                                               addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
    }
}
}

public static HealthCountsStream getInstance(HystrixCommandKey commandKey, int numBuckets, int bucketSizeInMs) {
    HealthCountsStream initialStream = streams.get(commandKey.name());

    if (initialStream != null) {
        return initialStream;
    } else {
        final HealthCountsStream healthStream;
        synchronized(HealthCountsStream.class) {
                                               HealthCountsStream existingStream = streams.get(commandKey.name());

                                               if (existingStream == null) {
                                                   HealthCountsStream newStream = new HealthCountsStream(commandKey, numBuckets, bucketSizeInMs, HystrixCommandMetrics.appendEventToBucket);
                                                   streams.putIfAbsent(commandKey.name(), newStream);
                                                   healthStream = newStream;
                                               } else {
                                                   healthStream = existingStream;
                                               }
        }

        healthStream.startCachingStreamValuesIfUnstarted();
        return healthStream;
    }
}

