public Map<ExecutionSignature, List < Integer >> getExecutionsMappedToLatencies() {
    Map<CommandAndCacheKey, Integer> cachingDetector = new HashMap<CommandAndCacheKey, Integer>();
    List < HystrixInvokableInfo < ?>> nonCachedExecutions = new ArrayList < HystrixInvokableInfo < ?>>(executions.size());
    for (HystrixInvokableInfo < ?> execution : executions) {
        if (execution.getPublicCacheKey() != null) {
            //eligible for caching - might be the initial, or might be from cache
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), execution.getPublicCacheKey());
            Integer count = cachingDetector.get(key);

            if (count != null) {
                //key already seen
                cachingDetector.put(key, count + 1);
            } else {
                //key not seen yet
                cachingDetector.put(key, 0);
            }
        }

        if (!execution.isResponseFromCache()) {
            nonCachedExecutions.add(execution);
        }
    }

    Map<ExecutionSignature, List < Integer >> commandDeduper = new HashMap<ExecutionSignature, List < Integer >>();
    for (HystrixInvokableInfo < ?> execution : nonCachedExecutions) {
        int cachedCount = 0;
        String cacheKey = execution.getPublicCacheKey();

        if (cacheKey != null) {
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), cacheKey);
            cachedCount = cachingDetector.get(key);
        }

        ExecutionSignature signature;

        if (cachedCount > 0) {
            //this has a RESPONSE_FROM_CACHE and needs to get split off
            signature = ExecutionSignature.from(execution, cacheKey, cachedCount);
        } else {
            //nothing cached from this, can collapse further
            signature = ExecutionSignature.from(execution);
        }

        List < Integer > currentLatencyList = commandDeduper.get(signature);

        if (currentLatencyList != null) {
            currentLatencyList.add(execution.getExecutionTimeInMilliseconds());
        } else {
            List < Integer > newLatencyList = new ArrayList < Integer >();
            newLatencyList.add(execution.getExecutionTimeInMilliseconds());
            commandDeduper.put(signature, newLatencyList);
        }
    }

    return commandDeduper;
}

@Override
public Map.Entry<T, ProfilingInfo> build(Object data) {
    List list = (List)data;
    if (list == null||list.isEmpty()) return null;
    if (list.get(0)instanceof KeyValue) { // RESP3
        Object resultsData = null, profileData = null;
        for(KeyValue keyValue :(List<KeyValue>)data) {
            String keyStr = BuilderFactory.STRING.build(keyValue.getKey());
            switch(keyStr) {
                                                          case PROFILE_STR_REDIS7: case PROFILE_STR_REDIS8:
                                                          profileData = keyValue.getValue();
                                                          break;
                           case RESULTS_STR_REDIS7:
                           resultsData = data;
                           break;
                           case RESULTS_STR_REDIS8:
                           resultsData = keyValue.getValue();
                           break;
            }
        }
        assert resultsData != null: "Could not detect Results data.";
        assert profileData != null: "Could not detect Profile data.";
        return KeyValue.of(resultsBuilder.build(resultsData), ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
    }
    return KeyValue.of(resultsBuilder.build(list.get(0)), ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

private Object buildNoModel(Map<Integer, ReadCellData <?>> cellDataMap, ReadSheetHolder readSheetHolder, AnalysisContext context) {
    int index = 0;
    Map<Integer, Object> map = MapUtils.newLinkedHashMapWithExpectedSize(cellDataMap.size());
    for(Map.Entry<Integer, ReadCellData <?>> entry :cellDataMap.entrySet()) {
        Integer key = entry.getKey();
        ReadCellData<?> cellData = entry.getValue();
        while(index < key) {
            map.put(index, null);
            index++;
        }
        index++;
        ReadDefaultReturnEnum readDefaultReturn = context.readWorkbookHolder().getReadDefaultReturn();
        if (readDefaultReturn == ReadDefaultReturnEnum.STRING) {
            // string
            map.put(key, (String) ConverterUtils.convertToJavaObject(cellData, null, null, readSheetHolder.converterMap(), context, context.readRowHolder().getRowIndex(), key));
        } else {
            // retrun ReadCellData
            ReadCellData<?> convertedReadCellData = convertReadCellData(cellData, context.readWorkbookHolder().getReadDefaultReturn(), readSheetHolder, context, key);
            if (readDefaultReturn == ReadDefaultReturnEnum.READ_CELL_DATA) {
                map.put(key, convertedReadCellData);
            } else {
                map.put(key, convertedReadCellData.getData());
            }
        }
    }
    // fix https://github.com/alibaba/easyexcel/issues/2014

    int headSize = calculateHeadSize(readSheetHolder);
    while(index < headSize) {
        map.put(index, null);
        index++;
    }
    return map;
}

private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response, FilterChain filterChain) throws IOException, ServletException {
   Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
   ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
   if (probe.isConsumed()) {
    response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
    filterChain.doFilter(request, response);
   } else {
     long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
     response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
     response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
     response.getWriter().write("Rate limit exceeded for POST requests.");
   }
}

