protected static String convertToJson(HystrixUtilization utilization) throws IOException {
    StringWriter jsonString = new StringWriter();
    JsonGenerator json = jsonFactory.createGenerator(jsonString);
    json.writeStartObject();
    json.writeStringField("type", "HystrixUtilization");
    json.writeObjectFieldStart("commands");
    for (Map.Entry<HystrixCommandKey, HystrixCommandUtilization> entry : utilization.getCommandUtilizationMap().entrySet()) {
        final HystrixCommandKey key = entry.getKey();
        final HystrixCommandUtilization commandUtilization = entry.getValue();
        writeCommandUtilizationJson(json, key, commandUtilization);
    }

    json.writeEndObject();
    json.writeObjectFieldStart("threadpools");
    for (Map.Entry<HystrixThreadPoolKey, HystrixThreadPoolUtilization> entry : utilization.getThreadPoolUtilizationMap().entrySet()) {
        final HystrixThreadPoolKey threadPoolKey = entry.getKey();
        final HystrixThreadPoolUtilization threadPoolUtilization = entry.getValue();
        writeThreadPoolUtilizationJson(json, threadPoolKey, threadPoolUtilization);
    }

    json.writeEndObject();
    json.writeEndObject();
    json.close();
    return jsonString.getBuffer().toString();
}

private void fillComment(CellWriteHandlerContext cellWriteHandlerContext, CommentData commentData) {
    if (commentData == null) {
        return;
    }
    ClientAnchor anchor;
    Integer rowIndex = cellWriteHandlerContext.getRowIndex();
    Integer columnIndex = cellWriteHandlerContext.getColumnIndex();
    Sheet sheet = cellWriteHandlerContext.getWriteSheetHolder().getSheet();
    Cell cell = cellWriteHandlerContext.getCell();
    if (writeContext.writeWorkbookHolder().getExcelType() == ExcelTypeEnum.XLSX) {
        anchor = new XSSFClientAnchor(StyleUtil.getCoordinate(commentData.getLeft()), StyleUtil.getCoordinate(commentData.getTop()), StyleUtil.getCoordinate(commentData.getRight()), StyleUtil.getCoordinate(commentData.getBottom()), StyleUtil.getCellCoordinate(columnIndex, commentData.getFirstColumnIndex(), commentData.getRelativeFirstColumnIndex()), StyleUtil.getCellCoordinate(rowIndex, commentData.getFirstRowIndex(), commentData.getRelativeFirstRowIndex()), StyleUtil.getCellCoordinate(columnIndex, commentData.getLastColumnIndex(), commentData.getRelativeLastColumnIndex()) + 1, StyleUtil.getCellCoordinate(rowIndex, commentData.getLastRowIndex(), commentData.getRelativeLastRowIndex()) + 1);
    } else {
        anchor = new HSSFClientAnchor(StyleUtil.getCoordinate(commentData.getLeft()), StyleUtil.getCoordinate(commentData.getTop()), StyleUtil.getCoordinate(commentData.getRight()), StyleUtil.getCoordinate(commentData.getBottom()), (short)StyleUtil.getCellCoordinate(columnIndex, commentData.getFirstColumnIndex(), commentData.getRelativeFirstColumnIndex()), StyleUtil.getCellCoordinate(rowIndex, commentData.getFirstRowIndex(), commentData.getRelativeFirstRowIndex()), (short) (StyleUtil.getCellCoordinate(columnIndex, commentData.getLastColumnIndex(), commentData.getRelativeLastColumnIndex()) + 1), StyleUtil.getCellCoordinate(rowIndex, commentData.getLastRowIndex(), commentData.getRelativeLastRowIndex()) + 1);
    }
    Comment comment = sheet.createDrawingPatriarch().createCellComment(anchor);
    if (commentData.getRichTextStringData() != null) {
        comment.setString(StyleUtil.buildRichTextString(writeContext.writeWorkbookHolder(), commentData.getRichTextStringData()));
    }

    if (commentData.getAuthor() != null) {
        comment.setAuthor(commentData.getAuthor());
    }
    cell.setCellComment(comment);
}

    private boolean verifyCertificateLicense(String licenseFile, LicenseContext context) {
        try {
            String encodedPayload = licenseFile;
            // Remove the header
            encodedPayload = encodedPayload.replace(CERT_PREFIX, "");
            // Remove the footer
            encodedPayload = encodedPayload.replace(CERT_SUFFIX, "");
            // Remove all newlines
            encodedPayload = encodedPayload.replaceAll("\\r?\\n", "");
            byte [] payloadBytes = Base64.getDecoder().decode(encodedPayload);
            String payload = new String(payloadBytes);
            log.info("Decoded certificate payload: {}", payload);
            String encryptedData = "";
            String encodedSignature = "";
            String algorithm = "";
            try {
                JSONObject attrs = new JSONObject(payload);
                encryptedData = (String)attrs.get("enc");
                encodedSignature = (String)attrs.get("sig");
                algorithm = (String)attrs.get("alg");
            }
catch(JSONException e ) {
                log.error("Failed to parse license file: {}", e.getMessage());
                return false;
            }

            // Verify license file algorithm
            if (!"base64+ed25519".equals(algorithm)) {
                log.error("Unsupported algorithm: {}. Only base64+ed25519 is supported.", algorithm);
                return false;
            }

            // Verify signature

            boolean isSignatureValid = verifyEd25519Signature(encryptedData, encodedSignature);
            if (!isSignatureValid) {
                log.error("License file signature is invalid");
                return false;
            }
            log.info("License file signature is valid");

            // Decode the base64 data
            String decodedData;
            try {
                decodedData = new String(Base64.getDecoder().decode(encryptedData));
            }
catch(IllegalArgumentException e ) {
                log.error("Failed to decode license data: {}", e.getMessage());
                return false;
            }

            // Process the certificate data

            boolean isValid = processCertificateData(decodedData, context);
            return isValid;
        }
            catch(Exception e ) {
            log.error("Error verifying certificate license: {}", e.getMessage(), e);
            return false;
        }
    }

private static String normalizeArch(String value) {
   value = normalize(value);
   if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
    return "x86_64";
   }

   if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
    return "x86_32";
   }

   if (value.matches("^(ia64w?|itanium64)$")) {
    return "itanium_64";
   }

   if ("ia64n".equals(value)) {
    return "itanium_32";
   }

   if (value.matches("^(sparc|sparc32)$")) {
    return "sparc_32";
   }

   if (value.matches("^(sparcv9|sparc64)$")) {
    return "sparc_64";
   }

   if (value.matches("^(arm|arm32)$")) {
    return "arm_32";
   }

   if ("aarch64".equals(value)) {
    return "aarch_64";
   }

   if (value.matches("^(mips|mips32)$")) {
    return "mips_32";
   }

   if (value.matches("^(mipsel|mips32el)$")) {
    return "mipsel_32";
   }

   if ("mips64".equals(value)) {
    return "mips_64";
   }

   if ("mips64el".equals(value)) {
    return "mipsel_64";
   }

   if (value.matches("^(ppc|ppc32)$")) {
    return "ppc_32";
   }

   if (value.matches("^(ppcle|ppc32le)$")) {
    return "ppcle_32";
   }

   if ("ppc64".equals(value)) {
    return "ppc_64";
   }

   if ("ppc64le".equals(value)) {
    return "ppcle_64";
   }

   if ("s390".equals(value)) {
    return "s390_32";
   }

   if ("s390x".equals(value)) {
    return "s390_64";
   }
   return value;
}

void startMission(DownloadMission mission) {
    synchronized(this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        // create metadata file
        while(true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile()) throw new RuntimeException("Cant create download metadata file");
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }
        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);
        if (mission.storage == null) {
            // noting to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) mission.errObject = new IOException("DownloadMission.storage == NULL");
            return;
        }
        boolean start = !mPrefQueueLimit|| getRunningMissionsCount() < 1;
        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

