public static void addImageToDocument(PDDocument doc,
    PDImageXObject image,
    String fitOption, boolean autoRotate) throws IOException {
    boolean imageIsLandscape = image.getWidth() > image.getHeight();
    PDRectangle pageSize = PDRectangle.A4;
    if (autoRotate && imageIsLandscape) {
        pageSize = new PDRectangle(pageSize.getHeight(), pageSize.getWidth());
    }
    if ("fitDocumentToImage".equals(fitOption)) {
        pageSize = new PDRectangle(image.getWidth(), image.getHeight());
    }

    PDPage page = new PDPage(pageSize);
    doc.addPage(page);
    float pageWidth = page.getMediaBox().getWidth();
    float pageHeight = page.getMediaBox().getHeight();
    try (PDPageContentStream contentStream = new PDPageContentStream(doc, page, AppendMode.APPEND, true, true)) {
        if ("fillPage".equals(fitOption)
            || "fitDocumentToImage".equals(fitOption)) {
            contentStream.drawImage(image, 0, 0, pageWidth, pageHeight);
        } else if ("maintainAspectRatio".equals(fitOption)) {
                   float imageAspectRatio = (float)image.getWidth()/(float)image.getHeight();
                   float pageAspectRatio = pageWidth/pageHeight;
                   float scaleFactor = 1.0f;
                   if (imageAspectRatio > pageAspectRatio) {
                       scaleFactor = pageWidth/image.getWidth();
                   } else {
                       scaleFactor = pageHeight/image.getHeight();
                   }

                   float xPos = (pageWidth - (image.getWidth()*scaleFactor))/ 2;
                   float yPos = (pageHeight - (image.getHeight()*scaleFactor))/ 2;
                   contentStream.drawImage(image, xPos, yPos, image.getWidth()*scaleFactor, image.getHeight()*scaleFactor);
               }
    }
        catch(IOException e ) {
        log.error("Error adding image to PDF", e);
        throw e;
    }
}

@Override int process(SharpStream out, SharpStream ... sources)throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    String format = getArgumentAt(0, null);
    boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");
    if (format == null|| format.equals("ttml")) {
        SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);
        try {
            writer.build(sources[0]);
        } catch (Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            return err instanceof IOException ? 1 : 8;
        }
        return OK_RESULT;
    } else if (format.equals("srt")) {
               byte[] buffer = new byte[8 * 1024];
               int read;
               while((read = sources[0].read(buffer)) > 0) {
                   out.write(buffer, 0, read);
               }
               return OK_RESULT;
           }
    throw new UnsupportedOperationException("Can't convert this subtitle, unimplemented format: " + format);
}

private Format getFormat(Double data, Short dataFormat, String dataFormatString) {

    // Might be better to separate out the n p and z formats, falling back to p when n and z are not set.
    // That however would require other code to be re factored.
    // String[] formatBits = formatStrIn.split(";");
    // int i = cellValue > 0.0 ? 0 : cellValue < 0.0 ? 1 : 2;
    // String formatStr = (i < formatBits.length) ? formatBits[i] : formatBits[0];
    String formatStr = dataFormatString;

    // Excel supports 2+ part conditional data formats, eg positive/negative/zero,
    //  or (>1000),(>0),(0),(negative). As Java doesn't handle these kinds
    //  of different formats for different ranges, just +ve/-ve, we need to
    //  handle these ourselves in a special way.
    // For now, if we detect 2+ parts, we call out to CellFormat to handle it
    // TODO Going forward, we should really merge the logic between the two classes
    if (formatStr.contains(";") && (formatStr.indexOf(';') != formatStr.lastIndexOf(';')
                                        || rangeConditionalPattern.matcher(formatStr).matches())) {
        try {
            // Ask CellFormat to get a formatter for it
            CellFormat cfmt = CellFormat.getInstance(locale, formatStr);
            // CellFormat requires callers to identify date vs not, so do so
            Object cellValueO = data;
            if (DateUtils.isADateFormat(dataFormat, formatStr) &&
                // don't try to handle Date value 0, let a 3 or 4-part format take care of it data.doubleValue() != 0.0) {
                cellValueO = DateUtils.getJavaDate(data, use1904windowing);
            }
            // Wrap and return (non-cachable - CellFormat does that)
            return new CellFormatResultWrapper(cfmt.apply(cellValueO));
        }

            catch(Exception e ) {
            LOGGER.warn("Formatting failed for format {}, falling back", formatStr, e);
        }
    }

    // See if we already have it cached
    Format format = formats.get(formatStr);
    if (format != null) {
        return format;
    }

    // Is it one of the special built in types, General or @?

    if ("General"
        .equalsIgnoreCase(formatStr)
            || "@"
                .equals(formatStr)) {
        format = getDefaultFormat();
        addFormat(formatStr, format);
        return format;
    }

    // Build a formatter, and cache it
    format = createFormat(dataFormat, formatStr);
    addFormat(formatStr, format);
    return format;
}

private static Object process(final RedisInputStream is) {
    final byte b = is.readByte();
// System.out.println("BYTE: " + (char) b);
    switch(b) {
              case PLUS_BYTE:
              return is.readLineBytes();
              case DOLLAR_BYTE: case EQUAL_BYTE:
        return processBulkReply(is);
              case ASTERISK_BYTE:
        return processMultiBulkReply(is);
              case UNDERSCORE_BYTE:
        return is.readNullCrLf();
              case HASH_BYTE:
        return is.readBooleanCrLf();
              case COLON_BYTE:
        return is.readLongCrLf();
              case COMMA_BYTE:
        return is.readDoubleCrLf();
              case LEFT_BRACE_BYTE:
        return is.readBigIntegerCrLf();
              case PERCENT_BYTE: // TODO: currently just to start working with HELLO
        return processMapKeyValueReply(is);
              case TILDE_BYTE: // TODO:
        return processMultiBulkReply(is);
              case GREATER_THAN_BYTE:
        return processMultiBulkReply(is);
              case MINUS_BYTE:
        processError(is);
        return null;
  // TODO: Blob error '!'
              default:
        throw new JedisConnectionException("Unknown reply: " + (char) b);
    }
}

private static Map<String, Object> doGetLoggerInfo(Logger logger) {
   Map<String, Object> info = new HashMap<String, Object>();
   info.put(LoggerHelper.name, logger.getName());
   info.put(LoggerHelper.clazz, logger.getClass());
   CodeSource codeSource = logger.getClass().getProtectionDomain().getCodeSource();
   if (codeSource != null) {
    info.put(LoggerHelper.codeSource, codeSource.getLocation());
   }
   info.put(LoggerHelper.additivity, logger.getAdditivity());
   Level level = logger.getLevel(), effectiveLevel = logger.getEffectiveLevel();
   if (level != null) {
    info.put(LoggerHelper.level, level.toString());
   }

   if (effectiveLevel != null) {
    info.put(LoggerHelper.effectiveLevel, effectiveLevel.toString());
   }
   @SuppressWarnings("unchecked" ) List<Map<String, Object>> result = doGetLoggerAppenders(logger.getAllAppenders());
   info.put(LoggerHelper.appenders, result);
   return info;
}

