private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();
    if (init != null&& init != currentThread && init.isAlive()) {
        init.interrupt();
        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()
    for (Thread thread : threads) {
        if (!thread.isAlive()|| thread == Thread.currentThread()) continue;
        thread.interrupt();
    }
    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) continue;
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }
            if (millis > 0) thread.join(millis);
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

@Override
public Connection getConnection() {
    List<ConnectionPool> pools = getShuffledNodesPool();
    JedisException suppressed = null;
    for(ConnectionPool pool :pools) {
        Connection jedis = null;
        try {
            jedis = pool.getResource();
            if (jedis == null) {
                continue;
            }
            jedis.ping();
            return jedis;
        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }
            if (jedis != null) {
                jedis.close();
            }
        }
    }


    JedisException noReachableNode = new JedisException("No reachable shard.");
    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }
    throw noReachableNode;
}

@Override public String toString() {
    StringBuffer sb = new StringBuffer();
    List < HystrixEventType > foundEventTypes = new ArrayList < HystrixEventType >();
    sb.append(getCommandKey().name()).append("[");
    for (HystrixEventType eventType : ALL_EVENT_TYPES) {
        if (executionResult.getEventCounts().contains(eventType)) {
            foundEventTypes.add(eventType);
        }
    }

    int i = 0;
    for (HystrixEventType eventType : foundEventTypes) {
        sb.append(eventType.name());

        int eventCount = executionResult.getEventCounts().getCount(eventType);

        if (eventCount > 1) {
            sb.append("x").append(eventCount);
        }

        if (i < foundEventTypes.size() - 1) {
            sb.append(", ");
        }

        i++;
    }

    sb.append("][").append(getExecutionLatency()).append(" ms]");
    return sb.toString();
}

