        HystrixCommandMetrics paymentInformationMetrics = HystrixCommandMetrics.getInstance(HystrixCommandKey.Factory.asKey(GetPaymentInformationCommand.class.getSimpleName()));

        if (shouldLog) {
                    // print out metrics
            StringBuilder out = new StringBuilder();
            out.append("\n");
            out.append("#####################################################################################").append("\n");
            out.append("# CreditCardCommand: " + getStatsStringFromMetrics(creditCardMetrics)).append("\n");
            out.append("# GetOrderCommand: " + getStatsStringFromMetrics(orderMetrics)).append("\n");
            out.append("# GetUserAccountCommand: " + getStatsStringFromMetrics(userAccountMetrics)).append("\n");
            out.append("# GetPaymentInformationCommand: " + getStatsStringFromMetrics(paymentInformationMetrics)).append("\n");
            out.append("#####################################################################################").append("\n");
            System.out.println(out.toString());
        }
    }
}

    @Override
    public CacheEntry set(CacheKey cacheKey,CacheEntry entry) {
        lock.lock();
        try {
            entry = putIntoStore(cacheKey, entry);
            EvictionPolicy policy = getEvictionPolicy();
            policy.touch(cacheKey);
            CacheKey evictedKey = policy.evictNext();
            if (evictedKey != null) {
                delete(evictedKey);
                stats.evict();
            }
            for(Object redisKey :cacheKey.getRedisKeys()) {
                ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);
                if (redisKeysToCacheKeys.containsKey(mapKey)) {
                    redisKeysToCacheKeys.get(mapKey).add(cacheKey);
                } else {
                    Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
                    set.add(cacheKey);
                    redisKeysToCacheKeys.put(mapKey, set);
                }
            }
            stats.load();
            return entry;
        }

finally {
            lock.unlock();
        }
    }

@ SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe @ResizeMode public static int nextResizeModeAndSaveToPrefs(final Player player, @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
        case AspectRatioFrameLayout.RESIZE_MODE_FIT:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_FILL:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
        default:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
            break;
    }

    // save the new resize mode so it can be restored in a future session
    player.getPrefs().edit().putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

private void processRequest(int limitPerDay,
    String identifier,
    Map<String, Bucket> buckets,
    HttpServletRequest request,
    HttpServletResponse response, FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill()/ 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

public static HealthCountsStream getInstance(HystrixCommandKey commandKey, int numBuckets, int bucketSizeInMs) {
   HealthCountsStream initialStream = streams.get(commandKey.name());
   if (initialStream != null) {
    return initialStream;
   } else {
     final HealthCountsStream healthStream;
     synchronized(HealthCountsStream.class) {
       HealthCountsStream existingStream = streams.get(commandKey.name());
       if (existingStream == null) {
         HealthCountsStream newStream = new HealthCountsStream(commandKey, numBuckets, bucketSizeInMs, HystrixCommandMetrics.appendEventToBucket);
         streams.putIfAbsent(commandKey.name(), newStream);
         healthStream = newStream;
       } else {
         healthStream = existingStream;
       }
     }
     healthStream.startCachingStreamValuesIfUnstarted();
     return healthStream;
   }
}

public SSLContext createSslContext() throws IOException, GeneralSecurityException {
    KeyManager[] keyManagers = null;
    TrustManager[] trustManagers = null;
    if (sslVerifyMode == SslVerifyMode.FULL) {
        this.sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
    } else if (sslVerifyMode == SslVerifyMode.CA) {
        this.sslParameters.setEndpointIdentificationAlgorithm("");
    } else if (sslVerifyMode == SslVerifyMode.INSECURE) {
               trustManagers = new TrustManager[] {
                                   INSECURE_TRUST_MANAGER};
           }
    if (keystoreResource != null) {
        KeyStore keyStore = KeyStore.getInstance(keyStoreType);
        try (InputStream keystoreStream = keystoreResource.get()) {
            keyStore.load(keystoreStream, keystorePassword);
        }

        KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(keyManagerAlgorithm);
        keyManagerFactory.init(keyStore, keystorePassword);
        keyManagers = keyManagerFactory.getKeyManagers();
    }
    if (trustManagers == null && truststoreResource != null) {
        KeyStore trustStore = KeyStore.getInstance(trustStoreType);
        try (InputStream truststoreStream = truststoreResource.get()) {
            trustStore.load(truststoreStream, truststorePassword);
        }

        TrustManagerFactory trustManagerFactory = TrustManagerFactory.getInstance(trustManagerAlgorithm);
        trustManagerFactory.init(trustStore);
        trustManagers = trustManagerFactory.getTrustManagers();
    }

    SSLContext sslContext = SSLContext.getInstance(sslProtocol);
    sslContext.init(keyManagers, trustManagers, null);
    return sslContext;
}

private void addJavaObjectToExcel(Object oneRowData, Row row, int rowIndex, int relativeRowIndex) {
    WriteHolder currentWriteHolder = writeContext.currentWriteHolder();
    BeanMap beanMap = BeanMapUtils.create(oneRowData);
    // Bean the contains of the Map Key method with poor performance,So to create a keySet here
    Set<String> beanKeySet = new HashSet <>(beanMap.keySet());
    Set<String> beanMapHandledSet = new HashSet <>();
    int maxCellIndex = - 1;
    // If it's a class it needs to be cast by type
    if (HeadKindEnum.CLASS.equals(writeContext.currentWriteHolder().excelWriteHeadProperty()
                                                                   .getHeadKind())) {
        Map<Integer, Head> headMap = writeContext.currentWriteHolder().excelWriteHeadProperty()
                                                                      .getHeadMap();
        for(Map.Entry<Integer, Head> entry :headMap.entrySet()) {
            int columnIndex = entry.getKey();
            Head head = entry.getValue();
            String name = head.getFieldName();
            if (!beanKeySet.contains(name)) {
                continue;
            }
            ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), name, currentWriteHolder);
            CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(writeContext, row, rowIndex, head, columnIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);
            WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);
            Cell cell = WorkBookUtil.createCell(row, columnIndex);
            cellWriteHandlerContext.setCell(cell);
            WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);
            cellWriteHandlerContext.setOriginalValue(beanMap.get(name));
            cellWriteHandlerContext.setOriginalFieldClass(head.getField().getType());
            converterAndSet(cellWriteHandlerContext);
            WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);
            beanMapHandledSet.add(name);
            maxCellIndex = Math.max(maxCellIndex, columnIndex);
        }
    }
    // Finish

    if (beanMapHandledSet.size() == beanMap.size()) {
        return;
    }
    maxCellIndex++;
    FieldCache fieldCache = ClassUtils.declaredFields(oneRowData.getClass(), writeContext.currentWriteHolder());
    for(Map.Entry<Integer, FieldWrapper> entry :fieldCache.getSortedFieldMap().entrySet()) {
        FieldWrapper field = entry.getValue();
        String fieldName = field.getFieldName();
        boolean uselessData = !beanKeySet.contains(fieldName)
        || beanMapHandledSet.contains(fieldName);
        if (uselessData) {
            continue;
        }
        Object value = beanMap.get(fieldName);
        ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), fieldName, currentWriteHolder);
        CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(writeContext, row, rowIndex, null, maxCellIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);
        WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);

        // fix https://github.com/alibaba/easyexcel/issues/1870
        // If there is data, it is written to the next cell
        Cell cell = WorkBookUtil.createCell(row, maxCellIndex);
        cellWriteHandlerContext.setCell(cell);
        WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);
        cellWriteHandlerContext.setOriginalValue(value);
        cellWriteHandlerContext.setOriginalFieldClass(FieldUtils.getFieldClass(beanMap, fieldName, value));
        converterAndSet(cellWriteHandlerContext);
        WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);
        maxCellIndex++;
    }
}

private Observable < R > handleRequestCacheHitAndEmitValues(final HystrixCommandResponseFromCache < R > fromCache, final AbstractCommand < R > _cmd) {
    try {
        executionHook.onCacheHit(this);
    } catch (Throwable hookEx) {
        logger.warn("Error calling HystrixCommandExecutionHook.onCacheHit", hookEx);
    }

    return fromCache.toObservableWithStateCopiedInto(this).doOnTerminate(new Action0() {
                                                                             @Override public void call() {
                                                                                 if (commandState.compareAndSet(CommandState.OBSERVABLE_CHAIN_CREATED, CommandState.TERMINAL)) {
                                                                                     cleanUpAfterResponseFromCache(false); //user code never ran
                                                                                 } else if (commandState.compareAndSet(CommandState.USER_CODE_EXECUTED, CommandState.TERMINAL)) {
                                                                                            cleanUpAfterResponseFromCache(true); //user code did run
                                                                                        }
                                                                             }

                                                                         }).doOnUnsubscribe(new Action0() {
                                                                                                @Override public void call() {
                                                                                                    if (commandState.compareAndSet(CommandState.OBSERVABLE_CHAIN_CREATED, CommandState.UNSUBSCRIBED)) {
                                                                                                        cleanUpAfterResponseFromCache(false); //user code never ran
                                                                                                    } else if (commandState.compareAndSet(CommandState.USER_CODE_EXECUTED, CommandState.UNSUBSCRIBED)) {
                                                                                                               cleanUpAfterResponseFromCache(true); //user code did run
                                                                                                           }
                                                                                                }

                                                                                            });
}

