void _processDo(int option) throws IOException {

if (debugoptions) {
    System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
}

if (__notifhand != null) {
    __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
}
boolean acceptNewState = false;


    /* open TelnetOptionHandler functionality (start)*/
if (optionHandlers[option] != null) {
    acceptNewState = optionHandlers[option].getAcceptLocal();
} else {
    /* open TelnetOptionHandler functionality (end)*/
        /* TERMINAL-TYPE option (start)*/

  if (option == TERMINAL_TYPE) {

           if ((terminalType != null) && (terminalType.length() > 0)) {
                                   acceptNewState = true;
           }
  }
        /* TERMINAL-TYPE option (end)*/
    /* open TelnetOptionHandler functionality (start)*/
}
    /* open TelnetOptionHandler functionality (end)*/

if (_willResponse[option] > 0) {
    -- _willResponse[option];
    if (_willResponse[option] > 0 && _stateIsWill(option)) {
                                   -- _willResponse[option];
    }
}

if (_willResponse[option] == 0) {

    if (_requestedWont(option)) {
    switch (option) {
        default:
        break;
    }

    if (acceptNewState) {
        _setWantWill(option);
        _sendWill(option);
    } else {
      ++ _willResponse[option];
      _sendWont(option);
    }
    } else {
            // Other end has acknowledged option.
      switch (option) {
        default:
          break;
      }
    }
}
_setWill(option);
}

@ StyleRes public static int getSettingsThemeStyle(final Context context) {
    final Resources res = context.getResources();
    final String lightTheme = res.getString(R.string.light_theme_key);
    final String blackTheme = res.getString(R.string.black_theme_key);
    final String automaticDeviceTheme = res.getString(R.string.auto_device_theme_key);
    final String selectedTheme = getSelectedThemeKey(context);
    if (selectedTheme.equals(lightTheme)) {
        return R.style.LightSettingsTheme;
    } else if (selectedTheme.equals(blackTheme)) {
               return R.style.BlackSettingsTheme;
           } else if (selectedTheme.equals(automaticDeviceTheme)) {
                      if (isDeviceDarkThemeEnabled(context)) {
            // use the dark theme variant preferred by the user
                          final String selectedNightTheme = getSelectedNightThemeKey(context);
                          if (selectedNightTheme.equals(blackTheme)) {
                              return R.style.BlackSettingsTheme;
                          } else {
                              return R.style.DarkSettingsTheme;
                          }
                      } else {
            // there is only one day theme
                          return R.style.LightSettingsTheme;
                      }
                  } else {
        // default to dark theme
                      return R.style.DarkSettingsTheme;
                  }
}

@Override
public TSInfo build(Object data) {
    List<KeyValue> list = (List<KeyValue>) data;
    Map<String, Object> properties = new HashMap< >();
    Map<String, String> labels = null;
    Map<String, Rule> rules = null;
    List<Map<String, Object>> chunks = null;
    for (KeyValue propertyValue : list) {
        String prop = BuilderFactory.STRING.build(propertyValue.getKey());
        Object value = propertyValue.getValue();
        if (value instanceof List) {
            switch(prop) {
                         case LABELS_PROPERTY:
                         labels = BuilderFactory.STRING_MAP.build(value);
                         value = labels;
                         break;
                         case RULES_PROPERTY:
                List<KeyValue> rulesDataList = (List<KeyValue>) value;
                Map<String, List<Object>> rulesValueMap = new HashMap< >(rulesDataList.size(), 1f);
                rules = new HashMap< >(rulesDataList.size());
                for (KeyValue rkv : rulesDataList) {
                    String ruleName = BuilderFactory.STRING.build(rkv.getKey());
                    List<Object> ruleValueList = BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());
                    rulesValueMap.put(ruleName, ruleValueList);
                    rules.put(ruleName, new Rule(ruleName, ruleValueList));
                }

                value = rulesValueMap;
                break;
                         case CHUNKS_PROPERTY:
                List<List<KeyValue>> chunksDataList = (List<List<KeyValue>>) value;
                List<Map<String, Object>> chunksValueList = new ArrayList< >(chunksDataList.size());
                chunks = new ArrayList< >(chunksDataList.size());
                for (List<KeyValue> chunkDataAsList : chunksDataList) {
                    Map<String, Object> chunk = chunkDataAsList.stream().collect(Collectors.toMap(kv->BuilderFactory.STRING.build(kv.getKey()), kv->BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));
                    chunksValueList.add(chunk);
                    chunks.add(chunk);
                }

                value = chunksValueList;
                break;
                         default:
                value = SafeEncoder.encodeObject(value);
                break;
            }
        } else if (value instanceof byte[]) {
                   value = BuilderFactory.STRING.build(value);
                   if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
                       try {
                           value = DuplicatePolicy.valueOf(((String) value).toUpperCase());
                       } catch (Exception e) {

                       }
                   }
               }

        properties.put(prop, value);
    }

    return new TSInfo(properties, labels, rules, chunks);
}

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe @ResizeMode public static int nextResizeModeAndSaveToPrefs(final Player player, @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
        case AspectRatioFrameLayout.RESIZE_MODE_FIT:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_FILL:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
        default:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
            break;
    }

    // save the new resize mode so it can be restored in a future session

    player.getPrefs().edit().putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

private static void serializeUtilization(HystrixUtilization utilization, JsonGenerator json) {
    try {
        json.writeStartObject();
        json.writeStringField("type", "HystrixUtilization");
        json.writeObjectFieldStart("commands");
        for (Map.Entry<HystrixCommandKey, HystrixCommandUtilization> entry : utilization.getCommandUtilizationMap().entrySet()) {
            final HystrixCommandKey key = entry.getKey();
            final HystrixCommandUtilization commandUtilization = entry.getValue();
            writeCommandUtilizationJson(json, key, commandUtilization);
        }

        json.writeEndObject();
        json.writeObjectFieldStart("threadpools");
        for (Map.Entry<HystrixThreadPoolKey, HystrixThreadPoolUtilization> entry : utilization.getThreadPoolUtilizationMap().entrySet()) {
            final HystrixThreadPoolKey threadPoolKey = entry.getKey();
            final HystrixThreadPoolUtilization threadPoolUtilization = entry.getValue();
            writeThreadPoolUtilizationJson(json, threadPoolKey, threadPoolUtilization);
        }

        json.writeEndObject();
        json.writeEndObject();
        json.close();
    } catch (Exception e) {
        throw new RuntimeException(e);
    }
}

public Map<ExecutionSignature, List<Integer>> getExecutionsMappedToLatencies() {
   Map<CommandAndCacheKey, Integer> cachingDetector = new HashMap<CommandAndCacheKey, Integer>();
   List<HystrixInvokableInfo<?>> nonCachedExecutions = new ArrayList<HystrixInvokableInfo<?>>(executions.size());

   for (HystrixInvokableInfo<?> execution : executions) {

    if (execution.getPublicCacheKey() != null) {
            //eligible for caching - might be the initial, or might be from cache
                                                            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), execution.getPublicCacheKey());
                                                            Integer count = cachingDetector.get(key);
                                                            if (count != null) {
                //key already seen
        cachingDetector.put(key, count + 1);
                                                            } else {
                //key not seen yet
                                                              cachingDetector.put(key, 0);
                                                            }
    }

    if (!execution.isResponseFromCache()) {
                                                            nonCachedExecutions.add(execution);
    }
   }
   Map<ExecutionSignature, List<Integer>> commandDeduper = new HashMap<ExecutionSignature, List<Integer>>();

   for (HystrixInvokableInfo<?> execution : nonCachedExecutions) {
    int cachedCount = 0;
    String cacheKey = execution.getPublicCacheKey();
    if (cacheKey != null) {
                                                                     CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), cacheKey);
                                                                     cachedCount = cachingDetector.get(key);
    }
    ExecutionSignature signature;
    if (cachedCount > 0) {
            //this has a RESPONSE_FROM_CACHE and needs to get split off
       signature = ExecutionSignature.from(execution, cacheKey, cachedCount);
    } else {
            //nothing cached from this, can collapse further
      signature = ExecutionSignature.from(execution);
    }
    List<Integer> currentLatencyList = commandDeduper.get(signature);
    if (currentLatencyList != null) {
       currentLatencyList.add(execution.getExecutionTimeInMilliseconds());
    } else {
      List<Integer> newLatencyList = new ArrayList<Integer>();
      newLatencyList.add(execution.getExecutionTimeInMilliseconds());
      commandDeduper.put(signature, newLatencyList);
    }
   }
   return commandDeduper;
}

public CellStyle createCellStyle(WriteCellStyle writeCellStyle, CellStyle originCellStyle) {
    if (writeCellStyle == null) {
        return originCellStyle;
    }

    short styleIndex = - 1;
    Font originFont = null;
    boolean useCache = true;
    if (originCellStyle != null) {
        styleIndex = originCellStyle.getIndex();
        if (originCellStyle instanceof XSSFCellStyle) {
            originFont = ((XSSFCellStyle) originCellStyle).getFont();
        } else if (originCellStyle instanceof HSSFCellStyle) {
                   originFont = ((HSSFCellStyle) originCellStyle).getFont(workbook);
               }
        useCache = false;
    }
    Map<WriteCellStyle, CellStyle> cellStyleMap = cellStyleIndexMap.computeIfAbsent(styleIndex, key -> MapUtils.newHashMap());
    CellStyle cellStyle = cellStyleMap.get(writeCellStyle);
    if (cellStyle != null) {
        return cellStyle;
    }

    if (log.isDebugEnabled()) {
        log.info("create new style:{},{}", writeCellStyle, originCellStyle);
    }
    WriteCellStyle tempWriteCellStyle = new WriteCellStyle();
    WriteCellStyle.merge(writeCellStyle, tempWriteCellStyle);
    cellStyle = StyleUtil.buildCellStyle(workbook, originCellStyle, tempWriteCellStyle);
    Short dataFormat = createDataFormat(tempWriteCellStyle.getDataFormatData(), useCache);
    if (dataFormat != null) {
        cellStyle.setDataFormat(dataFormat);
    }
    Font font = createFont(tempWriteCellStyle.getWriteFont(), originFont, useCache);
    if (font != null) {
        cellStyle.setFont(font);
    }
    cellStyleMap.put(tempWriteCellStyle, cellStyle);
    return cellStyle;
}

@ Override public Bitmap transform(final Bitmap source) {
    if (DEBUG) {
        Log.d(TAG, "Thumbnail - transform() called");
    }

    final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
    final Bitmap result = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth, (int) (source.getHeight()/(source.getWidth()/notificationThumbnailWidth)), null, true);
    if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
        final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth - 1, (int) (source.getHeight()/(source.getWidth()/(notificationThumbnailWidth - 1))), null, true);
        source.recycle();
        return copied;
    } else {
        source.recycle();
        return result;
    }
}

