private static void handleError(final Context context, final ErrorInfo errorInfo) {
    if (errorInfo.getThrowable() != null) {
        errorInfo.getThrowable().printStackTrace();
    }
    if (errorInfo.getThrowable() instanceof ReCaptchaException) {
        Toast.makeText(context, R.string.recaptcha_request_toast, Toast.LENGTH_LONG).show();
        // Starting ReCaptcha Challenge Activity
        final Intent intent = new Intent(context, ReCaptchaActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        context.startActivity(intent);
    } else if (errorInfo.getThrowable() != null && ExceptionUtils.isNetworkRelated(errorInfo.getThrowable())) {
               Toast.makeText(context, R.string.network_error, Toast.LENGTH_LONG).show();
           } else if (errorInfo.getThrowable() instanceof AgeRestrictedContentException) {
                      Toast.makeText(context, R.string.restricted_video_no_stream, Toast.LENGTH_LONG).show();
                  } else if (errorInfo.getThrowable() instanceof GeographicRestrictionException) {
                             Toast.makeText(context, R.string.georestricted_content, Toast.LENGTH_LONG).show();
                         } else if (errorInfo.getThrowable() instanceof PaidContentException) {
                                    Toast.makeText(context, R.string.paid_content, Toast.LENGTH_LONG).show();
                                } else if (errorInfo.getThrowable() instanceof PrivateContentException) {
                                           Toast.makeText(context, R.string.private_content, Toast.LENGTH_LONG).show();
                                       } else if (errorInfo.getThrowable() instanceof SoundCloudGoPlusContentException) {
                                                  Toast.makeText(context, R.string.soundcloud_go_plus_content, Toast.LENGTH_LONG).show();
                                              } else if (errorInfo.getThrowable() instanceof YoutubeMusicPremiumContentException) {
                                                         Toast.makeText(context, R.string.youtube_music_premium_content, Toast.LENGTH_LONG).show();
                                                     } else if (errorInfo.getThrowable() instanceof ContentNotAvailableException) {
                                                                Toast.makeText(context, R.string.content_not_available, Toast.LENGTH_LONG).show();
                                                            } else if (errorInfo.getThrowable() instanceof ContentNotSupportedException) {
                                                                       Toast.makeText(context, R.string.content_not_supported, Toast.LENGTH_LONG).show();
                                                                   } else {
                                                                       ErrorUtil.createNotification(context, errorInfo);
                                                                   }
    if (context instanceof RouterActivity) {
        ((RouterActivity)context).finish();
    }
}

private void buildHead(AnalysisContext analysisContext, Map<Integer, ReadCellData <?>> cellDataMap) {
    // Rule out empty head, and then take the largest column
    if (MapUtils.isNotEmpty(cellDataMap)) {
        cellDataMap.entrySet().stream()
            .filter(entry -> CellDataTypeEnum.EMPTY != entry.getValue().getType()).forEach(entry -> analysisContext.readSheetHolder().setMaxNotEmptyDataHeadSize(entry.getKey()));
    }

    if (!HeadKindEnum.CLASS.equals(analysisContext.currentReadHolder().excelReadHeadProperty()
                                                                      .getHeadKind())) {
        return;
    }
    Map<Integer, String> dataMap = ConverterUtils.convertToStringMap(cellDataMap, analysisContext);
    ExcelReadHeadProperty excelHeadPropertyData = analysisContext.readSheetHolder().excelReadHeadProperty();
    Map<Integer, Head> headMapData = excelHeadPropertyData.getHeadMap();
    Map<Integer, Head> tmpHeadMap = new HashMap<Integer, Head>(headMapData.size()* 4 / 3 + 1);
    for(Map.Entry<Integer, Head> entry :headMapData.entrySet()) {
        Head headData = entry.getValue();
        if (headData.getForceIndex()
                || !headData.getForceName()) {
            tmpHeadMap.put(entry.getKey(), headData);
            continue;
        }
        List<String> headNameList = headData.getHeadNameList();
        String headName = headNameList.get(headNameList.size() - 1);
        for(Map.Entry<Integer, String> stringEntry :dataMap.entrySet()) {
            if (stringEntry == null) {
                continue;
            }
            String headString = stringEntry.getValue();
            Integer stringKey = stringEntry.getKey();
            if (StringUtils.isEmpty(headString)) {
                continue;
            }

            if (analysisContext.currentReadHolder().globalConfiguration()
                                                   .getAutoTrim()) {
                headString = headString.trim();
            }

            if (headName.equals(headString)) {
                headData.setColumnIndex(stringKey);
                tmpHeadMap.put(stringKey, headData);
                break;
            }
        }
    }
    excelHeadPropertyData.setHeadMap(tmpHeadMap);
}

public ThreadPoolExecutor getThreadPool(final HystrixThreadPoolKey threadPoolKey, HystrixThreadPoolProperties threadPoolProperties) {
final ThreadFactory threadFactory = getThreadFactory(threadPoolKey);
final boolean allowMaximumSizeToDivergeFromCoreSize = threadPoolProperties.getAllowMaximumSizeToDivergeFromCoreSize().get();
final int dynamicCoreSize = threadPoolProperties.coreSize().get();
final int keepAliveTime = threadPoolProperties.keepAliveTimeMinutes().get();
final int maxQueueSize = threadPoolProperties.maxQueueSize().get();
final BlockingQueue<Runnable> workQueue = getBlockingQueue(maxQueueSize);
if (allowMaximumSizeToDivergeFromCoreSize) {
    final int dynamicMaximumSize = threadPoolProperties.maximumSize().get();
    if (dynamicCoreSize > dynamicMaximumSize) {
    logger.error("Hystrix ThreadPool configuration at startup for : " + threadPoolKey.name() + " is trying to set coreSize = " + dynamicCoreSize + " and maximumSize = " + dynamicMaximumSize + ".  Maximum size will be set to " + dynamicCoreSize + ", the coreSize value, since it must be equal to or greater than the coreSize value");
    return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
    } else {
      return new ThreadPoolExecutor(dynamicCoreSize, dynamicMaximumSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
    }
} else {
  return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
}
}

private void bind(Configure configure) throws Throwable {
long start = System.currentTimeMillis();
if (!isBindRef.compareAndSet(false, true)) {
    throw new IllegalStateException("already bind");
}

    // init random port

if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
    int newTelnetPort = SocketUtils.findAvailableTcpPort();
    configure.setTelnetPort(newTelnetPort);
    logger().info("generate random telnet port: " + newTelnetPort);
}

if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
    int newHttpPort = SocketUtils.findAvailableTcpPort();
    configure.setHttpPort(newHttpPort);
    logger().info("generate random http port: " + newHttpPort);
}
    // try to find appName

if (configure.getAppName() == null) {
    configure.setAppName(System.getProperty(ArthasConstants.PROJECT_NAME, System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
}

try {

    if (configure.getTunnelServer() != null) {
    tunnelClient = new TunnelClient();
    tunnelClient.setAppName(configure.getAppName());
    tunnelClient.setId(configure.getAgentId());
    tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
    tunnelClient.setVersion(ArthasBanner.version());
    ChannelFuture channelFuture = tunnelClient.start();
    channelFuture.await(10, TimeUnit.SECONDS);
    }
} catch (Throwable t) {
  logger().error("start tunnel client error", t);
}

try {
    ShellServerOptions options = new ShellServerOptions().setInstrumentation(instrumentation).setPid(PidUtils.currentLongPid()).setWelcomeMessage(ArthasBanner.welcome());
    if (configure.getSessionTimeout() != null) {
    options.setSessionTimeout(configure.getSessionTimeout() * 1000);
    }

    this.httpSessionManager = new HttpSessionManager();
    if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
            //  listen 0.0.0.0 
    String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! " + "No password is currently configured. " + "Therefore, a default password is generated, " + "and clients need to use the password to connect!";
    AnsiLog.error(errorMsg);
    configure.setPassword(StringUtils.randomString(64));
    AnsiLog.error("Generated arthas password: " + configure.getPassword());
    logger().error(errorMsg);
    logger().info("Generated arthas password: " + configure.getPassword());
    }

    this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());
    shellServer = new ShellServerImpl(options);
    List<String> disabledCommands = new ArrayList<String>();
    if (configure.getDisabledCommands() != null) {
    String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
    if (strings != null) {
                                                     disabledCommands.addAll(Arrays.asList(strings));
    }
    }
    BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
    List<CommandResolver> resolvers = new ArrayList<CommandResolver>();
    resolvers.add(builtinCommands);

        //worker group
    workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));

        // TODO: discover user provided command resolver
    if (configure.getTelnetPort() != null && configure.getTelnetPort() > 0) {
    logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
    shellServer.registerTermServer(new HttpTelnetTermServer(configure.getIp(), configure.getTelnetPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } else {
      logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
    }

    if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
    logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
    shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } else {
            // listen local address in VM communication

      if (configure.getTunnelServer() != null) {
               shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
      }
      logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
    }

    for (CommandResolver resolver : resolvers) {
    shellServer.registerCommandResolver(resolver);
    }
    shellServer.listen(new BindHandler(isBindRef));
    if (!isBind()) {
    throw new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: " + String.valueOf(configure.getTelnetPort()) + ", http port: " + String.valueOf(configure.getHttpPort()));
    }

        //http api session manager
    sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());
        //http api handler
    httpApiHandler = new HttpApiHandler(historyManager, sessionManager);
    logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(), configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());

        // 
    if (configure.getStatUrl() != null) {
    logger().info("arthas stat url: {}", configure.getStatUrl());
    }
    UserStatUtil.setStatUrl(configure.getStatUrl());
    UserStatUtil.setAgentId(configure.getAgentId());
    UserStatUtil.arthasStart();
    try {
    SpyAPI.init();
    } catch (Throwable e) {
            // ignore }
    logger().info("as-server started in {} ms", System.currentTimeMillis() - start);
} catch (Throwable e) {
  logger().error("Error during start as-server", e);
  destroy();
  throw e;
}
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart == null) return false;
    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }

        // Additional check for MimeMultipart
        try {
            Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }
        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

public static void openVideoDetailFragment(@NonNull final Context context,
    @ NonNull final FragmentManager fragmentManager,
    final int serviceId,
    @ Nullable final String url,
    @ NonNull final String title,
    @ Nullable final PlayQueue playQueue, final boolean switchingPlayers) {
    final boolean autoPlay;
    @ Nullable final PlayerType playerType = PlayerHolder.getInstance().getType();
    if (playerType == null) {
        // no player open
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else if (switchingPlayers) {
        // switching player to main player
               autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
           } else if (playerType == PlayerType.MAIN) {
        // opening new stream while already playing in main player
                      autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
                  } else {
        // opening new stream while already playing in another player
                      autoPlay = false;
                  }

    final RunnableWithVideoDetailFragment onVideoDetailFragmentReady = detailFragment -> {
                                                                           expandMainPlayer(detailFragment.requireActivity());
                                                                           detailFragment.setAutoPlay(autoPlay);
                                                                           if (switchingPlayers) {
            // Situation when user switches from players to main player. All needed data is
            // here, we can start watching (assuming newQueue equals playQueue).
            // Starting directly in fullscreen if the previous player type was popup.
                                                                               detailFragment.openVideoPlayer(playerType == PlayerType.POPUP || PlayerHelper.isStartMainPlayerFullscreenEnabled(context));
                                                                           } else {
                                                                               detailFragment.selectAndLoadVideo(serviceId, url, title, playQueue);
                                                                           }
                                                                           detailFragment.scrollToTop();
                                                                       };
    final Fragment fragment = fragmentManager.findFragmentById(R.id.fragment_player_holder);
    if (fragment instanceof VideoDetailFragment && fragment.isVisible()) {
        onVideoDetailFragmentReady.run((VideoDetailFragment)fragment);
    } else {
        // Specify no url here, otherwise the VideoDetailFragment will start loading the
        // stream automatically if it's the first time it is being opened, but then
        // onVideoDetailFragmentReady will kick in and start another loading process.
        // See VideoDetailFragment.wasCleared() and its usage in doInitialLoadLogic().
        final VideoDetailFragment instance = VideoDetailFragment.getInstance(serviceId, null, title, playQueue);
        instance.setAutoPlay(autoPlay);
        defaultTransaction(fragmentManager).replace(R.id.fragment_player_holder, instance).runOnCommit(() -> onVideoDetailFragmentReady.run(instance)).commit();
    }
}

@Override
public Connection getConnection() {
    List<ConnectionPool> pools = getShuffledNodesPool();
    JedisException suppressed = null;
    for (ConnectionPool pool : pools) {
        Connection jedis = null;
        try {
            jedis = pool.getResource();
            if (jedis == null) {
                continue;
            }

            jedis.ping();
            return jedis;
        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }
            if (jedis != null) {
                jedis.close();
            }
        }
    }

    JedisException noReachableNode = new JedisException("No reachable shard.");
    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }

    throw noReachableNode;
}

