private JsonNode validateLicense(String licenseKey, String machineFingerprint, LicenseContext context)throws Exception {
    String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}", licenseKey, machineFingerprint);
    HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key")).header("Content-Type", "application/vnd.api+json").header("Accept", "application/vnd.api+json")
                    // .header("Authorization", "License " + licenseKey).POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
    HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", response.body());
    JsonNode jsonResponse = objectMapper.readTree(response.body());
    if (response.statusCode() == 200) {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();
        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();
        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);

        // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
            context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
            context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);
            log.info("License floating (from license): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }

        // Also check the policy for floating license support if included
        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;
        if (includedNode.isArray()) {
            for (JsonNode node : includedNode) {
                if ("policies".equals(node.path("type").asText())) {
                    policyNode = node;
                    break;
                }
            }
        }
        if (policyNode != null) {
            // Check if this is a floating license from policy
            boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
            int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);

            // Policy takes precedence over license attributes
            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
            }
            log.info("License floating (from policy): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }

        // Extract user count, default to 1 if not specified
        int users = jsonResponse.path("data").path("attributes").path("metadata").path("users").asInt(1);
        applicationProperties.getPremium().setMaxUsers(users);

        // Extract isEnterprise flag
        context.isEnterpriseLicense = jsonResponse.path("data").path("attributes").path("metadata").path("isEnterprise").asBoolean(false);
        log.debug(applicationProperties.toString());
    } else {
        log.error("Error validating license. Status code: {}", response.statusCode());
    }
    return jsonResponse;
}

protected static String convertToJson(HystrixUtilization utilization) throws IOException {
   StringWriter jsonString = new StringWriter();
   JsonGenerator json = jsonFactory.createGenerator(jsonString);
   json.writeStartObject();
   json.writeStringField("type", "HystrixUtilization");
   json.writeObjectFieldStart("commands");
   for (Map.Entry<HystrixCommandKey, HystrixCommandUtilization> entry : utilization.getCommandUtilizationMap().entrySet()) {
    final HystrixCommandKey key = entry.getKey();
    final HystrixCommandUtilization commandUtilization = entry.getValue();
    writeCommandUtilizationJson(json, key, commandUtilization);
   }
   json.writeEndObject();
   json.writeObjectFieldStart("threadpools");
   for (Map.Entry<HystrixThreadPoolKey, HystrixThreadPoolUtilization> entry : utilization.getThreadPoolUtilizationMap().entrySet()) {
    final HystrixThreadPoolKey threadPoolKey = entry.getKey();
    final HystrixThreadPoolUtilization threadPoolUtilization = entry.getValue();
    writeThreadPoolUtilizationJson(json, threadPoolKey, threadPoolUtilization);
   }
   json.writeEndObject();
   json.writeEndObject();
   json.close();
   return jsonString.getBuffer().toString();
}

private int __read(boolean mayBlock) throws IOException {
int ch;
while (true) {

        // If there is no more data AND we were told not to block,
        // just return WOULD_BLOCK (-2). (More efficient than exception.)

    if (!mayBlock && super.available() == 0) {
                 return WOULD_BLOCK;
    }

        // Otherwise, exit only when we reach end of stream.

    if ((ch = super.read()) < 0) {
                 return EOF;
    }
    ch = (ch&0xff);

        /* Code Section added for supporting AYT (start)*/
    synchronized(__client) {
    __client._processAYTResponse();
    }
        /* Code Section added for supporting AYT (end)*/

        /* Code Section added for supporting spystreams (start)*/
    __client._spyRead(ch);
        /* Code Section added for supporting spystreams (end)*/
    switch (__receiveState) {
    case _STATE_CR:
        if (ch == '\0') {
                // Strip null
        continue;
        }
            // How do we handle newline after cr?
            //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&

            // Handle as normal data by falling through to _STATE_DATA case

            //$FALL-THROUGH$
    case _STATE_DATA:
        if (ch == TelnetCommand.IAC) {
        __receiveState = _STATE_IAC;
        continue;
        }

        if (ch == '\r') {
        synchronized(__client) {

            if (__client._requestedDont(TelnetOption.BINARY)) {
            __receiveState = _STATE_CR;
            } else {
              __receiveState = _STATE_DATA;
            }
        }
        } else {
          __receiveState = _STATE_DATA;
        }

        break;
    case _STATE_IAC:
        switch (ch) {
        case TelnetCommand.WILL:
            __receiveState = _STATE_WILL;
            continue;
        case TelnetCommand.WONT:
            __receiveState = _STATE_WONT;
            continue;
        case TelnetCommand.DO:
            __receiveState = _STATE_DO;
            continue;
        case TelnetCommand.DONT:
            __receiveState = _STATE_DONT;
            continue;
            /* TERMINAL-TYPE option (start)*/
        case TelnetCommand.SB:
            __suboption_count = 0;
            __receiveState = _STATE_SB;
            continue;
            /* TERMINAL-TYPE option (end)*/
        case TelnetCommand.IAC:
            __receiveState = _STATE_DATA;
            break; // exit to enclosing switch to return IAC from read
        case TelnetCommand.SE: // unexpected byte! ignore it (don't send it as a command)
            __receiveState = _STATE_DATA;
            continue;
        default:
            __receiveState = _STATE_DATA;
            __client._processCommand(ch); // Notify the user
            continue; // move on the next char
        }

        break; // exit and return from read
    case _STATE_WILL:
        synchronized(__client) {
        __client._processWill(ch);
        __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
    case _STATE_WONT:
        synchronized(__client) {
        __client._processWont(ch);
        __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
    case _STATE_DO:
        synchronized(__client) {
        __client._processDo(ch);
        __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
    case _STATE_DONT:
        synchronized(__client) {
        __client._processDont(ch);
        __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
        /* TERMINAL-TYPE option (start)*/
    case _STATE_SB:
        switch (ch) {
        case TelnetCommand.IAC:
            __receiveState = _STATE_IAC_SB;
            continue;
        default:
                // store suboption char
            if (__suboption_count < __suboption.length) {
            __suboption[__suboption_count ++] = ch;
            }

            break;
        }
        __receiveState = _STATE_SB;
        continue;
    case _STATE_IAC_SB: // IAC received during SB phase
        switch (ch) {
        case TelnetCommand.SE:
            synchronized(__client) {
            __client._processSuboption(__suboption, __suboption_count);
            __client._flushOutputStream();
            }
            __receiveState = _STATE_DATA;
            continue;
        case TelnetCommand.IAC: // De-dup the duplicated IAC
            if (__suboption_count < __suboption.length) {
            __suboption[__suboption_count ++] = ch;
            }

            break;
        default:            // unexpected byte! ignore it
            break;
        }
        __receiveState = _STATE_SB;
        continue;
        /* TERMINAL-TYPE option (end)*/
    }

    break;
}
return ch;
}

    private static String extractBasicHeader(String emlContent,String headerName) {
        try {
            String [] lines = emlContent.split("\r?\n");
            for(int i = 0;
i<lines.length;
i++) {
                String line = lines[i];
                if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
                    StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
                    // Handle multi-line headers
                    for(int j = i + 1;
j<lines.length;
j++) {
                        if (lines[j].startsWith(" ")||lines[j].startsWith("\t")) {
                            value.append(" ").append(lines[j].trim());
                        } else {
                            break;
                        }
                    }
                    // Apply MIME header decoding
                    return safeMimeDecode(value.toString());
                }
                if (line.trim().isEmpty()) break;
            }
        } catch (RuntimeException e) {
            log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
        }
        return "";
    }

@Override
public CacheEntry set(CacheKey cacheKey,CacheEntry entry) {
    lock.lock();
    try {
        entry = putIntoStore(cacheKey, entry);
        EvictionPolicy policy = getEvictionPolicy();
        policy.touch(cacheKey);
        CacheKey evictedKey = policy.evictNext();
        if (evictedKey != null) {
            delete(evictedKey);
            stats.evict();
        }

        for (Object redisKey : cacheKey.getRedisKeys()) {
            ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);
            if (redisKeysToCacheKeys.containsKey(mapKey)) {
                redisKeysToCacheKeys.get(mapKey).add(cacheKey);
            } else {
                Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
                set.add(cacheKey);
                redisKeysToCacheKeys.put(mapKey, set);
            }
        }

        stats.load();
        return entry;
    } finally {
        lock.unlock();
    }
}

@ Override public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);
    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT)
           || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);
    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages

    final List<Tracks.Group> textTracks = currentTracks.getGroups().stream().filter(trackGroupInfo -> C.TRACK_TYPE_TEXT == trackGroupInfo.getType()).collect(Collectors.toList());
    final List<String> availableLanguages = textTracks.stream().map(Tracks.Group::getMediaTrackGroup).filter(textTrack -> textTrack.length > 0).map(textTrack -> textTrack.getFormat(0).language).collect(Collectors.toList());

    // Find selected text track
    final Optional<Format> selectedTracks = textTracks.stream().filter(Tracks.Group::isSelected).filter(info -> info.getMediaTrackGroup().length >= 1).map(info -> info.getMediaTrackGroup().getFormat(0)).findFirst();

    // Build UI
    buildCaptionMenu(availableLanguages);
    if (player.getTrackSelector().getParameters().getRendererDisabled(player.getCaptionRendererIndex())
        || selectedTracks.isEmpty()) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
        binding.captionTextView.setText(selectedTracks.get().language);
    }
    binding.captionTextView.setVisibility(availableLanguages.isEmpty()?View.GONE : View.VISIBLE);
}

