EventCounts plus(HystrixEventType eventType, int count) {
   BitSet newBitSet = (BitSet) events.clone();
   int localNumEmits = numEmissions;
   int localNumFallbackEmits = numFallbackEmissions;
   int localNumCollapsed = numCollapsed;
   switch (eventType) {
    case EMIT:
       newBitSet.set(HystrixEventType.EMIT.ordinal());
       localNumEmits += count;
       break;
    case FALLBACK_EMIT:
       newBitSet.set(HystrixEventType.FALLBACK_EMIT.ordinal());
       localNumFallbackEmits += count;
       break;
    case COLLAPSED:
       newBitSet.set(HystrixEventType.COLLAPSED.ordinal());
       localNumCollapsed += count;
       break;
    default:
       newBitSet.set(eventType.ordinal());
       break;
   }
   return new EventCounts(newBitSet, localNumEmits, localNumFallbackEmits, localNumCollapsed);
}

private static String formatFileSize(long size) {
   String hrSize;
   double b = size;
   double k = size / 1024.0;
   double m = ((size / 1024.0) / 1024.0);
   double g = (((size / 1024.0) / 1024.0) / 1024.0);
   double t = ((((size / 1024.0) / 1024.0) / 1024.0) / 1024.0);
   DecimalFormat dec = new DecimalFormat("0.00");
   if (t > 1) {
    hrSize = dec.format(t).concat(" TB");
   } else if (g > 1) {
     hrSize = dec.format(g).concat(" GB");
   } else if (m > 1) {
     hrSize = dec.format(m).concat(" MB");
          } else if (k > 1) {
            hrSize = dec.format(k).concat(" KB");
          } else {
            hrSize = dec.format(b).concat(" Bytes");
          }
   return hrSize;
}

    private static void addAttachmentAnnotationsToDocument(PDDocument document,List<EmailAttachment> attachments) throws IOException {
        if (document.getNumberOfPages() == 0||attachments == null||attachments.isEmpty()) {
            return;
        }

        // 1. Find the screen position of all attachment markers


        AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
        finder.setSortByPosition(true); // Process pages in order
        finder.getText(document);
        List<MarkerPosition> markerPositions = finder.getPositions();

        // 2. Warn if the number of markers and attachments don't match
        if (markerPositions.size() != attachments.size()) {
            log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
        }

        // 3. Create an invisible annotation over each found marker


        int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
        for(int i = 0;
i<annotationsToAdd;
i++) {
            MarkerPosition position = markerPositions.get(i);
            EmailAttachment attachment = attachments.get(i);
            if (attachment.getEmbeddedFilename() != null) {
                PDPage page = document.getPage(position.getPageIndex());
                addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
            }
        }
    }

    public static RequestCreator loadScaledDownThumbnail(final Context context, @NonNull final List<Image> images) {
        // scale down the notification thumbnail for performance
        return PicassoHelper.loadThumbnail(images).transform(new Transformation() {
                                                                 @ Override public Bitmap transform(final Bitmap source) {
                                                                     if (DEBUG) {
                                                                         Log.d(TAG, "Thumbnail - transform() called");
                                                                     }

                                                                     final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
                                                                     final Bitmap result = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth, (int) (source.getHeight()/(source.getWidth()/notificationThumbnailWidth)), null, true);
                                                                     if (result == source || !result.isMutable()) {
                            // create a new mutable bitmap to prevent strange crashes on some
                            // devices (see #4638)
                                                                         final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth - 1, (int) (source.getHeight()/(source.getWidth()/(notificationThumbnailWidth - 1))), null, true);
                                                                         source.recycle();
                                                                         return copied;
                                                                     } else {
                                                                         source.recycle();
                                                                         return result;
                                                                     }
                                                                 }
                                                                 @ Override public String key() {
                                                                     return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
                                                                 }

});
    }

@Override
public Class<?> build(Object data) {
    if (data == null) return null;
    String str = STRING.build(data);
    switch(str) {
                case "null":
                return null;
                case "boolean":
        return boolean.class;
                case "integer":
        return int.class;
                case "number":
        return float.class;
                case "string":
        return String.class;
                case "object":
        return Object.class;
                case "array":
        return List.class;
                default:
        throw new JedisException("Unknown type: " + str);
    }
}

private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {
    if (emlBytes == null || emlBytes.length == 0) {
        throw new IllegalArgumentException("EML file is empty or null");
    }

    String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

    // Basic email parsing
    String subject = extractBasicHeader(emlContent, "Subject:");
    String from = extractBasicHeader(emlContent, "From:");
    String to = extractBasicHeader(emlContent, "To:");
    String cc = extractBasicHeader(emlContent, "Cc:");
    String bcc = extractBasicHeader(emlContent, "Bcc:");
    String date = extractBasicHeader(emlContent, "Date:");

    // Try to extract HTML content
    String htmlBody = extractHtmlBody(emlContent);

    if (htmlBody == null) {
        String textBody = extractTextBody(emlContent);
        htmlBody = convertTextToHtml(textBody != null ? textBody : "Email content could not be parsed");
    }

    // Generate HTML with custom styling based on request

    StringBuilder html = new StringBuilder();
    html.append("<!DOCTYPE html>\n");
    html.append("<html><head><meta charset=\"UTF-8\">\n");
    html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
    html.append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n");
    html.append("</head><body>\n");
    html.append("<div class=\"email-container\">\n");
    html.append("<div class=\"email-header\">\n");
    html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
    html.append("<div class=\"email-meta\">\n");
    html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
    html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

    // Include CC and BCC if present and requested

    if (request != null && request.isIncludeAllRecipients()) {
        if (!cc.trim().isEmpty()) {
            html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
        }

        if (!bcc.trim().isEmpty()) {
            html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
        }
    }

    if (!date.trim().isEmpty()) {
        html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
    }

    html.append("</div></div>\n");
    html.append("<div class=\"email-body\">\n");
    html.append(processEmailHtmlBody(htmlBody));
    html.append("</div>\n");

    // Add attachment information - always check for and display attachments
    String attachmentInfo = extractAttachmentInfo(emlContent);

    if (!attachmentInfo.isEmpty()) {
        html.append("<div class=\"attachment-section\">\n");
        html.append("<h3>Attachments</h3>\n");
        html.append(attachmentInfo);

        // Add a status message about attachment inclusion

        if (request != null && request.isIncludeAttachments()) {
            html.append("<div class=\"attachment-inclusion-note\">\n");
            html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");
            html.append("</div>\n");
        } else {
            html.append("<div class=\"attachment-info-note\">\n");
            html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
            html.append("</div>\n");
        }

        html.append("</div>\n");
    }

    // Show advanced features status if requested

    assert request != null;

    if (request.getFileInput().isEmpty()) {
        html.append("<div class=\"advanced-features-notice\">\n");
        html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");
        html.append("</div>\n");
    }

    html.append("</div>\n");
    html.append("</body></html>");
    return html.toString();
}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined, ProtectionDomain protectionDomain,byte[] classfileBuffer) throws IllegalClassFormatException {

if (className == null) {
    return null;
}
className = className.replace('/', '.');
List<RetransformEntry> allRetransformEntries = allRetransformEntries();
        // 
ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
while (listIterator.hasPrevious()) {
    RetransformEntry retransformEntry = listIterator.previous();
    int id = retransformEntry.getId();
            // 
    boolean updateFlag = false;
            //  loader loader
    if (className.equals(retransformEntry.getClassName())) {

                                       if (retransformEntry.getClassLoaderClass() != null|| retransformEntry.getHashCode() != null) {
        updateFlag = isLoaderMatch(retransformEntry, loader);
                                       } else {
                                         updateFlag = true;
                                       }
    }

    if (updateFlag) {
                                       logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}", className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
                                       retransformEntry.incTransformCount();
                                       return retransformEntry.getBytes();
    }
}
return null;
}

private static void writeThreadPoolMetrics(final HystrixThreadPoolMetrics threadPoolMetrics, JsonGenerator json) throws IOException {
   HystrixThreadPoolKey key = threadPoolMetrics.getThreadPoolKey();
   json.writeStartObject();
   json.writeStringField("type", "HystrixThreadPool");
   json.writeStringField("name", key.name());
   json.writeNumberField("currentTime", System.currentTimeMillis());
   json.writeNumberField("currentActiveCount", threadPoolMetrics.getCurrentActiveCount().intValue());
   json.writeNumberField("currentCompletedTaskCount", threadPoolMetrics.getCurrentCompletedTaskCount().longValue());
   json.writeNumberField("currentCorePoolSize", threadPoolMetrics.getCurrentCorePoolSize().intValue());
   json.writeNumberField("currentLargestPoolSize", threadPoolMetrics.getCurrentLargestPoolSize().intValue());
   json.writeNumberField("currentMaximumPoolSize", threadPoolMetrics.getCurrentMaximumPoolSize().intValue());
   json.writeNumberField("currentPoolSize", threadPoolMetrics.getCurrentPoolSize().intValue());
   json.writeNumberField("currentQueueSize", threadPoolMetrics.getCurrentQueueSize().intValue());
   json.writeNumberField("currentTaskCount", threadPoolMetrics.getCurrentTaskCount().longValue());
   safelyWriteNumberField(json, "rollingCountThreadsExecuted", new Func0<Long>() {
                                                                   @Override
                                                                   public Long call() {
                                                                   return threadPoolMetrics.getRollingCount(HystrixEventType.ThreadPool.EXECUTED);
                                                                   }

                                                               });
   json.writeNumberField("rollingMaxActiveThreads", threadPoolMetrics.getRollingMaxActiveThreads());
   safelyWriteNumberField(json, "rollingCountCommandRejections", new Func0<Long>() {
                                                                     @Override
                                                                     public Long call() {
                                                                     return threadPoolMetrics.getRollingCount(HystrixEventType.ThreadPool.REJECTED);
                                                                     }

                                                                 });
   json.writeNumberField("propertyValue_queueSizeRejectionThreshold",
       threadPoolMetrics.getProperties().queueSizeRejectionThreshold().get());
   json.writeNumberField("propertyValue_metricsRollingStatisticalWindowInMilliseconds",
       threadPoolMetrics.getProperties().metricsRollingStatisticalWindowInMilliseconds().get());
   json.writeNumberField("reportingHosts", 1); // this will get summed across all instances in a cluster
   json.writeEndObject();
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart == null) return false;
    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class < ?> multipartInterface = Class.forName("jakarta.mail.Multipart");

        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }

        // Additional check for MimeMultipart

        try {
            Class < ?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");

            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }

        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

    public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
        // :
       table.row("IS-RETURN", "" + false);
       table.row("IS-EXCEPTION", "" + true);

        // :
       Throwable cause;
       Throwable t = (Throwable) throwableVO.getObject();
       if (t instanceof InvocationTargetException) {
        cause = t.getCause();
       } else {
         cause = t;
       }

       if (throwableVO.needExpand()) {
        table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
       } else {
         StringWriter stringWriter = new StringWriter();
         PrintWriter printWriter = new PrintWriter(stringWriter);
         try {
           cause.printStackTrace(printWriter);
           table.row("THROW-EXCEPTION", stringWriter.toString());
         }

finally {
                  printWriter.close();
         }
       }
    }

private JsonNode validateLicense(String licenseKey, String machineFingerprint, LicenseContext context) throws Exception {
   String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}", licenseKey, machineFingerprint);
   HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key")).header("Content-Type", "application/vnd.api+json").header("Accept", "application/vnd.api+json")
                    // .header("Authorization", "License " + licenseKey).POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
   HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
   log.debug("ValidateLicenseResponse body: {}", response.body());
   JsonNode jsonResponse = objectMapper.readTree(response.body());
   if (response.statusCode() == 200) {
    JsonNode metaNode = jsonResponse.path("meta");
    boolean isValid = metaNode.path("valid").asBoolean();
    String detail = metaNode.path("detail").asText();
    String code = metaNode.path("code").asText();
    log.info("License validity: {}", isValid);
    log.info("Validation detail: {}", detail);
    log.info("Validation code: {}", code);

        // Check if the license itself has floating attribute
    JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
    if (!licenseAttrs.isMissingNode()) {
                                         context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
                                         context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);
                                         log.info("License floating (from license): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
    }

        // Also check the policy for floating license support if included
    JsonNode includedNode = jsonResponse.path("included");
    JsonNode policyNode = null;
    if (includedNode.isArray()) {
                                         for (JsonNode node : includedNode) {

        if ("policies".equals(node.path("type").asText())) {
                                                                                policyNode = node;
                                                                                break;
        }
                                         }
    }

    if (policyNode != null) {
            // Check if this is a floating license from policy
                                         boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
                                         int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);

            // Policy takes precedence over license attributes
                                         if (policyFloating) {
                                context.isFloatingLicense = true;
                                context.maxMachines = policyMaxMachines;
                                         }
                                         log.info("License floating (from policy): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
    }

        // Extract user count, default to 1 if not specified
    int users = jsonResponse.path("data").path("attributes").path("metadata").path("users").asInt(1);
    applicationProperties.getPremium().setMaxUsers(users);

        // Extract isEnterprise flag
    context.isEnterpriseLicense = jsonResponse.path("data").path("attributes").path("metadata").path("isEnterprise").asBoolean(false);
    log.debug(applicationProperties.toString());
   } else {
     log.error("Error validating license. Status code: {}", response.statusCode());
   }
   return jsonResponse;
}

    private AsyncProfiler profilerInstance() {

    if (profiler != null) {
        return profiler;
    }

        // try to load from special path

    if (ProfilerAction.load.toString().equals(action)) {
        profiler = AsyncProfiler.getInstance(this.actionArg);
    }

    if (libPath != null) {
            // load from arthas directory
            // libattach Native Library already loaded in another classloader
        FileOutputStream tmpLibOutputStream = null;
        FileInputStream libInputStream = null;
        try {
        File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);
        tmpLibOutputStream = new FileOutputStream(tmpLibFile);
        libInputStream = new FileInputStream(libPath);
        IOUtils.copy(libInputStream, tmpLibOutputStream);
        libPath = tmpLibFile.getAbsolutePath();
        logger.debug("copy {} to {}", libPath, tmpLibFile);
        } catch (Throwable e) {
          logger.error("try to copy lib error! libPath: {}", libPath, e);
        }

finally {
                             IOUtils.close(libInputStream);
                             IOUtils.close(tmpLibOutputStream);
        }
        profiler = AsyncProfiler.getInstance(libPath);
    } else {

      if (OSUtils.isLinux()|| OSUtils.isMac()) {
        throw new IllegalStateException("Can not find libasyncProfiler so, please check the arthas directory.");
      } else {
        throw new IllegalStateException("Current OS do not support AsyncProfiler, Only support Linux/Mac.");
      }
    }
    return profiler;
    }

