private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {
    if (emlBytes==null||emlBytes.length==0) {
        throw new IllegalArgumentException("EML file is empty or null");
    }
    String emlContent=new String(emlBytes, StandardCharsets.UTF_8);
    // Basic email parsing
    String subject=extractBasicHeader(emlContent, "Subject:");
    String from=extractBasicHeader(emlContent, "From:");
    String to=extractBasicHeader(emlContent, "To:");
    String cc=extractBasicHeader(emlContent, "Cc:");
    String bcc=extractBasicHeader(emlContent, "Bcc:");
    String date=extractBasicHeader(emlContent, "Date:");
    // Try to extract HTML content
    String htmlBody=extractHtmlBody(emlContent);
    if (htmlBody==null) {
        String textBody=extractTextBody(emlContent);
        htmlBody =
        convertTextToHtml(textBody!=null ? textBody : "Email content could not be parsed");
    }
    // Generate HTML with custom styling based on request
    StringBuilder html=new StringBuilder();
    html.append("<!DOCTYPE html>\n");
    html.append("<html><head><meta charset=\"UTF-8\">\n");
    html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
    html.append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n");
    html.append("</head><body>\n");
    html.append("<div class=\"email-container\">\n");
    html.append("<div class=\"email-header\">\n");
    html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
    html.append("<div class=\"email-meta\">\n");
    html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
    html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");
    // Include CC and BCC if present and requested
    if (request!=null&&request.isIncludeAllRecipients()) {
        if (!cc.trim().isEmpty()) {
            html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
        }
        if (!bcc.trim().isEmpty()) {
            html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
        }
    }
    if (!date.trim().isEmpty()) {
        html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
    }
    html.append("</div></div>\n");
    html.append("<div class=\"email-body\">\n");
    html.append(processEmailHtmlBody(htmlBody));
    html.append("</div>\n");
    // Add attachment information - always check for and display attachments
    String attachmentInfo=extractAttachmentInfo(emlContent);

    if (!attachmentInfo.isEmpty()) {
        html.append("<div class=\"attachment-section\">\n");
        html.append("<h3>Attachments</h3>\n");
        html.append(attachmentInfo);
        // Add a status message about attachment inclusion
        if (request!=null&&request.isIncludeAttachments()) {
            html.append("<div class=\"attachment-inclusion-note\">\n");
            html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");

            html.append("</div>\n");
        } else {

            html.append("<div class=\"attachment-info-note\">\n");

            html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
            html.append("</div>\n");
        }
        html.append("</div>\n");
    }

    // Show advanced features status if requested
    assert request!=null;
    if (request.getFileInput().isEmpty()) {
        html.append("<div class=\"advanced-features-notice\">\n");
        html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");

        html.append("</div>\n");
    }

    html.append("</div>\n");
    html.append("</body></html>");
    return html.toString();
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart==null) {
        return false;
    }
    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class<?> multipartInterface=Class.forName("jakarta.mail.Multipart");
        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }
        // Additional check for MimeMultipart
        try {
            Class<?> mimeMultipartClass=Class.forName("jakarta.mail.internet.MimeMultipart");
            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }
        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

private static String extractBasicHeader(String emlContent, String headerName) {
    try {
        String[] lines=emlContent.split("\r?\n");
        for (int i=0; i<lines.length; i++) {
            String line=lines[i];
            if (line.toLowerCase().startsWith(headerName.toLowerCase())) {


                StringBuilder value=new StringBuilder(line.substring(headerName.length()).trim());

                // Handle multi-line headers
                for (int j=i+1; j<lines.length; j++) {
                    if (lines[j].startsWith(" ")||lines[j].startsWith("\t")) {
                        value.append(" ").append(lines[j].trim());
                    } else {
                        break;
                    }
                }
                // Apply MIME header decoding
                return safeMimeDecode(value.toString());

            }
            if (line.trim().isEmpty()) {
                break;
            }
        }
    } catch (RuntimeException e) {
        log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
    }
    return "";
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List<EmailAttachment> attachments) throws IOException {
    if (document.getNumberOfPages()==0||attachments==null||attachments.isEmpty()) {
        return;
    }
    // 1. Find the screen position of all attachment markers
    AttachmentMarkerPositionFinder finder=new AttachmentMarkerPositionFinder();
    finder.setSortByPosition(true); // Process pages in order
    finder.getText(document);
    List<MarkerPosition> markerPositions=finder.getPositions();
    // 2. Warn if the number of markers and attachments don't match
    if (markerPositions.size()!=attachments.size()) {
        log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
    }
    // 3. Create an invisible annotation over each found marker
    int annotationsToAdd=Math.min(markerPositions.size(), attachments.size());
    for (int i=0; i<annotationsToAdd; i++) {
        MarkerPosition position=markerPositions.get(i);
        EmailAttachment attachment=attachments.get(i);

        if (attachment.getEmbeddedFilename()!=null) {
            PDPage page=document.getPage(position.getPageIndex());
            addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
        }
    }
}

public static HealthCountsStream getInstance(HystrixCommandKey commandKey, int numBuckets, int bucketSizeInMs) {
    HealthCountsStream initialStream = streams.get(commandKey.name());
    if (initialStream != null) {
        return initialStream;
    } else {
        final HealthCountsStream healthStream;

        synchronized (HealthCountsStream.class) {
            HealthCountsStream existingStream = streams.get(commandKey.name());
            if (existingStream != null) {
                healthStream = existingStream;
            } else {
                HealthCountsStream newStream = new HealthCountsStream(commandKey, numBuckets, bucketSizeInMs, HystrixCommandMetrics.appendEventToBucket);
                streams.putIfAbsent(commandKey.name(), newStream);
                healthStream = newStream;
            } 
        }
        healthStream.startCachingStreamValuesIfUnstarted();
        return healthStream;
    }
}

public Map<ExecutionSignature, List<Integer>> getExecutionsMappedToLatencies() {
    Map<CommandAndCacheKey, Integer> cachingDetector = new HashMap<CommandAndCacheKey, Integer>();
    List<HystrixInvokableInfo<?>> nonCachedExecutions = new ArrayList<HystrixInvokableInfo<?>>(executions.size());
    for (HystrixInvokableInfo<?> execution : executions) {
        if (execution.getPublicCacheKey() != null) {
            //eligible for caching - might be the initial, or might be from cache
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), execution.getPublicCacheKey());

            Integer count = cachingDetector.get(key);
            if (count != null) {
                //key already seen
                cachingDetector.put(key, count + 1);
            } else {
                //key not seen yet
                cachingDetector.put(key, 0);
            }
        }
        if (!execution.isResponseFromCache()) {
            nonCachedExecutions.add(execution);
        }
    }

    Map<ExecutionSignature, List<Integer>> commandDeduper = new HashMap<ExecutionSignature, List<Integer>>();
    for (HystrixInvokableInfo<?> execution : nonCachedExecutions) {
        int cachedCount = 0;
        String cacheKey = execution.getPublicCacheKey();
        if (cacheKey != null) {
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), cacheKey);

            cachedCount = cachingDetector.get(key);
        }
        ExecutionSignature signature = cachedCount > 0 ? ExecutionSignature.from(execution, cacheKey, cachedCount) : ExecutionSignature.from(execution); 

        List<Integer> currentLatencyList = commandDeduper.get(signature);
        if (currentLatencyList != null) {
            currentLatencyList.add(execution.getExecutionTimeInMilliseconds());
        } else {
            List<Integer> newLatencyList = new ArrayList<Integer>();
            newLatencyList.add(execution.getExecutionTimeInMilliseconds());

            commandDeduper.put(signature, newLatencyList);
        }
    }

    return commandDeduper;
}

public ThreadPoolExecutor getThreadPool(final HystrixThreadPoolKey threadPoolKey, HystrixThreadPoolProperties threadPoolProperties) {
    final ThreadFactory threadFactory = getThreadFactory(threadPoolKey);
    final boolean allowMaximumSizeToDivergeFromCoreSize = threadPoolProperties.getAllowMaximumSizeToDivergeFromCoreSize().get();
    final int dynamicCoreSize = threadPoolProperties.coreSize().get();
    final int keepAliveTime = threadPoolProperties.keepAliveTimeMinutes().get();
    final int maxQueueSize = threadPoolProperties.maxQueueSize().get();
    final BlockingQueue<Runnable> workQueue = getBlockingQueue(maxQueueSize);

    if (!allowMaximumSizeToDivergeFromCoreSize) {
        return new ThreadPoolExecutor(
        dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
    } else {
        final int dynamicMaximumSize = threadPoolProperties.maximumSize().get();
        if (dynamicCoreSize <= dynamicMaximumSize) {
            return new ThreadPoolExecutor(
            dynamicCoreSize, dynamicMaximumSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
        } else {
            logger.error("Hystrix ThreadPool configuration at startup for : " + threadPoolKey.name() + " is trying to set coreSize = " + dynamicCoreSize + " and maximumSize = " + dynamicMaximumSize + ".  Maximum size will be set to " + dynamicCoreSize + ", the coreSize value, since it must be equal to or greater than the coreSize value");
            return new ThreadPoolExecutor(
            dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
        } 
    } 
}

protected static String convertToJson(HystrixUtilization utilization) throws IOException {
    StringWriter jsonString = new StringWriter();
    JsonGenerator json = jsonFactory.createGenerator(jsonString);
    json.writeStartObject();

    json.writeStringField("type", "HystrixUtilization");
    json.writeObjectFieldStart("commands");
    for (Map.Entry<HystrixCommandKey, HystrixCommandUtilization> entry : utilization.getCommandUtilizationMap().entrySet()) {
        final HystrixCommandKey key = entry.getKey();
        final HystrixCommandUtilization commandUtilization = entry.getValue();
        writeCommandUtilizationJson(json, key, commandUtilization);
    }
    json.writeEndObject();
    json.writeObjectFieldStart("threadpools");

    for (Map.Entry<HystrixThreadPoolKey, HystrixThreadPoolUtilization> entry : utilization.getThreadPoolUtilizationMap().entrySet()) {
        final HystrixThreadPoolKey threadPoolKey = entry.getKey();
        final HystrixThreadPoolUtilization threadPoolUtilization = entry.getValue();
        writeThreadPoolUtilizationJson(json, threadPoolKey, threadPoolUtilization);
    }

    json.writeEndObject();

    json.writeEndObject();

    json.close();
    return jsonString.getBuffer().toString();
}

EventCounts plus(HystrixEventType eventType, int count) {
    BitSet newBitSet = (BitSet) events.clone();
    int localNumEmits = numEmissions;
    int localNumFallbackEmits =  numFallbackEmissions;

    int localNumCollapsed = numCollapsed;

    switch (eventType) {
        case EMIT:
            newBitSet.set(HystrixEventType.EMIT.ordinal());

            localNumEmits += count;

            break;
        case FALLBACK_EMIT:
            newBitSet.set(HystrixEventType.FALLBACK_EMIT.ordinal());

            localNumFallbackEmits += count;
            break;
        case COLLAPSED:
            newBitSet.set(HystrixEventType.COLLAPSED.ordinal());

            localNumCollapsed += count;

            break;
        default:
            newBitSet.set(eventType.ordinal());

            break;
    }
    return new EventCounts(newBitSet, localNumEmits, localNumFallbackEmits, localNumCollapsed);
}

