    if (playerType == null) autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);else if (switchingPlayers) autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
        else if (playerType == PlayerType.MAIN) autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);else autoPlay = false;
