            private void addJavaObjectToExcel(Object oneRowData, Row row, int rowIndex, int relativeRowIndex) {
        WriteHolder currentWriteHolder = writeContext.currentWriteHolder();

        BeanMap beanMap = BeanMapUtils.create(oneRowData);

        // Bean the contains of the Map Key method with poor performance,So to create a keySet here
        Set<String> beanKeySet = 
        new HashSet<>(beanMap.keySet());
        Set<String> beanMapHandledSet = new HashSet<>();

        int maxCellIndex = -1;
        // If it's a class it needs to be cast by type
        if (HeadKindEnum.CLASS
        .equals(writeContext.currentWriteHolder().excelWriteHeadProperty().getHeadKind())) {
    Map<Integer, Head> headMap = 
    writeContext.currentWriteHolder().excelWriteHeadProperty().getHeadMap();
    for (Map.Entry<Integer, Head> entry : headMap.entrySet()) {
int columnIndex = entry.getKey();
Head head = entry.getValue();
String name = head.getFieldName();
if (beanKeySet.contains(name)) { ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(
beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), name, currentWriteHolder);
CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(
writeContext, row, rowIndex, head, columnIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);

WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);

Cell cell = WorkBookUtil.createCell(row, columnIndex);

cellWriteHandlerContext.setCell(cell);

WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);

cellWriteHandlerContext.setOriginalValue(beanMap.get(name));

cellWriteHandlerContext.setOriginalFieldClass(head.getField().getType());
converterAndSet(cellWriteHandlerContext);
WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);

beanMapHandledSet.add(name);

maxCellIndex = Math.max(maxCellIndex, columnIndex);
} 
    }
        }

        // Finish
        if (beanMapHandledSet.size() == beanMap.size()) {
    return;
        }
        maxCellIndex++;
        FieldCache fieldCache = 
        ClassUtils.declaredFields(oneRowData.getClass(), 
        writeContext.currentWriteHolder());
        for (Map.Entry<Integer, FieldWrapper> entry : fieldCache.getSortedFieldMap().entrySet()) {
    FieldWrapper field = entry.getValue();
    String fieldName = field.getFieldName();
    boolean uselessData = 
    !beanKeySet.contains(fieldName) || beanMapHandledSet.contains(fieldName);
    if (!uselessData) { 
Object value = beanMap.get(fieldName);

ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), fieldName, currentWriteHolder);
CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(writeContext, row, rowIndex, null, maxCellIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);

WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);

// fix https://github.com/alibaba/easyexcel/issues/1870
// If there is data, it is written to the next cell
Cell cell = WorkBookUtil.createCell(row, maxCellIndex);

cellWriteHandlerContext.setCell(cell);

WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);

cellWriteHandlerContext.setOriginalValue(value);

cellWriteHandlerContext
.setOriginalFieldClass(FieldUtils.getFieldClass(beanMap, fieldName, value));
converterAndSet(cellWriteHandlerContext);

WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);

maxCellIndex++;
    } 
        }
            }

            public CellStyle createCellStyle(WriteCellStyle writeCellStyle, CellStyle originCellStyle) {
        if (writeCellStyle == null) {
    return originCellStyle;
        }
        short styleIndex = -1;
        Font originFont = null;
        boolean useCache = true;
        if (originCellStyle != null) {
    styleIndex = originCellStyle.getIndex();

    if (originCellStyle instanceof XSSFCellStyle) {
originFont = ((XSSFCellStyle) originCellStyle).getFont();
    } else if (originCellStyle instanceof HSSFCellStyle) {
originFont = ((HSSFCellStyle) originCellStyle).getFont(workbook);
}

    useCache = false;
        }

        Map<WriteCellStyle, CellStyle> cellStyleMap = 
        cellStyleIndexMap.computeIfAbsent(styleIndex, key -> MapUtils.newHashMap());
        CellStyle cellStyle = cellStyleMap.get(writeCellStyle);
        if (cellStyle != null) {
    return cellStyle;
        }
        if (log.isDebugEnabled()) {
    log.info("create new style:{},{}", writeCellStyle, originCellStyle);
        }
        WriteCellStyle tempWriteCellStyle = new WriteCellStyle();

        WriteCellStyle.merge(writeCellStyle, tempWriteCellStyle);

        cellStyle = StyleUtil.buildCellStyle(workbook, originCellStyle, tempWriteCellStyle);
        Short dataFormat = 
        createDataFormat(tempWriteCellStyle.getDataFormatData(), 
        useCache);
        if (dataFormat != null) {
    cellStyle.setDataFormat(dataFormat);
        }
        Font font = 
        createFont(tempWriteCellStyle.getWriteFont(), originFont, useCache);

        if (font != null) {
    cellStyle.setFont(font);
        }
        cellStyleMap.put(tempWriteCellStyle, cellStyle);
        return cellStyle;
            }

            private void buildHead(AnalysisContext analysisContext, Map<Integer, ReadCellData<?>> cellDataMap) {
        // Rule out empty head, and then take the largest column
        if (MapUtils.isNotEmpty(cellDataMap)) {
    cellDataMap.entrySet().stream().filter(entry -> 
    CellDataTypeEnum.EMPTY != entry.getValue().getType()).forEach(
    entry -> 
    analysisContext.readSheetHolder().setMaxNotEmptyDataHeadSize(entry.getKey()));
        }

        if (!HeadKindEnum.CLASS
        .equals(analysisContext.currentReadHolder().excelReadHeadProperty().getHeadKind())) {
    return;
        }
        Map<Integer, String> dataMap = 
        ConverterUtils.convertToStringMap(cellDataMap, analysisContext);
        ExcelReadHeadProperty excelHeadPropertyData = 
        analysisContext.readSheetHolder().excelReadHeadProperty();
        Map<Integer, Head> headMapData = excelHeadPropertyData.getHeadMap();
        Map<Integer, Head> tmpHeadMap = 
        new HashMap<Integer, Head>(headMapData.size() * 4 / 3 + 1);
        for (Map.Entry<Integer, Head> entry : headMapData.entrySet()) {
    Head headData = entry.getValue();
    if (headData.getForceIndex()) {
tmpHeadMap.put(entry.getKey(), headData);
continue;
    }
    if (!headData.getForceName()) {
    tmpHeadMap.put(entry.getKey(), headData);
continue;
}
    List<String> headNameList = headData.getHeadNameList();
    String headName = headNameList.get(headNameList.size() - 1);
    for (Map.Entry<Integer, String> stringEntry : dataMap.entrySet()) {
if (stringEntry != null) { 
String headString = stringEntry.getValue();
Integer stringKey = stringEntry.getKey();
if (StringUtils.isEmpty(headString)) {
continue;
}
if (analysisContext.currentReadHolder().globalConfiguration().getAutoTrim()) {
headString = headString.trim();
}
if (headName.equals(headString)) {
headData.setColumnIndex(stringKey);
tmpHeadMap.put(stringKey, headData);
break;
}
} 
    }
        }
        excelHeadPropertyData.setHeadMap(tmpHeadMap);
            }

