            public static int startArthasClient(
            String arthasHomeDir, List<String> telnetArgs, OutputStream out) throws Throwable {
        // start java telnet client
        // find arthas-client.jar
        URLClassLoader classLoader = 
        new URLClassLoader(new URL[]{
        new File(arthasHomeDir, "arthas-client.jar").toURI().toURL()});

        Class<?> telnetConsoleClass = 
        classLoader.loadClass("com.taobao.arthas.client.TelnetConsole");

        Method processMethod = telnetConsoleClass.getMethod("process", String[].class);
        //redirect System.out/System.err
        PrintStream originSysOut = System.out;
        PrintStream originSysErr = System.err;
        PrintStream newOut = new PrintStream(out);
        PrintStream newErr = new PrintStream(out);
        // call TelnetConsole.process()
        // fix https://github.com/alibaba/arthas/issues/833
        ClassLoader tccl = 
        Thread.currentThread().getContextClassLoader();

        try {
    System.setOut(newOut);
    System.setErr(newErr);
    Thread.currentThread().setContextClassLoader(classLoader);
    return (Integer) processMethod.invoke(null, new Object[]{
    telnetArgs.toArray(new String[0])});
        } catch (Throwable e) {
    //java.lang.reflect.InvocationTargetException : java.net.ConnectException
    e = e.getCause();
    if (e instanceof IOException || e instanceof InterruptedException) {
// ignore connection error and interrupted error
return STATUS_ERROR;
    } else {
// process error
AnsiLog.error("process error: {}", e.toString());
AnsiLog.error(e);
return STATUS_EXEC_ERROR;
    }
        } finally {
    Thread.currentThread().setContextClassLoader(tccl);
    //reset System.out/System.err
    System.setOut(originSysOut);
    System.setErr(originSysErr);
    //flush output
    newOut.flush();
    newErr.flush();
        }
            }

            public static FieldVO[] getFields(Class clazz, Integer expand) {
        Field[] fields = clazz.getDeclaredFields();
        if (fields.length == 0) {
    return new FieldVO[0];
        }
        List<FieldVO> list = 
        new ArrayList<FieldVO>(fields.length);
        for (Field field : fields) {
    FieldVO fieldVO = new FieldVO();
    fieldVO.setName(field.getName());

    fieldVO.setType(StringUtils.classname(field.getType()));
    fieldVO.setModifier(StringUtils.modifier(field.getModifiers(), ','));

    fieldVO.setAnnotations(getAnnotations(field.getAnnotations()));

    if (!(Modifier.isStatic(field.getModifiers()))) {
fieldVO.setStatic(false);
    } else {
fieldVO.setStatic(true);
fieldVO.setValue(new ObjectVO(getFieldValue(field), expand));
    } 

    list.add(fieldVO);
        }
        return list.toArray(new FieldVO[0]);
            }

import null;private JsonNode validateLicense(String licenseKey, String machineFingerprint, LicenseContext context) throws Exception {
    String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}", licenseKey, machineFingerprint);
    HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key")).header("Content-Type", "application/vnd.api+json").header("Accept", "application/vnd.api+json")// .header("Authorization", "License " + licenseKey)
    .POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
    HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", response.body());
    JsonNode jsonResponse = objectMapper.readTree(response.body());
    if (response.statusCode() == 200) {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();
        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();
        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);
        // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
            context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);

            context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);

            log.info("License floating (from license): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }
        // Also check the policy for floating license support if included
        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;
        if (includedNode.isArray()) {
            for (JsonNode node : includedNode) {
                if ("policies".equals(node.path("type").asText())) {
                    policyNode = node;
                    break;
                }
            }
        }
        if (policyNode != null) {
            // Check if this is a floating license from policy
            boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
            int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);
            // Policy takes precedence over license attributes
            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
            }
            log.info("License floating (from policy): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }
        // Extract user count, default to 1 if not specified
        int users = jsonResponse.path("data").path("attributes").path("metadata").path("users").asInt(1);

        applicationProperties.getPremium().setMaxUsers(users);
        // Extract isEnterprise flag
        JsonNode tmp = jsonResponse.path("data").path("attributes");
        context.isEnterpriseLicense = tmp.path("metadata").path("isEnterprise").asBoolean(false);
        log.debug(applicationProperties.toString());
    } else {
        log.error("Error validating license. Status code: {}", response.statusCode());
    }
    return jsonResponse;
}

@PostMapping(consumes = "multipart/form-data", value = "/file/pdf")
