public static int startArthasClient(String arthasHomeDir, List<String> telnetArgs, OutputStream out) throws Throwable {
    // start java telnet client
    // find arthas-client.jar
    URLClassLoader classLoader = new URLClassLoader(new URL[]{
    new File(arthasHomeDir, "arthas-client.jar").toURI().toURL()});
    Class<?> telnetConsoleClass = classLoader.loadClass("com.taobao.arthas.client.TelnetConsole");
    Method processMethod = telnetConsoleClass.getMethod("process", String[].class);
    //redirect System.out/System.err
    PrintStream originSysOut = System.out;
    PrintStream originSysErr = System.err;
    PrintStream newOut = new PrintStream(out);
    PrintStream newErr = new PrintStream(out);

    // call TelnetConsole.process()
    // fix https://github.com/alibaba/arthas/issues/833
    ClassLoader tccl = Thread.currentThread().getContextClassLoader();

    try {
        System.setOut(newOut);
        System.setErr(newErr);
        Thread.currentThread().setContextClassLoader(classLoader);
        return (Integer) processMethod.invoke(null, new Object[]{
        telnetArgs.toArray(new String[0])});
    } catch (Throwable e) {
        //java.lang.reflect.InvocationTargetException : java.net.ConnectException
        e = e.getCause();
        if (e instanceof IOException || e instanceof InterruptedException) return STATUS_ERROR;else {
            // process error
            AnsiLog.error("process error: {}", e.toString());
            AnsiLog.error(e);
            return STATUS_EXEC_ERROR;
        }
    } finally {
        Thread.currentThread().setContextClassLoader(tccl);
        //reset System.out/System.err
        System.setOut(originSysOut);
        System.setErr(originSysErr);

        //flush output
        newOut.flush();
        newErr.flush();
    }
}

public static FieldVO[] getFields(Class clazz, Integer expand) {
    Field[] fields = clazz.getDeclaredFields();
    if (fields.length == 0) {
        return new FieldVO[0];
    }
    List<FieldVO> list = new ArrayList<FieldVO>(fields.length);
    for (Field field : fields) {
        FieldVO fieldVO = new FieldVO();
        fieldVO.setName(field.getName());
        fieldVO.setType(StringUtils.classname(field.getType()));

        fieldVO.setModifier(StringUtils.modifier(field.getModifiers(), ','));
        fieldVO.setAnnotations(getAnnotations(field.getAnnotations()));
        if (!(Modifier.isStatic(field.getModifiers()))) fieldVO.setStatic(false);else {
            fieldVO.setStatic(true);
            fieldVO.setValue(new ObjectVO(getFieldValue(field), expand));
        } 
        list.add(fieldVO);
    }

    return list.toArray(new FieldVO[0]);
}

private static String normalizeArch(String value) {

  value = normalize(value);

  if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
    return "x86_64";
  }
  if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
    return "x86_32";
  }
  if (value.matches("^(ia64w?|itanium64)$")) {
    return "itanium_64";
  }

  if ("ia64n".equals(value)) {
    return "itanium_32";
  }
  if (value.matches("^(sparc|sparc32)$")) {
    return "sparc_32";
  }
  if (value.matches("^(sparcv9|sparc64)$")) {
    return "sparc_64";
  }
  if (value.matches("^(arm|arm32)$")) {
    return "arm_32";
  }
  if ("aarch64".equals(value)) {
    return "aarch_64";
  }
  if (value.matches("^(mips|mips32)$")) {
    return "mips_32";
  }
  if (value.matches("^(mipsel|mips32el)$")) {
    return "mipsel_32";
  }
  if ("mips64".equals(value)) {
    return "mips_64";
  }
  if ("mips64el".equals(value)) {
    return "mipsel_64";
  }
  if (value.matches("^(ppc|ppc32)$")) {
    return "ppc_32";
  }
  if (value.matches("^(ppcle|ppc32le)$")) {
    return "ppcle_32";
  }
  if ("ppc64".equals(value)) {
    return "ppc_64";
  }
  if ("ppc64le".equals(value)) {
    return "ppcle_64";
  }

  if ("s390".equals(value)) {
    return "s390_32";
  }

  if ("s390x".equals(value)) {
    return "s390_64";
  }

  return value;

}

private static Map<String, Object> doGetLoggerInfo(Logger logger) {
  Map<String, Object> info = new HashMap<String, Object>();

  info.put(LoggerHelper.name, logger.getName());
  info.put(LoggerHelper.clazz, logger.getClass());
  CodeSource codeSource = logger.getClass().getProtectionDomain().getCodeSource();
  if (codeSource != null) {
    info.put(LoggerHelper.codeSource, codeSource.getLocation());
  }

  info.put(LoggerHelper.additivity, logger.getAdditivity());

  Level level = logger.getLevel(), effectiveLevel = logger.getEffectiveLevel();
  if (level != null) {
    info.put(LoggerHelper.level, level.toString());
  }

  if (effectiveLevel != null) {
    info.put(LoggerHelper.effectiveLevel, effectiveLevel.toString());
  }

  @SuppressWarnings("unchecked") List<Map<String, Object>> result = doGetLoggerAppenders(logger.getAllAppenders());
  info.put(LoggerHelper.appenders, result);
  return info;

}

@SuppressWarnings("resource")
public static long select(boolean v, long telnetPortPid, String select) throws InputMismatchException {

  Map<Long, String> processMap = listProcessByJps(v);

  // Put the port that is already listening at the first
  if (0 < telnetPortPid && processMap.containsKey(telnetPortPid)) {
    String telnetPortProcess = processMap.get(telnetPortPid);
    processMap.remove(telnetPortPid);
    Map<Long, String> newProcessMap = new LinkedHashMap<Long, String>();
    newProcessMap.put(telnetPortPid, telnetPortProcess);
    newProcessMap.putAll(processMap);

    processMap = newProcessMap;
  }

  if (processMap.isEmpty()) {
    AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
    return -1;
  }

  // select target process by the '--select' option when match only one process
  if (select != null && !select.trim().isEmpty()) {
    int matchedSelectCount = 0;
    Long matchedPid = null;
    for (Entry<Long, String> entry : processMap.entrySet()) {
      if (entry.getValue().contains(select)) {
        matchedSelectCount++;
        matchedPid = entry.getKey();
      }
    }
    if (matchedSelectCount == 1) {
      return matchedPid;
    }
  }

  AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
  // print list
  int count = 1;
  for (String process : processMap.values()) {
    if (count == 1) {
      System.out.println("* [" + count + "]: " + process);
    } else {
      System.out.println("  [" + count + "]: " + process);
    }
    count++;

  }
  // read choice
  String line = new Scanner(System.in).nextLine();

  if (line.trim().isEmpty()) {
    // get the first process id
    return processMap.keySet().iterator().next();
  }

  int choice = new Scanner(line).nextInt();
  if (choice <= 0 || processMap.size()< choice) {
    return -1;
  }

  Iterator<Long> idIter = processMap.keySet().iterator();

  for (int i = 1; i <= choice; ++i) {
    if (i == choice) {
      return idIter.next();
    }
    idIter.next();

  }
  return -1;
}

void _processDo(int option) throws IOException
{
  if (debugoptions) {
    System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
  }
  if (__notifhand != null) {
    __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
  }

  boolean acceptNewState = false;
  /* open TelnetOptionHandler functionality (start)*/

  if (optionHandlers[option] == null) { /* open TelnetOptionHandler functionality (end)*//* TERMINAL-TYPE option (start)*/
    if (option == TERMINAL_TYPE) {
      if ((terminalType != null) && (0< terminalType.length())) {
        acceptNewState = true;
      }
    }/* TERMINAL-TYPE option (end)*//* open TelnetOptionHandler functionality (start)*/
  }/* open TelnetOptionHandler functionality (end)*/else {
    acceptNewState = optionHandlers[option].getAcceptLocal();
  }

  if (0< _willResponse[option]) {

    --_willResponse[option];

    if (0 < _willResponse[option] && _stateIsWill(option)) {
      --_willResponse[option];
    }
  }

  if (_willResponse[option] == 0) {
    if (_requestedWont(option)) {
      switch (option) {default:
          break;

      }
      if (acceptNewState) {
        _setWantWill(option);
        _sendWill(option);
      } else {
        ++_willResponse[option];
        _sendWont(option);
      }
    } else
    { // Other end has acknowledged option.

      switch (option) {default:
          break;

      }
    }
  }

  _setWill(option);
}

private void bind(Configure configure) throws Throwable {
  long start = System.currentTimeMillis();
  if (!isBindRef.compareAndSet(false, true)) {
    throw new IllegalStateException("already bind");
  }
  // init random port
  if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
    int newTelnetPort = SocketUtils.findAvailableTcpPort();

    configure.setTelnetPort(newTelnetPort);
    logger().info("generate random telnet port: " + newTelnetPort);
  }
  if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
    int newHttpPort = SocketUtils.findAvailableTcpPort();

    configure.setHttpPort(newHttpPort);
    logger().info("generate random http port: " + newHttpPort);
  }
  // try to find appName
  if (configure.getAppName() == null) {
    configure.setAppName(System.getProperty(ArthasConstants.PROJECT_NAME, System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
  }

  try {
    if (configure.getTunnelServer() != null) {
      tunnelClient = new TunnelClient();
      tunnelClient.setAppName(configure.getAppName());
      tunnelClient.setId(configure.getAgentId());
      tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
      tunnelClient.setVersion(ArthasBanner.version());
      ChannelFuture channelFuture = tunnelClient.start();
      channelFuture.await(10, TimeUnit.SECONDS);
    }
  } catch (Throwable t) {
    logger().error("start tunnel client error", t);
  }

  try {
    ShellServerOptions options = new ShellServerOptions().setInstrumentation(instrumentation).setPid(PidUtils.currentLongPid()).setWelcomeMessage(ArthasBanner.welcome());
    if (configure.getSessionTimeout() != null) {
      options.setSessionTimeout(configure.getSessionTimeout() * 1000);
    }

    this.httpSessionManager = new HttpSessionManager();

    if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
      //  listen 0.0.0.0 
      String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! " + "No password is currently configured. " + "Therefore, a default password is generated, " + "and clients need to use the password to connect!";
      AnsiLog.error(errorMsg);
      configure.setPassword(StringUtils.randomString(64));
      AnsiLog.error("Generated arthas password: " + configure.getPassword());

      logger().error(errorMsg);

      logger().info("Generated arthas password: " + configure.getPassword());
    }
    this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());

    shellServer = new ShellServerImpl(options);

    List<String> disabledCommands = new ArrayList<String>();
    if (configure.getDisabledCommands() != null) {

      String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");

      if (strings != null) {
        disabledCommands.addAll(Arrays.asList(strings));
      }
    }
    BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
    List<CommandResolver> resolvers = new ArrayList<CommandResolver>();

    resolvers.add(builtinCommands);

    //worker group
    workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));

    // TODO: discover user provided command resolver
    if (configure.getTelnetPort() != null && 0< configure.getTelnetPort()) {
      logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
      shellServer.registerTermServer(new HttpTelnetTermServer(configure.getIp(), configure.getTelnetPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } else {
      logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
    }

    if (!(configure.getHttpPort() != null && 0< configure.getHttpPort())) {

      // listen local address in VM communication
      if (configure.getTunnelServer() != null) {
        shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
      }

      logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
    }

    else {
      logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
      shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } for (CommandResolver resolver : resolvers) {
      shellServer.registerCommandResolver(resolver);
    }
    shellServer.listen(new BindHandler(isBindRef));
    if (!isBind()) {
      throw new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: " + String.valueOf(configure.getTelnetPort()) + ", http port: " + String.valueOf(configure.getHttpPort()));
    }
    //http api session manager
    sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());

    //http api handler
    httpApiHandler = new HttpApiHandler(historyManager, sessionManager);

    logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(), configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());

    // 
    if (configure.getStatUrl() != null) {
      logger().info("arthas stat url: {}", configure.getStatUrl());
    }
    UserStatUtil.setStatUrl(configure.getStatUrl());
    UserStatUtil.setAgentId(configure.getAgentId());
    UserStatUtil.arthasStart();
    try {
      SpyAPI.init();
    } catch (Throwable e) { // ignore
    }
    logger().info("as-server started in {} ms", System.currentTimeMillis() - start);

  } catch (Throwable e) {
    logger().error("Error during start as-server", e);

    destroy();
    throw e;

  }
}

private int __read(boolean mayBlock) throws IOException{
  int ch;
  while (true) {
    // If there is no more data AND we were told not to block,
    // just return WOULD_BLOCK (-2). (More efficient than exception.)
    if (!mayBlock && super.available() == 0) {
      return WOULD_BLOCK;
    }
    // Otherwise, exit only when we reach end of stream.
    if ((ch = super.read()) < 0) {
      return EOF;
    }

    ch = (ch & 0xff);
    /* Code Section added for supporting AYT (start)*/

    synchronized (__client) {
      __client._processAYTResponse();
    }/* Code Section added for supporting AYT (end)*//* Code Section added for supporting spystreams (start)*/
    __client._spyRead(ch);/* Code Section added for supporting spystreams (end)*/
    switch (__receiveState) {

      case _STATE_CR:
        if (ch == '\0') {
          // Strip null
          continue;
        }
        // How do we handle newline after cr?
        //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&

        // Handle as normal data by falling through to _STATE_DATA case

        //$FALL-THROUGH$
      case _STATE_DATA:
        if (ch == TelnetCommand.IAC) {
          __receiveState = _STATE_IAC;
          continue;

        }
        if (ch == '\r') {
          synchronized (__client) {
            if (__client._requestedDont(TelnetOption.BINARY)) {
              __receiveState = _STATE_CR;
            } else {
              __receiveState = _STATE_DATA;
            }
          }
        } else {
          __receiveState = _STATE_DATA;
        }
        break;

      case _STATE_IAC:
        switch (ch) {
          case TelnetCommand.WILL:
            __receiveState = _STATE_WILL;
            continue;
          case TelnetCommand.WONT:
            __receiveState = _STATE_WONT;
            continue;
          case TelnetCommand.DO:
            __receiveState = _STATE_DO;
            continue;
          case TelnetCommand.DONT:
            __receiveState = _STATE_DONT;
            continue;
            /* TERMINAL-TYPE option (start)*/
          case TelnetCommand.SB:
            __suboption_count = 0;
            __receiveState = _STATE_SB;
            continue;
            /* TERMINAL-TYPE option (end)*/
          case TelnetCommand.IAC:
            __receiveState = _STATE_DATA;
            break; // exit to enclosing switch to return IAC from read
          case TelnetCommand.SE : // unexpected byte! ignore it (don't send it as a command)
            __receiveState = _STATE_DATA;
            continue;
          default:
            __receiveState = _STATE_DATA;

            __client._processCommand(ch); // Notify the user

            continue; // move on the next char
        }
        break; // exit and return from read
      case _STATE_WILL:
        synchronized (__client) {
          __client._processWill(ch);
          __client._flushOutputStream();
        }

        __receiveState = _STATE_DATA;
        continue;
      case _STATE_WONT:
        synchronized (__client) {
          __client._processWont(ch);
          __client._flushOutputStream();
        }

        __receiveState = _STATE_DATA;
        continue;
      case _STATE_DO:
        synchronized (__client) {
          __client._processDo(ch);
          __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
      case _STATE_DONT:
        synchronized (__client) {
          __client._processDont(ch);
          __client._flushOutputStream();
        }

        __receiveState = _STATE_DATA;
        continue;
        /* TERMINAL-TYPE option (start)*/
      case _STATE_SB:
        switch (ch) {
          case TelnetCommand.IAC:
            __receiveState = _STATE_IAC_SB;
            continue;
          default:
            // store suboption char
            if (__suboption_count < __suboption.length) {
              __suboption[__suboption_count++] = ch;
            }
            break;
        }
        __receiveState = _STATE_SB;
        continue;
      case _STATE_IAC_SB : // IAC received during SB phase
        switch (ch) {
          case TelnetCommand.SE:
            synchronized (__client) {

              __client._processSuboption(__suboption, __suboption_count);

              __client._flushOutputStream();
            }

            __receiveState = _STATE_DATA;
            continue;
          case TelnetCommand.IAC : // De-dup the duplicated IAC
            if (__suboption_count < __suboption.length) {
              __suboption[__suboption_count++] = ch;
            }
            break;
          default :            // unexpected byte! ignore it
            break;
        }
        __receiveState = _STATE_SB;
        continue;
        /* TERMINAL-TYPE option (end)*/
    }
    break;

  }
  return ch;

}

private static String formatFileSize(long size) {
  String hrSize;

  double b = size;
  double k = size / 1024.0;
  double m = ((size / 1024.0) / 1024.0);
  double g = (((size / 1024.0) / 1024.0) / 1024.0);
  double t = ((((size / 1024.0) / 1024.0) / 1024.0) / 1024.0);

  DecimalFormat dec = new DecimalFormat("0.00");
  if (1< t) {
    hrSize = dec.format(t).concat(" TB");
  } else {
    if (1< g) {
      hrSize = dec.format(g).concat(" GB");
    } else {
      if (1 >= m) {
        if (1< k) {
          hrSize = dec.format(k).concat(" KB");
        } else {
          hrSize = dec.format(b).concat(" Bytes");
        }
      } else {
        hrSize = dec.format(m).concat(" MB");
      } 
    }
  }
  return hrSize;

}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined,
ProtectionDomain protectionDomain, byte[] classfileBuffer) throws IllegalClassFormatException {
  if (className == null) {
    return null;
  }
  className = className.replace('/', '.');

  List<RetransformEntry> allRetransformEntries = allRetransformEntries();
  // 
  ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
  while (listIterator.hasPrevious()) {

    RetransformEntry retransformEntry = listIterator.previous();

    int id = retransformEntry.getId();

    // 
    boolean updateFlag = false;
    //  loader loader
    if (className.equals(retransformEntry.getClassName())) {
      if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
        updateFlag = isLoaderMatch(retransformEntry, loader);
      } else {
        updateFlag = true;
      }
    }

    if (updateFlag) {
      logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}", className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
      retransformEntry.incTransformCount();
      return retransformEntry.getBytes();
    }

  }
  return null;

}

public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
  // :
  table.row("IS-RETURN", "" + false);
  table.row("IS-EXCEPTION", "" + true);

  // :
  Throwable cause;
  Throwable t = (Throwable) throwableVO.getObject();
  if (t instanceof InvocationTargetException) {
    cause = t.getCause();
  } else {
    cause = t;
  }
  if (!(throwableVO.needExpand())) {
    StringWriter stringWriter = new StringWriter();

    PrintWriter printWriter = new PrintWriter(stringWriter);
    try {
      cause.printStackTrace(printWriter);

      table.row("THROW-EXCEPTION", stringWriter.toString());
    } finally {
      printWriter.close();
    }
  } else {
    table.row("THROW-EXCEPTION", 
    new ObjectView(cause, throwableVO.expandOrDefault()).draw());
  } 
}

private AsyncProfiler profilerInstance() {
  if (profiler != null) {
    return profiler;
  }
  // try to load from special path
  if (ProfilerAction.load.toString().equals(action)) {
    profiler = AsyncProfiler.getInstance(this.actionArg);
  }
  if (libPath != null) {
    // load from arthas directory
    // libattach Native Library already loaded in another classloader
    FileOutputStream tmpLibOutputStream = null;
    FileInputStream libInputStream = null;

    try {

      File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);

      tmpLibOutputStream = new FileOutputStream(tmpLibFile);

      libInputStream = new FileInputStream(libPath);
      IOUtils.copy(libInputStream, tmpLibOutputStream);

      libPath = tmpLibFile.getAbsolutePath();
      logger.debug("copy {} to {}", libPath, tmpLibFile);
    } catch (Throwable e) {
      logger.error("try to copy lib error! libPath: {}", libPath, e);
    } finally {
      IOUtils.close(libInputStream);
      IOUtils.close(tmpLibOutputStream);
    }

    profiler = AsyncProfiler.getInstance(libPath);
  } else {
    if (OSUtils.isLinux() || OSUtils.isMac()) {
      throw new IllegalStateException("Can not find libasyncProfiler so, please check the arthas directory.");
    } else {
      throw new IllegalStateException("Current OS do not support AsyncProfiler, Only support Linux/Mac.");
    }
  }

  return profiler;
}

private JsonNode validateLicense(String licenseKey, String machineFingerprint, LicenseContext context) throws Exception {
    String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}", licenseKey, machineFingerprint);
    HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key")).header("Content-Type", "application/vnd.api+json").header("Accept", "application/vnd.api+json")// .header("Authorization", "License " + licenseKey)
    .POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
    HttpResponse<String> response = httpClient.send(request, 
    HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", 
    response.body());
    JsonNode jsonResponse = objectMapper.readTree(response.body());
    if (response.statusCode() != 200) {
        log.error("Error validating license. Status code: {}", 
        response.statusCode());
    } else {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();
        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();
        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);
        // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
            context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
            context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);

            log.info("License floating (from license): {}, maxMachines: {}",
            context.isFloatingLicense,
            context.maxMachines);
        }
        // Also check the policy for floating license support if included
        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;
        if (includedNode.isArray()) {
            for (JsonNode node : includedNode) {
                if ("policies".equals(node.path("type").asText())) {
                    policyNode = node;
                    break;
                }
            }
        }
        if (policyNode != null) {
            // Check if this is a floating license from policy
            boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
            int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);
            // Policy takes precedence over license attributes
            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
            }
            log.info("License floating (from policy): {}, maxMachines: {}",
            context.isFloatingLicense,
            context.maxMachines);
        }

        // Extract user count, default to 1 if not specified
        int users = jsonResponse.path("data").path("attributes").path("metadata").path("users").asInt(1);
        applicationProperties.getPremium().setMaxUsers(users);
        // Extract isEnterprise flag
        context.isEnterpriseLicense = jsonResponse.path("data").path("attributes").path("metadata").path("isEnterprise").asBoolean(false);
        log.debug(applicationProperties.toString());
    } 
    return jsonResponse;
}

