@Bean(name = "machineType")
public String determineMachineType() {
    try {
        boolean isDocker = runningInDocker();
        boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;

        boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));
        if (!isKubernetes) {
            if (isDocker) {
                return "Docker";
            } else {
                if (!isBrowserOpen) {
                    return "Server-jar";
                } else {
                    String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);
                    if (os.contains("win")) {
                        return "Client-windows";
                    } else {
                        if (os.contains("mac")) {
                            return "Client-mac";
                        } else {
                            return "Client-unix";
                        }
                    }
                } 
            }
        }else {
            return "Kubernetes";
        } 
    } catch (Exception e) {
        return "Unknown";
    }
}

private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets,
HttpServletRequest request,
HttpServletResponse response, FilterChain filterChain)
throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, 
        response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds",
        Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

public static Map<String, String> toStringMap(Map<String, Object> input, boolean stringEscape) {
    Map<String, String> output = new HashMap<>(input.size());
    for (Map.Entry<String, Object> entry : input.entrySet()) {
        String key = entry.getKey();
        Object obj = entry.getValue();
        if (key == null || obj == null) {
            throw new NullPointerException("A null argument cannot be sent to Redis.");
        }
        String str;
        if (!(obj instanceof byte[])) {
            if (obj instanceof redis.clients.jedis.GeoCoordinate) {
                redis.clients.jedis.GeoCoordinate geo = (redis.clients.jedis.GeoCoordinate) obj;
                str = geo.getLongitude() + "," + geo.getLatitude();
            } else {
                if (obj instanceof String) {
                    if (stringEscape) { 
                        str = escape((String) obj); 
                    } else { 
                        str = (String) obj; 
                    } 
                } else {
                    str = String.valueOf(obj);
                }
            }
        }else {
            str = SafeEncoder.encode((byte[]) obj);
        } 
        output.put(key, str);
    }

    return output;
}

@Override
