private static Object process(final RedisInputStream is) {
    final byte b=is.readByte();
    // System.out.println("BYTE: " + (char) b);
    switch (b) {
        case PLUS_BYTE:
            return is.readLineBytes();
        case DOLLAR_BYTE:
        case EQUAL_BYTE:
            return processBulkReply(is);
        case ASTERISK_BYTE:
            return processMultiBulkReply(is);
        case UNDERSCORE_BYTE:
            return is.readNullCrLf();
        case HASH_BYTE:
            return is.readBooleanCrLf();
        case COLON_BYTE:
            return is.readLongCrLf();
        case COMMA_BYTE:
            return is.readDoubleCrLf();
        case LEFT_BRACE_BYTE:
            return is.readBigIntegerCrLf();
        case PERCENT_BYTE: // TODO: currently just to start working with HELLO
            return processMapKeyValueReply(is);
        case TILDE_BYTE: // TODO:
            return processMultiBulkReply(is);
        case GREATER_THAN_BYTE:
            return processMultiBulkReply(is);
        case MINUS_BYTE:
            processError(is);

            return null;
            // TODO: Blob error '!'
        default:
            throw new JedisConnectionException("Unknown reply: "+(char) b);
    }
}

@Override
public Map.Entry<T, ProfilingInfo> build(Object data) {
    List list=(List) data;
    if (list==null||list.isEmpty()) {
        return null;
    }

    if (list.get(0)instanceofKeyValue) { // RESP3
        Object resultsData=null, profileData=null;
        for (KeyValue keyValue : (List<KeyValue>) data) {
            String keyStr=BuilderFactory.STRING.build(keyValue.getKey());
            switch (keyStr) {
                case PROFILE_STR_REDIS7:
                case PROFILE_STR_REDIS8:
                    profileData=keyValue.getValue();
                    break;
                case RESULTS_STR_REDIS7:
                    resultsData=data;

                    break;
                case RESULTS_STR_REDIS8:
                    resultsData=keyValue.getValue();
                    break;
            }

        }
        assert resultsData!=null : "Could not detect Results data.";
        assert profileData!=null : "Could not detect Profile data.";
        return KeyValue.of(resultsBuilder.build(resultsData), ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
    }
    return KeyValue.of(resultsBuilder.build(list.get(0)), ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

public SSLContext createSslContext() throws IOException, GeneralSecurityException {
    KeyManager[] keyManagers=null;
    TrustManager[] trustManagers=null;
    if (sslVerifyMode==SslVerifyMode.FULL) {
        this.sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
    } else {
        if (sslVerifyMode==SslVerifyMode.CA) {
            this.sslParameters.setEndpointIdentificationAlgorithm("");
        } else {
            if (sslVerifyMode==SslVerifyMode.INSECURE) {
                trustManagers=new TrustManager[] { 
                INSECURE_TRUST_MANAGER };
            }
        }
    }
    if (keystoreResource!=null) {
        KeyStore keyStore=KeyStore.getInstance(keyStoreType);
        try (InputStream keystoreStream=keystoreResource.get()) {
            keyStore.load(keystoreStream, keystorePassword);
        }
        KeyManagerFactory keyManagerFactory=KeyManagerFactory.getInstance(keyManagerAlgorithm);
        keyManagerFactory.init(keyStore, keystorePassword);
        keyManagers=keyManagerFactory.getKeyManagers();
    }

    if (trustManagers==null&&truststoreResource!=null) {
        KeyStore trustStore=KeyStore.getInstance(trustStoreType);
        try (InputStream truststoreStream=truststoreResource.get()) {
            trustStore.load(truststoreStream, truststorePassword);
        }
        TrustManagerFactory trustManagerFactory=TrustManagerFactory.getInstance(trustManagerAlgorithm);
        trustManagerFactory.init(trustStore);
        trustManagers=trustManagerFactory.getTrustManagers();
    }
    SSLContext sslContext=SSLContext.getInstance(sslProtocol);
    sslContext.init(keyManagers, trustManagers, null);

    return sslContext;
}

@Override
public TSInfo build(Object data) {
    List<KeyValue> list=(List<KeyValue>) data;
    Map<String, Object> properties=new HashMap<>();
    Map<String, String> labels=null;
    Map<String, Rule> rules=null;
    List<Map<String, Object>> chunks=null;
    for (KeyValue propertyValue : list) {
        String prop=BuilderFactory.STRING.build(propertyValue.getKey());
        Object value=propertyValue.getValue();
        if (valueinstanceofList) {
            switch (prop) {
                case LABELS_PROPERTY:
                    labels=BuilderFactory.STRING_MAP.build(value);
                    value=labels;

                    break;
                case RULES_PROPERTY:
                    List<KeyValue> rulesDataList=(List<KeyValue>) value;
                    Map<String, List<Object>> rulesValueMap=new HashMap<>(rulesDataList.size(), 1f);
                    rules=new HashMap<>(rulesDataList.size());
                    for (KeyValue rkv : rulesDataList) {
                        String ruleName=BuilderFactory.STRING.build(rkv.getKey());
                        List<Object> ruleValueList=BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());
                        rulesValueMap.put(ruleName, ruleValueList);
                        rules.put(ruleName, new Rule(ruleName, ruleValueList));
                    }
                    value=rulesValueMap;

                    break;
                case CHUNKS_PROPERTY:
                    List<List<KeyValue>> chunksDataList=(List<List<KeyValue>>) value;

                    List<Map<String, Object>> chunksValueList=new ArrayList<>(chunksDataList.size());

                    chunks=new ArrayList<>(chunksDataList.size());
                    for (List<KeyValue> chunkDataAsList : chunksDataList) {
                        Map<String, Object> chunk=chunkDataAsList.stream().collect(Collectors.toMap(kv->BuilderFactory.STRING.build(kv.getKey()), kv->BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));
                        chunksValueList.add(chunk);
                        chunks.add(chunk);
                    }
                    value=chunksValueList;

                    break;
                default:
                    value=SafeEncoder.encodeObject(value);

                    break;
            }
        } else {
            if (valueinstanceofbyte[]) {
                value=BuilderFactory.STRING.build(value);
                if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
                    try {
                        value=DuplicatePolicy.valueOf(((String) value).toUpperCase());
                    } catch (Exception e) { 
                    }
                }
            }
        }
        properties.put(prop, value);
    }
    return new TSInfo(properties, labels, rules, chunks);
}

@Override
public void addParams(CommandArguments args) {
    if (retentionPeriod!=null) {
        args.add(RETENTION).add(toByteArray(retentionPeriod));
    }
    if (encoding!=null) {
        args.add(ENCODING).add(encoding);
    }
    if (chunkSize!=null) {
        args.add(CHUNK_SIZE).add(toByteArray(chunkSize));
    }
    if (duplicatePolicy!=null) {
        args.add(DUPLICATE_POLICY).add(duplicatePolicy);
    }
    if (duplicatePolicy!=null) {
        args.add(DUPLICATE_POLICY).add(duplicatePolicy);
    }

    if (onDuplicate!=null) {
        args.add(ON_DUPLICATE).add(onDuplicate);
    }
    if (ignore) {
        args.add(IGNORE).add(ignoreMaxTimediff).add(ignoreMaxValDiff);
    }
    if (labels!=null) {
        args.add(LABELS);
        labels.entrySet().forEach((entry)->args.add(entry.getKey()).add(entry.getValue()));
    }
}

@Override
public Connection getConnection() {
    List<ConnectionPool> pools=getShuffledNodesPool();
    JedisException suppressed=null;
    for (ConnectionPool pool : pools) {
        Connection jedis=null;
        try {
            jedis=pool.getResource();
            if (jedis!=null) { jedis.ping();
                return jedis;
            } 
        } catch (JedisException ex) {
            if (suppressed==null) { // remembering first suppressed exception
                suppressed=ex;
            }
            if (jedis!=null) {
                jedis.close();
            }
        }
    }
    JedisException noReachableNode=new JedisException("No reachable shard.");
    if (suppressed!=null) {
        noReachableNode.addSuppressed(suppressed);
    }
    throw noReachableNode;
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
    lock.lock();
    try {
        entry=putIntoStore(cacheKey, entry);
        EvictionPolicy policy=getEvictionPolicy();
        policy.touch(cacheKey);
        CacheKey evictedKey=policy.evictNext();
        if (evictedKey!=null) {
            delete(evictedKey);
            stats.evict();
        }
        for (Object redisKey : cacheKey.getRedisKeys()) {
            ByteBuffer mapKey=makeKeyForRedisKeysToCacheKeys(redisKey);
            if (!(redisKeysToCacheKeys.containsKey(mapKey))) {
                Set<CacheKey<?>> set=ConcurrentHashMap.newKeySet();
                set.add(cacheKey);

                redisKeysToCacheKeys.put(mapKey, set);
            }
            else {
                redisKeysToCacheKeys.get(mapKey).add(cacheKey);
            } 
        }
        stats.load();

        return entry;
    } finally {
        lock.unlock();
    }
}

@Override
public Class<?> build(Object data) {
    if (data==null) {
        return null;
    }
    String str=STRING.build(data);
    switch (str) {
        case "null":
            return null;
        case "boolean":
            return boolean.class;
        case "integer":
            return int.class;
        case "number":
            return float.class;
        case "string":
            return String.class;
        case "object":
            return Object.class;
        case "array":
            return List.class;
        default:
            throw new JedisException("Unknown type: "+str);
    }
}

@Override
public void startElement(
XlsxReadContext xlsxReadContext, String name, Attributes attributes
) {
    String ref = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_REF);
    if (StringUtils.isEmpty(ref)) {
        return;

    }

    // Hyperlink has 2 case:
    // case 1In the 'location' tag
    String location = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_LOCATION);
    if (location != null) {
        CellExtra cellExtra = new CellExtra(CellExtraTypeEnum.HYPERLINK, location, ref);

        xlsxReadContext.readSheetHolder().setCellExtra(cellExtra);
        xlsxReadContext.analysisEventProcessor().extra(xlsxReadContext);
        return;
    }
    // case 2, In the 'r:id' tag, Then go to 'PackageRelationshipCollection' to get inside
    String rId = attributes.getValue(ExcelXmlConstants.ATTRIBUTE_RID);
    PackageRelationshipCollection packageRelationshipCollection = xlsxReadContext.xlsxReadSheetHolder().getPackageRelationshipCollection();
    if (rId == null || packageRelationshipCollection == null) {
        return;

    }
    Optional.ofNullable(packageRelationshipCollection.getRelationshipByID(rId))
    .map(PackageRelationship::getTargetURI).ifPresent(uri -> {
        CellExtra cellExtra = new CellExtra(CellExtraTypeEnum.HYPERLINK, uri.toString(), ref);

        xlsxReadContext.readSheetHolder().setCellExtra(cellExtra);
        xlsxReadContext.analysisEventProcessor().extra(xlsxReadContext);
    });
}

@Override
public void processRecord(XlsReadContext xlsReadContext, Record record) {
    LabelSSTRecord lsrec = (LabelSSTRecord)record;
    ReadCache readCache = xlsReadContext.readWorkbookHolder().getReadCache();
    Map<Integer, Cell> cellMap = xlsReadContext.xlsReadSheetHolder().getCellMap();
    if (readCache == null) {
        cellMap.put((int)lsrec.getColumn()
        , ReadCellData.newEmptyInstance(lsrec.getRow(), (int)lsrec.getColumn()));
        return;
    }
    String data = readCache.get(lsrec.getSSTIndex());

    if (data == null) {
        cellMap.put((int)lsrec.getColumn()
        , ReadCellData.newEmptyInstance(lsrec.getRow(), (int)lsrec.getColumn()));
        return;
    }

    if (xlsReadContext.currentReadHolder().globalConfiguration().getAutoTrim()) {
        data = data.trim();
    }
    cellMap.put((int)lsrec.getColumn()
    , ReadCellData.newInstance(data, lsrec.getRow(), (int)lsrec.getColumn()));
    xlsReadContext.xlsReadSheetHolder().setTempRowType(RowTypeEnum.DATA);
}

private Row createRowIfNecessary(
Sheet sheet, Sheet cachedSheet, Integer lastRowIndex, FillConfig fillConfig, AnalysisCell analysisCell, boolean isOriginalCell, RowWriteHandlerContext rowWriteHandlerContext
) {
    rowWriteHandlerContext.setRowIndex(lastRowIndex);

    Row row = sheet.getRow(lastRowIndex);

    if (row != null) {
        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);
        rowWriteHandlerContext.setRow(row);

        return row;
    }

    row = cachedSheet.getRow(lastRowIndex);

    if (row != null) {
        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);
        rowWriteHandlerContext.setRow(row);
    } else {
        rowWriteHandlerContext.setRowIndex(lastRowIndex);

        WriteHandlerUtils.beforeRowCreate(rowWriteHandlerContext);

        if (fillConfig.getForceNewRow()) {
            row = cachedSheet.createRow(lastRowIndex);

        } else {
            // The last row of the middle disk inside empty rows, resulting in cachedSheet can not get inside.
            // Will throw Attempting to write a row[" + rownum + "] " + "in the range [0," + this._sh
            // .getLastRowNum() + "] that is already written to disk.
            try {
                row = sheet.createRow(lastRowIndex);
            } catch (IllegalArgumentException ignore) {
                row = cachedSheet.createRow(lastRowIndex);

            }

        }

        rowWriteHandlerContext.setRow(row);

        checkRowHeight(analysisCell, fillConfig, isOriginalCell, row);

        WriteHandlerUtils.afterRowCreate(rowWriteHandlerContext);
    } 

    return row;
}

private Object buildNoModel(
Map<Integer, ReadCellData<?>> cellDataMap, ReadSheetHolder readSheetHolder, AnalysisContext context
) {
    int index = 0;
    Map<Integer, Object> map = MapUtils.newLinkedHashMapWithExpectedSize(cellDataMap.size());
    for (Map.Entry<Integer, ReadCellData<?>> entry : cellDataMap.entrySet()) {
        Integer key = entry.getKey();
        ReadCellData<?> cellData = entry.getValue();
        while (index < key) {
            map.put(index, null);
            index++;
        }
        index++;
        ReadDefaultReturnEnum readDefaultReturn = context.readWorkbookHolder().getReadDefaultReturn();
        if (readDefaultReturn == ReadDefaultReturnEnum.STRING) {
            // string
            map.put(key,(String)ConverterUtils.convertToJavaObject(cellData, null, null, readSheetHolder.converterMap(), context, context.readRowHolder().getRowIndex(), key));

        } else {
            // retrun ReadCellData
            ReadCellData<?> convertedReadCellData = convertReadCellData(cellData, context.readWorkbookHolder().getReadDefaultReturn(), readSheetHolder, context, key);
            if (readDefaultReturn == ReadDefaultReturnEnum.READ_CELL_DATA) {
                map.put(key, convertedReadCellData);

            } else {
                map.put(key, convertedReadCellData.getData());
            }
        }
    }

    // fix https://github.com/alibaba/easyexcel/issues/2014
    int headSize = calculateHeadSize(readSheetHolder);

    while (index < headSize) {
        map.put(index, null);
        index++;
    }

    return map;
}

private void fillComment(
CellWriteHandlerContext cellWriteHandlerContext, CommentData commentData
) {
    if (commentData == null) {
        return;

    }
    ClientAnchor anchor;

    Integer rowIndex = cellWriteHandlerContext.getRowIndex();

    Integer columnIndex = cellWriteHandlerContext.getColumnIndex();
    Sheet sheet = cellWriteHandlerContext.getWriteSheetHolder().getSheet();

    Cell cell = cellWriteHandlerContext.getCell();
    if (writeContext.writeWorkbookHolder().getExcelType() == ExcelTypeEnum.XLSX) {
        anchor = new XSSFClientAnchor(StyleUtil.getCoordinate(commentData.getLeft()), StyleUtil.getCoordinate(commentData.getTop()), StyleUtil.getCoordinate(commentData.getRight()), StyleUtil.getCoordinate(commentData.getBottom()), StyleUtil.getCellCoordinate(columnIndex, commentData.getFirstColumnIndex(), commentData.getRelativeFirstColumnIndex()), StyleUtil.getCellCoordinate(rowIndex, commentData.getFirstRowIndex(), commentData.getRelativeFirstRowIndex()), StyleUtil.getCellCoordinate(columnIndex, commentData.getLastColumnIndex(), commentData.getRelativeLastColumnIndex()) + 1, StyleUtil.getCellCoordinate(rowIndex, commentData.getLastRowIndex(), commentData.getRelativeLastRowIndex()) + 1);

    } else {
        anchor = new HSSFClientAnchor(StyleUtil.getCoordinate(commentData.getLeft()), StyleUtil.getCoordinate(commentData.getTop()), StyleUtil.getCoordinate(commentData.getRight()), StyleUtil.getCoordinate(commentData.getBottom()),(short)StyleUtil.getCellCoordinate(columnIndex, commentData.getFirstColumnIndex(), commentData.getRelativeFirstColumnIndex()), StyleUtil.getCellCoordinate(rowIndex, commentData.getFirstRowIndex(), commentData.getRelativeFirstRowIndex()),(short)(StyleUtil.getCellCoordinate(columnIndex, commentData.getLastColumnIndex(), commentData.getRelativeLastColumnIndex()) + 1), StyleUtil.getCellCoordinate(rowIndex, commentData.getLastRowIndex(), commentData.getRelativeLastRowIndex()) + 1);

    }
    Comment comment = sheet.createDrawingPatriarch().createCellComment(anchor);
    if (commentData.getRichTextStringData() != null) {
        comment.setString(StyleUtil.buildRichTextString(writeContext.writeWorkbookHolder(), commentData.getRichTextStringData()));

    }
    if (commentData.getAuthor() != null) {
        comment.setAuthor(commentData.getAuthor());

    }
    cell.setCellComment(comment);
}

private Format getFormat(Double data, Short dataFormat, String dataFormatString) {
    // Might be better to separate out the n p and z formats, falling back to p when n and z are not set.
    // That however would require other code to be re factored.
    // String[] formatBits = formatStrIn.split(";");
    // int i = cellValue > 0.0 ? 0 : cellValue < 0.0 ? 1 : 2;
    // String formatStr = (i < formatBits.length) ? formatBits[i] : formatBits[0];
    String formatStr = dataFormatString;
    // Excel supports 2+ part conditional data formats, eg positive/negative/zero,
    //  or (>1000),(>0),(0),(negative). As Java doesn't handle these kinds
    //  of different formats for different ranges, just +ve/-ve, we need to
    //  handle these ourselves in a special way.
    // For now, if we detect 2+ parts, we call out to CellFormat to handle it
    // TODO Going forward, we should really merge the logic between the two classes
    if (formatStr.contains(";") && (formatStr.indexOf(';') != formatStr.lastIndexOf(';') || rangeConditionalPattern.matcher(formatStr).matches())) {
        try {
            // Ask CellFormat to get a formatter for it
            CellFormat cfmt = CellFormat.getInstance(locale, formatStr);

            // CellFormat requires callers to identify date vs not, so do so
            Object cellValueO = data;
            if (DateUtils.isADateFormat(dataFormat, formatStr) && // don't try to handle Date value 0, let a 3 or 4-part format take care of it
            data.doubleValue() != 0.0) {
                cellValueO = DateUtils.getJavaDate(data, use1904windowing);
            }
            // Wrap and return (non-cachable - CellFormat does that)
            return new CellFormatResultWrapper(cfmt.apply(cellValueO));
        } catch (Exception e) {
            LOGGER.warn("Formatting failed for format {}, falling back", formatStr, e);
        }

    }
    // See if we already have it cached
    Format format = formats.get(formatStr);
    if (format != null) {
        return format;

    }
    // Is it one of the special built in types, General or @?
    if ("General".equalsIgnoreCase(formatStr) || "@".equals(formatStr)) {
        format = getDefaultFormat();
        addFormat(formatStr, format);

        return format;
    }
    // Build a formatter, and cache it
    format = createFormat(dataFormat, formatStr);
    addFormat(formatStr, format);
    return format;
}

