private void addJavaObjectToExcel(
Object oneRowData, Row row, int rowIndex, int relativeRowIndex
) {
    WriteHolder currentWriteHolder = writeContext.currentWriteHolder();

    BeanMap beanMap = BeanMapUtils.create(oneRowData);

    // Bean the contains of the Map Key method with poor performance,So to create a keySet here
    Set<String> beanKeySet = new HashSet<>(beanMap.keySet());

    Set<String> beanMapHandledSet = new HashSet<>();

    int maxCellIndex = -1;

    // If it's a class it needs to be cast by type
    if (HeadKindEnum.CLASS.equals(writeContext.currentWriteHolder().excelWriteHeadProperty().getHeadKind())) {
        Map<Integer, Head> headMap = writeContext.currentWriteHolder().excelWriteHeadProperty().getHeadMap();
        for (Map.Entry<Integer, Head> entry : headMap.entrySet()) {
            int columnIndex = entry.getKey();
            Head head = entry.getValue();

            String name = head.getFieldName();

            if (!beanKeySet.contains(name)) {
                continue;

            }
            ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), name, currentWriteHolder);

            CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(writeContext, row, rowIndex, head, columnIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);
            WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);

            Cell cell = WorkBookUtil.createCell(row, columnIndex);
            cellWriteHandlerContext.setCell(cell);
            WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);

            cellWriteHandlerContext.setOriginalValue(beanMap.get(name));
            cellWriteHandlerContext.setOriginalFieldClass(head.getField().getType());

            converterAndSet(cellWriteHandlerContext);

            WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);

            beanMapHandledSet.add(name);

            maxCellIndex = Math.max(maxCellIndex, columnIndex);

        }
    }

    // Finish
    if (beanMapHandledSet.size() == beanMap.size()) {
        return;

    }

    maxCellIndex++;

    FieldCache fieldCache = ClassUtils.declaredFields(oneRowData.getClass(), writeContext.currentWriteHolder());
    for (Map.Entry<Integer, FieldWrapper> entry : fieldCache.getSortedFieldMap().entrySet()) {
        FieldWrapper field = entry.getValue();
        String fieldName = field.getFieldName();

        boolean uselessData = !beanKeySet.contains(fieldName) || beanMapHandledSet.contains(fieldName);
        if (uselessData) {
            continue;

        }
        Object value = beanMap.get(fieldName);
        ExcelContentProperty excelContentProperty = ClassUtils.declaredExcelContentProperty(beanMap, currentWriteHolder.excelWriteHeadProperty().getHeadClazz(), fieldName, currentWriteHolder);

        CellWriteHandlerContext cellWriteHandlerContext = WriteHandlerUtils.createCellWriteHandlerContext(writeContext, row, rowIndex, null, maxCellIndex, relativeRowIndex, Boolean.FALSE, excelContentProperty);
        WriteHandlerUtils.beforeCellCreate(cellWriteHandlerContext);

        // fix https://github.com/alibaba/easyexcel/issues/1870
        // If there is data, it is written to the next cell
        Cell cell = WorkBookUtil.createCell(row, maxCellIndex);
        cellWriteHandlerContext.setCell(cell);
        WriteHandlerUtils.afterCellCreate(cellWriteHandlerContext);
        cellWriteHandlerContext.setOriginalValue(value);

        cellWriteHandlerContext
        .setOriginalFieldClass(FieldUtils.getFieldClass(beanMap, fieldName, value));
        converterAndSet(cellWriteHandlerContext);

        WriteHandlerUtils.afterCellDispose(cellWriteHandlerContext);

        maxCellIndex++;
    }
}

public CellStyle createCellStyle(WriteCellStyle writeCellStyle, CellStyle originCellStyle) {
    if (writeCellStyle == null) {
        return originCellStyle;
    }

    short styleIndex = -1;

    Font originFont = null;
    boolean useCache = true;
    if (originCellStyle != null) {
        styleIndex = originCellStyle.getIndex();
        if (originCellStyle instanceof XSSFCellStyle) {
            originFont = ((XSSFCellStyle)originCellStyle).getFont();
        } else {
            if (originCellStyle instanceof HSSFCellStyle) {
                originFont = ((HSSFCellStyle)originCellStyle).getFont(workbook);
            }

        }
        useCache = false;
    }

    Map<WriteCellStyle, CellStyle> cellStyleMap = cellStyleIndexMap.computeIfAbsent(styleIndex, key -> MapUtils.newHashMap());

    CellStyle cellStyle = cellStyleMap.get(writeCellStyle);
    if (cellStyle != null) {
        return cellStyle;
    }
    if (log.isDebugEnabled()) {
        log.info("create new style:{},{}", writeCellStyle, originCellStyle);
    }
    WriteCellStyle tempWriteCellStyle = new WriteCellStyle();
    WriteCellStyle.merge(writeCellStyle, tempWriteCellStyle);
    cellStyle = StyleUtil.buildCellStyle(workbook, originCellStyle, tempWriteCellStyle);
    Short dataFormat = createDataFormat(tempWriteCellStyle.getDataFormatData(), useCache);
    if (dataFormat != null) {
        cellStyle.setDataFormat(dataFormat);

    }
    Font font = createFont(tempWriteCellStyle.getWriteFont(), originFont, useCache);

    if (font != null) {
        cellStyle.setFont(font);
    }
    cellStyleMap.put(tempWriteCellStyle, cellStyle);
    return cellStyle;
}

private void buildHead(
AnalysisContext analysisContext, Map<Integer, ReadCellData<?>> cellDataMap
) {
    // Rule out empty head, and then take the largest column
    if (MapUtils.isNotEmpty(cellDataMap)) {
        cellDataMap.entrySet().stream().filter(entry -> CellDataTypeEnum.EMPTY != entry.getValue().getType()).forEach(entry -> analysisContext.readSheetHolder().setMaxNotEmptyDataHeadSize(entry.getKey()));

    }
    if (!HeadKindEnum.CLASS.equals(analysisContext.currentReadHolder().excelReadHeadProperty().getHeadKind())) {
        return;

    }
    Map<Integer, String> dataMap = ConverterUtils.convertToStringMap(cellDataMap, analysisContext);
    ExcelReadHeadProperty excelHeadPropertyData = analysisContext.readSheetHolder().excelReadHeadProperty();

    Map<Integer, Head> headMapData = excelHeadPropertyData.getHeadMap();

    Map<Integer, Head> tmpHeadMap = new HashMap<Integer, Head>(headMapData.size() * 4 / 3 + 1);
    for (Map.Entry<Integer, Head> entry : headMapData.entrySet()) {
        Head headData = entry.getValue();
        if (headData.getForceIndex() || !headData.getForceName()) {
            tmpHeadMap.put(entry.getKey(), headData);
            continue;
        }

        List<String> headNameList = headData.getHeadNameList();
        String headName = headNameList.get(headNameList.size() - 1);
        for (Map.Entry<Integer, String> stringEntry : dataMap.entrySet()) {
            if (stringEntry == null) {
                continue;

            }
            String headString = stringEntry.getValue();

            Integer stringKey = stringEntry.getKey();

            if (StringUtils.isEmpty(headString)) {
                continue;

            }
            if (analysisContext.currentReadHolder().globalConfiguration().getAutoTrim()) {
                headString = headString.trim();
            }
            if (headName.equals(headString)) {
                headData.setColumnIndex(stringKey);
                tmpHeadMap.put(stringKey, headData);

                break;
            }
        }
    }

    excelHeadPropertyData.setHeadMap(tmpHeadMap);
}

public static HealthCountsStream getInstance(HystrixCommandKey commandKey, int numBuckets, int bucketSizeInMs) {
    HealthCountsStream initialStream = streams.get(commandKey.name());
    if (initialStream != null) return initialStream;else {
        final HealthCountsStream healthStream;
        synchronized (HealthCountsStream.class) {
            HealthCountsStream existingStream = streams.get(commandKey.name());
            if (existingStream != null) healthStream = existingStream;else {
                HealthCountsStream newStream = new HealthCountsStream(commandKey, numBuckets, bucketSizeInMs, HystrixCommandMetrics.appendEventToBucket);
                streams.putIfAbsent(commandKey.name(), newStream);
                healthStream = newStream;
            } 
        }
        healthStream.startCachingStreamValuesIfUnstarted();
        return healthStream;
    }
}

public Map<ExecutionSignature, List<Integer>> getExecutionsMappedToLatencies() {
    Map<CommandAndCacheKey, Integer> cachingDetector = new HashMap<CommandAndCacheKey, Integer>();
    List<HystrixInvokableInfo<?>> nonCachedExecutions = new ArrayList<HystrixInvokableInfo<?>>(executions.size());
    for (HystrixInvokableInfo<?> execution : executions) {
        if (execution.getPublicCacheKey() != null) {
            //eligible for caching - might be the initial, or might be from cache
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), execution.getPublicCacheKey());
            Integer count = cachingDetector.get(key);

            if (count != null) cachingDetector.put(key, count + 1);else cachingDetector.put(key, 0);
        }
        if (execution.isResponseFromCache()) { 
            continue ; 
        }  
        nonCachedExecutions.add(execution);
    }

    Map<ExecutionSignature, List<Integer>> commandDeduper = new HashMap<ExecutionSignature, List<Integer>>();
    for (HystrixInvokableInfo<?> execution : nonCachedExecutions) {
        int cachedCount = 0;
        String cacheKey = execution.getPublicCacheKey();
        if (cacheKey != null) {
            CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), cacheKey);
            cachedCount = cachingDetector.get(key);
        }
        ExecutionSignature signature = cachedCount > 0? ExecutionSignature.from(execution, cacheKey, cachedCount) : ExecutionSignature.from(execution); List<Integer> currentLatencyList = commandDeduper.get(signature);
        if (currentLatencyList != null) { 
            currentLatencyList.add(execution.getExecutionTimeInMilliseconds());
            continue; 
        }  
        List<Integer> newLatencyList = new ArrayList<Integer>();
        newLatencyList.add(execution.getExecutionTimeInMilliseconds());

        commandDeduper.put(signature, newLatencyList);
    }
    return commandDeduper;
}

public ThreadPoolExecutor getThreadPool(final HystrixThreadPoolKey threadPoolKey, HystrixThreadPoolProperties threadPoolProperties) {
    final ThreadFactory threadFactory = getThreadFactory(threadPoolKey);
    final boolean allowMaximumSizeToDivergeFromCoreSize = threadPoolProperties.getAllowMaximumSizeToDivergeFromCoreSize().get();
    final int dynamicCoreSize = threadPoolProperties.coreSize().get();
    final int keepAliveTime = threadPoolProperties.keepAliveTimeMinutes().get();
    final int maxQueueSize = threadPoolProperties.maxQueueSize().get();
    final BlockingQueue<Runnable> workQueue = getBlockingQueue(maxQueueSize);
    if (!allowMaximumSizeToDivergeFromCoreSize) return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);else {
        final int dynamicMaximumSize = threadPoolProperties.maximumSize().get();
        if (dynamicCoreSize <= dynamicMaximumSize) return new ThreadPoolExecutor(dynamicCoreSize, dynamicMaximumSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);else {
            logger.error("Hystrix ThreadPool configuration at startup for : " + threadPoolKey.name() + " is trying to set coreSize = " + dynamicCoreSize + " and maximumSize = " + dynamicMaximumSize + ".  Maximum size will be set to " + dynamicCoreSize + ", the coreSize value, since it must be equal to or greater than the coreSize value");
            return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
        } 
    } }

