    metadata.put("CreationDate", formatDate(info.getCreationDate()));
    metadata.put("ModificationDate", formatDate(info.getModificationDate()));
    jsonOutput.set("Metadata", metadata);

    // Total file size of the PDF
    long fileSizeInBytes = inputFile.getSize();

    basicInfo.put("FileSizeInBytes", fileSizeInBytes);
    // Number of words, paragraphs, and images in the entire document
    String fullText = new PDFTextStripper().getText(pdfBoxDoc);
    String[] words = fullText.split("\\s+");

    int wordCount = words.length;
    int paragraphCount = fullText.split("\r\n|\r|\n").length;
    basicInfo.put("WordCount", wordCount);
    basicInfo.put("ParagraphCount", paragraphCount);
    // Number of characters in the entire document (including spaces and special characters)
    int charCount = fullText.length();
    basicInfo.put("CharacterCount", charCount);
    // Initialize the flags and types
    boolean hasCompression = false;
    String compressionType = "None";
    basicInfo.put("Compression", hasCompression);
    if (hasCompression) {
      basicInfo.put("CompressionType", compressionType);
    }
    String language = pdfBoxDoc.getDocumentCatalog().getLanguage();
    basicInfo.put("Language", language);
    basicInfo.put("Number of pages", pdfBoxDoc.getNumberOfPages());
    PDDocumentCatalog catalog = pdfBoxDoc.getDocumentCatalog();
    String pageMode = catalog.getPageMode().name();
    // Document Information using PDFBox
    docInfoNode.put("PDF version", pdfBoxDoc.getVersion());
    docInfoNode.put("Trapped", info.getTrapped());
    docInfoNode.put("Page Mode", getPageModeDescription(pageMode));
    ;
    PDAcroForm acroForm = pdfBoxDoc.getDocumentCatalog().getAcroForm();
    ObjectNode formFieldsNode = objectMapper.createObjectNode();
    if (acroForm != null) {
      for (PDField field : acroForm.getFieldTree()) {
        formFieldsNode.put(field.getFullyQualifiedName(), field.getValueAsString());
      }
    }
    jsonOutput.set("FormFields", formFieldsNode);
    // Generate structured summary data about PDF characteristics
    ObjectNode summaryData = generatePDFSummaryData(pdfBoxDoc);

    if (summaryData != null && 0< summaryData.size()) {
      jsonOutput.set("SummaryData", summaryData);
    }

    // embeed files TODO size
    if (catalog.getNames() != null) {

      PDEmbeddedFilesNameTreeNode efTree = catalog.getNames().getEmbeddedFiles();

      ArrayNode embeddedFilesArray = objectMapper.createArrayNode();
      if (efTree != null) {
        Map<String, PDComplexFileSpecification> efMap = efTree.getNames();
        if (efMap != null) {
          for (Map.Entry<String, PDComplexFileSpecification> entry : efMap.entrySet()) {
            ObjectNode embeddedFileNode = objectMapper.createObjectNode();
            embeddedFileNode.put("Name", entry.getKey());
            PDEmbeddedFile embeddedFile = entry.getValue().getEmbeddedFile();

            if (embeddedFile != null) {
              embeddedFileNode.put("FileSize", embeddedFile.getLength()); // size in bytes
            }

            embeddedFilesArray.add(embeddedFileNode);

          }
        }
      }
      other.set("EmbeddedFiles", embeddedFilesArray);
    }

    // attachments TODO size
    ArrayNode attachmentsArray = objectMapper.createArrayNode();
    for (PDPage page : pdfBoxDoc.getPages()) {
      for (PDAnnotation annotation : page.getAnnotations()) {
        if (annotation instanceof PDAnnotationFileAttachment fileAttachmentAnnotation) {

          ObjectNode attachmentNode = objectMapper.createObjectNode();

          attachmentNode.put("Name", fileAttachmentAnnotation.getAttachmentName());
          attachmentNode.put("Description", fileAttachmentAnnotation.getContents());

          attachmentsArray.add(attachmentNode);
        }
      }
    }

    other.set("Attachments", attachmentsArray);
    // Javascript
    PDDocumentNameDictionary namesDict = catalog.getNames();
    ArrayNode javascriptArray = objectMapper.createArrayNode();
    if (namesDict != null) {

      PDJavascriptNameTreeNode javascriptDict = namesDict.getJavaScript();

      if (javascriptDict != null) {
        try {
          Map<String, PDActionJavaScript> jsEntries = javascriptDict.getNames();
          for (Map.Entry<String, PDActionJavaScript> entry : jsEntries.entrySet()) {
            ObjectNode jsNode = objectMapper.createObjectNode();

            jsNode.put("JS Name", entry.getKey());

            PDActionJavaScript jsAction = entry.getValue();

            if (jsAction != null) {
              String jsCodeStr = jsAction.getAction();
              if (jsCodeStr != null) {
                jsNode.put("JS Script Length", jsCodeStr.length());
              }
            }
            javascriptArray.add(jsNode);
          }
        } catch (IOException e) {
          log.error("exception", e);
        }
      }
    }

    other.set("JavaScript", javascriptArray);
    // TODO size
    PDOptionalContentProperties ocProperties = pdfBoxDoc.getDocumentCatalog().getOCProperties();
    ArrayNode layersArray = objectMapper.createArrayNode();
    if (ocProperties != null) {
      for (PDOptionalContentGroup ocg : ocProperties.getOptionalContentGroups()) {
        ObjectNode layerNode = objectMapper.createObjectNode();

        layerNode.put("Name", ocg.getName());
        layersArray.add(layerNode);
      }
    }
    other.set("Layers", layersArray);// TODO Security

    PDStructureTreeRoot structureTreeRoot = pdfBoxDoc.getDocumentCatalog().getStructureTreeRoot();

    ArrayNode structureTreeArray;
    try {
      if (structureTreeRoot != null) {

        structureTreeArray = exploreStructureTree(structureTreeRoot.getKids());

        other.set("StructureTree", structureTreeArray);

      }
    } catch (Exception e) {
      // TODO Auto-generated catch block
      log.error("exception", e);
    }

    boolean isPdfACompliant = checkForStandard(pdfBoxDoc, "PDF/A");
    boolean isPdfXCompliant = checkForStandard(pdfBoxDoc, "PDF/X");
    boolean isPdfECompliant = checkForStandard(pdfBoxDoc, "PDF/E");
    boolean isPdfVTCompliant = checkForStandard(pdfBoxDoc, "PDF/VT");
    boolean isPdfUACompliant = checkForStandard(pdfBoxDoc, "PDF/UA");
    boolean isPdfBCompliant = checkForStandard(pdfBoxDoc, "PDF/B"); // If you want to check for PDF/Broadcast, though this isn't
    // an official ISO standard.
    boolean isPdfSECCompliant = checkForStandard(pdfBoxDoc, "PDF/SEC"); // This might not be effective since PDF/SEC was under
    // development in 2021.
    compliancy.put("IsPDF/ACompliant", isPdfACompliant);
    compliancy.put("IsPDF/XCompliant", isPdfXCompliant);
    compliancy.put("IsPDF/ECompliant", isPdfECompliant);
    compliancy.put("IsPDF/VTCompliant", isPdfVTCompliant);
    compliancy.put("IsPDF/UACompliant", isPdfUACompliant);
    compliancy.put("IsPDF/BCompliant", isPdfBCompliant);
    compliancy.put("IsPDF/SECCompliant", isPdfSECCompliant);

    PDOutlineNode root = pdfBoxDoc.getDocumentCatalog().getDocumentOutline();
    ArrayNode bookmarksArray = objectMapper.createArrayNode();
    if (root != null) {
      for (PDOutlineItem child : root.children()) {
        addOutlinesToArray(child, bookmarksArray);
      }
    }
    other.set("Bookmarks/Outline/TOC", bookmarksArray);

    PDMetadata pdMetadata = pdfBoxDoc.getDocumentCatalog().getMetadata();
    String xmpString = null;
    if (pdMetadata != null) {
      try {
        try (COSInputStream is = pdMetadata.createInputStream()) {
          DomXmpParser domXmpParser = new DomXmpParser();
          XMPMetadata xmpMeta = domXmpParser.parse(is);

          ByteArrayOutputStream os = new ByteArrayOutputStream();
          new XmpSerializer().serialize(xmpMeta, os, true);
          xmpString = new String(os.toByteArray(), StandardCharsets.UTF_8);
        }
      } catch (XmpParsingException e) {

        // XMP parsing failed, try to read raw metadata instead
        log.debug("XMP parsing failed, reading raw metadata: {}", e.getMessage());

        try (COSInputStream is = pdMetadata.createInputStream()) {
          byte[] metadataBytes = is.readAllBytes();
          xmpString = new String(metadataBytes, StandardCharsets.UTF_8);
        }
      } catch (IOException e) {
        log.error("exception", e);
      }
    }
    other.put("XMPMetadata", xmpString);

    if (pdfBoxDoc.isEncrypted()) {
      encryption.put("IsEncrypted", true);

      // Retrieve encryption details using getEncryption()
      PDEncryption pdfEncryption = pdfBoxDoc.getEncryption();
      encryption.put("EncryptionAlgorithm", pdfEncryption.getFilter());
      encryption.put("KeyLength", pdfEncryption.getLength());
      // Add other encryption-related properties as needed
    } else {
      encryption.put("IsEncrypted", false);
    }

    ObjectNode permissionsNode = objectMapper.createObjectNode();
    setNodePermissions(pdfBoxDoc, permissionsNode);
    ObjectNode pageInfoParent = objectMapper.createObjectNode();
    for (int pageNum = 0; pageNum < pdfBoxDoc.getNumberOfPages(); pageNum++) {
      ObjectNode pageInfo = objectMapper.createObjectNode();

      // Retrieve the page
      PDPage page = pdfBoxDoc.getPage(pageNum);

      // Page-level Information
      PDRectangle mediaBox = page.getMediaBox();
      float width = mediaBox.getWidth();
      float height = mediaBox.getHeight();

      ObjectNode sizeInfo = objectMapper.createObjectNode();
      getDimensionInfo(sizeInfo, width, height);
      sizeInfo.put("Standard Page", getPageSize(width, height));

      pageInfo.set("Size", sizeInfo);

      pageInfo.put("Rotation", page.getRotation());
      pageInfo.put("Page Orientation", getPageOrientation(width, height));

      // Boxes
      pageInfo.put("MediaBox", mediaBox.toString());

      // Assuming the following boxes are defined for your document; if not, you may get
      // null values.
      PDRectangle cropBox = page.getCropBox();

      pageInfo.put("CropBox", cropBox == null ? "Undefined" : cropBox.toString());

      PDRectangle bleedBox = page.getBleedBox();

      pageInfo.put("BleedBox", bleedBox == null ? "Undefined" : bleedBox.toString());

      PDRectangle trimBox = page.getTrimBox();

      pageInfo.put("TrimBox", trimBox == null ? "Undefined" : trimBox.toString());

      PDRectangle artBox = page.getArtBox();

      pageInfo.put("ArtBox", artBox == null ? "Undefined" : artBox.toString());

      // Content Extraction
      PDFTextStripper textStripper = new PDFTextStripper();

      textStripper.setStartPage(pageNum + 1);

      textStripper.setEndPage(pageNum + 1);

      String pageText = textStripper.getText(pdfBoxDoc);
      pageInfo.put("Text Characters Count", pageText.length()); //
      // Annotations
      List<PDAnnotation> annotations = page.getAnnotations();

      int subtypeCount = 0;
      int contentsCount = 0;
      for (PDAnnotation annotation : annotations) {
        if (annotation.getSubtype() != null) {
          subtypeCount++; // Increase subtype count
        }

        if (annotation.getContents() != null) {
          contentsCount++; // Increase contents count
        }

      }
      ObjectNode annotationsObject = objectMapper.createObjectNode();
      annotationsObject.put("AnnotationsCount", annotations.size());
      annotationsObject.put("SubtypeCount", subtypeCount);
      annotationsObject.put("ContentsCount", contentsCount);
      pageInfo.set("Annotations", annotationsObject);
      // Images (simplified)
      // This part is non-trivial as images can be embedded in multiple ways in a PDF.
      // Here is a basic structure to recognize image XObjects on a page.
      ArrayNode imagesArray = objectMapper.createArrayNode();
      PDResources resources = page.getResources();
      for (COSName name : resources.getXObjectNames()) {
        PDXObject xObject = resources.getXObject(name);
        if (xObject instanceof PDImageXObject image) {
          ObjectNode imageNode = objectMapper.createObjectNode();

          imageNode.put("Width", image.getWidth());

          imageNode.put("Height", image.getHeight());

          if (image.getMetadata() != null && image.getMetadata().getFile() != null && image.getMetadata().getFile().getFile() != null) {
            imageNode.put("Name", image.getMetadata().getFile().getFile());
          }
          if (image.getColorSpace() != null) {
            imageNode.put("ColorSpace", image.getColorSpace().getName());
          }
          imagesArray.add(imageNode);
        }
      }

      pageInfo.set("Images", imagesArray);
      // Links
      ArrayNode linksArray = objectMapper.createArrayNode();
      Set<String> uniqueURIs = new HashSet<>(); // To store unique URIs
      for (PDAnnotation annotation : annotations) {
        if (annotation instanceof PDAnnotationLink linkAnnotation) {
          if (linkAnnotation.getAction() instanceof PDActionURI uriAction) {
            String uri = uriAction.getURI();
            uniqueURIs.add(uri); // Add to set to ensure uniqueness
          }
        }
      }

      // Add unique URIs to linksArray
      for (String uri : uniqueURIs) {
        ObjectNode linkNode = objectMapper.createObjectNode();

        linkNode.put("URI", uri);
        linksArray.add(linkNode);
      }

      pageInfo.set("Links", linksArray);
      // Fonts
      ArrayNode fontsArray = objectMapper.createArrayNode();
      Map<String, ObjectNode> uniqueFontsMap = new HashMap<>();
      for (COSName fontName : resources.getFontNames()) {
        PDFont font = resources.getFont(fontName);

        ObjectNode fontNode = objectMapper.createObjectNode();
        fontNode.put("IsEmbedded", font.isEmbedded());
        // PDFBox provides Font's BaseFont (i.e., the font name) directly
        fontNode.put("Name", font.getName());

        fontNode.put("Subtype", font.getType());

        PDFontDescriptor fontDescriptor = font.getFontDescriptor();

        if (fontDescriptor != null) {
          fontNode.put("ItalicAngle", fontDescriptor.getItalicAngle());
          int flags = fontDescriptor.getFlags();

          fontNode.put("IsItalic", (flags & 1) != 0);

          fontNode.put("IsBold", (flags & 64) != 0);

          fontNode.put("IsFixedPitch", (flags & 2) != 0);

          fontNode.put("IsSerif", (flags & 4) != 0);

          fontNode.put("IsSymbolic", (flags & 8) != 0);

          fontNode.put("IsScript", (flags & 16) != 0);

          fontNode.put("IsNonsymbolic", (flags & 32) != 0);

          fontNode.put("FontFamily", fontDescriptor.getFontFamily());
          // Font stretch and BBox are not directly available in PDFBox's API, so
          // these are omitted for simplicity
          fontNode.put("FontWeight", fontDescriptor.getFontWeight());
        }

        // Create a unique key for this font node based on its attributes
        String uniqueKey = fontNode.toString();

        // Increment count if this font exists, or initialize it if new
        if (uniqueFontsMap.containsKey(uniqueKey)) {
          ObjectNode existingFontNode = uniqueFontsMap.get(uniqueKey);
          int count = existingFontNode.get("Count").asInt() + 1;
          existingFontNode.put("Count", count);
        } else {
          fontNode.put("Count", 1);
          uniqueFontsMap.put(uniqueKey, fontNode);
        }
      }

      // Add unique font entries to fontsArray
      for (ObjectNode uniqueFontNode : uniqueFontsMap.values()) {
        fontsArray.add(uniqueFontNode);
      }
      pageInfo.set("Fonts", fontsArray);

      // Access resources dictionary
      ArrayNode colorSpacesArray = objectMapper.createArrayNode();

      Iterable<COSName> colorSpaceNames = resources.getColorSpaceNames();
      for (COSName name : colorSpaceNames) {
        PDColorSpace colorSpace = resources.getColorSpace(name);

        if (colorSpace instanceof PDICCBased iccBased) {
          PDStream iccData = iccBased.getPDStream();
          byte[] iccBytes = iccData.toByteArray();

          // TODO: Further decode and analyze the ICC data if needed
          ObjectNode iccProfileNode = objectMapper.createObjectNode();
          iccProfileNode.put("ICC Profile Length", iccBytes.length);
          colorSpacesArray.add(iccProfileNode);
        }
      }
      pageInfo.set("Color Spaces & ICC Profiles", colorSpacesArray);
      // Other XObjects
      Map<String, Integer> xObjectCountMap = new HashMap<>(); // To store the count for each type
      for (COSName name : resources.getXObjectNames()) {
        PDXObject xObject = resources.getXObject(name);
        String xObjectType;
        if (!(xObject instanceof PDImageXObject)) {
          if (xObject instanceof PDFormXObject) {
            xObjectType = "Form";
          } else {
            xObjectType = "Other";
          }
          // Increment the count for this type in the map
        } else {
          xObjectType = "Image";
        } 
        xObjectCountMap.put(xObjectType, xObjectCountMap.getOrDefault(xObjectType, 0) + 1);

      }
      // Add the count map to pageInfo (or wherever you want to store it)
      ObjectNode xObjectCountNode = objectMapper.createObjectNode();
      for (Map.Entry<String, Integer> entry : xObjectCountMap.entrySet()) {
        xObjectCountNode.put(entry.getKey(), entry.getValue());
      }
      pageInfo.set("XObjectCounts", xObjectCountNode);
      ArrayNode multimediaArray = objectMapper.createArrayNode();
      for (PDAnnotation annotation : annotations) {
        if ("RichMedia".equals(annotation.getSubtype())) {
          ObjectNode multimediaNode = objectMapper.createObjectNode();
          // Extract details from the annotation as needed
          multimediaArray.add(multimediaNode);
        }
      }

      pageInfo.set("Multimedia", multimediaArray);
      pageInfoParent.set("Page " + (pageNum + 1), pageInfo);
    }

    jsonOutput.set("BasicInfo", basicInfo);

    jsonOutput.set("DocumentInfo", docInfoNode);

    jsonOutput.set("Compliancy", compliancy);

    jsonOutput.set("Encryption", encryption);

    jsonOutput.set("Permissions", permissionsNode); // set the node under "Permissions"
    jsonOutput.set("Other", other);
    jsonOutput.set("PerPageInfo", pageInfoParent);
    // Save JSON to file
    String jsonString = objectMapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonOutput);
    return WebResponseUtils.bytesToWebResponse(jsonString.getBytes(StandardCharsets.UTF_8), "response.json", MediaType.APPLICATION_JSON);
  } catch (Exception e) {
    log.error("exception", e);
  }
  return null;

}

private boolean verifyCertificateLicense(String licenseFile, LicenseContext context) {
  try {
    String encodedPayload = licenseFile;
    // Remove the header
    encodedPayload = encodedPayload.replace(CERT_PREFIX, "");
    // Remove the footer
    encodedPayload = encodedPayload.replace(CERT_SUFFIX, "");
    // Remove all newlines
    encodedPayload = encodedPayload.replaceAll("\\r?\\n", "");
    byte[] payloadBytes = Base64.getDecoder().decode(encodedPayload);
    String payload = new String(payloadBytes);
    log.info("Decoded certificate payload: {}", payload);
    String encryptedData = "";
    String encodedSignature = "";
    String algorithm = "";
    try {
      JSONObject attrs = new JSONObject(payload);
      encryptedData = (String) attrs.get("enc");

      encodedSignature = (String) attrs.get("sig");

      algorithm = (String) attrs.get("alg");
    } catch (JSONException e) {
      log.error("Failed to parse license file: {}", e.getMessage());
      return false;

    }

    // Verify license file algorithm
    if (!"base64+ed25519".equals(algorithm)) {

      log.error("Unsupported algorithm: {}. Only base64+ed25519 is supported.", algorithm);

      return false;

    }

    // Verify signature
    boolean isSignatureValid = verifyEd25519Signature(encryptedData, encodedSignature);

    if (!isSignatureValid) {
      log.error("License file signature is invalid");

      return false;

    }

    log.info("License file signature is valid");
    // Decode the base64 data
    String decodedData;
    try {
      decodedData = new String(Base64.getDecoder().decode(encryptedData));
    } catch (IllegalArgumentException e) {
      log.error("Failed to decode license data: {}", e.getMessage());
      return false;

    }
    // Process the certificate data
    boolean isValid = processCertificateData(decodedData, context);
    return isValid;

  } catch (Exception e) {
    log.error("Error verifying certificate license: {}", e.getMessage(), e);
    return false;
  }
}

private static void sanitizeHtmlFilesInZip(Path zipFilePath, boolean disableSanitize, TempFileManager tempFileManager)
throws IOException {
  try (TempDirectory tempUnzippedDir = new TempDirectory(tempFileManager)) {
    try (ZipInputStream zipIn = ZipSecurity.createHardenedInputStream(new ByteArrayInputStream(Files.readAllBytes(zipFilePath)))) {
      ZipEntry entry = zipIn.getNextEntry();
      while (entry != null) {
        Path filePath = tempUnzippedDir.getPath().resolve(sanitizeZipFilename(entry.getName()));
        if (!entry.isDirectory()) {
          Files.createDirectories(filePath.getParent());

          if (entry.getName().toLowerCase().endsWith(".html") || entry.getName().toLowerCase().endsWith(".htm")) {

            String content = new String(zipIn.readAllBytes(), StandardCharsets.UTF_8);

            String sanitizedContent = sanitizeHtmlContent(content, disableSanitize);

            Files.write(filePath, sanitizedContent.getBytes(StandardCharsets.UTF_8));
          } else {
            Files.copy(zipIn, filePath);
          }
        }
        zipIn.closeEntry();
        entry = zipIn.getNextEntry();
      }
    }

    // Repack the sanitized files
    zipDirectory(tempUnzippedDir.getPath(), zipFilePath);
  } // tempUnzippedDir auto-cleaned
}

public static void addImageToDocument(PDDocument doc, PDImageXObject image, String fitOption, boolean autoRotate)
throws IOException {
  boolean imageIsLandscape = image.getHeight()< image.getWidth();
  PDRectangle pageSize = PDRectangle.A4;
  if (autoRotate && imageIsLandscape) {
    pageSize = new PDRectangle(pageSize.getHeight(), pageSize.getWidth());
  }
  if ("fitDocumentToImage".equals(fitOption)) {
    pageSize = new PDRectangle(image.getWidth(), image.getHeight());
  }

  PDPage page = new PDPage(pageSize);

  doc.addPage(page);
  float pageWidth = page.getMediaBox().getWidth();
  float pageHeight = page.getMediaBox().getHeight();
  try (PDPageContentStream contentStream = new PDPageContentStream(doc, page, AppendMode.APPEND, true, true)) {
    if (!("fillPage".equals(fitOption) || "fitDocumentToImage".equals(fitOption))) {
      if ("maintainAspectRatio".equals(fitOption)) {
        float imageAspectRatio = (float) image.getWidth() / (float) image.getHeight();
        float pageAspectRatio = pageWidth / pageHeight;
        float scaleFactor = 1.0f;
        if (pageAspectRatio< imageAspectRatio) {
          scaleFactor = pageWidth / image.getWidth();
        } else {
          scaleFactor = pageHeight / image.getHeight();
        }
        float xPos = (pageWidth - (image.getWidth() * scaleFactor)) / 2;
        float yPos = (pageHeight - (image.getHeight() * scaleFactor)) / 2;
        contentStream.drawImage(image, xPos, yPos, image.getWidth() * scaleFactor, image.getHeight() * scaleFactor);
      }
    } else {
      contentStream.drawImage(image, 0, 0, pageWidth, pageHeight);
    } 
  } catch (IOException e) {
    log.error("Error adding image to PDF", e);
    throw e;

  }
}

public static String generateMachineFingerprint() {
  try {
    // Get the MAC address
    StringBuilder sb = new StringBuilder();
    InetAddress ip = InetAddress.getLocalHost();

    NetworkInterface network = NetworkInterface.getByInetAddress(ip);

    if (network == null) {
      Enumeration<NetworkInterface> networks = NetworkInterface.getNetworkInterfaces();
      while (networks.hasMoreElements()) {
        NetworkInterface net = networks.nextElement();
        byte[] mac = net.getHardwareAddress();
        if (mac != null) {
          for (int i = 0; i < mac.length; i++) {
            sb.append(String.format("%02X", mac[i]));
          }
          break; // Use the first network interface with a MAC address
        }
      }
    } else {
      byte[] mac = network.getHardwareAddress();
      if (mac != null) {
        for (int i = 0; i < mac.length; i++) {
          sb.append(String.format("%02X", mac[i]));
        }
      }
    }
    // Hash the MAC address for privacy and consistency
    MessageDigest md = MessageDigest.getInstance("SHA-256");

    byte[] hash = md.digest(sb.toString().getBytes(StandardCharsets.UTF_8));

    StringBuilder fingerprint = new StringBuilder();
    for (byte b : hash) {
      fingerprint.append(String.format("%02x", b));
    }

    return fingerprint.toString();
  } catch (Exception e) {
    return "GenericID";
  }
}

private void setCommonMetadata(PDDocument pdf, PdfMetadata pdfMetadata) {
  String title = pdfMetadata.getTitle();

  pdf.getDocumentInformation().setTitle(title);
  pdf.getDocumentInformation().setProducer(stirlingPDFLabel);

  pdf.getDocumentInformation().setSubject(pdfMetadata.getSubject());
  pdf.getDocumentInformation().setKeywords(pdfMetadata.getKeywords());

  pdf.getDocumentInformation().setModificationDate(Calendar.getInstance());

  String author = pdfMetadata.getAuthor();

  if (applicationProperties.getPremium().getProFeatures().getCustomMetadata().isAutoUpdateMetadata() && runningProOrHigher) {
    author = applicationProperties.getPremium().getProFeatures().getCustomMetadata().getAuthor();
    if (userService != null) {
      author = author.replace("username", userService.getCurrentUsername());
    }
  }
  pdf.getDocumentInformation().setAuthor(author);
}

@Bean(name = "machineType")
public String determineMachineType() {
  try {
    boolean isDocker = runningInDocker();
    boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;

    boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));

    if (isKubernetes) {
      return "Kubernetes";
    } else {
      if (!isDocker) {
        if (isBrowserOpen) {
          String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);
          if (!(os.contains("win"))) {
            if (os.contains("mac")) {
              return "Client-mac";
            } else {
              return "Client-unix";
            }
          } else {
            return "Client-windows";
          } 
        } else {
          return "Server-jar";
        }
      } else {
        return "Docker";
      } 
    }
  } catch (Exception e) {
    return "Unknown";
  }
}

private void processRequest(int limitPerDay,
String identifier,
Map<String, Bucket> buckets,
HttpServletRequest request,
HttpServletResponse response,
FilterChain filterChain)
throws IOException, ServletException {

  Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));

  ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
  if (!(probe.isConsumed())) {
    long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
    response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
    response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
    response.getWriter().write("Rate limit exceeded for POST requests.");
  } else {
    response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
    filterChain.doFilter(request, response);
  } 
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();
    notifyPostProcessing(1);
    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }
    Exception exception = null;
    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);
        if (err instanceof InterruptedIOException || err instanceof ClosedByInterruptException || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }
        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }
        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }
    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }
    notifyFinished();
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();
    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }


    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            return;// let the download thread handle this error
        default:
            if (statusCode < 200 || 299< statusCode) {
                throw new HttpError(statusCode);
            }
    }

}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }
    final Elst obj = new Elst();
    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags
    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }
    if (!v1) {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    } else {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
    } 
    obj.bMediaRate = stream.readInt();
    return obj;
}

@Override
public void run() {
    boolean done;
    long start = mMission.fallbackResumeOffset;
    if (DEBUG && !mMission.unknownLength && 0< start) {
        Log.i(TAG, "Resuming a single-thread download at " + start);
    }

    try {
        long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;
        int mId = 1;
        mConn = mMission.openConnection(false, 
        rangeStart, 
        -1);

        if (mRetryCount == 0 && rangeStart == -1) {
            // workaround: bypass android connection pool
            mConn.setRequestProperty("Range", 
            "bytes=0-");
        }

        mMission.establishConnection(mId, mConn);
        // check if the download can be resumed
        if (mConn.getResponseCode() == 416 && 0< start) {
            mMission.notifyProgress(-start);
            start = 0;
            mRetryCount--;
            throw new DownloadMission.HttpError(416);
        }

        // secondary check for the file length
        if (!mMission.unknownLength) {
            mMission.unknownLength = Utility.getContentLength(mConn) == -1;
        } if (mMission.unknownLength || mConn.getResponseCode() == 200) {
            // restart amount of bytes downloaded
            mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
        }
        mF = mMission.storage.getStream();
        mF.seek(mMission.offsets[mMission.current] + start);
        mIs = mConn.getInputStream();
        byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
        int len = 0;
        while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
            mF.write(buf, 0, len);
            start += len;
            mMission.notifyProgress(len);
        }
        dispose();
        // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
        done = len == -1;
    } catch (Exception e) {
        dispose();
        mMission.fallbackResumeOffset = start;
        if (!mMission.running || e instanceof ClosedByInterruptException) {
            return;
        }
        if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
            // for youtube streams. The url has expired, recover
            dispose();
            mMission.doRecover(ERROR_HTTP_FORBIDDEN);
            return;
        }
        if (mRetryCount++ >= mMission.maxRetry) {
            mMission.notifyError(e);
            return;
        }
        if (DEBUG) {
            Log.e(TAG, "got exception, retrying...", e);
        }
        run();// try again
        return;
    }

    if (done) {
        mMission.notifyFinished();
    } else {
        mMission.fallbackResumeOffset = start;
    }
}

private void flushAuxiliar(long amount) throws IOException {
    if (aux.length < 1) {
        return;
    }
    out.flush();
    aux.flush();
    boolean underflow = aux.offset < aux.length || out.offset < out.length;
    byte[] buffer = new byte[COPY_BUFFER_SIZE];
    aux.target.seek(0);
    out.target.seek(out.length);
    long length = amount;
    while (0< length) {
        int read = (int) Math.min(length, Integer.MAX_VALUE);
        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
        if (read < 1) {
            amount -= length;
            break;
        }
        out.writeProof(buffer, read);
        length -= read;
    }
    if (!underflow) {
        out.offset += amount;
        aux.offset -= amount;
    } else {
        if (out.offset < out.length) {
            aux.offset = 0;
        } else {
            // calculate the aux underflow pointer
            if (aux.offset >= amount) {
                aux.offset -= amount;
                out.offset = out.length + amount;
            } else {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } 
        } 
    } 
    out.length += amount;
    if (maxLengthKnown< out.length) {
        maxLengthKnown = out.length;
    }
    if (amount < aux.length) {
        // move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;
        aux.length -= amount;
        length = aux.length;
        while (0< length) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
            aux.target.seek(writeOffset);
            aux.writeProof(buffer, read);

            writeOffset += read;
            readOffset += read;
            length -= read;
            aux.target.seek(readOffset);
        }
        aux.target.setLength(aux.length);
        return;
    }

    if (THRESHOLD_AUX_LENGTH< aux.length) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH);// or setLength(0);
    }
    aux.reset();
}

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);
    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo)
        {
            request.append(' ').append(recovery.toString()).append(' ');
        }
    }
    request.append("]");
    String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(mContext, new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject), action,
    service, request.toString(), reason));
}

void startMission(DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;
        // create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, 
            String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile())
                    {
                        throw new RuntimeException("Cant create download metadata file");
                    }
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }
        mSelfMissionsControl = true;
        mMissionsPending.add(mission);
        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);
        if (mission.storage == null) {
            // noting to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) {
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            }

            return;
        }
        boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;
        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

importnull;@Override
int process(SharpStream out, SharpStream... sources) throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    String format = getArgumentAt(0, null);
    boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");
    String tmp = format.equals("ttml");

    if (!(format == null || tmp)) {
        if (format.equals("srt")) {
            byte[] buffer = new byte[8 * 1024];
            int read;
            while (0< (read = sources[0].read(buffer))) {
                out.write(buffer, 0, read);
            }
            return OK_RESULT;
        }
    } else {
        SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);
        try {
            writer.build(sources[0]);
        } catch (Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            return err instanceof IOException ? 1 : 8;
        }
        return OK_RESULT;
    } 
    throw new UnsupportedOperationException("Can't convert this subtitle, unimplemented format: " + format);
}

private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();
    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();
        if (0< millis) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }// if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) {
            continue;
        }
        thread.interrupt();
    }
    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) {
                continue;
            }
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }
            if (0< millis) {
                thread.join(millis);
            }
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

public static HealthCountsStream getInstance(HystrixCommandKey commandKey, int numBuckets, int bucketSizeInMs) {
  HealthCountsStream initialStream = streams.get(commandKey.name());
  if (initialStream == null) {
    final HealthCountsStream healthStream;
    synchronized (HealthCountsStream.class) {
      HealthCountsStream existingStream = streams.get(commandKey.name());
      if (existingStream == null) {
        HealthCountsStream newStream = new HealthCountsStream(commandKey, numBuckets, bucketSizeInMs, HystrixCommandMetrics.appendEventToBucket);

        streams.putIfAbsent(commandKey.name(), newStream);
        healthStream = newStream;
      } else {
        healthStream = existingStream;
      }
    }
    healthStream.startCachingStreamValuesIfUnstarted();
    return healthStream;
  } else {
    return initialStream;
  } 
}

public Map<ExecutionSignature, List<Integer>> getExecutionsMappedToLatencies() {
  Map<CommandAndCacheKey, Integer> cachingDetector = new HashMap<CommandAndCacheKey, Integer>();
  List<HystrixInvokableInfo<?>> nonCachedExecutions = new ArrayList<HystrixInvokableInfo<?>>(executions.size());
  for (HystrixInvokableInfo<?> execution : executions) {

    if (execution.getPublicCacheKey() != null) {
      //eligible for caching - might be the initial, or might be from cache
      CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), execution.getPublicCacheKey());
      Integer count = cachingDetector.get(key);

      if (count != null) {
        //key already seen
        cachingDetector.put(key, count + 1);
      } else {
        //key not seen yet
        cachingDetector.put(key, 0);
      }

    }

    if (!execution.isResponseFromCache()) {
      nonCachedExecutions.add(execution);
    }
  }

  Map<ExecutionSignature, List<Integer>> commandDeduper = new HashMap<ExecutionSignature, List<Integer>>();
  for (HystrixInvokableInfo<?> execution : nonCachedExecutions) {
    int cachedCount = 0;

    String cacheKey = execution.getPublicCacheKey();
    if (cacheKey != null) {
      CommandAndCacheKey key = new CommandAndCacheKey(execution.getCommandKey().name(), cacheKey);
      cachedCount = cachingDetector.get(key);
    }
    ExecutionSignature signature = cachedCount > 0? ExecutionSignature.from(execution, cacheKey, cachedCount) : ExecutionSignature.from(execution); List<Integer> currentLatencyList = commandDeduper.get(signature);
    if (currentLatencyList == null) {
      List<Integer> newLatencyList = new ArrayList<Integer>();
      newLatencyList.add(execution.getExecutionTimeInMilliseconds());
      commandDeduper.put(signature, newLatencyList);
    } else {
      currentLatencyList.add(execution.getExecutionTimeInMilliseconds());
    } 
  }

  return commandDeduper;
}

public ThreadPoolExecutor getThreadPool(final HystrixThreadPoolKey threadPoolKey, HystrixThreadPoolProperties threadPoolProperties) {
  final ThreadFactory threadFactory = getThreadFactory(threadPoolKey);
  final boolean allowMaximumSizeToDivergeFromCoreSize = threadPoolProperties.getAllowMaximumSizeToDivergeFromCoreSize().get();
  final int dynamicCoreSize = threadPoolProperties.coreSize().get();
  final int keepAliveTime = threadPoolProperties.keepAliveTimeMinutes().get();
  final int maxQueueSize = threadPoolProperties.maxQueueSize().get();
  final BlockingQueue<Runnable> workQueue = getBlockingQueue(maxQueueSize);
  if (allowMaximumSizeToDivergeFromCoreSize) {

    final int dynamicMaximumSize = threadPoolProperties.maximumSize().get();

    if (dynamicMaximumSize< dynamicCoreSize) {
      logger.error("Hystrix ThreadPool configuration at startup for : " + threadPoolKey.name() + " is trying to set coreSize = " + dynamicCoreSize + " and maximumSize = " + dynamicMaximumSize + ".  Maximum size will be set to " + dynamicCoreSize + ", the coreSize value, since it must be equal to or greater than the coreSize value");
      return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
    } else {
      return new ThreadPoolExecutor(dynamicCoreSize, dynamicMaximumSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
    }
  } else {
    return new ThreadPoolExecutor(dynamicCoreSize, dynamicCoreSize, keepAliveTime, TimeUnit.MINUTES, workQueue, threadFactory);
  }
}

protected static String convertToJson(HystrixUtilization utilization) throws IOException {
  StringWriter jsonString = new StringWriter();
  JsonGenerator json = jsonFactory.createGenerator(jsonString);
  json.writeStartObject();
  json.writeStringField("type", "HystrixUtilization");
  json.writeObjectFieldStart("commands");
  for (Map.Entry<HystrixCommandKey, HystrixCommandUtilization> entry : utilization.getCommandUtilizationMap().entrySet()) {
    final HystrixCommandKey key = entry.getKey();
    final HystrixCommandUtilization commandUtilization = entry.getValue();
    writeCommandUtilizationJson(json, key, commandUtilization);
  }
  json.writeEndObject();
  json.writeObjectFieldStart("threadpools");
  for (Map.Entry<HystrixThreadPoolKey, HystrixThreadPoolUtilization> entry : utilization.getThreadPoolUtilizationMap().entrySet()) {

    final HystrixThreadPoolKey threadPoolKey = entry.getKey();

    final HystrixThreadPoolUtilization threadPoolUtilization = entry.getValue();

    writeThreadPoolUtilizationJson(json, threadPoolKey, threadPoolUtilization);
  }
  json.writeEndObject();
  json.writeEndObject();
  json.close();
  return jsonString.getBuffer().toString();

}

EventCounts plus(HystrixEventType eventType, int count) {
  BitSet newBitSet = (BitSet) events.clone();
  int localNumEmits = numEmissions;
  int localNumFallbackEmits =  numFallbackEmissions;

  int localNumCollapsed = numCollapsed;
  switch (eventType) {
    case EMIT:
      newBitSet.set(HystrixEventType.EMIT.ordinal());
      localNumEmits += count;
      break;
    case FALLBACK_EMIT:
      newBitSet.set(HystrixEventType.FALLBACK_EMIT.ordinal());

      localNumFallbackEmits += count;
      break;
    case COLLAPSED:
      newBitSet.set(HystrixEventType.COLLAPSED.ordinal());
      localNumCollapsed += count;
      break;
    default:
      newBitSet.set(eventType.ordinal());
      break;
  }
  return new EventCounts(newBitSet, localNumEmits, localNumFallbackEmits, localNumCollapsed);
}

