private void onBroadcastReceived(final Intent intent) {
if (intent == null|| intent.getAction() == null) {
    return;
}

if (DEBUG) {
    Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
}

switch(intent.getAction()) {
    case
                               AudioManager.ACTION_AUDIO_BECOMING_NOISY:
    pause();
    break;
                           case
                               ACTION_CLOSE:
    service.destroyPlayerAndStopService();
    break;
                           case
                               ACTION_PLAY_PAUSE:
    playPause();
    break;
                           case
                               ACTION_PLAY_PREVIOUS:
    playPrevious();
    break;
                           case
                               ACTION_PLAY_NEXT:
    playNext();
    break;
                           case
                               ACTION_FAST_REWIND:
    fastRewind();
    break;
                           case
                               ACTION_FAST_FORWARD:
    fastForward();
    break;
                           case
                               ACTION_REPEAT:
    cycleNextRepeatMode();
    break;
                           case
                               ACTION_SHUFFLE:
    toggleShuffleModeEnabled();
    break;
                           case
                               Intent.ACTION_CONFIGURATION_CHANGED:
    if (DEBUG) {
        Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
    }

    break;
}
UIs.call(playerUi-> playerUi.onBroadcastReceived(intent));
}

    private void openDetailFragmentFromCommentReplies(@ NonNull final FragmentManager fm, final boolean popBackStack) {
        // obtain the name of the fragment under the replies fragment that's going to be popped
    @
        Nullable final String fragmentUnderEntryName;
    if (fm.getBackStackEntryCount() < 2) {
                                             fragmentUnderEntryName = null;
    } else {
        fragmentUnderEntryName = fm.getBackStackEntryAt(fm.getBackStackEntryCount() - 2).getName();
    }

        // the root comment is the comment for which the user opened the replies page

    @
        Nullable final CommentRepliesFragment repliesFragment = (CommentRepliesFragment) fm.findFragmentByTag(CommentRepliesFragment.TAG);
    @
        Nullable final CommentsInfoItem rootComment = repliesFragment == null ?
        null: repliesFragment.getCommentsInfoItem();

        // sometimes this function pops the backstack, other times it's handled by the system
    if (popBackStack) {
        fm.popBackStackImmediate();
    }

        // only expand the bottom sheet back if there are no more nested comment replies fragments
        // stacked under the one that is currently being popped

    if (CommentRepliesFragment.TAG.equals(fragmentUnderEntryName)) {
        return;
    }

    final BottomSheetBehavior < FragmentContainerView > behavior = BottomSheetBehavior.from(mainBinding.fragmentPlayerHolder);
        // do not return to the comment if the details fragment was closed
    if (behavior.getState() == BottomSheetBehavior.STATE_HIDDEN) {
        return;
    }

        // scroll to the root comment once the bottom sheet expansion animation is finished
    behavior.addBottomSheetCallback(new BottomSheetBehavior . BottomSheetCallback() {
                                        @
                                        Override public void onStateChanged(@ NonNull final View bottomSheet, final int newState) {
                                        if (newState == BottomSheetBehavior.STATE_EXPANDED) {
                                            final Fragment detailFragment = fm.findFragmentById(R.id.fragment_player_holder);
                                            if (detailFragment instanceof VideoDetailFragment && rootComment != null) {
                        // should always be the case
                                                ((VideoDetailFragment) detailFragment).scrollToComment(rootComment);
                                            }
                                            behavior.removeBottomSheetCallback(this);
                                        }
                                        }
                                        @
                                        Override public void onSlide(@ NonNull final View bottomSheet, final float slideOffset) {
                // not needed, listener is removed once the sheet is expanded
}

});
    behavior.setState(BottomSheetBehavior.STATE_EXPANDED);
    }

private void buildCaptionMenu(@ NonNull final List < String > availableLanguages) {
if (captionPopupMenu == null) {
    return;
}
captionPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_CAPTION);
captionPopupMenu.setOnDismissListener(this);

    // Add option for turning off caption
final MenuItem captionOffItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION, 0, Menu.NONE, R.string.caption_none);
captionOffItem.setOnMenuItemClickListener(menuItem-> {
                                          final int textRendererIndex = player.getCaptionRendererIndex();
                                          if (textRendererIndex != RENDERER_UNAVAILABLE) {
                                              player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setRendererDisabled(textRendererIndex, true));
                                          }
                                          player.getPrefs().edit().remove(context.getString(R.string.caption_user_set_key)).apply();
                                          return true;
                                          });

    // Add all available captions
for (int i = 0; i < availableLanguages.size(); i++) {
final String captionLanguage = availableLanguages.get(i);
final MenuItem captionItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION, i + 1, Menu.NONE, captionLanguage);
captionItem.setOnMenuItemClickListener(menuItem-> {
                                       final int textRendererIndex = player.getCaptionRendererIndex();
                                       if (textRendererIndex != RENDERER_UNAVAILABLE) {
                // DefaultTrackSelector will select for text tracks in the following order.
                // When multiple tracks share the same rank, a random track will be chosen.
                // 1. ANY track exactly matching preferred language name
                // 2. ANY track exactly matching preferred language stem
                // 3. ROLE_FLAG_CAPTION track matching preferred language stem
                // 4. ROLE_FLAG_DESCRIBES_MUSIC_AND_SOUND track matching preferred language stem
                // This means if a caption track of preferred language is not available,
                // then an auto-generated track of that language will be chosen automatically.
                                           player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setPreferredTextLanguages(captionLanguage, PlayerHelper.captionLanguageStemOf(captionLanguage)).setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION).setRendererDisabled(textRendererIndex, false));
                                           player.getPrefs().edit().putString(context.getString(R.string.caption_user_set_key), captionLanguage).apply();
                                       }

                                       return true;
                                       });
}
captionPopupMenu.setOnDismissListener(this);

    // apply caption language from previous user preference
final int textRendererIndex = player.getCaptionRendererIndex();
if (textRendererIndex == RENDERER_UNAVAILABLE) {
    return;
}

    // If user prefers to show no caption, then disable the renderer.
    // Otherwise, DefaultTrackSelector may automatically find an available caption
    // and display that.

final String userPreferredLanguage = player.getPrefs().getString(context.getString(R.string.caption_user_set_key), null);
if (userPreferredLanguage == null) {
    player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setRendererDisabled(textRendererIndex, true));
    return;
}

    // Only set preferred language if it does not match the user preference,
    // otherwise there might be an infinite cycle at onTextTracksChanged.

final List < String > selectedPreferredLanguages = player.getTrackSelector().getParameters().preferredTextLanguages;
if (!selectedPreferredLanguages.contains(userPreferredLanguage)) {
    player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setPreferredTextLanguages(userPreferredLanguage, PlayerHelper.captionLanguageStemOf(userPreferredLanguage)).setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION).setRendererDisabled(textRendererIndex, false));
}
}

