private static
String decodeMimeHeader(String encodedText) {
    if (encodedText == null||encodedText.trim() .isEmpty()) {
        return encodedText;
    }

    try {
        StringBuilder result = new StringBuilder();
        Matcher matcher = MimeConstants.MIME_ENCODED_PATTERN.matcher(encodedText);
        int lastEnd = 0;
        while(matcher.find()) {
            // Add any text before the encoded part
            result.append(encodedText, lastEnd, matcher.start());
            String charset = matcher.group(1);
            String encoding = matcher.group(2) .toUpperCase();
            String encodedValue = matcher.group(3);
            try {
                String decodedValue;
                if ("B".equals(encoding)) {
                    // Base64 decoding
                    byte [] decodedBytes = Base64.getDecoder() .decode(encodedValue);
                    decodedValue = new String(decodedBytes, Charset.forName(charset));
                } else if ("Q".equals(encoding)) {
                    // Quoted-printable decoding
                           decodedValue = decodeQuotedPrintable(encodedValue, charset);
                       } else {
                    // Unknown encoding, keep original
                           decodedValue = matcher.group(0);
                       }

                result.append(decodedValue);
            } catch (Exception e) {
                log.warn("Failed to decode MIME header part: {}", matcher.group(0), e);
                // If decoding fails, keep the original encoded text
                result.append(matcher.group(0));
            }

            lastEnd = matcher.end();
        }

        // Add any remaining text after the last encoded part

        result.append(encodedText.substring(lastEnd));
        return result.toString();
    } catch (Exception e) {
        log.warn("Error decoding MIME header: {}", encodedText, e);
        return encodedText; // Return original if decoding fails
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/eml/pdf") @Operation(summary = "Convert EML to PDF", description = "This endpoint converts EML (email) files to PDF format with extensive"+ " customization options. Features include font settings, image constraints, display modes, attachment handling," + " and HTML debug output. Input: EML file, Output: PDF" + " or HTML file. Type: SISO")
public ResponseEntity<byte
                          []> convertEmlToPdf(@ModelAttribute EmlToPdfRequest request) {
    MultipartFile inputFile = request.getFileInput();
    String originalFilename = inputFile.getOriginalFilename();

    // Validate input
    if (inputFile.isEmpty()) {
        log.error("No file provided for EML to PDF conversion.");
        return ResponseEntity.badRequest() .body("No file provided".getBytes(StandardCharsets.UTF_8));
    }

    if (originalFilename == null||originalFilename.trim() .isEmpty()) {
        log.error("Filename is null or empty.");
        return ResponseEntity.badRequest() .body("Please provide a valid filename".getBytes(StandardCharsets.UTF_8));
    }

    // Validate file type - support EML

    String lowerFilename = originalFilename.toLowerCase();
    if (!lowerFilename.endsWith(".eml")) {
        log.error("Invalid file type for EML to PDF: {}", originalFilename);
        return ResponseEntity.badRequest() .body("Please upload a valid EML file".getBytes(StandardCharsets.UTF_8));
    }

    String baseFilename = Filenames.toSimpleFileName(originalFilename); // Use Filenames utility
    try {
        byte [] fileBytes = inputFile.getBytes();
        if (request.isDownloadHtml()) {
            try {
                String htmlContent = EmlToPdf.convertEmlToHtml(fileBytes, request);
                log.info("Successfully converted EML to HTML: {}", originalFilename);
                return WebResponseUtils.bytesToWebResponse(htmlContent.getBytes(StandardCharsets.UTF_8), baseFilename + ".html", MediaType.TEXT_HTML);
            } catch (IOException|IllegalArgumentException e) {
                log.error("HTML conversion failed for {}", originalFilename, e);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body(("HTML conversion failed: "+ e.getMessage()) .getBytes(StandardCharsets.UTF_8));
            }
        }

        // Convert EML to PDF with enhanced options

        try {
            byte [] pdfBytes = EmlToPdf.convertEmlToPdf(runtimePathConfig.getWeasyPrintPath(), // Use configured WeasyPrint path request, fileBytes, originalFilename, false, pdfDocumentFactory, tempFileManager);
            if (pdfBytes == null||pdfBytes.length == 0) {
                log.error("PDF conversion failed - empty output for {}", originalFilename);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body("PDF conversion failed - empty output".getBytes(StandardCharsets.UTF_8));
            }

            log.info("Successfully converted EML to PDF: {}", originalFilename);
            return WebResponseUtils.bytesToWebResponse(pdfBytes, baseFilename + ".pdf", MediaType.APPLICATION_PDF);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            log.error("EML to PDF conversion was interrupted for {}", originalFilename, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body("Conversion was interrupted".getBytes(StandardCharsets.UTF_8));
        }

          catch (IllegalArgumentException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        }

          catch (RuntimeException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        }
    } catch (IOException e) {
        log.error("File processing error for EML to PDF: {}", originalFilename, e);
        return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR) .body("File processing error".getBytes(StandardCharsets.UTF_8));
    }
}

private static String formatFileSize(long size) {
    String hrSize;
    double b = size;
    double k = size/1024.0;
    double m = ((size/1024.0)/1024.0);
    double g = (((size/1024.0)/1024.0)/1024.0);
    double t = ((((size/1024.0)/1024.0)/1024.0)/1024.0);
    DecimalFormat dec = new DecimalFormat("0.00");
    if (t > 1) {
        hrSize = dec.format(t).concat(" TB");
    } else if (g > 1) {
               hrSize = dec.format(g).concat(" GB");
           } else if (m > 1) {
                      hrSize = dec.format(m).concat(" MB");
                  } else if (k > 1) {
                             hrSize = dec.format(k).concat(" KB");
                         } else {
                             hrSize = dec.format(b).concat(" Bytes");
                         }
    return hrSize;
}

private static Class[] parseTypes(String desc, ClassLoader loader) throws ClassNotFoundException {
    int lparen = desc.indexOf('(');
    int rparen = desc.indexOf(')', lparen);
    List params = new ArrayList();
    int start = lparen + 1;

    for (;;) {
        int comma = desc.indexOf(',', start);
        if (comma < 0) {
            break;
        }
        params.add(desc.substring(start, comma).trim());
        start = comma + 1;
    }

    if (start <rparen) {
        params.add(desc.substring(start, rparen).trim());
    }

    Class [] types = new Class[params.size()];

    for (int i = 0;i <types.length;
    i++) {
        types[i] = getClass((String)params.get(i), loader);
    }
    return types;
}

@Override
public void process(final CommandProcess process) {
    Session session = process.session();
    timer = new Timer("Timer-for-arthas-dashboard-" + session.getSessionId(), true);

    // ctrl-C support
    process.interruptHandler(new DashboardInterruptHandler(process, timer));

    /*
     * handlesuspendendtimerresumetimer
     */
    Handler<Void> stopHandler = new Handler<Void>() {
                                    @Override
                                    public void handle(Void event) {
                                        stop();
                                    }

                                };
    Handler<Void> restartHandler = new Handler<Void>() {
                                       @Override
                                       public void handle(Void event) {
                                           restart(process);
                                       }

                                   };
    process.suspendHandler(stopHandler);
    process.resumeHandler(restartHandler);
    process.endHandler(stopHandler);

    // q exit support
    process.stdinHandler(new QExitHandler(process));

    // start the timer
    timer.scheduleAtFixedRate(new DashboardTimerTask(process), 0, getInterval());
}

public boolean isAvailable() {
    if (isConnected()) {
        try {
            if (_socket_.getInetAddress() == null) {
                return false;
            }

            if (_socket_.getPort() == 0) {
                return false;
            }

            if (_socket_.getRemoteSocketAddress() == null) {
                return false;
            }

            if (_socket_.isClosed()) {
                return false;
            }
            /* these aren't exact checks (a Socket can be half-open),
               but since we usually require two-way data transfer,
               we check these here too: */

            if (_socket_.isInputShutdown()) {
                return false;
            }

            if (_socket_.isOutputShutdown()) {
                return false;
            }
            /* ignore the result, catch exceptions: */
            _socket_.getInputStream();
            _socket_.getOutputStream();
        } catch (IOException ioex) {
            return false;
        }
        return true;
    } else {
        return false;
    }
}

public void start() throws Exception {
    // Configure SSL.
    final SslContext sslCtx;
    if (ssl) {
        SelfSignedCertificate ssc = new SelfSignedCertificate();
        sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
    } else {
        sslCtx = null;
    }

    ServerBootstrap b = new ServerBootstrap();
    b.group(bossGroup, workerGroup).channel(NioServerSocketChannel.class ).handler(new LoggingHandler(LogLevel.INFO) ).childHandler(new TunnelSocketServerInitializer(this, sslCtx));
    if (StringUtils.isBlank(host)) {
        channel = b.bind(port).sync().channel();
    } else {
        channel = b.bind(host, port).sync().channel();
    }
    logger.info("Tunnel server listen at {}:{}", host, port);
    workerGroup.scheduleWithFixedDelay(new Runnable() {
                                           @Override
                                           public void run() {
                                               agentInfoMap.entrySet().removeIf(e->!e.getValue().getChannelHandlerContext().channel().isActive());
                                               clientConnectionInfoMap.entrySet().removeIf(e->!e.getValue().getChannelHandlerContext().channel().isActive());

            // key
                                               if (tunnelClusterStore != null && clientConnectHost != null) {
                                                   try {
                                                       for (Entry<String, AgentInfo> entry : agentInfoMap.entrySet()) {
                                                           tunnelClusterStore.addAgent(entry.getKey(), new AgentClusterInfo(entry.getValue(), clientConnectHost, port), 60* 60, TimeUnit.SECONDS);
                                                       }
                                                   } catch (Throwable t) {
                                                       logger.error("update tunnel info error", t);
                                                   }
                                               }
                                           }

                                       }, 60, 60, TimeUnit.SECONDS);
}

public Map<String, Class<?>> build() {
    errors.clear();
    warnings.clear();
    JavaFileManager fileManager = new DynamicJavaFileManager(standardFileManager, dynamicClassLoader);
    DiagnosticCollector<JavaFileObject> collector = new DiagnosticCollector<JavaFileObject>();
    JavaCompiler.CompilationTask task = javaCompiler.getTask(null, fileManager, collector, options, null, compilationUnits);

    try {
        if (!compilationUnits.isEmpty()) {
            boolean result = task.call();
            if (!result
                    || collector.getDiagnostics().size()> 0) {
                for (Diagnostic<? extends JavaFileObject> diagnostic : collector.getDiagnostics()) {
                    switch(diagnostic.getKind()) {
                                                 case NOTE: case MANDATORY_WARNING: case WARNING:
                                                 warnings.add(diagnostic);
                                                 break;
                                                 case OTHER: case ERROR: default:
                        errors.add(diagnostic);
                        break;
                    }
                }

                if (!errors.isEmpty()) {
                    throw new DynamicCompilerException("Compilation Error", errors);
                }
            }
        }
        return dynamicClassLoader.getClasses();
    } catch (Throwable e) {
        throw new DynamicCompilerException(e, errors);
    }

    finally {
            compilationUnits.clear();
    }
}

public SimpleHttpResponse query(String targetUrl) throws InterruptedException {
    final Promise<SimpleHttpResponse> httpResponsePromise = GlobalEventExecutor.INSTANCE.newPromise();
    final EventLoopGroup group = new NioEventLoopGroup(1, new DefaultThreadFactory("arthas-ProxyClient", true));
    ChannelFuture closeFuture = null;

    try {
        Bootstrap b = new Bootstrap();
        b.option(ChannelOption.CONNECT_TIMEOUT_MILLIS, 5000);
        b.group(group).channel(LocalChannel.class ).handler(new ChannelInitializer<LocalChannel>() {
                                                                @Override
                                                                protected void initChannel(LocalChannel ch) {
                                                                    ChannelPipeline p = ch.pipeline();
                                                                    p.addLast(new HttpClientCodec(), new HttpObjectAggregator(ArthasConstants.MAX_HTTP_CONTENT_LENGTH), new HttpProxyClientHandler(httpResponsePromise));
                                                                }

                                                            });
        LocalAddress localAddress = new LocalAddress(ArthasConstants.NETTY_LOCAL_ADDRESS);
        Channel localChannel = b.connect(localAddress).sync().channel();

        // Prepare the HTTP request.
        HttpRequest request = new DefaultFullHttpRequest(HttpVersion.HTTP_1_1, HttpMethod.GET, targetUrl, Unpooled.EMPTY_BUFFER);
        request.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.CLOSE);
        localChannel.writeAndFlush(request);
        closeFuture = localChannel.closeFuture();
        logger.info("proxy client connect to server success, targetUrl: " + targetUrl);
        return httpResponsePromise.get(5000, TimeUnit.MILLISECONDS);
    } catch (Throwable e) {
        logger.error("ProxyClient error, targetUrl: {}", targetUrl, e);
    }

    finally {
            if (closeFuture != null) {
                closeFuture.addListener(new ChannelFutureListener() {
                                            @Override
                                            public void operationComplete(ChannelFuture channelFuture) throws Exception {
                                                group.shutdownGracefully();
                                            }

                                        });
            } else {
                group.shutdownGracefully();
            }
    }

    SimpleHttpResponse httpResponse = new SimpleHttpResponse();

    try {
        httpResponse.setContent("error".getBytes("utf-8"));
    } catch (UnsupportedEncodingException e) {
        // ignore
    }
    return httpResponse;
}

@Override
public void write(int ch) throws IOException {
    synchronized(__client) {
                           ch &= 0xff;
                           if (__client._requestedWont(TelnetOption.BINARY)) // i.e. ASCII {
                               if (__lastWasCR) {
                                   if (__convertCRtoCRLF) {
                                       __client._sendByte('\n');
                                       if (ch == '\n') // i.e. was CRLF anyway {
                                           __lastWasCR = false;
                                           return;
                                       }
                                   } // __convertCRtoCRLF else if (ch != '\n') {
                                                                   __client._sendByte('\0'); // RFC854 requires CR NUL for bare CR
                                                               }
                               }
                               switch(ch) {
                                          case '\r':
                                          __client._sendByte('\r');
                                          __lastWasCR = true;
                                          break;
                                          case '\n':
                                   if (!__lastWasCR) { // convert LF to CRLF
                                       __client._sendByte('\r');
                                   }
                                   __client._sendByte(ch);
                                   __lastWasCR = false;
                                   break;
                                          case TelnetCommand.IAC:
                                   __client._sendByte(TelnetCommand.IAC);
                                   __client._sendByte(TelnetCommand.IAC);
                                   __lastWasCR = false;
                                   break;
                                          default:
                                   __client._sendByte(ch);
                                   __lastWasCR = false;
                                   break;
                               }
                           } // end ASCII else if (ch == TelnetCommand.IAC) {
                                                   __client._sendByte(ch);
                                                   __client._sendByte(TelnetCommand.IAC);
                                               } else {
                                                   __client._sendByte(ch);
                                               }
    }
}

private String drawField() {
    final StringBuilder fieldSB = new StringBuilder();
    final Field[] fields = clazz.getDeclaredFields();
    if (fields.length > 0) {
        for (Field field : fields) {
            final KVView kvView = new KVView(new TableView.ColumnDefine(TableView.Align.RIGHT), new TableView.ColumnDefine(50, false, TableView.Align.LEFT)).add("modifier", StringUtils.modifier(field.getModifiers(), ',')).add("type", StringUtils.classname(field.getType())).add("name", field.getName());
            final StringBuilder annotationSB = new StringBuilder();
            final Annotation[] annotationArray = field.getAnnotations();
            if (null != annotationArray && annotationArray.length > 0) {
                for (Annotation annotation : annotationArray) {
                    annotationSB.append(StringUtils.classname(annotation.annotationType())).append(",");
                }

                if (annotationSB.length()> 0) {
                    annotationSB.deleteCharAt(annotationSB.length() - 1);
                }
                kvView.add("annotation", annotationSB);
            }

            if (Modifier.isStatic(field.getModifiers())) {
                final boolean isAccessible = field.isAccessible();

                try {
                    field.setAccessible(true);
                    kvView.add("value", StringUtils.objectToString(field.get(null)));
                } catch (IllegalAccessException e) {
                    //
                }

                finally {
                        field.setAccessible(isAccessible);
                }
            }//if
            fieldSB.append(kvView.draw()).append("\n");
        }//for
    }
    return fieldSB.toString();
}

