@ Override public List<ResultModel> pollResults() {
try {
    lastAccessTime = System.currentTimeMillis();
    long accessTime = lastAccessTime;
    if (lock.tryLock(500, TimeUnit.MILLISECONDS)) {
        polling = true;
        sendingItemCount = 0;
        long firstResultTime = 0;
            // sending delay: time elapsed after firstResultTime
        long sendingDelay = 0;
            // waiting time: time elapsed after access
        long waitingTime = 0;
        List<ResultModel> sendingResults = new ArrayList<ResultModel>(resultBatchSizeLimit);
        while (!closed && sendingResults.size() < resultBatchSizeLimit && sendingDelay < 100 && waitingTime < pollTimeLimit) {
            ResultModel aResult = resultQueue.poll(100, TimeUnit.MILLISECONDS);
            if (aResult != null) {
                sendingResults.add(aResult);
                    //
                if (firstResultTime == 0) {
                    firstResultTime = System.currentTimeMillis();
                }
                    //

                if (shouldFlush(sendingResults, aResult)) {
                    break;
                }
            } else {
                if (firstResultTime > 0) {
                        //
                    sendingDelay = System.currentTimeMillis() - firstResultTime;
                }
                    //
                waitingTime = System.currentTimeMillis() - accessTime;
            }
        }

            //resultQueue.drainTo(sendingResults, resultSizeLimit-sendingResults.size());

        if (logger.isDebugEnabled()) {
            logger.debug("pollResults: {}, results: {}", sendingResults.size(), JSON.toJSONString(sendingResults));
        }
        return sendingResults;
    }
} catch (InterruptedException e) {
        //e.printStackTrace();

} finally {
    if (lock.isHeldByCurrentThread()) {
        lastAccessTime = System.currentTimeMillis();
        polling = false;
        lock.unlock();
    }
}
return Collections.emptyList();
}

private void initializeSlotsCache(Set < HostAndPort > startNodes, JedisClientConfig clientConfig) {
    if (startNodes.isEmpty()) {
        throw new JedisClusterOperationException("No nodes to initialize cluster slots cache.");
    }

    ArrayList < HostAndPort > startNodeList = new ArrayList < >(startNodes);
    Collections.shuffle(startNodeList);
    JedisException firstException = null;

    for(HostAndPort hostAndPort : startNodeList) {
        try (Connection jedis = new Connection(hostAndPort, clientConfig)) {
            cache.discoverClusterNodesAndSlots(jedis);
            return;
        }

            catch(JedisException e) {
            if (firstException == null) {
                firstException = e;
            }
    // try next nodes
        }
    }

    if (System.getProperty(INIT_NO_ERROR_PROPERTY) != null) {
        return;
    }

    JedisClusterOperationException uninitializedException = new JedisClusterOperationException("Could not initialize cluster slots cache.");
    uninitializedException.addSuppressed(firstException);
    throw uninitializedException;
}

