    @Override
    public
    final
    <
        T >T broadcastCommand(CommandObject <T> commandObject) {
                                                                   Map< String,
                                                                        ConnectionPool> connectionMap = provider.getConnectionMap();
                                                                   boolean isErrored = false;
                                                                   T reply = null;
                                                                   JedisBroadcastException bcastError = new JedisBroadcastException();

                                                                   for(Map .Entry< String,
                                                                                   ConnectionPool> entry :connectionMap.entrySet()
                                                                   )
                                                                   {
                                                                       HostAndPort node = HostAndPort.from(entry.getKey());
                                                                       ConnectionPool pool = entry.getValue();
                                                                       try
                                                                           (Connection connection = pool.getResource())
                                                                           {
                                                                               T aReply = execute(connection,
                                                                                                  commandObject);
                                                                               bcastError.addReply(
                                                                                   node,
                                                                                   aReply);
                                                                               if (isErrored)
                                                                               { // already errored
                                                                               }
                                                                               else
                                                                               if (reply == null)
                                                                               {
                                                                                   reply = aReply; // ok
                                                                               }
                                                                               else
if (reply.equals(aReply))
{
          // ok
}
else
{
   isErrored = true;
   reply = null;
}
                                                                           }
                                                                           catch (Exception anError)
                                                                           {
                                                                               bcastError.addReply(
                                                                                   node,
                                                                                   anError);
                                                                               isErrored = true;
                                                                           }
                                                                   }

                                                                   if (isErrored)
                                                                   {
                                                                       throw bcastError;
                                                                   }

                                                                   return reply;
                                                               }

@Override
public
final
<
    T >T executeCommand(CommandObject <T> commandObject) {
                                                             Instant deadline = Instant.now()
                                                                                    .plus(maxTotalRetriesDuration);
                                                             int consecutiveConnectionFailures = 0;
                                                             JedisException lastException = null;

                                                             for(int attemptsLeft = this
                                                                                        .maxAttempts; attemptsLeft > 0; attemptsLeft--) {
                                                                                                                                            Connection connection = null;
                                                                                                                                            try
                                                                                                                                            {
                                                                                                                                                connection = provider.getConnection(commandObject.getArguments());
                                                                                                                                                return execute(connection,
                                                                                                                                                               commandObject);
                                                                                                                                            }
                                                                                                                                            catch (JedisConnectionException jce)
                                                                                                                                            {
                                                                                                                                                lastException = jce;
                                                                                                                                                ++consecutiveConnectionFailures;
                                                                                                                                                log.debug(
                                                                                                                                                    "Failed connecting to Redis: {}",
                                                                                                                                                    connection,
                                                                                                                                                    jce);
    // "- 1" because we just did one, but the attemptsLeft counter hasn't been decremented yet
                                                                                                                                                boolean reset = handleConnectionProblem(attemptsLeft - 1,
                                                                                                                                                                                        consecutiveConnectionFailures,
                                                                                                                                                                                        deadline);

                                                                                                                                                if (reset)
                                                                                                                                                {
                                                                                                                                                    consecutiveConnectionFailures = 0;
                                                                                                                                                }
                                                                                                                                            }

                                                                                                                                            finally
                                                                                                                                                {
                                                                                                                                                    if (connection != null)
                                                                                                                                                    {
                                                                                                                                                        connection.close();
                                                                                                                                                    }
                                                                                                                                                }

                                                                                                                                            if (Instant.now()
                                                                                                                                                    .isAfter(deadline))
                                                                                                                                            {
                                                                                                                                                throw new JedisException("Retry deadline exceeded.");
                                                                                                                                            }
                                                             }

                                                             JedisException maxAttemptsException = new JedisException("No more attempts left.");
                                                             maxAttemptsException.addSuppressed(lastException);
                                                             throw maxAttemptsException;
                                                         }

    @Override
    public Map< String,
                Map< String,
                     Double>> build(Object data)
    {
        List rawDataList = (List)data;

        if (rawDataList.isEmpty())
        return Collections.emptyMap();
        if (rawDataList.get(0) instanceof KeyValue)
        {
            KeyValue rawData = (KeyValue)rawDataList.get(0);
            String header = STRING.build(rawData.getKey());

            if (!RESULTS.equals(header))
            {
                throw new IllegalStateException("Unrecognized header: "
                          + header);
            }

            return ((List <KeyValue>)rawData.getValue())
                       .stream()
.collect(Collectors.toMap(rawTerm->STRING.build(rawTerm.getKey()),
                          rawTerm->((List <List <KeyValue>>)rawTerm.getValue())
                                       .stream()
.collect(Collectors.toMap(entry->STRING.build(entry.get(0)
                                                  .getKey()),
                          entry->BuilderFactory.DOUBLE.build(entry.get(0)
                                                                 .getValue()))),
                          (x,y)->x,LinkedHashMap::new));
        }

        Map< String,
             Map< String,
                  Double>> returnTerms = new LinkedHashMap < >(rawDataList.size());

        for(Object rawData :rawDataList)
        {
            List <Object> rawElements = (List <Object>)rawData;
            String header = STRING.build(rawElements.get(0));

            if (!TERM.equals(header))
            {
                throw new IllegalStateException("Unrecognized header: "
                          + header);
            }

            String term = STRING.build(rawElements.get(1));
            List <List <Object>> list = (List <List <Object>>)rawElements.get(2);
            Map< String,
                 Double> entries = new LinkedHashMap < >(list.size());
            list.forEach(entry->entries.put(STRING.build(entry.get(1)),
                                            BuilderFactory.DOUBLE.build(entry.get(0))));
            returnTerms.put(
                term,
                entries);
        }

        return returnTerms;
    }

@Override
public
final
List <Object> exec()
{
    if (!inMulti)
    {
        throw new IllegalStateException("EXEC without MULTI");
    }

    try
        (Connection connection = failoverProvider.getConnection())
        {
            commands.forEach((command)->connection.sendCommand(command.getKey()));
  // following connection.getMany(int) flushes anyway, so no flush here.

  // ignore QUEUED (or ERROR)
            connection.getMany(commands.size());

  // remove extra response builders
            for(int idx = 0; idx < extraCommandCount.get(); ++idx) {
                                                                       commands.poll();
            }

            connection.sendCommand(EXEC);
            List <Object> unformatted = connection.getObjectMultiBulkReply();

            if (unformatted == null)
            {
                commands.clear();
                return null;
            }

            List <Object> formatted = new ArrayList < >(unformatted.size() - extraCommandCount.get());

            for(Object rawReply :unformatted)
            {
                try
                {
                    Response <
                                 ?
                             > response = commands.poll()
                                              .getValue();
                    response.set(rawReply);
                    formatted.add(response.get());
                }
                catch (JedisDataException e)
                {
                    formatted.add(e);
                }
            }

            return formatted;
        }
        finally
        {
            inMulti = false;
            inWatch = false;
        }
}

    private
    void process()
    {
        do
        {
            Object reply = authenticator.client.getUnflushedObject();

            if (reply instanceof List)
            {
                List <Object> listReply = (List <Object>)reply;
                final Object firstObj = listReply.get(0);

                if (!(firstObj instanceof byte[]))
                {
                    throw new JedisException("Unknown message type: "
                              + firstObj);
                }

                final byte[] resp = (byte[])firstObj;

                if (Arrays.equals(SSUBSCRIBE.getRaw(),
                                  resp))
                {
                    subscribedChannels = ((Long)listReply.get(2))
                                             .intValue();
                    final byte[] bchannel = (byte[])listReply.get(1);
                    final T enchannel = (bchannel == null)?null:encode(bchannel);
                    onSSubscribe(enchannel,
                                 subscribedChannels);
                }
                else
                if (Arrays.equals(SUNSUBSCRIBE.getRaw(),
                                  resp))
                {
                    subscribedChannels = ((Long)listReply.get(2))
                                             .intValue();
                    final byte[] bchannel = (byte[])listReply.get(1);
                    final T enchannel = (bchannel == null)?null:encode(bchannel);
                    onSUnsubscribe(enchannel,
                                   subscribedChannels);
                }
                else
if (Arrays.equals(SMESSAGE.getRaw(),
                 resp))
{
   final byte[] bchannel = (byte[])listReply.get(1);
   final byte[] bmesg = (byte[])listReply.get(2);
   final T enchannel = (bchannel == null)?null:encode(bchannel);
   final T enmesg = (bmesg == null)?null:encode(bmesg);
   onSMessage(enchannel,
              enmesg);
}
else
{
   throw new JedisException("Unknown message type: "
             + firstObj);
}
            }
            else
            if (reply instanceof byte[])
            {
                Consumer <Object> resultHandler = authenticator.resultHandler.poll();

                if (resultHandler == null)
                {
                    throw new JedisException("Unexpected message : "
                              + SafeEncoder.encode((byte[])reply));
                }

                resultHandler.accept(reply);
            }
            else
            {
                throw new JedisException("Unknown message type: "
                          + reply);
            }
        }

        while (!Thread.currentThread()
                .isInterrupted() && isSubscribed());

//    /* Invalidate instance since this thread is no longer listening */
//    this.client = null;
    }

@Override
public boolean equals(Object o)
{
    if (o == this)
    {
        return true;
    }

    if (!(o instanceof CommandObject))
    {
        return false;
    }

    Iterator <Rawable> e1 = arguments.iterator();
    Iterator <Rawable> e2 = ((CommandObject)o)
                                .arguments .iterator();

    while (e1.hasNext() && e2.hasNext())
    {
        Rawable o1 = e1.next();
        Rawable o2 = e2.next();

        if (!(o1 == null?o2 == null:o1.equals(o2)))
        {
            return false;
        }
    }

    if (e1.hasNext()||e2.hasNext())
    {
        return false;
    }

    return builder == ((CommandObject)o)
                          .builder;
}

