public static RequestCreator loadScaledDownThumbnail(final Context context, @NonNull final List < Image > images) {
    // scale down the notification thumbnail for performance
    return PicassoHelper.loadThumbnail(images) .transform(new Transformation() {
                                                              @Override
                                                              public Bitmap transform(final Bitmap source) {
                                                                  if (DEBUG) {
                                                                      Log.d(TAG, "Thumbnail - transform() called");
                                                                  }
                                                                  final float notificationThumbnailWidth = Math.min(context.getResources() .getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
                                                                  final Bitmap result = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth, (int) (source.getHeight()/(source.getWidth()/ notificationThumbnailWidth)), null, true);
                                                                  if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
                                                                      final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth - 1, (int) (source.getHeight()/(source.getWidth()/(notificationThumbnailWidth - 1))), null, true);
                                                                      source.recycle();

                                                                      return copied;
                                                                  } else {
                                                                      source.recycle();

                                                                      return result;
                                                                  }
                                                              }

                                                              @Override
                                                              public String key() {
                                                                  return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
                                                              }

                                                          });
}

private void buildQualityMenu() {
    if (qualityPopupMenu == null) {
        return;
    }

    qualityPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_QUALITY);

    final List < VideoStream > availableStreams = Optional.ofNullable(player.getCurrentMetadata()) .flatMap(MediaItemTag ::getMaybeQuality) .map(MediaItemTag.Quality::getSortedVideoStreams) .orElse(null);
    if (availableStreams == null) {
        return;
    }
    for (int i = 0; i < availableStreams.size(); i++) {
        final VideoStream videoStream = availableStreams.get(i);
        qualityPopupMenu.getMenu().add(POPUP_MENU_ID_QUALITY, i, Menu.NONE, MediaFormat.getNameById(videoStream.getFormatId())+ " " + videoStream.getResolution());
    }

    qualityPopupMenu.setOnMenuItemClickListener(this);
    qualityPopupMenu.setOnDismissListener(this);
    player.getSelectedVideoStream().ifPresent(s ->
        binding.qualityTextView.setText(s.getResolution()));
}

private void buildCaptionMenu(@NonNull final List < String > availableLanguages) {
    if (captionPopupMenu == null) {
        return;
    }

    captionPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_CAPTION);
    captionPopupMenu.setOnDismissListener(this);

    // Add option for turning off caption

    final MenuItem captionOffItem = captionPopupMenu.getMenu() .add(POPUP_MENU_ID_CAPTION, 0, Menu.NONE, R.string.caption_none);
    captionOffItem.setOnMenuItemClickListener(menuItem -> {
                                                  final int textRendererIndex = player.getCaptionRendererIndex();
                                                  if (textRendererIndex != RENDERER_UNAVAILABLE) {
                                                      player.getTrackSelector().setParameters(player.getTrackSelector() .buildUponParameters() .setRendererDisabled(textRendererIndex, true));
                                                  }

                                                  player.getPrefs() .edit() .remove(context.getString(R.string.caption_user_set_key)).apply();

                                                  return true;
                                              });

    // Add all available captions

    for (int i = 0; i < availableLanguages.size(); i++) {
        final String captionLanguage = availableLanguages.get(i);
        final MenuItem captionItem = captionPopupMenu.getMenu() .add(POPUP_MENU_ID_CAPTION, i + 1, Menu.NONE, captionLanguage);
        captionItem.setOnMenuItemClickListener(menuItem -> {
                                                   final int textRendererIndex = player.getCaptionRendererIndex();
                                                   if (textRendererIndex != RENDERER_UNAVAILABLE) {
                // DefaultTrackSelector will select for text tracks in the following order.
                // When multiple tracks share the same rank, a random track will be chosen.
                // 1. ANY track exactly matching preferred language name
                // 2. ANY track exactly matching preferred language stem
                // 3. ROLE_FLAG_CAPTION track matching preferred language stem
                // 4. ROLE_FLAG_DESCRIBES_MUSIC_AND_SOUND track matching preferred language stem
                // This means if a caption track of preferred language is not available,
                // then an auto-generated track of that language will be chosen automatically.
                                                       player.getTrackSelector().setParameters(player.getTrackSelector() .buildUponParameters() .setPreferredTextLanguages(captionLanguage, PlayerHelper.captionLanguageStemOf(captionLanguage)) .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION) .setRendererDisabled(textRendererIndex, false));
                                                       player.getPrefs() .edit() .putString(context.getString(R.string.caption_user_set_key), captionLanguage).apply();
                                                   }
                                                   return true;
                                               });
    }

    captionPopupMenu.setOnDismissListener(this);

    // apply caption language from previous user preference

    final int textRendererIndex = player.getCaptionRendererIndex();
    if (textRendererIndex == RENDERER_UNAVAILABLE) {
        return;
    }

    // If user prefers to show no caption, then disable the renderer.
    // Otherwise, DefaultTrackSelector may automatically find an available caption
    // and display that.
    final String userPreferredLanguage = player.getPrefs() .getString(context.getString(R.string.caption_user_set_key), null);
    if (userPreferredLanguage == null) {
        player.getTrackSelector().setParameters(player.getTrackSelector() .buildUponParameters() .setRendererDisabled(textRendererIndex, true));

        return;
    }

    // Only set preferred language if it does not match the user preference,
    // otherwise there might be an infinite cycle at onTextTracksChanged.
    final List < String > selectedPreferredLanguages = player.getTrackSelector() .getParameters().preferredTextLanguages;
    if (!selectedPreferredLanguages.contains(userPreferredLanguage)) {
        player.getTrackSelector().setParameters(player.getTrackSelector() .buildUponParameters() .setPreferredTextLanguages(userPreferredLanguage, PlayerHelper.captionLanguageStemOf(userPreferredLanguage)) .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION) .setRendererDisabled(textRendererIndex, false));
    }
}

    @SuppressLint("PrivateResource" ) // we currently use Exoplayer's internal strings and icons
    @Nullable
    public static NotificationActionData fromNotificationActionEnum(@NonNull final Player player, @NotificationConstants.Action final int selectedAction) {
        final int baseActionIcon = NotificationConstants.ACTION_ICONS[selectedAction];
        final Context ctx = player.getContext();
        switch(selectedAction) {
            case NotificationConstants.PREVIOUS:
            return new NotificationActionData(ACTION_PLAY_PREVIOUS, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_previous_description), baseActionIcon);
            case NotificationConstants.NEXT:
            return new NotificationActionData(ACTION_PLAY_NEXT, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_next_description), baseActionIcon);
            case NotificationConstants.REWIND:
            return new NotificationActionData(ACTION_FAST_REWIND, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_rewind_description), baseActionIcon);
            case NotificationConstants.FORWARD:
            return new NotificationActionData(ACTION_FAST_FORWARD, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_fastforward_description), baseActionIcon);
            case NotificationConstants.SMART_REWIND_PREVIOUS:
            if (player.getPlayQueue() != null && player.getPlayQueue() .size() > 1) {
                return new NotificationActionData(ACTION_PLAY_PREVIOUS, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_previous_description), com.google.android.exoplayer2.ui.R.drawable.exo_notification_previous);
            } else {
                return new NotificationActionData(ACTION_FAST_REWIND, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_rewind_description), com.google.android.exoplayer2.ui.R.drawable.exo_controls_rewind);
            }
            case NotificationConstants.SMART_FORWARD_NEXT:
            if (player.getPlayQueue() != null && player.getPlayQueue() .size() > 1) {
                return new NotificationActionData(ACTION_PLAY_NEXT, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_next_description), com.google.android.exoplayer2.ui.R.drawable.exo_notification_next);
            } else {
                return new NotificationActionData(ACTION_FAST_FORWARD, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_fastforward_description), com.google.android.exoplayer2.ui.R.drawable.exo_controls_fastforward);
            }
            case NotificationConstants.PLAY_PAUSE_BUFFERING:
            if (player.getCurrentState() == Player.STATE_PREFLIGHT || player.getCurrentState() == Player.STATE_BLOCKED || player.getCurrentState() == Player.STATE_BUFFERING) {
                return new NotificationActionData(ACTION_PLAY_PAUSE, ctx.getString(R.string.notification_action_buffering), R.drawable.ic_hourglass_top);
            }

                // fallthrough
            case NotificationConstants.PLAY_PAUSE:
            if (player.getCurrentState() == Player.STATE_COMPLETED) {
                return new NotificationActionData(ACTION_PLAY_PAUSE, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_pause_description), R.drawable.ic_replay);
            } else
                if (player.isPlaying()|| player.getCurrentState() == Player.STATE_PREFLIGHT || player.getCurrentState() == Player.STATE_BLOCKED || player.getCurrentState() == Player.STATE_BUFFERING) {
                    return new NotificationActionData(ACTION_PLAY_PAUSE, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_pause_description), com.google.android.exoplayer2.ui.R.drawable.exo_notification_pause);
                } else {
                    return new NotificationActionData(ACTION_PLAY_PAUSE, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_play_description), com.google.android.exoplayer2.ui.R.drawable.exo_notification_play);
                }
            case NotificationConstants.REPEAT:
            if (player.getRepeatMode() == REPEAT_MODE_ALL) {
                return new NotificationActionData(ACTION_REPEAT, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_all_description), com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_all);
            } else
                if (player.getRepeatMode() == REPEAT_MODE_ONE) {
                    return new NotificationActionData(ACTION_REPEAT, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_one_description), com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_one);
                } else /* player.getRepeatMode() == REPEAT_MODE_OFF */ {
                    return new NotificationActionData(ACTION_REPEAT, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_off_description), com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_off);
                }
            case NotificationConstants.SHUFFLE:
            if (player.getPlayQueue() != null && player.getPlayQueue() .isShuffled()) {
                return new NotificationActionData(ACTION_SHUFFLE, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_shuffle_on_description), com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_on);
            } else {
                return new NotificationActionData(ACTION_SHUFFLE, ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_shuffle_off_description), com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_off);
            }
            case NotificationConstants.CLOSE:
            return new NotificationActionData(ACTION_CLOSE, ctx.getString(R.string.close), R.drawable.ic_close);
            case NotificationConstants.NOTHING: default:
                // do nothing
            return null;
}
    }

private void handleCookiesFromUrl(@Nullable final String url) {
    if (MainActivity.DEBUG) {
        Log.d(TAG, "handleCookiesFromUrl: url=" + (url == null ? "null" : url));
    }
    if (url == null) {
        return;
    }
    final String cookies = CookieManager.getInstance() .getCookie(url);
    handleCookies(cookies);

    // sometimes cookies are inside the url

    final int abuseStart = url.indexOf("google_abuse=");
    if (abuseStart != -1) {
        final int abuseEnd = url.indexOf("+path");
        try {
            handleCookies(Utils.decodeUrlUtf8(url.substring(abuseStart + 13, abuseEnd)));
        } catch (final StringIndexOutOfBoundsException e) {
            if (MainActivity.DEBUG) {
                Log.e(TAG, "handleCookiesFromUrl: invalid google abuse starting at " + abuseStart + " and ending at " + abuseEnd + " for url " + url, e);
            }
        }
    }
}

public void toggleFullscreen() {
    if (DEBUG) {
        Log.d(TAG, "toggleFullscreen() called");
    }
    final PlayerServiceEventListener fragmentListener = player.getFragmentListener() .orElse(null);
    if (fragmentListener == null || player.exoPlayerIsNull()) {
        return;
    }

    isFullscreen = !isFullscreen;

    if (isFullscreen) {
        // Android needs tens milliseconds to send new insets but a user is able to see
        // how controls changes it's position from `0` to `nav bar height` padding.
        // So just hide the controls to hide this visual inconsistency
        hideControls(0, 0);
    } else {
        // Apply window insets because Android will not do it when orientation changes
        // from landscape to portrait (open vertical video to reproduce)
        binding.playbackControlRoot.setPadding(0, 0, 0, 0);
    }

    fragmentListener.onFullscreenStateChanged(isFullscreen);
    binding.titleTextView.setVisibility(isFullscreen ? View.VISIBLE : View.GONE);
    binding.channelTextView.setVisibility(isFullscreen ? View.VISIBLE : View.GONE);
    binding.playerCloseButton.setVisibility(isFullscreen ? View.GONE : View.VISIBLE);
    setupScreenRotationButton();
}

private void initPlayer(final boolean playOnReady) {
    if (DEBUG) {
        Log.d(TAG, "initPlayer() called with: playOnReady = [" + playOnReady + "]");
    }

    simpleExoPlayer = new ExoPlayer.Builder(context, renderFactory) .setTrackSelector(trackSelector) .setLoadControl(loadController) .setUsePlatformDiagnostics(false) .build();
    simpleExoPlayer.addListener(this);
    simpleExoPlayer.setPlayWhenReady(playOnReady);
    simpleExoPlayer.setSeekParameters(PlayerHelper.getSeekParameters(context));
    simpleExoPlayer.setWakeMode(C.WAKE_MODE_NETWORK);
    simpleExoPlayer.setHandleAudioBecomingNoisy(true);
    audioReactor = new AudioReactor(context, simpleExoPlayer);
    registerBroadcastReceiver();

    // Setup UIs
    UIs.call(PlayerUi ::initPlayer);

    // Disable media tunneling if requested by the user from ExoPlayer settings

    if (!PreferenceManager.getDefaultSharedPreferences(context) .getBoolean(context.getString(R.string.disable_media_tunneling_key), false)) {
        trackSelector.setParameters(trackSelector.buildUponParameters() .setTunnelingEnabled(true));
    }
}

private MediaMetadataCompat buildMediaMetadata() {
    if (DEBUG) {
        Log.d(TAG, "buildMediaMetadata called");
    }

    // set title and artist
    final MediaMetadataCompat .Builder builder = new MediaMetadataCompat.Builder() .putString(MediaMetadataCompat.METADATA_KEY_TITLE, player.getVideoTitle()) .putString(MediaMetadataCompat.METADATA_KEY_ARTIST, player.getUploaderName());

    // set duration (-1 for livestreams or if unknown, see the METADATA_KEY_DURATION docs)
    final long duration = player.getCurrentStreamInfo() .filter(info -> !StreamTypeUtil.isLiveStream(info.getStreamType())) .map(info ->
                                                                                                                                info.getDuration() * 1000L) .orElse(-1L);
    builder.putLong(MediaMetadataCompat.METADATA_KEY_DURATION, duration);

    // set album art, unless the user asked not to, or there is no thumbnail available

    final boolean showThumbnail = player.getPrefs() .getBoolean(context.getString(R.string.show_thumbnail_key), true);
    Optional.ofNullable(player.getThumbnail()) .filter(bitmap ->
                                                   showThumbnail).ifPresent(bitmap -> {
                                                                                builder.putBitmap(MediaMetadataCompat.METADATA_KEY_ALBUM_ART, bitmap);
                                                                                builder.putBitmap(MediaMetadataCompat.METADATA_KEY_DISPLAY_ICON, bitmap);
                                                                            });

    return builder.build();
}

public MediaDescriptionCompat getQueueMetadata(final int index) {
    if (player.getPlayQueue() == null) {
        return null;
    }
    final PlayQueueItem item = player.getPlayQueue() .getItem(index);
    if (item == null) {
        return null;
    }
    final MediaDescriptionCompat .Builder descBuilder = new MediaDescriptionCompat.Builder() .setMediaId(String.valueOf(index)) .setTitle(item.getTitle()) .setSubtitle(item.getUploader());

    // set additional metadata for A2DP/AVRCP (Audio/Video Bluetooth profiles)
    final Bundle additionalMetadata = new Bundle();
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_TITLE, item.getTitle());
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_ARTIST, item.getUploader());
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_DURATION, item.getDuration() * 1000);
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_TRACK_NUMBER, index + 1L);
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_NUM_TRACKS, player.getPlayQueue() .size());
    descBuilder.setExtras(additionalMetadata);

    try {
        descBuilder.setIconUri(Uri.parse(ImageStrategy.choosePreferredImage(item.getThumbnails())));
    } catch (final Throwable e) {
        // no thumbnail available at all, or the user disabled image loading,
        // or the obtained url is not a valid `Uri`
    }
    return descBuilder.build();
}

private synchronized NotificationCompat .Builder createNotification() {
    if (DEBUG) {
        Log.d(TAG, "createNotification()");
    }

    notificationManager = NotificationManagerCompat.from(player.getContext());

    final NotificationCompat .Builder builder = new NotificationCompat.Builder(player.getContext(), player.getContext() .getString(R.string.notification_channel_id));
    final MediaStyle mediaStyle = new MediaStyle();

    // setup media style (compact notification slots and media session)
    if (Build.VERSION.SDK_INT < Build.VERSION_CODES.TIRAMISU) {
        // notification actions are ignored on Android 13+, and are replaced by code in
        // MediaSessionPlayerUi
        final int[] compactSlots = initializeNotificationSlots();
        mediaStyle.setShowActionsInCompactView(compactSlots);
    }

    player.UIs() .get(MediaSessionPlayerUi.class) .flatMap(MediaSessionPlayerUi ::getSessionToken).ifPresent(mediaStyle ::setMediaSession);

    // setup notification builder
    builder.setStyle(mediaStyle) .setPriority(NotificationCompat.PRIORITY_HIGH) .setVisibility(NotificationCompat.VISIBILITY_PUBLIC) .setCategory(NotificationCompat.CATEGORY_TRANSPORT) .setShowWhen(false) .setSmallIcon(R.drawable.ic_newpipe_triangle_white) .setColor(ContextCompat.getColor(player.getContext(), R.color.dark_background_color)) .setColorized(player.getPrefs() .getBoolean(player.getContext() .getString(R.string.notification_colorize_key), true)).setDeleteIntent(PendingIntentCompat.getBroadcast(player.getContext(), NOTIFICATION_ID, new Intent(ACTION_CLOSE), FLAG_UPDATE_CURRENT, false));

    // set the initial value for the video thumbnail, updatable with updateNotificationThumbnail
    setLargeIcon(builder);

    return builder;
}

    void fastPath(Subscriber<?
                                super R> a,
                                Iterator<?
                                            extends R> iterator) {
    for(;
;
) {
        if (cancelled) {
            return ;
        }

        R v;
        try {
            v = iterator.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return ;
        }

        a.onNext(v);
        if (cancelled) {
            return ;
        }

        boolean b;
        try {
            b = iterator.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return ;
        }

        if (!b) {
            a.onComplete();
            return ;
        }
    }
    }

@Override
public void onComplete() {
    if (done) {
        return ;
    }

    try {
        onComplete.run();
    } catch (Throwable e) {
        fail(e);
        return ;
    }

    done = true;
    downstream.onComplete();
    try {
        onAfterTerminate.run();
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        RxJavaPlugins.onError(e);
    }
}

    @Override
    public void run(long n) {
    long emitted = 0L;
    Iterator<T> iterator = this.iterator;
    ConditionalSubscriber<?
                             super T> downstream = this.downstream;
    for(;
;
) {
        if (cancelled) {
            clear();
            break;
        } else {
            T next;
            try {
                next = Objects.requireNonNull(iterator.next(), "The Stream's Iterator returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (downstream.tryOnNext(next)) {
                emitted++;
            }

            if (cancelled) {
                continue;
            }

            try {
                if (!iterator.hasNext()) {
                    downstream.onComplete();
                    cancelled = true;
                    continue;
                }
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (emitted != n) {
                continue;
            }
        }

        n = get();
        if (emitted == n) {
            if (compareAndSet(n, 0L)) {
                break;
            }

            n = get();
        }
    }
    }

    void replay(CacheSubscription<T> consumer) {
        // make sure there is only one replay going on at a time
        if (consumer.getAndIncrement() != 0) {
            return ;
        }

        // see if there were more replay request in the meantime

        int missed = 1;
        // read out state into locals upfront to avoid being re-read due to volatile reads
        long index = consumer.index;
        int offset = consumer.offset;
        Node<T> node = consumer.node;
        AtomicLong requested = consumer.requested;
        Subscriber<?
                      super T> downstream = consumer.downstream;
        int capacity = capacityHint;
        for(;
;
) {
            // first see if the source has terminated, read order matters!
            boolean sourceDone = done;
            // and if the number of items is the same as this consumer has received
            boolean empty = size == index;

            // if the source is done and we have all items so far, terminate the consumer
            if (sourceDone && empty) {
                // release the node object to avoid leaks through retained consumers
                consumer.node = null;
                // if error is not null then the source failed
                Throwable ex = error;
                if (ex != null) {
                    downstream.onError(ex);
                } else {
                    downstream.onComplete();
                }

                return ;
            }

            // there are still items not sent to the consumer

            if (!empty) {
                // see how many items the consumer has requested in total so far
                long consumerRequested = requested.get();
                // MIN_VALUE indicates a cancelled consumer, we stop replaying
                if (consumerRequested == Long.MIN_VALUE) {
                    // release the node object to avoid leaks through retained consumers
                    consumer.node = null;
                    return ;
                }
                // if the consumer has requested more and there is more, we will emit an item

                if (consumerRequested != index) {

                    // if the offset in the current node has reached the node capacity
                    if (offset == capacity) {
                        // switch to the subsequent node
                        node = node.next;
                        // reset the in-node offset
                        offset = 0;
                    }

                    // emit the cached item

                    downstream.onNext(node.values[ offset ]);

                    // move the node offset forward
                    offset++;
                    // move the total consumed item count forward
                    index++;

                    // retry for the next item/terminal event if any
                    continue;
                }
            }

            // commit the changed references back

            consumer.index = index;
            consumer.offset = offset;
            consumer.node = node;
            // release the changes and see if there were more replay request in the meantime
            missed = consumer.addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
        }

    @SuppressWarnings("unchecked") void drain() {
        if (getAndIncrement() != 0) {
            return ;
        }

        Observer<?
                    super R> downstream = this.downstream;
        SimpleQueue<T> queue = this.queue;
        AtomicThrowable errors = this.errors;
        for(;
;
) {
            if (!active) {
                if (cancelled) {
                    queue.clear();
                    return ;
                }

                if (!tillTheEnd) {
                    Throwable ex = errors.get();
                    if (ex != null) {
                        queue.clear();
                        cancelled = true;
                        errors.tryTerminateConsumer(downstream);
                        return ;
                    }
                }

                boolean d = done;
                T v;
                try {
                    v = queue.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancelled = true;
                    this.upstream.dispose();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return ;
                }

                boolean empty = v == null;
                if (d && empty) {
                    cancelled = true;
                    errors.tryTerminateConsumer(downstream);
                    return ;
                }

                if (!empty) {
                    ObservableSource<?
                                        extends R> o;
                    try {
                        o = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null ObservableSource");
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        cancelled = true;
                        this.upstream.dispose();
                        queue.clear();
                        errors.tryAddThrowableOrReport(ex);
                        errors.tryTerminateConsumer(downstream);
                        return ;
                    }

                    if (o instanceof Supplier) {
                        R w;
                        try {
                            w = ((Supplier<R>)o).get();
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            errors.tryAddThrowableOrReport(ex);
                            continue;
                        }

                        if (w != null && !cancelled) {
                            downstream.onNext(w);
                        }

                        continue;
                    } else {
                        active = true;
                        o.subscribe(observer);
                    }
                }
            }

            if (decrementAndGet() == 0) {
                break;
            }
        }
    }

    void drain() {
        if (getAndIncrement() != 0) {
            return ;
        }

        int missed = 1;
        final Subscriber<?
                            super R> downstream = this.downstream;
        final SimpleQueue<T> queue = this.queue;
        final AtomicThrowable error = this.error;
        Iterator<?
                    extends R> iterator = this.currentIterator;
        long requested = this.requested.get();
        long emitted = this.emitted;
        final int limit = prefetch - (prefetch> > 2);
        boolean canRequest = sourceMode != QueueFuseable.SYNC;
        for(;
;
) {
            if (cancelled) {
                queue.clear();
                clearCurrentSuppressCloseError();
            } else {
                boolean isDone = upstreamDone;
                if (error.get() != null) {
                    downstream.onError(error.get());
                    cancelled = true;
                    continue;
                }

                if (iterator == null) {
                    T t;
                    try {
                        t = queue.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        trySignalError(downstream, ex);
                        continue;
                    }

                    boolean isEmpty = t == null;
                    if (isDone && isEmpty) {
                        downstream.onComplete();
                        cancelled = true;
                    } else if (!isEmpty) {
                               if (canRequest && ++consumed == limit) {
                                   consumed = 0;
                                   upstream.request(limit);
                               }

                               Stream<?
                                         extends R> stream;
                               try {
                                   stream = Objects.requireNonNull(mapper.apply(t), "The mapper returned a null Stream");
                                   iterator = stream.iterator();
                                   if (iterator.hasNext()) {
                                       currentIterator = iterator;
                                       currentCloseable = stream;
                                   } else {
                                       iterator = null;
                                   }
                               } catch (Throwable ex) {
                                   Exceptions.throwIfFatal(ex);
                                   trySignalError(downstream, ex);
                               }

                               continue;
                           }
                }

                if (iterator != null && emitted != requested) {
                    R item;
                    try {
                        item = Objects.requireNonNull(iterator.next(), "The Stream.Iterator returned a null value");
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        trySignalError(downstream, ex);
                        continue;
                    }

                    if (!cancelled) {
                        downstream.onNext(item);
                        emitted++;
                        if (!cancelled) {
                            try {
                                if (!iterator.hasNext()) {
                                    iterator = null;
                                    clearCurrentRethrowCloseError();
                                }
                            } catch (Throwable ex) {
                                Exceptions.throwIfFatal(ex);
                                trySignalError(downstream, ex);
                            }
                        }
                    }

                    continue;
                }
            }

            this.emitted = emitted;
            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }

            requested = this.requested.get();
        }
        }

    void drain() {
        if (getAndIncrement() != 0) {
            return ;
        }

        final Subscriber<?
                            super R> a = downstream;
        final ZipSubscriber<T, R>[] qs = subscribers;
        final int n = qs.length;
        Object[] values = current;
        int missed = 1;
        for(;
;
) {
            long r = requested.get();
            long e = 0L;
            while (r != e) {
                if (cancelled) {
                    return ;
                }

                if (!delayErrors && errors.get() != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(a);
                    return ;
                }

                boolean empty = false;
                for(int j = 0;
j<n;
j++) {
                    ZipSubscriber<T, R> inner = qs[ j ];
                    if (values[ j ] == null) {
                        boolean d = inner.done;
                        SimpleQueue<T> q = inner.queue;
                        T v = null;
                        try {
                            v = q != null ?
                                q.poll() : null;
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            errors.tryAddThrowableOrReport(ex);
                            if (!delayErrors) {
                                cancelAll();
                                errors.tryTerminateConsumer(a);
                                return ;
                            }

                            d = true;
                        }

                        boolean sourceEmpty = v == null;
                        if (d && sourceEmpty) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return ;
                        }

                        if (!sourceEmpty) {
                            values[ j ] = v;
                        } else {
                            empty = true;
                        }
                    }
                }

                if (empty) {
                    break;
                }

                R v;
                try {
                    v = Objects.requireNonNull(zipper.apply(values.clone()), "The zipper returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancelAll();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(a);
                    return ;
                }

                a.onNext(v);
                e++;
                Arrays.fill(values, null);
            }

            if (r == e) {
                if (cancelled) {
                    return ;
                }

                if (!delayErrors && errors.get() != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(a);
                    return ;
                }

                for(int j = 0;
j<n;
j++) {
                    ZipSubscriber<T, R> inner = qs[ j ];
                    if (values[ j ] == null) {
                        boolean d = inner.done;
                        SimpleQueue<T> q = inner.queue;
                        T v = null;
                        try {
                            v = q != null ?
                                q.poll() : null;
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            errors.tryAddThrowableOrReport(ex);
                            if (!delayErrors) {
                                cancelAll();
                                errors.tryTerminateConsumer(a);
                                return ;
                            }

                            d = true;
                        }

                        boolean empty = v == null;
                        if (d && empty) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return ;
                        }

                        if (!empty) {
                            values[ j ] = v;
                        }
                    }
                }
            }

            if (e != 0L) {
                for (ZipSubscriber<T, R> inner : qs) {
                    inner.request(e);
                }

                if (r != Long.MAX_VALUE) {
                    requested.addAndGet(-e);
                }
            }

            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
        }

    @Override Node getHead() {
    long timeLimit = scheduler.now(unit) - maxAge;
    Node prev = get();
    Node next = prev.get();
    for(;
;
) {
        if (next == null) {
            break;
        }

        Timed<?> v = (Timed<?>)next.value;
        if (NotificationLite.isComplete(v.value())||
            NotificationLite.isError(v.value())) {
            break;
        }

        if (v.time() <= timeLimit) {
            prev = next;
            next = next.get();
        } else {
            break;
        }
    }

    return prev;
    }

    @SuppressWarnings("unchecked") void remove(AsyncSubscription<T> ps) {
        for(;
;
) {
            AsyncSubscription<T>[] a = subscribers.get();
            int n = a.length;
            if (n == 0) {
                return ;
            }

            int j = - 1;
            for(int i = 0;
i<n;
i++) {
                if (a[ i ] == ps) {
                    j = i;
                    break;
                }
            }

            if (j < 0) {
                return ;
            }

            AsyncSubscription<T>[] b;
            if (n == 1) {
                b = EMPTY;
            } else {
                b = new
AsyncSubscription [ n - 1];
                System.arraycopy(a, 0, b, 0, j);
                System.arraycopy(a, j + 1, b, j, n - j - 1);
            }

            if (subscribers.compareAndSet(a, b)) {
                return ;
            }
        }
    }

@Override
public void onSubscribe(Disposable d) {
    if (DisposableHelper.validate(this.upstream, d)) {
        this.upstream = d;
        if (d instanceof QueueDisposable) {
            @SuppressWarnings("unchecked") QueueDisposable<T> qd = (QueueDisposable<T>)d;
            int m = qd.requestFusion(QueueDisposable.ANY);
            if (m == QueueDisposable.SYNC) {
                fusionMode = m;
                queue = qd;
                done = true;
                downstream.onSubscribe(this);
                drain();
                return ;
            }

            if (m == QueueDisposable.ASYNC) {
                fusionMode = m;
                queue = qd;
                downstream.onSubscribe(this);
                return ;
            }
        }

        queue = new SpscLinkedArrayQueue<>(bufferSize);
        downstream.onSubscribe(this);
    }
}

private void saveDocumentToZip(PDDocument document,
                               ZipOutputStream zipOut,
                               String baseFilename,
                               int index) throws IOException {
    log.debug("Starting saveDocumentToZip for document part {}", index);
    ByteArrayOutputStream outStream = new ByteArrayOutputStream();

    try {
        log.debug("Saving document part {} to byte array", index);
        document.save(outStream);
        log.debug("Successfully saved document part {} ({} bytes)", index, outStream.size());
    } catch (Exception e) {
        log.error("Error saving document part {} to byte array", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }

    try {
        log.debug("Closing document part {}", index);
        document.close();
        log.debug("Successfully closed document part {}", index);
    } catch (Exception e) {
        log.error("Error closing document part {}", index, e);
        // Continue despite close error
    }

    try {
        // Create a new zip entry
        String entryName = baseFilename + "_" + index + ".pdf";
        log.debug("Creating ZIP entry: {}", entryName);
        ZipEntry zipEntry = new ZipEntry(entryName);
        zipOut.putNextEntry(zipEntry);
        byte [] bytes = outStream.toByteArray();
        log.debug("Writing {} bytes to ZIP entry", bytes.length);
        zipOut.write(bytes);
        log.debug("Closing ZIP entry");
        zipOut.closeEntry();
        log.debug("Successfully added document part {} to ZIP", index);
    } catch (Exception e) {
        log.error("Error adding document part {} to ZIP", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }
}

private void processRequest(int limitPerDay,
                            String identifier,
                            Map<String, Bucket> buckets,
                            HttpServletRequest request,
                            HttpServletResponse response,
                            FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k->createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill()/ 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

@SuppressWarnings("deprecation")
public void migrateEnterpriseSettingsToPremium(ApplicationProperties applicationProperties) {
    EnterpriseEdition enterpriseEdition = applicationProperties.getEnterpriseEdition();
    Premium premium = applicationProperties.getPremium();

    // Only proceed if both objects exist
    if (enterpriseEdition == null||premium == null) {
        return;
    }

    // Copy the license key if it's set in enterprise but not in premium

    if (premium.getKey() == null||"00000000-0000-0000-0000-000000000000".equals(premium.getKey() )) {
        if (enterpriseEdition.getKey() != null && !"00000000-0000-0000-0000-000000000000".equals(enterpriseEdition.getKey() )) {
            premium.setKey(enterpriseEdition.getKey());
        }
    }

    // Copy enabled state if enterprise is enabled but premium is not

    if (!premium.isEnabled() && enterpriseEdition.isEnabled()) {
        premium.setEnabled(true);
    }

    // Copy SSO auto login setting

    if (!premium.getProFeatures() .isSsoAutoLogin() && enterpriseEdition.isSsoAutoLogin()) {
        premium.getProFeatures().setSsoAutoLogin(true);
    }

    // Copy CustomMetadata settings

    Premium.ProFeatures.CustomMetadata premiumMetadata = premium.getProFeatures() .getCustomMetadata();
    EnterpriseEdition.CustomMetadata enterpriseMetadata = enterpriseEdition.getCustomMetadata();
    if (enterpriseMetadata != null && premiumMetadata != null) {
        // Copy autoUpdateMetadata setting
        if (!premiumMetadata.isAutoUpdateMetadata() && enterpriseMetadata.isAutoUpdateMetadata()) {
            premiumMetadata.setAutoUpdateMetadata(true);
        }

        // Copy author if not set in premium but set in enterprise

        if ((premiumMetadata.getAuthor() == null||premiumMetadata.getAuthor() .trim() .isEmpty()||"username".equals(premiumMetadata.getAuthor() ))&& enterpriseMetadata.getAuthor() != null && !enterpriseMetadata.getAuthor() .trim() .isEmpty()) {
            premiumMetadata.setAuthor(enterpriseMetadata.getAuthor());
        }

        // Copy creator if not set in premium but set in enterprise and different from default

        if ((premiumMetadata.getCreator() == null||"Stirling-PDF".equals(premiumMetadata.getCreator() ))&& enterpriseMetadata.getCreator() != null && !"Stirling-PDF".equals(enterpriseMetadata.getCreator() )) {
            premiumMetadata.setCreator(enterpriseMetadata.getCreator());
        }

        // Copy producer if not set in premium but set in enterprise and different from default

        if ((premiumMetadata.getProducer() == null||"Stirling-PDF".equals(premiumMetadata.getProducer() ))&& enterpriseMetadata.getProducer() != null && !"Stirling-PDF".equals(enterpriseMetadata.getProducer() )) {
            premiumMetadata.setProducer(enterpriseMetadata.getProducer());
        }
    }
}

private void applyGhostscriptCompression(OptimizePdfRequest request,
                                         int optimizeLevel,
                                         Path currentFile,
                                         List<Path> tempFiles) throws IOException {
    long preGsSize = Files.size(currentFile);
    log.info("Pre-Ghostscript file size: {}", GeneralUtils.formatBytes(preGsSize));

    // Create output file for Ghostscript
    Path gsOutputFile = Files.createTempFile("gs_output_", ".pdf");
    tempFiles.add(gsOutputFile);

    // Build Ghostscript command based on optimization level
    List<String> command = new ArrayList<>();
    command.add("gs");
    command.add("-sDEVICE=pdfwrite");
    command.add("-dCompatibilityLevel=1.5");
    command.add("-dNOPAUSE");
    command.add("-dQUIET");
    command.add("-dBATCH");

    // Map optimization levels to Ghostscript settings
    switch(optimizeLevel) {
        case 1:
        command.add("-dPDFSETTINGS=/prepress");
        break;
                          case 2:
        command.add("-dPDFSETTINGS=/printer");
        break;
                          case 3:
        command.add("-dPDFSETTINGS=/ebook");
        break;
                          case 4:
        case 5:
        command.add("-dPDFSETTINGS=/screen");
        break;
                          case 6:
        case 7:
        command.add("-dPDFSETTINGS=/screen");
        command.add("-dColorImageResolution=150");
        command.add("-dGrayImageResolution=150");
        command.add("-dMonoImageResolution=300");
        break;
                          case 8:
        case 9:
        command.add("-dPDFSETTINGS=/screen");
        command.add("-dColorImageResolution=100");
        command.add("-dGrayImageResolution=100");
        command.add("-dMonoImageResolution=200");
        break;
                          case 10:
        command.add("-dPDFSETTINGS=/screen");
        command.add("-dColorImageResolution=72");
        command.add("-dGrayImageResolution=72");
        command.add("-dMonoImageResolution=150");
        break;
                          default:
        command.add("-dPDFSETTINGS=/screen");
        break;
    }

    command.add("-sOutputFile=" + gsOutputFile.toString());
    command.add(currentFile.toString());
    ProcessExecutorResult returnCode = null;

    try {
        returnCode = ProcessExecutor.getInstance(ProcessExecutor.Processes.GHOSTSCRIPT) .runCommandWithOutputHandling(command);

        if (returnCode.getRc() == 0) {
            // Update current file to the Ghostscript output
            Files.copy(gsOutputFile, currentFile, StandardCopyOption.REPLACE_EXISTING);

            long postGsSize = Files.size(currentFile);
            double gsReduction = 100.0 - ((postGsSize*100.0)/preGsSize);
            log.info("Post-Ghostscript file size: {} (reduced by {}%)", GeneralUtils.formatBytes(postGsSize), String.format("%.1f", gsReduction));
        } else {
            log.warn("Ghostscript compression failed with return code: {}", returnCode.getRc());
            throw new IOException("Ghostscript compression failed");
        }
    } catch (Exception e) {
        log.warn("Ghostscript compression failed, will fallback to other methods", e);
        throw new IOException("Ghostscript compression failed", e);
    }
}

private Object executeWithRetries(ProceedingJoinPoint joinPoint,
                                  Object [] args,
                                  boolean async,
                                  long timeout,
                                  int maxRetries,
                                  boolean trackProgress,
                                  boolean queueable,
                                  int resourceWeight) {

    // Keep jobId reference for progress tracking in TaskManager
    AtomicReference<String> jobIdRef = new AtomicReference<>();
    return jobExecutorService.runJobGeneric(async, ()-> {
                // Use iterative approach instead of recursion to avoid stack overflow
                                                       Throwable lastException = null;

                // Attempt counter starts at 1 for first try

                                                       for (int currentAttempt = 1; currentAttempt <= maxRetries; currentAttempt++) {
                                                           try {
                                                               if (trackProgress && async) {
                            // Get jobId for progress tracking in TaskManager
                            // This enables REST API progress queries, not WebSocket
                                                                   if (jobIdRef.get() == null) {
                                                                       jobIdRef.set(getJobIdFromContext());
                                                                   }

                                                                   String jobId = jobIdRef.get();
                                                                   if (jobId != null) {
                                                                       log.debug("Tracking progress for job {} (attempt {}/{})", jobId, currentAttempt, maxRetries);
                                // Progress is tracked in TaskManager for REST API access
                                // No WebSocket notifications sent here
                                                                   }
                                                               }

                        // Attempt to execute the operation
                                                               return joinPoint.proceed(args);
                                                           } catch (Throwable ex) {
                                                               lastException = ex;
                                                               log.error("AutoJobAspect caught exception during job execution (attempt {}/{}): {}", currentAttempt, maxRetries, ex.getMessage(), ex);

                        // Check if we should retry

                                                               if (currentAttempt < maxRetries) {
                                                                   log.info("Retrying operation, attempt {}/{}", currentAttempt + 1, maxRetries);

                                                                   if (trackProgress && async) {
                                                                       String jobId = jobIdRef.get();
                                                                       if (jobId != null) {
                                                                           log.debug("Recording retry attempt for job {} in TaskManager", jobId);
                                    // Retry info is tracked in TaskManager for REST API access
                                                                       }
                                                                   }

                            // Use non-blocking delay for all retry attempts to avoid blocking
                            // threads
                            // For sync jobs this avoids starving the tomcat thread pool under
                            // load

                                                                   long delayMs = RETRY_BASE_DELAY.toMillis()*currentAttempt;

                            // Execute the retry after a delay through the JobExecutorService
                            // rather than blocking the current thread with sleep
                                                                   CompletableFuture<Object> delayedRetry = new CompletableFuture<>();

                            // Use a delayed executor for non-blocking delay
                                                                   CompletableFuture.delayedExecutor(delayMs, TimeUnit.MILLISECONDS).execute(()-> {
                                                // Continue the retry loop in the next iteration
                                                // We can't return from here directly since
                                                // we're in a Runnable
                                                                                                                                                 delayedRetry.complete(null);
                                                                                                                                             });

                            // Wait for the delay to complete before continuing
                                                                   try {
                                                                       delayedRetry.join();
                                                                   } catch (Exception e) {
                                                                       Thread.currentThread().interrupt();
                                                                       break;
                                                                   }
                                                               } else {
                            // No more retries, we'll throw the exception after the loop
                                                                   break;
                                                               }
                                                           }
                                                       }

                // If we get here, all retries failed

                                                       if (lastException != null) {
                                                           throw new RuntimeException("Job failed after " + maxRetries + " attempts: " + lastException.getMessage(), lastException);
                                                       }

                // This should never happen if lastException is properly tracked

                                                       throw new RuntimeException("Job failed but no exception was recorded");
                                                   }, timeout, queueable, resourceWeight);
}

