private void monitorSubscription(final ChannelInfo info) {
    final Consumer<Throwable> onError = (final Throwable throwable)-> {
                                            animate(binding.channelSubscribeButton, false, 100);
                                            showSnackBarError(new ErrorInfo(throwable, UserAction.SUBSCRIPTION_GET, "Get subscription status", currentInfo));
                                        };
    final Observable<List<SubscriptionEntity>> observable = subscriptionManager.subscriptionTable() .getSubscriptionFlowable(info.getServiceId(), info.getUrl() ) .toObservable();
    disposables.add(observable.observeOn(AndroidSchedulers.mainThread()) .subscribe(getSubscribeUpdateMonitor(info), onError));
    disposables.add(observable.map(List::isEmpty) .distinctUntilChanged() .observeOn(AndroidSchedulers.mainThread()) .subscribe(isEmpty ->updateSubscribeButton(!isEmpty), onError));
    disposables.add(observable.map(List::isEmpty) .distinctUntilChanged() .skip(1) // channel has just been opened .filter(x ->NotificationHelper.areNewStreamsNotificationsEnabled(requireContext())) .observeOn(AndroidSchedulers.mainThread()) .subscribe(isEmpty -> {
                                                                                                                                                                                                                                                                 if (!isEmpty) {
                                                                                                                                                                                                                                                                     showNotifySnackbar();
                                                                                                                                                                                                                                                                 }
                                                                                                                                                                                                                                                             }, onError));
}

private void runOnVisible(final Consumer<AppCompatActivity> runnable) {
    getActivityContext().ifPresentOrElse(context -> {
                                             if (getLifecycle() .getCurrentState() .isAtLeast(Lifecycle.State.STARTED)) {
                                                 context.runOnUiThread(()-> {
                                                                           runnable.accept(context);
                                                                           inFlight(false);
                                                                       });
                                             } else {
                                                 getLifecycle().addObserver(new DefaultLifecycleObserver() {
                                                                                @Override
                                                                                public void onResume(@NonNull final LifecycleOwner owner) {
                                                                                    getLifecycle().removeObserver(this);
                                                                                    getActivityContext().ifPresentOrElse(context ->context.runOnUiThread(()-> {
                                                                                                                                                             runnable.accept(context);
                                                                                                                                                             inFlight(false);
                                                                                                                                                         }), ()->inFlight(false));
                                                                                }

                                                                            });
                // this trick doesn't seem to work on Android 10+ (API 29)
                // which places restrictions on starting activities from the background
                                                 if (Build.VERSION.SDK_INT < Build.VERSION_CODES.Q && !context.isChangingConfigurations()) {
                    // try to bring the activity back to front if minimised
                                                     final Intent i = new Intent(context, RouterActivity.class);
                                                     i.setFlags(Intent.FLAG_ACTIVITY_REORDER_TO_FRONT);
                                                     startActivity(i);
                                                 }
                                             }
                                         }, ()->
            // this branch is executed if there is no activity contextinFlight(false));
}

public static void showMetaInfoInTextView(@Nullable final List<MetaInfo> metaInfos, final TextView metaInfoTextView, final View metaInfoSeparator ,final CompositeDisposable disposables) {
    final Context context = metaInfoTextView.getContext();

    if (metaInfos == null||metaInfos.isEmpty()||!PreferenceManager.getDefaultSharedPreferences(context) .getBoolean(context.getString(R.string.show_meta_info_key), true )) {
        metaInfoTextView.setVisibility(View.GONE);
        metaInfoSeparator.setVisibility(View.GONE);
    } else {
        final StringBuilder stringBuilder = new StringBuilder();

        for(final MetaInfo metaInfo :metaInfos) {
            if (!isNullOrEmpty(metaInfo.getTitle())) {
                stringBuilder.append("<b>") .append(metaInfo.getTitle()) .append("</b>").append(Localization.DOT_SEPARATOR);
            }

            String content = metaInfo.getContent() .getContent() .trim();

            if (content.endsWith(".")) {
                content = content.substring(0, content.length() - 1); // remove . at end
            }

            stringBuilder.append(content);
            for (int i = 0; i < metaInfo.getUrls() .size(); i++) {
                if (i == 0) {
                    stringBuilder.append(Localization.DOT_SEPARATOR);
                } else {
                    stringBuilder.append("<br/><br/>");
                }

                stringBuilder.append("<a href=\"") .append(metaInfo.getUrls() .get(i)) .append("\">") .append(capitalizeIfAllUppercase(metaInfo.getUrlTexts() .get(i) .trim())).append("</a>");
            }
        }

        metaInfoSeparator.setVisibility(View.VISIBLE);
        TextLinkifier.fromHtml(metaInfoTextView, stringBuilder.toString(), HtmlCompat.FROM_HTML_SEPARATOR_LINE_BREAK_HEADING, null, null, disposables, SET_LINK_MOVEMENT_METHOD);
    }
}

    @Override // own playback listener
    public void onPlaybackSynchronize(@NonNull final PlayQueueItem item ,final
boolean wasBlocked) {
        if (DEBUG) {
            Log.d(TAG, "Playback - onPlaybackSynchronize(was blocked: " + wasBlocked + ") called with item=[" + item.getTitle()+ "], url=[" + item.getUrl()+ "]");
        }

        if (exoPlayerIsNull()||playQueue == null||currentItem == item) {
            return; // nothing to synchronize
        }

        final int playQueueIndex = playQueue.indexOf(item);
        final int playlistIndex = simpleExoPlayer.getCurrentMediaItemIndex();
        final int playlistSize = simpleExoPlayer.getCurrentTimeline() .getWindowCount();
        final boolean removeThumbnailBeforeSync = currentItem == null||currentItem.getServiceId() != item.getServiceId()||!currentItem.getUrl() .equals(item.getUrl());
        currentItem = item;
        if (playQueueIndex != playQueue.getIndex()) {
            // wrong window (this should be impossible, as this method is called with
            // `item=playQueue.getItem()`, so the index of that item must be equal to `getIndex()`)
            Log.e(TAG, "Playback - Play Queue may be not in sync: item index=[" + playQueueIndex + "], " + "queue index=[" + playQueue.getIndex()+ "]");
        } else if ((playlistSize > 0 && playQueueIndex >= playlistSize)||playQueueIndex < 0) {
            // the queue and the player's timeline are not in sync, since the play queue index
            // points outside of the timeline
                   Log.e(TAG, "Playback - Trying to seek to invalid index=[" + playQueueIndex + "] with playlist length=[" + playlistSize + "]");
               } else if (wasBlocked ||playlistIndex != playQueueIndex ||!isPlaying()) {
            // either the player needs to be unblocked, or the play queue index has just been
            // changed and needs to be synchronized, or the player is not playing
                          if (DEBUG) {
                              Log.d(TAG, "Playback - Rewinding to correct index=[" + playQueueIndex + "], " + "from=[" + playlistIndex + "], size=[" + playlistSize + "].");
                          }

                          if (removeThumbnailBeforeSync) {
                // unset the current (now outdated) thumbnail to ensure it is not used during sync
                              onThumbnailLoaded(null);
                          }

            // sync the player index with the queue index, and seek to the correct position

                          if (item.getRecoveryPosition() != PlayQueueItem.RECOVERY_UNSET) {
                              simpleExoPlayer.seekTo(playQueueIndex, item.getRecoveryPosition());
                              playQueue.unsetRecovery(playQueueIndex);
                          } else {
                              simpleExoPlayer.seekToDefaultPosition(playQueueIndex);
                          }
                      }
    }

private void updateTabLayoutPosition() {
    final ScrollableTabLayout tabLayout = binding.mainTabLayout;
    final ViewPager viewPager = binding.pager;
    final boolean bottom = mainTabsPositionBottom;

    // change layout params to make the tab layout appear either at the top or at the bottom
    final var tabParams = (RelativeLayout.LayoutParams)tabLayout.getLayoutParams();
    final var pagerParams = (RelativeLayout.LayoutParams)viewPager.getLayoutParams();
    tabParams.removeRule(bottom ?ALIGN_PARENT_TOP:ALIGN_PARENT_BOTTOM);
    tabParams.addRule(bottom ?ALIGN_PARENT_BOTTOM:ALIGN_PARENT_TOP);
    pagerParams.removeRule(bottom ?BELOW:ABOVE);
    pagerParams.addRule(bottom ?ABOVE:BELOW, R.id.main_tab_layout);
    tabLayout.setSelectedTabIndicatorGravity(bottom ?INDICATOR_GRAVITY_TOP:INDICATOR_GRAVITY_BOTTOM);
    tabLayout.setLayoutParams(tabParams);
    viewPager.setLayoutParams(pagerParams);

    // change the background and icon color of the tab layout:
    // service-colored at the top, app-background-colored at the bottom
    tabLayout.setBackgroundColor(ThemeHelper.resolveColorFromAttr(requireContext(), bottom ?android.R.attr.windowBackground:R.attr.colorPrimary));
    @ColorInt final int iconColor = bottom ?ThemeHelper.resolveColorFromAttr(requireContext(), android.R.attr.colorAccent):Color.WHITE;
    tabLayout.setTabRippleColor(ColorStateList.valueOf(iconColor) .withAlpha(32));
    tabLayout.setTabIconTint(ColorStateList.valueOf(iconColor));
    tabLayout.setSelectedTabIndicatorColor(iconColor);
}

