    private LogEntry readNextEntry() {
        LogEntry e = null;
        try {
            long crcValue;
            byte [] bytes;
            try {
                crcValue = logStream.readLong("crcvalue");
                bytes = logStream.readBuffer("txnEntry");
            }
                catch(EOFException ex) {
                return null;
            }
            if (bytes.length == 0) {
                return null;
            }
            Checksum crc = new Adler32();
            crc.update(bytes, 0, bytes.length);
            if (crcValue != crc.getValue()) {
                throw new IOException("CRC doesn't match " + crcValue + " vs " + crc.getValue());
            }
            TxnLogEntry logEntry = SerializeUtils.deserializeTxn(bytes);
            TxnHeader hdr = logEntry.getHeader();
            Record r = logEntry.getTxn();
            switch(hdr.getType()) {
                case OpCode.createSession: {
                                               e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "createSession");
                                           }
                                           break;
                case OpCode.closeSession: {
                                              e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "closeSession");
                                          }
                                          break;
                case OpCode.create:
                if (r != null) {
                    CreateTxn create = (CreateTxn) r;
                    String path = create.getPath();
                    e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "create", path);
                }
                break;
                case OpCode.setData:
                if (r != null) {
                    SetDataTxn set = (SetDataTxn) r;
                    String path = set.getPath();
                    e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "setData", path);
                }
                break;
                case OpCode.setACL:
                if (r != null) {
                    SetACLTxn setacl = (SetACLTxn) r;
                    String path = setacl.getPath();
                    e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "setACL", path);
                }
                break;
                case OpCode.error:
                if (r != null) {
                    ErrorTxn error = (ErrorTxn) r;
                    e = new TransactionEntry(hdr.getTime(),
                                             hdr.getClientId(),
                                             hdr.getCxid(),
                                             hdr.getZxid(),
                                             "error",
                                             "Error: " + error.getErr());
                }
                break;
                default:
                LOG.info("Unknown op: " + hdr.getType());
                break;
}
            if (logStream.readByte("EOR") != 'B') {
                throw new EOFException("Last transaction was partial.");
            }
        }
            catch(Exception ex) {
            LOG.error("Error reading transaction from (" + src.file + ") :" + e);
            return null;
        }
        return e;
    }

public void run() {
    try {
        while(!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            }
                catch(RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            }
                catch(Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

            // Close connections still pending on the selector. Any others
            // with in-flight work, let drain out of the work queue.
        for(SelectionKey key :selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    }
        finally {
        closeSelector();
            // This will wake up the accept thread and the other selector
            // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public void reconfigureInstance(String name,
                                String params) throws NoAssignmentException, InterruptedException, KeeperException {
    if (LOG.isDebugEnabled()) {
        LOG.debug("Reconfiguring " + name + " with " + params);
    }
    Assigned assigned = instanceToAssignment.get(name);
    if (assigned == null) {
        throw new NoAssignmentException();
    }
    KeeperException lastException = null;
    for (int i = 0; i < maxTries; i++) {
        try {
            zk.setData(assignmentsNode + '/' + assigned.container + '/' + name,
                       ("update " + params).getBytes(),
                       - 1);
            break;
        }
            catch(ConnectionLossException e) {
            lastException = e;
        }
    }
    if (lastException != null) {
        throw lastException;
    }
}

public static void closeSock(SocketChannel sock) {
    if (!sock.isOpen()) {
        return;
    }
    try {
        /*
         * The following sequence of code is stupid! You would think that
         * only sock.close() is needed, but alas, it doesn't work that way.
         * If you just do sock.close() there are cases where the socket
         * doesn't actually close...
         */
        sock.socket().shutdownOutput();
    }
        catch(IOException e) {
        // This is a relatively common exception that we can't avoid
        LOG.debug("ignoring exception during output shutdown", e);
    }
    try {
        sock.socket().shutdownInput();
    }
        catch(IOException e) {
        // This is a relatively common exception that we can't avoid
        LOG.debug("ignoring exception during input shutdown", e);
    }
    try {
        sock.socket().close();
    }
        catch(IOException e) {
        LOG.debug("ignoring exception during socket close", e);
    }
    try {
        sock.close();
    }
        catch(IOException e) {
        LOG.debug("ignoring exception during socketchannel close", e);
    }
}

String handleRequest(JsonRequest request) throws Exception {
    long starttime = 0;
    long endtime = 0;
    long period = 0;
    FilterOp fo = null;
    starttime = request.getNumber("start", 0);
    endtime = request.getNumber("end", 0);
    period = request.getNumber("period", 0);
    String filterstr = request.getString("filter", "");
    if (filterstr.length()> 0) {
        fo = new FilterParser(filterstr).parse();
    }
    if (starttime == 0) {
        starttime = source.getStartTime();
    }
    if (endtime == 0) {
        if (period > 0) {
            endtime = starttime + period;
        } else {
            endtime = starttime + DEFAULT_PERIOD;
        }
    }
    if (LOG.isDebugEnabled()) {
        LOG.debug("handle(start= " + starttime + ", end=" + endtime + ", period=" + period + ")");
    }
    LogIterator iterator = (fo != null)
        ? source.iterator(starttime, endtime, fo) : source.iterator(starttime, endtime);
    return new JsonGenerator(iterator).toString();
}

@Override void cleanup() {
    if (sockKey != null) {
        SocketChannel sock = (SocketChannel) sockKey.channel();
        sockKey.cancel();
        try {
            sock.socket().shutdownInput();
        }
            catch(IOException e) {
            LOG.debug("Ignoring exception during shutdown input", e);
        }
        try {
            sock.socket().shutdownOutput();
        }
            catch(IOException e) {
            LOG.debug("Ignoring exception during shutdown output", e);
        }
        try {
            sock.socket().close();
        }
            catch(IOException e) {
            LOG.debug("Ignoring exception during socket close", e);
        }
        try {
            sock.close();
        }
            catch(IOException e) {
            LOG.debug("Ignoring exception during channel close", e);
        }
    }
    try {
        Thread.sleep(100);
    }
        catch(InterruptedException e) {
        LOG.debug("SendThread interrupted during sleep, ignoring");
    }
    sockKey = null;
}

protected void animateVertically(int posx,
                                 int fromY,
                                 int toY) throws InterruptedException {
    toaster.setLocation(posx, fromY);
    if (toY < fromY) {
        for (int i = fromY; i > toY; i -= step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    } else {
        for (int i = fromY; i < toY; i += step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    }
    toaster.setLocation(posx, toY);
}

@Override protected void subscribeActual(CompletableObserver observer) {
    InnerCompletableCache inner = new InnerCompletableCache(observer);
    observer.onSubscribe(inner);
    if (add(inner)) {
        if (inner.isDisposed()) {
            remove(inner);
        }

        if (once.compareAndSet(
            false, true)) {
            source.subscribe(this);
        }
    } else {
        Throwable ex = error;
        if (ex != null) {
            observer.onError(ex);
        } else {
            observer.onComplete();
        }
    }
}

@Override
public boolean tryOnError(Throwable t) {
    if (t == null) {
        t =
            ExceptionHelper.createNullPointerException("onError called with a null Throwable.");
    }

    if (get() != DisposableHelper.DISPOSED) {
        Disposable d = getAndSet(DisposableHelper.DISPOSED);
        if (d != DisposableHelper.DISPOSED) {
            try {
                downstream.onError(t);
            }

            finally {
                    if (d != null) {
                        d.dispose();
                    }
            }

            return true;
        }
    }

    return false;
}

    @ SuppressWarnings("unchecked")
    public static <T, R> boolean tryScalarXMapSubscribe(ObservableSource<T> source, Observer<? super R> observer, Function<? super T, ? extends ObservableSource <? extends R>> mapper) {
        if (source instanceof Supplier) {
            T t;
            try {
                t =
                    ((Supplier <T>)source)
.get();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                EmptyDisposable.error(ex, observer);
                return true;
            }

            if (t == null) {
                EmptyDisposable.complete(observer);
                return true;
            }

            ObservableSource<? extends R> r;
            try {
                r =
                    Objects.requireNonNull(
                        mapper.apply(t), "The mapper returned a null ObservableSource");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                EmptyDisposable.error(ex, observer);
                return true;
            }

            if (r instanceof Supplier) {
                R u;
                try {
                    u =
                        ((Supplier <R>)r)
.get();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    EmptyDisposable.error(ex, observer);
                    return true;
                }

                if (u == null) {
                    EmptyDisposable.complete(observer);
                    return true;
                }

                ScalarDisposable<R> sd = new ScalarDisposable <>(observer, u);
                observer.onSubscribe(sd);
                sd.run();
            } else {
                r.subscribe(observer);
            }

            return true;
        }

        return false;
    }

void drainLoop() {
