            }

            a.onNext(v);
        }

        missed =
            addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable <T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;
    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window =
                null;
            this.window =
                null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;
            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }

                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }

                    downstream.onComplete();
                }

                cleanupResources();
                upstreamCancelled =
                    true;
                continue;
            } else if (!isEmpty) {
                       if (o instanceof WindowBoundaryRunnable) {
                           WindowBoundaryRunnable boundary = (WindowBoundaryRunnable)o;
                           if (boundary.index == emitted || !restartTimerOnMaxSize) {
                               this.count =
                                   0;
                               window =
                                   createNewWindow(window);
                           }
                       } else if (window != null) {
                                  @ SuppressWarnings("unchecked") T item = (T)o;
                                  window.onNext(item);


                                  long count = this.count + 1;
                                  if (count == maxSize) {
                                      this.count =
                                          0;
                                      window =
                                          createNewWindow(window);
                                  } else {
                                      this.count =
                                          count;
                                  }
                              }

                       continue;
                   }
        }

        missed =
            addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    Subscriber<? super R> a = downstream;
    Iterator<? extends R> iterator = this.it;
    if (outputFused && iterator != null) {
        a.onNext(null);
        a.onComplete();
        return;
    }

    int missed = 1;
    for (;;) {
        if (iterator != null) {
            long r = requested.get();
            if (r == Long.MAX_VALUE) {
                fastPath(a, iterator);
                return;
            }

            long e = 0L;
            while(e != r) {
                if (cancelled) {
                    return;
                }

                R v;
                try {
                    v =
                        Objects.requireNonNull(
                            iterator.next(), "The iterator returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    a.onError(ex);
                    return;
                }

                a.onNext(v);
                if (cancelled) {
                    return;
                }

                e++;


                boolean b;
                try {
                    b =
                        iterator.hasNext();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    a.onError(ex);
                    return;
                }

                if (!b) {
                    a.onComplete();
                    return;
                }
            }

            if (e != 0L) {
                BackpressureHelper.produced(requested, e);
            }
        }

        missed =
            addAndGet(-missed);
        if (missed == 0) {
            break;
        }

        if (iterator == null) {
            iterator =
                it;
        }
    }
}

void drainLoop() {
    Subscriber<? super T> actual = this.downstream;
    int missed = 1;
    long e = emitted;
    int c = consumed;
    int lim = limit;
    for (;;) {
        long r = requested.get();
        while(e != r) {
            if (cancelled) {
                singleItem =
                    null;
                queue =
                    null;
                return;
            }

            if (errors.get() != null) {
                singleItem =
                    null;
                queue =
                    null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem =
                    null;
                otherState =
                    OTHER_STATE_CONSUMED_OR_EMPTY;
                os =
                    OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
                e++;
                continue;
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;
            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue =
                    null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
            e++;
            if (++c == lim) {
                c =
                    0;
                mainSubscription.get().request(lim);
            }
        }

        if (e == r) {
            if (cancelled) {
                singleItem =
                    null;
                queue =
                    null;
                return;
            }

            if (errors.get() != null) {
                singleItem =
                    null;
                queue =
                    null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            boolean empty = q == null || q.isEmpty();
            if (d && empty && otherState == 2) {
                queue =
                    null;
                actual.onComplete();
                return;
            }
        }

        emitted =
            e;
        consumed =
            c;
        missed =
            addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

