                    Exceptions.throwIfFatal(ex);
                    upstream.cancel();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                boolean empty = v == null;
                if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (empty) {
                    break;
                }

                if (!syncFused) {
                    int c = consumed + 1;
                    if (c == limit) {
                        consumed =
                            0;
                        upstream.request(limit);
                    } else {
                        consumed =
                            c;
                    }
                }

                MaybeSource<? extends R> ms;
                try {
                    ms =
                        Objects.requireNonNull(
                            mapper.apply(v), "The mapper returned a null MaybeSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    upstream.cancel();
                    queue.clear();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                state =
                    STATE_ACTIVE;
                ms.subscribe(inner);
                break;
            } else if (s == STATE_RESULT_VALUE) {
                       long e = emitted;
                       if (e != requested.get()) {
                           R w = item;
                           item =
                               null;
                           downstream.onNext(w);
                           emitted =
                               e + 1;
                           state =
                               STATE_INACTIVE;
                       } else {
                           break;
                       }
                   } else {
                       break;
                   }
        }

        missed =
            addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@ SuppressWarnings("unchecked") void remove(@NonNull SingleDisposable<T> inner) {
    for (;;) {
        SingleDisposable<T>[] a = observers.get();
        int n = a.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (a[i] == inner) {
                j =
                    i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        SingleDisposable<T>[] b;
        if (n == 1) {
            b =
                EMPTY;
        } else {
            b =
                new SingleDisposable [n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        if (observers.compareAndSet(
            a, b)) {
            return;
        }
    }
}

@Override protected void subscribeActual(@NonNull Subscriber<? super T> s) {
    AsyncSubscription<T> as = new AsyncSubscription <>(s, this);
    s.onSubscribe(as);
    if (add(as)) {
        if (as.isCancelled()) {
            remove(as);
        }
    } else {
        Throwable ex = error;
        if (ex != null) {
            s.onError(ex);
        } else {
            T v = value;
            if (v != null) {
                as.complete(v);
            } else {
                as.onComplete();
            }
        }
    }
}

    private static EmailContent extractEmailContentAdvanced(byte[] emlBytes, EmlToPdfRequest request) {
