void drain() {
    if (wip.getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    SimpleQueue<T> q = queue;

    int upstreamConsumed = consumed;

    int localLimit = limit;

    boolean canRequest = sourceMode != QueueSubscription.SYNC;

    AtomicReference<MulticastSubscription<T> []> subs = subscribers;

    MulticastSubscription<T>[] array = subs.get();

    outer: for (;;) {
               int n = array.length;
               if (q != null && n != 0) {
                   long r = Long.MAX_VALUE;
                   for(MulticastSubscription<T> ms :array) {
                       long u = ms.get() - ms.emitted;
                       if (u != Long.MIN_VALUE) {
                           if (r > u) {
                               r = u;
                           }
                       } else {
                           n--;
                       }
                   }
                   if (n == 0) {
                       r = 0;
                   }

                   while (r != 0) {
                       if (isDisposed()) {
                           q.clear();
                           return;
                       }

                       boolean d = done;
                       if (d && !delayError) {
                           Throwable ex = error;
                           if (ex != null) {
                               errorAll(ex);
                               return;
                           }
                       }

                       T v;
                       try {
                           v = q.poll();
                       } catch (Throwable ex) {
                                                  Exceptions.throwIfFatal(ex);
                                                  SubscriptionHelper.cancel(upstream);
                                                  errorAll(ex);
                                                  return;
                                              }

                       boolean empty = v == null;
                       if (d && empty) {
                           Throwable ex = error;
                           if (ex != null) {
                               errorAll(ex);
                           } else {
                               completeAll();
                           }

                           return;
                       }
                       if (empty) {
                           break;
                       }

                       boolean subscribersChange = false;
                       for(MulticastSubscription<T> ms :array) {
                           long msr = ms.get();
                           if (msr != Long.MIN_VALUE) {
                               if (msr != Long.MAX_VALUE) {
                                   ms.emitted++;
                               }

                               ms.downstream.onNext(v);
                           } else {
                               subscribersChange = true;
                           }
                       }

                       r--;
                       if (canRequest && ++upstreamConsumed == localLimit) {
                           upstreamConsumed = 0;
                           upstream.get().request(localLimit);
                       }

                       MulticastSubscription<T>[] freshArray = subs.get();
                       if (subscribersChange
                               || freshArray != array) {
                           array = freshArray;
                           continue outer;
                       }
                   }
                   if (r == 0) {
                       if (isDisposed()) {
                           q.clear();
                           return;
                       }

                       boolean d = done;
                       if (d && !delayError) {
                           Throwable ex = error;
                           if (ex != null) {
                               errorAll(ex);
                               return;
                           }
                       }
                       if (d && q.isEmpty()) {
                           Throwable ex = error;
                           if (ex != null) {
                               errorAll(ex);
                           } else {
                               completeAll();
                           }

                           return;
                       }
                   }
               }

               consumed = upstreamConsumed;
               missed = wip.addAndGet(-missed);
               if (missed == 0) {
                   break;
               }
               if (q == null) {
                   q = queue;
               }

               array = subs.get();
           }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> a = downstream;
    final ZipSubscriber<T, R>[] qs = subscribers;
    final int n = qs.length;

    Object[] values = current;

    int missed = 1;
    for (;;) {
        long r = requested.get();

        long e = 0L;
        while (r != e) {
            if (cancelled) {
                return;
            }
            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            boolean empty = false;
            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs [j];
                if (values [j] == null) {
                    boolean d = inner.done;

                    SimpleQueue<T> q = inner.queue;

                    T v = null;
                    try {
                        v = q != null?q.poll(): null;
                    } catch (Throwable ex) {
                                               Exceptions.throwIfFatal(ex);
                                               errors.tryAddThrowableOrReport(ex);
                                               if (!delayErrors) {
                                                   cancelAll();
                                                   errors.tryTerminateConsumer(a);
                                                   return;
                                               }

                                               d = true;
                                           }

                    boolean sourceEmpty = v == null;
                    if (d && sourceEmpty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!sourceEmpty) {
                        values[j] = v;
                    } else {
                        empty = true;
                    }
                }
            }
            if (empty) {
                break;
            }

            R v;
            try {
                v = Objects.requireNonNull(zipper.apply(values.clone()), "The zipper returned a null value");
            } catch (Throwable ex) {
                                       Exceptions.throwIfFatal(ex);
                                       cancelAll();
                                       errors.tryAddThrowableOrReport(ex);
                                       errors.tryTerminateConsumer(a);
                                       return;
                                   }

            a.onNext(v);
            e++;
            Arrays.fill(values, null);
        }
        if (r == e) {
            if (cancelled) {
                return;
            }
            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs [j];
                if (values [j] == null) {
                    boolean d = inner.done;

                    SimpleQueue<T> q = inner.queue;

                    T v = null;
                    try {
                        v = q != null?q.poll(): null;
                    } catch (Throwable ex) {
                                               Exceptions.throwIfFatal(ex);
                                               errors.tryAddThrowableOrReport(ex);
                                               if (!delayErrors) {
                                                   cancelAll();
                                                   errors.tryTerminateConsumer(a);
                                                   return;
                                               }

                                               d = true;
                                           }

                    boolean empty = v == null;
                    if (d && empty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!empty) {
                        values[j] = v;
                    }
                }
            }
        }
        if (e != 0L) {
            for(ZipSubscriber<T, R> inner :qs) {
                inner.request(e);
            }
            if (r != Long.MAX_VALUE) {
                requested.addAndGet(-e);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void innerSuccess(InnerObserver inner, R value) {
    set.delete(inner);
    if (get() == 0 && compareAndSet(0, 1)) {
        downstream.onNext(value);

        boolean d = active.decrementAndGet() == 0;

        SpscLinkedArrayQueue<R> q = queue.get();
        if (d && (q == null
                      || q.isEmpty())) {
            errors.tryTerminateConsumer(downstream);
            return;
        }
        if (decrementAndGet() == 0) {
            return;
        }
    } else {
        SpscLinkedArrayQueue<R> q = getOrCreateQueue();
        synchronized(q) {
                        q.offer(value);
        }

        active.decrementAndGet();
        if (getAndIncrement() != 0) {
            return;
        }
    }

    drainLoop();
}

@Override
public void onSubscribe(Subscription s) {
    boolean cancel;
    if (!done) {
        synchronized(this) {
                               if (done) {
                                   cancel = true;
                               } else {
                                   if (emitting) {
                                       AppendOnlyLinkedArrayList<Object> q = queue;
                                       if (q == null) {
                                           q = new AppendOnlyLinkedArrayList<>(4);
                                           queue = q;
                                       }

                                       q.add(NotificationLite.subscription(s));
                                       return;
                                   }

                                   emitting = true;
                                   cancel = false;
                               }
                           }
    } else {
        cancel = true;
    }
    if (cancel) {
        s.cancel();
    } else {
        actual.onSubscribe(s);
        emitLoop();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false, success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();
        triedToClose = true;
        super.close();
        success = true;
    }
        finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete()|| !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

void handleWrite(SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    /*
     * This is going to reset the buffer position to 0 and the
     * limit to the size of the buffer, so that we can fill it
     * with data from the non-direct buffers that we need to
     * send.
     */

    ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        // Use gathered write call. This updates the positions of the
        // byte buffers to reflect the bytes that were written out.
        sock.write(outgoingBuffers.toArray(bufferList));

        // Remove the buffers that we have sent
        ByteBuffer bb;
        while((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }

            outgoingBuffers.remove();
        }
    } else {
        directBuffer.clear();
        for (ByteBuffer b : outgoingBuffers) {
            if (directBuffer.remaining() < b.remaining()) {
                /*
                 * When we call put later, if the directBuffer is to
                 * small to hold everything, nothing will be copied,
                 * so we've got to slice the buffer if it's too big.
                 */
                b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
            }
            /*
             * put() is going to modify the positions of both
             * buffers, put we don't want to change the position of
             * the source buffers (we'll do that after the send, if
             * needed), so we save and reset the position after the
             * copy
             */

            int p = b.position();
            directBuffer.put(b);
            b.position(p);
            if (directBuffer.remaining() == 0) {
                break;
            }
        }
        /*
         * Do the flip: limit becomes position, position gets set to
         * 0. This sets us up for the write.
         */

        directBuffer.flip();

        int sent = sock.write(directBuffer);
        ByteBuffer bb;

        // Remove the buffers that we have sent
        while((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (sent < bb.remaining()) {
                /*
                 * We only partially sent this buffer, so we update
                 * the position and exit the loop.
                 */
                bb.position(bb.position() + sent);
                break;
            }
            /* We've sent the whole buffer, so drop the buffer */

            sent -= bb.remaining();
            outgoingBuffers.remove();
        }
    }
}

