   downstream.onSuccess(true);
   return;
                                 }

                                 if ((d1&&
                                         d2)&& (e1 != e2)) {
   cancelAndClear();
   downstream.onSuccess(false);
   return;
                                 }

                                 if (e1 || e2) {
            break;
                                 }

                                 boolean c;
                                 try {
   c = comparer.test(a, b);
