void _processDo(int option) throws IOException {
if (debugoptions) {
    System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
}

if (__notifhand != null) {
    __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
}

boolean acceptNewState = false;


    /* open TelnetOptionHandler functionality (start)*/
if (optionHandlers[option] != null) {
    acceptNewState = optionHandlers[option].getAcceptLocal();
} else {
    /* open TelnetOptionHandler functionality (end)*/
        /* TERMINAL-TYPE option (start)*/
  if (option == TERMINAL_TYPE) {
    if ((terminalType != null) && (terminalType.length() > 0)) {
                                   acceptNewState = true;
    }
  }
        /* TERMINAL-TYPE option (end)*/
    /* open TelnetOptionHandler functionality (start)*/
}
    /* open TelnetOptionHandler functionality (end)*/

if (_willResponse[option] > 0) {
    -- _willResponse[option];

    if (_willResponse[option] > 0 && _stateIsWill(option)) {
    -- _willResponse[option];
    }
}

if (_willResponse[option] == 0) {
    if (_requestedWont(option)) {
    switch(option) {
    default:
        break;
    }

    if (acceptNewState) {
        _setWantWill(option);
        _sendWill(option);
    } else {
      ++_willResponse[option];
      _sendWont(option);
    }
    } else {
            // Other end has acknowledged option.
      switch(option) {
      default:
        break;
      }
    }
}
_setWill(option);
}

public static Map<String, Map<String, Object>> getLoggers(String name, boolean includeNoAppender) {
Map<String, Map<String, Object>> loggerInfoMap = new HashMap<String, Map<String, Object>>();
if (!Log4j) {
    return loggerInfoMap;
}

if (name != null && !name.trim().isEmpty()) {
    Logger logger = LogManager.getLoggerRepository().exists(name);
    if (logger != null) {
                                                loggerInfoMap.put(name, doGetLoggerInfo(logger));
    }
} else {
        // loggerappender
  @SuppressWarnings("unchecked") Enumeration<Logger> loggers = LogManager.getLoggerRepository().getCurrentLoggers();
  if (loggers != null) {
    while (loggers.hasMoreElements()) {
      Logger logger = loggers.nextElement();

      Map<String, Object> info = doGetLoggerInfo(logger);
      if (!includeNoAppender) {
        List<?> appenders = (List<?>) info.get(LoggerHelper.appenders);
        if (appenders != null && !appenders.isEmpty()) {
                                  loggerInfoMap.put(logger.getName(), info);
        }
      } else {
        loggerInfoMap.put(logger.getName(), info);
      }
    }
  }

  Logger root = LogManager.getLoggerRepository().getRootLogger();
  if (root != null) {
           Map<String, Object> info = doGetLoggerInfo(root);
           if (!includeNoAppender) {
      List<?> appenders = (List<?>) info.get(LoggerHelper.appenders);
      if (appenders != null && !appenders.isEmpty()) {
                                       loggerInfoMap.put(root.getName(), info);
      }
           } else {
             loggerInfoMap.put(root.getName(), info);
           }
  }
}
return loggerInfoMap;
}

