    startServers();
    LOG.debug("Connecting to " + getHostPort());
    ZooKeeper zk = new ZooKeeper(getHostPort(), 15000, this);
    waitForConnect(zk, 10000);
    zk.create("/simpleCase", "orig".getBytes(), Ids.OPEN_ACL_UNSAFE, CreateMode.PERSISTENT);
    startClients();

    // Check that all clients connect properly
    for (int i = 0; i < getClientCount(); i++) {
        for (int j = 0; j < maxTries; j++) {
            try {
                byte b [] = zk.getData("/simpleCase/" + i, false, stat);
                Assert.assertEquals("orig", new String(b));
            } catch (NoNodeException e) {
                if (j + 1 == maxTries) {
                    Assert.fail("Max tries exceeded on client " + i);
                }

                Thread.sleep(1000);
            }
        }
    }

    // Kill half the servers, make a change, restart the dead
    // servers, and then bounce the other servers one by one

    for (int i = 0; i < getServerCount(); i++) {
        stopServer(i);
        if (i + 1 > getServerCount()/ 2) {
            startServer(i);
        } else if (i + 1 == getServerCount()/ 2) {
                   Assert.assertTrue("Connection didn't recover", waitForConnect(zk, 10000));
                   try {
                       zk.setData("/simpleCase", "new".getBytes(), -1);
                   } catch (ConnectionLossException e) {
                       Assert.assertTrue("Connection didn't recover", waitForConnect(zk, 10000));
                       zk.setData("/simpleCase", "new".getBytes(), -1);
                   }

                   for (int j = 0; j < i; j++) {
                       LOG.info("Starting server " + j);
                       startServer(i);
                   }
               }
    }

    Thread.sleep(100); // wait for things to stabilize
    Assert.assertTrue("Servers didn't bounce", waitForConnect(zk, 15000));
    try {
        zk.getData("/simpleCase", false, stat);
    } catch (ConnectionLossException e) {
        Assert.assertTrue("Servers didn't bounce", waitForConnect(zk, 15000));
    }

    // check that the change has propagated to everyone

    for (int i = 0; i < getClientCount(); i++) {
        for (int j = 0; j < maxTries; j++) {
            byte [] data = zk.getData("/simpleCase/" + i, false, stat);
            if (new String(data).equals("new")) {
                break;
            }

            if (j + 1 == maxTries) {
                Assert.fail("max tries exceeded for " + i);
            }

            Thread.sleep(1000);
        }
    }

    // send out the kill signal

    zk.setData("/simpleCase", "die".getBytes(), -1);

    // watch for everyone to die
    for (int i = 0; i < getClientCount(); i++) {
        try {
            for (int j = 0; j < maxTries; j++) {
                zk.getData("/simpleCase/" + i, false, stat);
                if (j + 1 == maxTries) {
                    Assert.fail("max tries exceeded waiting for child " + i + " to die");
                }

                Thread.sleep(200);
            }
        } catch (NoNodeException e) {
            // Great this is what we were hoping for!
        }
    }

    stopClients();
    stopServers();
}

public void start() throws IOException {
    System.out.println("Starting grizzly ...");

    boolean useSSL = cfg.useSSL();
    String zkRestResourcesTempPath = Files.createTempDirectory("zkRestResourcesTempPath").toFile().getCanonicalPath();
    gws = new GrizzlyWebServer(cfg.getPort(), zkRestResourcesTempPath, useSSL);
   // BUG: Grizzly needs a doc root if you are going to register multiple adapters
    for(Endpoint e :cfg.getEndpoints()) {
        ZooKeeperService.mapContext(e.getContext(), e);
        gws.addGrizzlyAdapter(createJerseyAdapter(e), new String [] {
                                                          e.getContext()});
    }

    if (useSSL) {
        System.out.println("Starting SSL ...");
        String jks = cfg.getJKS("keys/rest.jks");
        String jksPassword = cfg.getJKSPassword();
        SSLConfig sslConfig = new SSLConfig();
        URL resource = getClass().getClassLoader().getResource(jks);
        if (resource == null) {
            LOG.error("Unable to find the keystore file: " + jks);
            System.exit(2);
        }

        try {
            sslConfig.setKeyStoreFile(new File(resource.toURI()).getAbsolutePath());
        } catch (URISyntaxException e1) {
            LOG.error("Unable to load keystore: " + jks, e1);
            System.exit(2);
        }

        sslConfig.setKeyStorePass(jksPassword);
        gws.setSSLConfig(sslConfig);
    }

    gws.start();
}

synchronized private LogEntry readNextEntry() {
    try {
        try {
            while(true) {
                String line = in.readLine();
                if (line == null) {
                    break;
                }

                Matcher m = src.timep.matcher(line);
                if (m.lookingAt()) {
                    if (buf.length()> 0) {
                        LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
                        buf = line;
                        return e;
                    }

                    buf = line;
                } else if (buf.length()> 0) {
                           buf += line + "\n";
                       }
            }
        } catch (EOFException eof) {
        // ignore, we've simply come to the end of the file
        }

        if (buf.length()> 0) {
            LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
            buf = "";
            return e;
        }
    } catch (Exception e) {
        LOG.error("Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }

    return null;
}

public void reconfigureInstance(String name, String params) throws NoAssignmentException, InterruptedException, KeeperException {
    if (LOG.isDebugEnabled()) {
        LOG.debug("Reconfiguring " + name + " with " + params);
    }

    Assigned assigned = instanceToAssignment.get(name);
    if (assigned == null) {
        throw new NoAssignmentException();
    }

    KeeperException lastException = null;
    for (int i = 0; i < maxTries; i++) {
        try {
            zk.setData(assignmentsNode + '/' + assigned.container + '/' + name, ("update " + params).getBytes(), -1);
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        }
    }

    if (lastException != null) {
        throw lastException;
    }
}

private LogEntry readNextEntry() {
    LogEntry e = null;

    try {
        long crcValue;
        byte [] bytes;

        try {
            crcValue = logStream.readLong("crcvalue");
            bytes = logStream.readBuffer("txnEntry");
        } catch (EOFException ex) {
            return null;
        }

        if (bytes.length == 0) {
            return null;
        }

        Checksum crc = new Adler32();
        crc.update(bytes, 0, bytes.length);
        if (crcValue != crc.getValue()) {
            throw new IOException("CRC doesn't match " + crcValue + " vs " + crc.getValue());
        }

        TxnLogEntry logEntry = SerializeUtils.deserializeTxn(bytes);
        TxnHeader hdr = logEntry.getHeader();
        Record r = logEntry.getTxn();

        switch(hdr.getType()) {
                              case OpCode.createSession: {
                                                             e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "createSession");
                                                         }

                                                         break;
                              case OpCode.closeSession: {
                                                            e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "closeSession");
                                                        }

                                                        break;
                              case OpCode.create:
            if (r != null) {
                CreateTxn create = (CreateTxn) r;
                String path = create.getPath();
                e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "create", path);
            }

            break;
                              case OpCode.setData:
            if (r != null) {
                SetDataTxn set = (SetDataTxn) r;
                String path = set.getPath();
                e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "setData", path);
            }

            break;
                              case OpCode.setACL:
            if (r != null) {
                SetACLTxn setacl = (SetACLTxn) r;
                String path = setacl.getPath();
                e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "setACL", path);
            }

            break;
                              case OpCode.error:
            if (r != null) {
                ErrorTxn error = (ErrorTxn) r;
                e = new TransactionEntry(hdr.getTime(), hdr.getClientId(), hdr.getCxid(), hdr.getZxid(), "error", "Error: " + error.getErr());
            }

            break;
                              default:
            LOG.info("Unknown op: " + hdr.getType());
            break;
        }

        if (logStream.readByte("EOR") != 'B') {
            throw new EOFException("Last transaction was partial.");
        }
    } catch (Exception ex) {
        LOG.error("Error reading transaction from (" + src.file + ") :" + e);
        return null;
    }

    return e;
}

public synchronized void dumpConnectionInfo(PrintWriter pwriter, boolean brief) {
    pwriter.print(" ");
    pwriter.print(getRemoteSocketAddress());
    pwriter.print("[");
    int interestOps = getInterestOps();
    pwriter.print(interestOps == 0? "0": Integer.toHexString(interestOps));
    pwriter.print("](queued=");
    pwriter.print(getOutstandingRequests());
    pwriter.print(",recved=");
    pwriter.print(getPacketsReceived());
    pwriter.print(",sent=");
    pwriter.print(getPacketsSent());
    if (!brief) {
        long sessionId = getSessionId();
        if (sessionId != 0) {
            pwriter.print(",sid=0x");
            pwriter.print(Long.toHexString(sessionId));
            pwriter.print(",lop=");
            pwriter.print(getLastOperation());
            pwriter.print(",est=");
            pwriter.print(getEstablished().getTime());
            pwriter.print(",to=");
            pwriter.print(getSessionTimeout());

            long lastCxid = getLastCxid();
            if (lastCxid >= 0) {
                pwriter.print(",lcxid=0x");
                pwriter.print(Long.toHexString(lastCxid));
            }

            pwriter.print(",lzxid=0x");
            pwriter.print(Long.toHexString(getLastZxid()));
            pwriter.print(",lresp=");
            pwriter.print(getLastResponseTime());
            pwriter.print(",llat=");
            pwriter.print(getLastLatency());
            pwriter.print(",minlat=");
            pwriter.print(getMinLatency());
            pwriter.print(",avglat=");
            pwriter.print(getAvgLatency());
            pwriter.print(",maxlat=");
            pwriter.print(getMaxLatency());
        }
    }

    pwriter.print(")");
}

@Override protected void subscribeActual(MaybeObserver<? super T> observer) {
    MaybeDisposable<T> md = new MaybeDisposable<>(observer, this);
    observer.onSubscribe(md);
    if (add(md)) {
        if (md.isDisposed()) {
            remove(md);
        }
    } else {
        Throwable ex = error;
        if (ex != null) {
            observer.onError(ex);
        } else {
            T v = value;
            if (v == null) {
                observer.onComplete();
            } else {
                observer.onSuccess(v);
            }
        }
    }
}

@Override protected void subscribeActual(CompletableObserver observer) {
    Disposable d = Disposable.empty();
    observer.onSubscribe(d);
    if (!d.isDisposed()) {
        try {
            run.run();
        }
            catch(Throwable e) {
            Exceptions.throwIfFatal(e);
            if (!d.isDisposed()) {
                observer.onError(e);
            } else {
                RxJavaPlugins.onError(e);
            }
            return;
        }
        if (!d.isDisposed()) {
            observer.onComplete();
        }
    }
}

@Override
public void onNext(T value) {
    if (upstream == DisposableHelper.DISPOSED) {
        return;
    }
    Iterator<? extends R> it;
    try {
        it = mapper.apply(value)
            .iterator();
    }
        catch(Throwable ex) {
        Exceptions.throwIfFatal(ex);
        upstream.dispose();
        onError(ex);
        return;
    }
    Observer<? super R> a = downstream;
    for (;;) {
        boolean b;
        try {
            b = it.hasNext();
        }
            catch(Throwable ex) {
            Exceptions.throwIfFatal(ex);
            upstream.dispose();
            onError(ex);
            return;
        }
        if (b) {
            R v;
            try {
                v = Objects.requireNonNull(it.next(), "The iterator returned a null value");
            }
                catch(Throwable ex) {
                Exceptions.throwIfFatal(ex);
                upstream.dispose();
                onError(ex);
                return;
            }
            a.onNext(v);
        } else {
            break;
        }
    }
}

@Override
public boolean tryOnNext(T t) {
    if (!done) {
        long retries = 0L;
        for (;;) {
            boolean b;
            try {
                b = predicate.test(t);
            }
                catch(Throwable ex) {
                Exceptions.throwIfFatal(ex);
                ParallelFailureHandling h;
                try {
                    h = Objects.requireNonNull(
                        errorHandler.apply(++ retries, ex),
                        "The errorHandler returned a null ParallelFailureHandling");
                }
                    catch(Throwable exc) {
                    Exceptions.throwIfFatal(exc);
                    cancel();
                    onError(new CompositeException(ex, exc));
                    return false;
                }
                switch (h) {
                    case RETRY:
                        continue;
                    case SKIP:
                        return false;
                    case STOP:
                        cancel();
                        onComplete();
                        return false;
                    default:
                        cancel();
                        onError(ex);
                        return false;
                }
            }
            return b && downstream.tryOnNext(t);
        }
    }
    return false;
}

    @Override
@NonNull
    public synchronized Throwable getCause() { // NOPMD
        if (cause == null) {
            String separator = System.getProperty("line.separator");
            if (exceptions.size() > 1) {
                Map<Throwable, Boolean> seenCauses = new IdentityHashMap<>();
                StringBuilder aggregateMessage = new StringBuilder();
                aggregateMessage.append("Multiple exceptions (")
                    .append(exceptions.size())
                        .append(")").append(separator);
                for(Throwable inner :exceptions) {
                    int depth = 0;
                    while(inner != null) {
                        for (int i = 0; i < depth; i++) {
                            aggregateMessage.append("  ");
                        }
                        aggregateMessage.append("|-- ");
                        aggregateMessage.append(inner.getClass()
                            .getCanonicalName()).append(": ");
                        String innerMessage = inner.getMessage();
                        if (innerMessage != null && innerMessage.contains(separator)) {
                            aggregateMessage.append(separator);
                            for(String line :innerMessage.split(separator)) {
                                for (int i = 0; i < depth + 2; i++) {
                                    aggregateMessage.append("  ");
                                }
                                aggregateMessage.append(line).append(separator);
                            }
                        } else {
                            aggregateMessage.append(innerMessage);
                            aggregateMessage.append(separator);
                        }
                        for (int i = 0; i < depth + 2; i++) {
                            aggregateMessage.append("  ");
                        }
                        StackTraceElement [] st = inner.getStackTrace();
                        if (st.length > 0) {
                            aggregateMessage.append("at ")
                                .append(st[0]).append(separator);
                        }
                        if (!seenCauses.containsKey(inner)) {
                            seenCauses.put(inner, true);
                            inner = inner.getCause();
                            depth++;
                        } else {
                            inner = inner.getCause();
                            if (inner != null) {
                                for (int i = 0; i < depth + 2; i++) {
                                    aggregateMessage.append("  ");
                                }
                                aggregateMessage.append("|-- ");
                                aggregateMessage.append("(cause not expanded again) ");
                                aggregateMessage.append(inner.getClass()
                                    .getCanonicalName()).append(": ");
                                aggregateMessage.append(inner.getMessage());
                                aggregateMessage.append(separator);
                            }
                            break;
                        }
                    }
                }
                cause = new ExceptionOverview(aggregateMessage.toString()
                    .trim());
            } else {
                cause = exceptions.get(0);
            }
        }
        return cause;
    }

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Deque<T> dq = deque;
    Subscriber<? super T> a = downstream;
    for (;;) {
        long r = requested.get();
        long e = 0L;
        while(e != r) {
            if (cancelled) {
                clear(dq);
                return;
            }

            boolean d = done;
            T v;
            synchronized(dq) {
                v = dq.poll();
            }

            boolean empty = v == null;
            if (d) {
                Throwable ex = error;
                if (ex != null) {
                    clear(dq);
                    a.onError(ex);
                    return;
                }
                if (empty) {
                    a.onComplete();
                    return;
                }
            }
            if (empty) {
                break;
            }
            a.onNext(v);
            e++;
        }
        if (e == r) {
            if (cancelled) {
                clear(dq);
                return;
            }

            boolean d = done;
            boolean empty;
            synchronized(dq) {
                empty = dq.isEmpty();
            }
            if (d) {
                Throwable ex = error;
                if (ex != null) {
                    clear(dq);
                    a.onError(ex);
                    return;
                }
                if (empty) {
                    a.onComplete();
                    return;
                }
            }
        }
        if (e != 0L) {
            BackpressureHelper.produced(requested, e);
        }
        missed = addAndGet(- missed);
        if (missed == 0) {
            break;
        }
    }
}

