            break;
        }

        int u = s | HAS_SUBSCRIBER;

        if (once.compareAndSet(s, u)) {
            observer.onSubscribe(this);
            actual.lazySet(observer);

            if (cancelled.get()) {
                actual.lazySet(null);
            } else {
                drain();
            }

            return;
        }
    }

    EmptyDisposable.error(new IllegalStateException("Only one Observer allowed!"), observer);
}

@Override
public void onSuccess(T value) {
    Observer < ? super R > a = downstream;
    Iterator < ? extends R > iterator;
    boolean has;

    try {
        iterator = mapper.apply(value).iterator();
        has = iterator.hasNext();
    } catch (Throwable ex) {
        Exceptions.throwIfFatal(ex);
        downstream.onError(ex);
        return;
    }

    if (!has) {
        a.onComplete();
        return;
    }

    if (outputFused) {
        it = iterator;
        a.onNext(null);
        a.onComplete();
    } else {
        for (;;) {
            if (cancelled) {
                return;
            }

            R v;

            try {
                v = iterator.next();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                a.onError(ex);
                return;
            }

            a.onNext(v);

            if (cancelled) {
                return;
            }

            boolean b;

            try {
                b = iterator.hasNext();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                a.onError(ex);
                return;
            }

            if (!b) {
                a.onComplete();
                return;
            }
        }
    }
}

@Override void fastPath() {
    Iterator < ? extends T > it = this.iterator;
    ConditionalSubscriber < ? super T > a = downstream;

    for (;;) {
        if (cancelled) {
            return;
        }

        T t;

        try {
            t = it.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (cancelled) {
            return;
        }

        if (t == null) {
            a.onError(new NullPointerException("Iterator.next() returned a null value"));
            return;
        } else {
            a.tryOnNext(t);
        }

        if (cancelled) {
            return;
        }

        boolean b;

        try {
            b = it.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            if (!cancelled) {
                a.onComplete();
            }

            return;
        }
    }
}

    static < T > boolean postCompleteDrain(long n, Subscriber < ? super T > actual, Queue < T > queue, AtomicLong state, BooleanSupplier isCancelled) {

// TODO enable fast-path
//        if (n == -1 || n == Long.MAX_VALUE) {
//            for (;;) {
//                if (isCancelled.getAsBoolean()) {
//                    break;
//                }
//
//                T v = queue.poll();
//
//                if (v == null) {
//                    actual.onComplete();
//                    break;
//                }
//
//                actual.onNext(v);
//            }
//
//            return true;
//        }
        long e = n & COMPLETED_MASK;

        for (;;) {
            while (e != n) {
                if (isCancelled(isCancelled)) {
                    return true;
                }

                T t = queue.poll();

                if (t == null) {
                    actual.onComplete();
                    return true;
                }

                actual.onNext(t);
                e++;
            }

            if (isCancelled(isCancelled)) {
                return true;
            }

            if (queue.isEmpty()) {
                actual.onComplete();
                return true;
            }

            n = state.get();

            if (n == e) {
                n = state.addAndGet(-(e & REQUESTED_MASK));

                if ((n & REQUESTED_MASK) == 0L) {
                    return false;
                }

                e = n & COMPLETED_MASK;
            }
        }
    }

void drainLoop() {
    int missed = 1;
    Subscriber < ? super R > a = downstream;
    AtomicInteger n = active;
    AtomicReference < SpscLinkedArrayQueue < R >> qr = queue;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        while (e != r) {
            if (cancelled) {
                clear();
                return;
            }

            if (!delayErrors) {
                Throwable ex = errors.get();

                if (ex != null) {
                    clear();
                    errors.tryTerminateConsumer(a);
                    return;
                }
            }

            boolean d = n.get() == 0;
            SpscLinkedArrayQueue < R > q = qr.get();
            R v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }

            if (empty) {
                break;
            }

            a.onNext(v);
            e++;
        }

        if (e == r) {
            if (cancelled) {
                clear();
                return;
            }

            if (!delayErrors) {
                Throwable ex = errors.get();

                if (ex != null) {
                    clear();
                    errors.tryTerminateConsumer(a);
                    return;
                }
            }

            boolean d = n.get() == 0;
            SpscLinkedArrayQueue < R > q = qr.get();
            boolean empty = q == null || q.isEmpty();

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }
        }

        if (e != 0L) {
            BackpressureHelper.produced(requested, e);

            if (maxConcurrency != Integer.MAX_VALUE) {
                upstream.request(e);
            }
        }

        missed = addAndGet(-missed);

        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Subscriber < ? super T > a = downstream;
    List < T > [] lists = this.lists;
    int[] indexes = this.indexes;
    int n = indexes.length;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        while (e != r) {
            if (cancelled) {
                Arrays.fill(lists, null);
                return;
            }

            Throwable ex = error.get();

            if (ex != null) {
                cancelAll();
                Arrays.fill(lists, null);
                a.onError(ex);
                return;
            }

            T min = null;
            int minIndex = -1;

            for (int i = 0; i < n; i++) {
                List < T > list = lists[i];
                int index = indexes[i];

                if (list.size() != index) {
                    if (min == null) {
                        min = list.get(index);
                        minIndex = i;
                    } else {
                        T b = list.get(index);
                        boolean smaller;

                        try {
                            smaller = comparator.compare(min, b) > 0;
                        } catch (Throwable exc) {
                            Exceptions.throwIfFatal(exc);
                            cancelAll();
                            Arrays.fill(lists, null);

                            if (!error.compareAndSet(null, exc)) {
                                RxJavaPlugins.onError(exc);
                            }

                            a.onError(error.get());
                            return;
                        }

                        if (smaller) {
                            min = b;
                            minIndex = i;
                        }
                    }
                }
            }

            if (min == null) {
                Arrays.fill(lists, null);
                a.onComplete();
                return;
            }

            a.onNext(min);
            indexes[minIndex]++;
            e++;
        }

        if (cancelled) {
            Arrays.fill(lists, null);
            return;
        }

        Throwable ex = error.get();

        if (ex != null) {
            cancelAll();
            Arrays.fill(lists, null);
            a.onError(ex);
            return;
        }

        boolean empty = true;

        for (int i = 0; i < n; i++) {
            if (indexes[i] != lists[i].size()) {
                empty = false;
                break;
            }
        }

        if (empty) {
            Arrays.fill(lists, null);
            a.onComplete();
            return;
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);

        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    Subscriber < ? super T > actual = this.downstream;
    int missed = 1;
    long e = emitted;
    int c = consumed;
    int lim = limit;

    for (;;) {
        long r = requested.get();

        while (e != r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            int os = otherState;

            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
                e++;
                continue;
            }

            boolean d = mainDone;
            SimplePlainQueue < T > q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
            e++;

            if (++c == lim) {
                c = 0;
                mainSubscription.get().request(lim);
            }
        }

        if (e == r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            boolean d = mainDone;
            SimplePlainQueue < T > q = queue;
            boolean empty = q == null || q.isEmpty();

            if (d && empty && otherState == 2) {
                queue = null;
                actual.onComplete();
                return;
            }
        }

        emitted = e;
        consumed = c;
        missed = addAndGet(-missed);

        if (missed == 0) {
            break;
        }
    }
}

@ SuppressWarnings("unchecked" ) @NonNull
public final U assertValueAt(int index, @NonNull Predicate < T > valuePredicate) {
    int s = values.size();

    if (s == 0) {
        throw fail("No values");
    }

    if (index < 0 || index >= s) {
        throw fail("Index " + index + " is out of range [0, " + s + ")");
    }

    boolean found = false;
    T v = values.get(index);

    try {
        if (valuePredicate.test(v)) {
            found = true;
        }
    } catch (Throwable ex) {
        throw ExceptionHelper.wrapOrThrow(ex);
    }

    if (!found) {
        throw fail("Value " + valueAndClass(v) + " at position " + index + " did not pass the predicate");
    }

    return (U) this;
}

@Override
public void onSubscribe(@NonNull Subscription s) {
    if (SubscriptionHelper.validate(this.upstream, s)) {
        this.upstream = s;
        try {
            downstream.onSubscribe(this);
        } catch (Throwable e) {
            Exceptions.throwIfFatal(e);
            done = true;
            // can't call onError because the actual's state may be corrupt at this point
            try {
                s.cancel();
            } catch (Throwable e1) {
                Exceptions.throwIfFatal(e1);
                RxJavaPlugins.onError(new CompositeException(e, e1));
                return;
            }

            RxJavaPlugins.onError(e);
        }
    }
}
