private void finishing(Advice advice) {
    try {
        double cost = threadLocalWatch.costInMillis();
        boolean conditionResult = isConditionMet(command.getConditionExpress(), advice, cost);

        if (this.isVerbose()) {
            process.write("Condition express: {} , result: {}\n", 
                           command.getConditionExpress(), conditionResult);
        }

        if (conditionResult) {
            StackModel stackModel = ThreadUtil.getThreadStackModel(advice.getLoader(), Thread.currentThread());
            stackModel.setTs(LocalDateTime.now());
            process.appendResult(stackModel);
            process.times().incrementAndGet();

            if (isLimitExceeded(command.getNumberOfLimit(), process.times().get())) {
                abortProcess(process, command.getNumberOfLimit());
            }
        }
    } catch (Throwable e) {
        logger.warn("Stack processing failed. Condition: {}, Error: {}", 
                    command.getConditionExpress(), e.getMessage(), e);
        process.end(-1, "Stack processing failed. Condition: " + command.getConditionExpress() + 
                   ", " + e.getMessage() + ". See " + LogUtil.loggingFile() + " for details.");
    }
}

private Process createProcess(Session session, List<CliToken> line, InternalCommandManager commandManager, 
                             int jobId, Term term, ResultDistributor resultDistributor) {
    try {
        ListIterator<CliToken> tokens = line.listIterator();
        while (tokens.hasNext()) {
            CliToken token = tokens.next();
            if (token.isText()) {
                checkPermission(session, token);
                Command command = commandManager.getCommand(token.value());

                if (command == null) {
                    throw new IllegalArgumentException("Command not found: " + token.value());
                }
                return createCommandProcess(command, tokens, jobId, term, resultDistributor);
            }
        }
        throw new IllegalArgumentException("No valid command tokens found");
    } catch (Exception e) {
        throw new RuntimeException("Process creation failed", e);
    }
}

private void initCommands(List<String> disabledCommands) {
    List<Class<? extends AnnotatedCommand>> commandClassList = new ArrayList<>(50);
    Collections.addAll(commandClassList,
        HelpCommand.class, AuthCommand.class, KeymapCommand.class, SearchClassCommand.class,
        SearchMethodCommand.class, ClassLoaderCommand.class, JadCommand.class, GetStaticCommand.class,
        MonitorCommand.class, StackCommand.class, ThreadCommand.class, TraceCommand.class,
        WatchCommand.class, TimeTunnelCommand.class, JvmCommand.class, MemoryCommand.class,
        PerfCounterCommand.class, OgnlCommand.class, MemoryCompilerCommand.class, RedefineCommand.class,
        RetransformCommand.class, DashboardCommand.class, DumpClassCommand.class, HeapDumpCommand.class,
        JulyCommand.class, ThanksCommand.class, OptionsCommand.class, ClsCommand.class, ResetCommand.class,
        VersionCommand.class, SessionCommand.class, SystemPropertyCommand.class, SystemEnvCommand.class,
        VMOptionCommand.class, LoggerCommand.class, HistoryCommand.class, CatCommand.class,
        Base64Command.class, EchoCommand.class, PwdCommand.class, MBeanCommand.class,
        GrepCommand.class, TeeCommand.class, ProfilerCommand.class, VmToolCommand.class,
        StopCommand.class);

    try {
        if (ClassLoader.getSystemClassLoader().getResource("jdk/jfr/Recording.class") != null) {
            commandClassList.add(JFRCommand.class);
        }
    } catch (Throwable e) {
        logger.error("JDK version does not support JFR command", e);
    }

    for (Class<? extends AnnotatedCommand> clazz : commandClassList) {
        Name name = clazz.getAnnotation(Name.class);
        if (name != null && name.value() != null && disabledCommands.contains(name.value())) {
            continue;
        }
        commands.add(Command.create(clazz));
    }
}

public synchronized EnhancerAffect enhance(final Instrumentation inst, int maxNumOfMatchedClass) 
        throws UnmodifiableClassException {

    this.matchingClasses = GlobalOptions.isDisableSubClass
        ? SearchUtils.searchClass(inst, classNameMatcher)
        : SearchUtils.searchSubClass(inst, SearchUtils.searchClass(inst, classNameMatcher));

    if (matchingClasses.size() > maxNumOfMatchedClass) {
        affect.setOverLimitMsg("Matched classes exceeded limit: " + matchingClasses.size() + 
                              " > " + maxNumOfMatchedClass + ". Use '-m' to adjust limit.");
        return affect;
    }

    List<Pair<Class<?>, String>> filteredList = filter(matchingClasses);
    for (Pair<Class<?>, String> filtered : filteredList) {
        logger.info("Ignoring class: {}, Reason: {}", filtered.getFirst().getName(), filtered.getSecond());
    }

    logger.info("Enhancing matched classes: {}", matchingClasses);
    affect.setTransformer(this);

    try {
        ArthasBootstrap.getInstance().getTransformerManager().addTransformer(this, isTracing);

        if (GlobalOptions.isBatchReTransform) {
            Class<?>[] classArray = matchingClasses.toArray(new Class<?>[0]);
            if (classArray.length > 0) {
                inst.retransformClasses(classArray);
                logger.info("Batch transform successful: {}", Arrays.toString(classArray));
            }
        } else {
            for (Class<?> clazz : matchingClasses) {
                try {
                    inst.retransformClasses(clazz);
                    logger.info("Class transformed: {}", clazz);
                } catch (Throwable t) {
                    logger.warn("Transform failed: {}", clazz.getName(), t);
                    if (t instanceof UnmodifiableClassException) {
                        throw t;
                    }
                    throw new RuntimeException("Transformation error", t);
                }
            }
        }
    } catch (Throwable e) {
        logger.error("Enhancer error. Classes: {}", matchingClasses, e);
        affect.setThrowable(e);
    }
    return affect;
}

public static DefaultFullHttpResponse directView(File dir, String path, FullHttpRequest request, 
                                                ChannelHandlerContext ctx) throws IOException {
    path = path.startsWith("/") ? path.substring(1) : path;
    File file = new File(dir.getParent(), path);

    if (!isSubFile(dir, file)) {
        return null;
    }

    DefaultFullHttpResponse response = new DefaultFullHttpResponse(request.protocolVersion(), HttpResponseStatus.OK);
    if (file.isDirectory()) {
        if (!path.endsWith("/")) {
            response.setStatus(HttpResponseStatus.FOUND);
            response.headers().set(HttpHeaderNames.LOCATION, "/" + path + "/");
        }
        response.content().writeBytes(renderDir(file, !isSameFile(dir, file)).getBytes(StandardCharsets.UTF_8));
        response.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/html; charset=utf-8");
        ctx.write(response);
        ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT).addListener(ChannelFutureListener.CLOSE);
        return response;
    }

    if (file.isHidden() || !file.exists() || !file.isFile()) {
        return null;
    }

    long fileLength = file.length();
    if (fileLength < MIN_NETTY_DIRECT_SEND_SIZE) {
        try (FileInputStream fis = new FileInputStream(file)) {
            response.content().writeBytes(IOUtils.toByteArray(fis));
        }
        HttpUtil.setContentLength(response, response.content().readableBytes());
        ctx.writeAndFlush(response).addListener(future -> {
            if (future.isSuccess()) {
                ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT).addListener(ChannelFutureListener.CLOSE);
            } else {
                future.channel().close();
            }
        });
        return response;
    }

    logger.info("Sending large file: {} ({} bytes)", file.getName(), fileLength);
    HttpResponse fileResponse = new DefaultHttpResponse(HTTP_1_1, OK);
    HttpUtil.setContentLength(fileResponse, fileLength);
    setContentTypeHeader(fileResponse, file);
    setDateAndCacheHeaders(fileResponse, file);

    if (HttpUtil.isKeepAlive(request)) {
        fileResponse.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.KEEP_ALIVE);
    }

    ctx.write(fileResponse);
    try (RandomAccessFile raf = new RandomAccessFile(file, "r")) {
        ChannelFuture sendFuture;
        if (ctx.pipeline().get(SslHandler.class) == null) {
            sendFuture = ctx.write(new DefaultFileRegion(raf.getChannel(), 0, fileLength));
        } else {
            sendFuture = ctx.writeAndFlush(new HttpChunkedInput(new ChunkedFile(raf, 0, fileLength, 8192)));
        }

        sendFuture.addListener((ChannelProgressiveFutureListener) (future, progress, total) -> 
            logger.info("{} Transfer: {}/{}", future.channel(), progress, total));

        if (!HttpUtil.isKeepAlive(request)) {
            sendFuture.addListener(ChannelFutureListener.CLOSE);
        }
    }
    return response;
}

@Override
public byte[] transform(final ClassLoader loader, String className, Class<?> redefinedClass,
                        ProtectionDomain domain, byte[] buffer) throws IllegalClassFormatException {
    try {
        if (loader != null) {
            try {
                loader.loadClass(SpyAPI.class.getName());
            } catch (Throwable e) {
                logger.error("SpyAPI load failed. ClassLoader: {}, Class: {}", 
                             loader.getClass().getName(), className, e);
                return null;
            }
        }

        if (matchingClasses != null && !matchingClasses.contains(redefinedClass)) {
            return null;
        }

        ClassNode classNode = new ClassNode(Opcodes.ASM9);
        ClassReader reader = AsmUtils.toClassNode(buffer, classNode);
        classNode = AsmUtils.removeJSRInstructions(classNode);

        DefaultInterceptorClassParser parser = new DefaultInterceptorClassParser();
        List<InterceptorProcessor> processors = new ArrayList<>();
        processors.addAll(parser.parse(SpyInterceptor1.class));
        processors.addAll(parser.parse(SpyInterceptor2.class));
        processors.addAll(parser.parse(SpyInterceptor3.class));

        if (this.isTracing) {
            if (!this.skipJDKTrace) {
                processors.addAll(parser.parse(SpyTraceInterceptor1.class));
                processors.addAll(parser.parse(SpyTraceInterceptor2.class));
                processors.addAll(parser.parse(SpyTraceInterceptor3.class));
            } else {
                processors.addAll(parser.parse(SpyTraceExcludeJDKInterceptor1.class));
                processors.addAll(parser.parse(SpyTraceExcludeJDKInterceptor2.class));
                processors.addAll(parser.parse(SpyTraceExcludeJDKInterceptor3.class));
            }
        }

        List<MethodNode> matchedMethods = new ArrayList<>();
        for (MethodNode method : classNode.methods) {
            if (!isIgnore(method, methodNameMatcher)) {
                matchedMethods.add(method);
            }
        }

        if (AsmUtils.isEnhancerByCGLIB(className)) {
            for (MethodNode method : matchedMethods) {
                if (AsmUtils.isConstructor(method)) {
                    AsmUtils.fixConstructorExceptionTable(method);
                }
            }
        }

        GroupLocationFilter filter = new GroupLocationFilter();
        filter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atEnter", LocationType.ENTER));
        filter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atExit", LocationType.EXIT));
        filter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atExceptionExit", LocationType.EXCEPTION_EXIT));
        filter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atBeforeInvoke", LocationType.INVOKE));
        filter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_COMPLETED));
        filter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_EXCEPTION_EXIT));

        for (MethodNode method : matchedMethods) {
            if (AsmUtils.isNative(method)) {
                logger.info("Skipping native method: {}", 
                           AsmUtils.methodDeclaration(Type.getObjectType(classNode.name), method));
                continue;
            }

            if (AsmUtils.containsMethodInsnNode(method, Type.getInternalName(SpyAPI.class), "atBeforeInvoke")) {
                for (AbstractInsnNode insn : method.instructions) {
                    if (insn instanceof MethodInsnNode) {
                        MethodInsnNode minsn = (MethodInsnNode) insn;
                        if (this.skipJDKTrace && minsn.owner.startsWith("java/")) continue;
                        if (AsmOpUtils.isBoxType(Type.getObjectType(minsn.owner))) continue;
                        AdviceListenerManager.registerTraceAdviceListener(loader, className, 
                                                                         minsn.owner, minsn.name, 
                                                                         minsn.desc, listener);
                    }
                }
            } else {
                MethodProcessor processor = new MethodProcessor(classNode, method, filter);
                for (InterceptorInterceptor interceptor : processors) {
                    try {
                        for (Location loc : interceptor.process(processor)) {
                            if (loc instanceof MethodInsnNodeWare) {
                                MethodInsnNodeWare node = (MethodInsnNodeWare) loc;
                                MethodInsnNode minsn = node.methodInsnNode();
                                AdviceListenerManager.registerTraceAdviceListener(loader, className, 
                                                                                 minsn.owner, minsn.name, 
                                                                                 minsn.desc, listener);
                            }
                        }
                    } catch (Throwable e) {
                        logger.error("Interceptor error. Class: {}, Method: {}, Interceptor: {}", 
                                     classNode.name, method.name, interceptor.getClass().getName(), e);
                    }
                }
            }

            AdviceListenerManager.registerAdviceListener(loader, className, method.name, method.desc, listener);
            affect.addMethodAndCount(loader, className, method.name, method.desc);
        }

        if (AsmUtils.getMajorVersion(classNode.version) < 49) {
            classNode.version = AsmUtils.setMajorVersion(classNode.version, 49);
        }

        byte[] enhanced = AsmUtils.toBytes(classNode, loader, reader);
        classBytesCache.put(redefinedClass, new Object());
        dumpClassIfNecessary(className, enhanced, affect);
        affect.cCnt(1);
        return enhanced;
    } catch (Throwable t) {
        logger.warn("Transform failed. Loader: {}, Class: {}", loader, className, t);
        affect.setThrowable(t);
    }
    return null;
}

public static boolean completeMethodName(Completion completion) {
    List<CliToken> tokens = completion.lineTokens();
    String lastToken = tokens.isEmpty() ? "" : tokens.get(tokens.size() - 1).value();

    String className = tokens.size() < 2 ? "" : 
        StringUtils.isBlank(lastToken) 
            ? tokens.get(tokens.size() - 2).value() 
            : tokens.get(tokens.size() - 3).value();

    Set<Class<?>> classes = SearchUtils.searchClassOnly(
        completion.session().getInstrumentation(), className, 2);

    if (classes.size() != 1) {
        completion.complete(Collections.emptyList());
        return true;
    }

    Class<?> clazz = classes.iterator().next();
    List<String> methods = new ArrayList<>();
    for (Method method : clazz.getDeclaredMethods()) {
        if (StringUtils.isBlank(lastToken) || method.getName().startsWith(lastToken)) {
            methods.add(method.getName());
        }
    }
    methods.add("<init>");

    if (methods.size() == 1) {
        completion.complete(methods.get(0).substring(lastToken.length()), true);
    } else {
        CompletionUtils.complete(completion, methods);
    }
    return true;
}

private void agentRegister(ChannelHandlerContext ctx, HandshakeComplete handshake, String uri) 
        throws URISyntaxException {

    QueryStringDecoder decoder = new QueryStringDecoder(uri);
    Map<String, List<String>> params = decoder.parameters();

    String appName = extractFirstParam(params, URIConstans.APP_NAME);
    String id = generateAgentId(appName);
    id = extractFirstParam(params, URIConstans.ID, id);
    String arthasVersion = extractFirstParam(params, URIConstans.ARTHAS_VERSION);

    AgentInfo info = new AgentInfo();
    String clientIp = HttpUtils.findClientIP(handshake.requestHeaders());

    if (clientIp == null && ctx.channel().remoteAddress() instanceof InetSocketAddress) {
        InetSocketAddress addr = (InetSocketAddress) ctx.channel().remoteAddress();
        info.setHost(addr.getHostString());
        info.setPort(addr.getPort());
    } else {
        info.setHost(clientIp);
        Integer port = HttpUtils.findClientPort(handshake.requestHeaders());
        if (port != null) info.setPort(port);
    }

    info.setChannelHandlerContext(ctx);
    if (arthasVersion != null) info.setArthasVersion(arthasVersion);

    tunnelServer.addAgent(id, info);
    ctx.channel().closeFuture().addListener(future -> tunnelServer.removeAgent(id));

    URI responseUri = UriComponentsBuilder.newInstance()
        .scheme(URIConstans.RESPONSE)
        .path("/")
        .queryParam(URIConstans.METHOD, MethodConstants.AGENT_REGISTER)
        .queryParam(URIConstans.ID, id)
        .build()
        .toUri();

    ctx.channel().writeAndFlush(new TextWebSocketFrame(responseUri.toString()));
}

private String generateAgentId(String appName) {
    String random = RandomStringUtils.random(20, true, true).toUpperCase();
    return appName != null ? appName + "_" + random : random;
}

private String extractFirstParam(Map<String, List<String>> params, String key) {
    return extractFirstParam(params, key, null);
}

private String extractFirstParam(Map<String, List<String>> params, String key, String defaultValue) {
    List<String> values = params.get(key);
    return (values != null && !values.isEmpty()) ? values.get(0) : defaultValue;
}

@Override
public int[] startSubnegotiationLocal() {
    int windowSize = m_nWidth * 0x10000 + m_nHeight;
    int responseSize = 5;

    if ((m_nWidth % 0xFF) == 0xFF) responseSize++;
    if ((m_nWidth / 0x100) == 0xFF) responseSize++;
    if ((m_nHeight % 0xFF) == 0xFF) responseSize++;
    if ((m_nHeight / 0x100) == 0xFF) responseSize++;

    int[] response = new int[responseSize];
    response[0] = WINDOW_SIZE;

    for (int idx = 1, shift = 24; idx < responseSize; idx++, shift -= 8) {
        int mask = 0xFF << shift;
        response[idx] = (windowSize & mask) >>> shift;

        if (response[idx] == 0xFF) {
            response[++idx] = 0xFF;
        }
    }
    return response;
}

public static URL[] getUrls(ClassLoader loader) {
    if (loader instanceof URLClassLoader) {
        try {
            return ((URLClassLoader) loader).getURLs();
        } catch (Throwable e) {
            logger.error("URL retrieval failed. ClassLoader: {}", loader, e);
        }
    }

    if (loader.getClass().getName().startsWith("jdk.internal.loader.ClassLoaders$")) {
        try {
            Field unsafeField = Unsafe.class.getDeclaredField("theUnsafe");
            unsafeField.setAccessible(true);
            Unsafe unsafe = (Unsafe) unsafeField.get(null);

            Class<?> clazz = loader.getClass();
            Field ucpField = null;
            while (ucpField == null && !clazz.equals(Object.class)) {
                try {
                    ucpField = clazz.getDeclaredField("ucp");
                } catch (NoSuchFieldException ex) {
                    clazz = clazz.getSuperclass();
                }
            }
            if (ucpField == null) return null;

            Object ucp = unsafe.getObject(loader, unsafe.objectFieldOffset(ucpField));
            if (ucp == null) return null;

            Field pathField = ucpField.getType().getDeclaredField("path");
            ArrayList<URL> path = (ArrayList<URL>) unsafe.getObject(ucp, unsafe.objectFieldOffset(pathField));
            return path.toArray(new URL[0]);
        } catch (Throwable e) {
            logger.debug("Advanced URL retrieval failed", e);
        }
    }
    return null;
}

private Minf parseMinf(Box ref) throws IOException {
    Minf obj = new Minf();
    Box b;
    while ((b = untilAnyBox(ref)) != null) {
        switch (b.type) {
            case ATOM_DINF:
                obj.dinf = readFullBox(b);
                break;
            case ATOM_STBL:
                obj.stblStsd = parseStbl(b);
                break;
            case ATOM_VMHD:
            case ATOM_SMHD:
                obj.mhd = readFullBox(b);
                break;
        }
        ensure(b);
    }
    return obj;
}

private Moov parseMoov(Box ref) throws IOException {
    Box b = readBox(ATOM_MVHD);
    Moov moov = new Moov();
    moov.mvhd = parseMvhd();
    ensure(b);

    ArrayList<Trak> tmp = new ArrayList<>((int) moov.mvhd.nextTrackId);
    while ((b = untilBox(ref, ATOM_TRAK, ATOM_MVEX)) != null) {
        switch (b.type) {
            case ATOM_TRAK:
                tmp.add(parseTrak(b));
                break;
            case ATOM_MVEX:
                moov.mvexTrex = parseMvex(b, (int) moov.mvhd.nextTrackId);
                break;
        }
        ensure(b);
    }

    moov.trak = tmp.toArray(new Trak[0]);
    return moov;
}

private ArrayList<Object> getSpecialItems() {
    synchronized (DownloadManager.this) {
        ArrayList<Mission> pending = new ArrayList<>(mMissionsPending);
        ArrayList<Mission> finished = new ArrayList<>(mMissionsFinished);
        List<Mission> remove = new ArrayList<>(hidden);

        remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

        int fakeTotal = pending.size();
        if (fakeTotal > 0) {
            fakeTotal++;
        }

        fakeTotal += finished.size();
        if (finished.size() > 0) {
            fakeTotal++;
        }

        ArrayList<Object> list = new ArrayList<>(fakeTotal);
        if (pending.size() > 0) {
            list.add(PENDING);
            list.addAll(pending);
        }
        if (finished.size() > 0) {
            list.add(FINISHED);
            list.addAll(finished);
        }

        hasFinished = finished.size() > 0;
        return list;
    }
}

@Override
public void run() {
    if (mMission.current > 0) {
        mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);
    }

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) {
                        return;
                    }
                    long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) {
                        finalLength += length;
                    }
                    if (length < lowestSize) {
                        lowestSize = length;
                    }
                }

                mMission.nearLength = finalLength;

                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    if (lowestSize < 1) {
                        mMission.offsets[0] = RESERVE_SPACE_DEFAULT;
                    } else {
                        mMission.offsets[0] = lowestSize < RESERVE_SPACE_MAXIMUM ? lowestSize : RESERVE_SPACE_MAXIMUM;
                    }
                }
            } else {
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;
                if (DEBUG) {
                    Log.d(TAG, "falling back (unknown length)");
                }
            } else {
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {
                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) {
                                count++;
                            }
                            mMission.blocks = new int[count];
                        } else {
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }
                        if (DEBUG) {
                            Log.d(TAG, "http response code = " + mConn.getResponseCode());
                        }
                    } else {
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;
                        if (DEBUG) {
                            Log.d(TAG, "falling back due http response code = " + mConn.getResponseCode());
                        }
                    }
                }

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) {
                return;
            }

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (IOException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) {
                return;
            }

            if (e instanceof DownloadMission.HttpError && ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }

            Log.e(TAG, "initializer failed, retrying", e);
        }
    }

    mMission.start();
}

@SuppressLint("DefaultLocale")
private void updateProgress(ViewHolderItem h) {
    if (h == null || h.item == null || h.item.mission instanceof FinishedMission) {
        return;
    }

    DownloadMission mission = (DownloadMission) h.item.mission;
    double done = mission.done;
    long length = mission.getLength();
    long now = System.currentTimeMillis();
    boolean hasError = mission.errCode != ERROR_NOTHING;

    h.progress.setMarquee(mission.isRecovering() || !hasError && (!mission.isInitialized() || mission.unknownLength));

    double progress;
    if (mission.unknownLength) {
        progress = Double.NaN;
        h.progress.setProgress(0.0f);
    } else {
        progress = done / length;
    }

    if (hasError) {
        h.progress.setProgress(isNotFinite(progress) ? 1d : progress);
        h.status.setText(R.string.msg_error);
    } else if (isNotFinite(progress)) {
        h.status.setText(UNDEFINED_PROGRESS);
    } else {
        h.status.setText(String.format("%.2f%%", progress * 100));
        h.progress.setProgress(progress);
    }

    @StringRes int state;
    String sizeStr = Utility.formatBytes(length).concat("  ");

    if (mission.isPsFailed() || mission.errCode == ERROR_POSTPROCESSING_HOLD) {
        h.size.setText(sizeStr);
        return;
    } else if (!mission.running) {
        state = mission.enqueued ? R.string.queued : R.string.paused;
    } else if (mission.isPsRunning()) {
        state = R.string.post_processing;
    } else if (mission.isRecovering()) {
        state = R.string.recovering;
    } else {
        state = 0;
    }

    if (state != 0) {
        h.size.setText(sizeStr.concat("(").concat(mContext.getString(state)).concat(")"));
        h.resetSpeedMeasure();
        return;
    }

    if (h.lastTimestamp < 0) {
        h.size.setText(sizeStr);
        h.lastTimestamp = now;
        h.lastDone = done;
        return;
    }

    long deltaTime = now - h.lastTimestamp;
    double deltaDone = done - h.lastDone;

    if (h.lastDone > done) {
        h.lastDone = done;
        h.size.setText(sizeStr);
        return;
    }

    if (deltaDone > 0 && deltaTime > 0) {
        float speed = (float) ((deltaDone * 1000d) / deltaTime);
        float averageSpeed = speed;

        if (h.lastSpeedIdx < 0) {
            Arrays.fill(h.lastSpeed, speed);
            h.lastSpeedIdx = 0;
        } else {
            for (int i = 0; i < h.lastSpeed.length; i++) {
                averageSpeed += h.lastSpeed[i];
            }
            averageSpeed /= h.lastSpeed.length + 1.0f;
        }

        String speedStr = Utility.formatSpeed(averageSpeed);
        String etaStr;

        if (mission.unknownLength) {
            etaStr = "";
        } else {
            long eta = (long) Math.ceil((length - done) / averageSpeed);
            etaStr = Utility.formatBytes((long) done) + "/" + Utility.stringifySeconds(eta) + "  ";
        }

        h.size.setText(sizeStr.concat(etaStr).concat(speedStr));

        h.lastTimestamp = now;
        h.lastDone = done;
        h.lastSpeed[h.lastSpeedIdx++] = speed;

        if (h.lastSpeedIdx >= h.lastSpeed.length) {
            h.lastSpeedIdx = 0;
        }
    }
}

private Trun parseTrun() throws IOException {
    Trun obj = new Trun();
    obj.bFlags = stream.readInt();
    obj.entryCount = stream.readInt();

    obj.entriesRowSize = 0;
    if (hasFlag(obj.bFlags, 0x0100)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0200)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0400)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0800)) {
        obj.entriesRowSize += 4;
    }
    obj.bEntries = new byte[obj.entriesRowSize * obj.entryCount];

    if (hasFlag(obj.bFlags, 0x0001)) {
        obj.dataOffset = stream.readInt();
    }
    if (hasFlag(obj.bFlags, 0x0004)) {
        obj.bFirstSampleFlags = stream.readInt();
    }

    stream.read(obj.bEntries);

    for (int i = 0; i < obj.entryCount; i++) {
        TrunEntry entry = obj.getEntry(i);
        if (hasFlag(obj.bFlags, 0x0100)) {
            obj.chunkDuration += entry.sampleDuration;
        }
        if (hasFlag(obj.bFlags, 0x0200)) {
            obj.chunkSize += entry.sampleSize;
        }
        if (hasFlag(obj.bFlags, 0x0800)) {
            if (!hasFlag(obj.bFlags, 0x0100)) {
                obj.chunkDuration += entry.sampleCompositionTimeOffset;
            }
        }
    }
    return obj;
}

private void makeMvhd(long longestTrack) throws IOException {
    auxWrite(new byte[]{
        0x00, 0x00, 0x00, 0x78, 0x6D, 0x76, 0x68, 0x64, 0x01, 0x00, 0x00, 0x00
    });
    auxWrite(ByteBuffer.allocate(28)
            .putLong(time)
            .putLong(time)
            .putInt(DEFAULT_TIMESCALE)
            .putLong(longestTrack)
            .array()
    );

    auxWrite(new byte[]{
        0x00, 0x01, 0x00, 0x00, 0x01, 0x00,
        0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x00, 0x00, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x00, 0x00, 0x00,
        0x40, 0x00, 0x00, 0x00
    });
    auxWrite(new byte[24]);
    auxWrite(ByteBuffer.allocate(4)
            .putInt(tracks.length + 1)
            .array()
    );
}

public Mp4DashChunk getNextChunk(boolean infoOnly) throws IOException {
    Mp4Track track = tracks[selectedTrack];
    while (stream.available()) {
        if (chunkZero) {
            ensure(box);
            if (!stream.available()) {
                break;
            }
            box = readBox();
        } else {
            chunkZero = true;
        }

        switch (box.type) {
            case ATOM_MOOF:
                if (moof != null) {
                    throw new IOException("moof found without mdat");
                }
                moof = parseMoof(box, track.trak.tkhd.trackId);

                if (moof.traf != null) {
                    if (hasFlag(moof.traf.trun.bFlags, 0x0001)) {
                        moof.traf.trun.dataOffset -= box.size + 8;
                        if (moof.traf.trun.dataOffset < 0) {
                            throw new IOException("trun box has wrong data offset, points outside of concurrent mdat box");
                        }
                    }

                    if (moof.traf.trun.chunkSize < 1) {
                        if (hasFlag(moof.traf.tfhd.bFlags, 0x10)) {
                            moof.traf.trun.chunkSize = moof.traf.tfhd.defaultSampleSize * moof.traf.trun.entryCount;
                        } else {
                            moof.traf.trun.chunkSize = (int) (box.size - 8);
                        }
                    }
                    if (!hasFlag(moof.traf.trun.bFlags, 0x900) && moof.traf.trun.chunkDuration == 0) {
                        if (hasFlag(moof.traf.tfhd.bFlags, 0x20)) {
                            moof.traf.trun.chunkDuration = moof.traf.tfhd.defaultSampleDuration * moof.traf.trun.entryCount;
                        }
                    }
                }
                break;
            case ATOM_MDAT:
                if (moof == null) {
                    throw new IOException("mdat found without moof");
                }
                if (moof.traf == null) {
                    moof = null;
                    continue;
                }

                Mp4DashChunk chunk = new Mp4DashChunk();
                chunk.moof = moof;
                if (!infoOnly) {
                    chunk.data = stream.getView(moof.traf.trun.chunkSize);
                }

                moof = null;
                stream.skipBytes(chunk.moof.traf.trun.dataOffset);
                return chunk;
        }
    }
    return null;
}

public void updateMission(Mission mission) {
    ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    SQLiteDatabase database = getWritableDatabase();
    String ts = String.valueOf(mission.timestamp);
    int rowsAffected;

    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[]{ts});
        } else {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_PATH + " = ?", new String[]{
                mission.storage.getUri().toString()
            });
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e("FinishedMissionStore", "Expected 1 row to be affected by update but got " + rowsAffected);
    }
}

private void animatePopupOverlayAndFinishService() {
    final int targetTranslationY = (int) (closeOverlayBinding.closeButton.getRootView().getHeight() - closeOverlayBinding.closeButton.getY());

    closeOverlayBinding.closeButton.animate().setListener(null).cancel();
    closeOverlayBinding.closeButton.animate()
        .setInterpolator(new AnticipateInterpolator())
        .translationY(targetTranslationY)
        .setDuration(400)
        .setListener(new AnimatorListenerAdapter() {
            @Override public void onAnimationCancel(Animator animation) { end(); }
            @Override public void onAnimationEnd(Animator animation) { end(); }
            private void end() {
                windowManager.removeView(closeOverlayBinding.getRoot());
                closeOverlayBinding = null;
                player.getService().destroyPlayerAndStopService();
            }
        }).start();
}

public void play() {
    if (audioReactor == null || playQueue == null || exoPlayerIsNull()) return;

    if (!isMuted()) audioReactor.requestAudioFocus();

    if (currentState == STATE_COMPLETED) {
        if (playQueue.getIndex() == 0) seekToDefault();
        else playQueue.setIndex(0);
    }

    simpleExoPlayer.play();
    saveStreamProgressState();
}

