private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) return;

    switch (intent.getAction()) {
        case AudioManager.ACTION_AUDIO_BECOMING_NOISY: pause(); break;
        case ACTION_CLOSE: service.destroyPlayerAndStopService(); break;
        case ACTION_PLAY_PAUSE: playPause(); break;
        case ACTION_PLAY_PREVIOUS: playPrevious(); break;
        case ACTION_PLAY_NEXT: playNext(); break;
        case ACTION_FAST_REWIND: fastRewind(); break;
        case ACTION_FAST_FORWARD: fastForward(); break;
        case ACTION_REPEAT: cycleNextRepeatMode(); break;
        case ACTION_SHUFFLE: toggleShuffleModeEnabled(); break;
        case Intent.ACTION_CONFIGURATION_CHANGED: break;
    }

    UIs.call(playerUi -> playerUi.onBroadcastReceived(intent));
}

protected void addImagesMetadataItem(final LayoutInflater inflater, final LinearLayout layout, @StringRes final int type, final List<Image> images) {
    final String preferredImageUrl = ImageStrategy.choosePreferredImage(images);
    if (preferredImageUrl == null) return;

    final ItemMetadataBinding itemBinding = ItemMetadataBinding.inflate(inflater, layout, false);
    itemBinding.metadataTypeView.setText(type);

    final SpannableStringBuilder urls = new SpannableStringBuilder();
    for (final Image image : images) {
        if (urls.length() != 0) urls.append(", ");
        final int entryBegin = urls.length();

        if (image.getHeight() != Image.HEIGHT_UNKNOWN || image.getWidth() != Image.WIDTH_UNKNOWN || image.getEstimatedResolutionLevel() == Image.ResolutionLevel.UNKNOWN) {
            urls.append(imageSizeToText(image.getHeight())).append('x').append(imageSizeToText(image.getWidth()));
        } else {
            switch (image.getEstimatedResolutionLevel()) {
                case LOW -> urls.append(getString(R.string.image_quality_low));
                case MEDIUM -> urls.append(getString(R.string.image_quality_medium));
                case HIGH -> urls.append(getString(R.string.image_quality_high));
                default -> {}
            }
        }

        urls.setSpan(new ClickableSpan() {
            @Override public void onClick(@NonNull View widget) {
                ShareUtils.openUrlInBrowser(requireContext(), image.getUrl());
            }
        }, entryBegin, urls.length(), Spanned.SPAN_EXCLUSIVE_EXCLUSIVE);

        if (preferredImageUrl.equals(image.getUrl())) {
            urls.setSpan(new StyleSpan(Typeface.BOLD), entryBegin, urls.length(), Spanned.SPAN_EXCLUSIVE_EXCLUSIVE);
        }
    }

    itemBinding.metadataContentView.setText(urls);
    itemBinding.metadataContentView.setMovementMethod(LinkMovementMethod.getInstance());
    layout.addView(itemBinding.getRoot());
}

private void openDetailFragmentFromCommentReplies(@NonNull FragmentManager fm, boolean popBackStack) {
    @Nullable String fragmentUnderEntryName;
    if (fm.getBackStackEntryCount() < 2) fragmentUnderEntryName = null;
    else fragmentUnderEntryName = fm.getBackStackEntryAt(fm.getBackStackEntryCount() - 2).getName();

    @Nullable CommentRepliesFragment repliesFragment = (CommentRepliesFragment) fm.findFragmentByTag(CommentRepliesFragment.TAG);
    @Nullable CommentsInfoItem rootComment = repliesFragment == null ? null : repliesFragment.getCommentsInfoItem();

    if (popBackStack) fm.popBackStackImmediate();

    if (CommentRepliesFragment.TAG.equals(fragmentUnderEntryName)) return;

    final BottomSheetBehavior<FragmentContainerView> behavior = BottomSheetBehavior.from(mainBinding.fragmentPlayerHolder);
    if (behavior.getState() == BottomSheetBehavior.STATE_HIDDEN) return;

    behavior.addBottomSheetCallback(new BottomSheetBehavior.BottomSheetCallback() {
        @Override public void onStateChanged(@NonNull View bottomSheet, int newState) {
            if (newState == BottomSheetBehavior.STATE_EXPANDED) {
                final Fragment detailFragment = fm.findFragmentById(R.id.fragment_player_holder);
                if (detailFragment instanceof VideoDetailFragment && rootComment != null) {
                    ((VideoDetailFragment) detailFragment).scrollToComment(rootComment);
                }
                behavior.removeBottomSheetCallback(this);
            }
        }
        @Override public void onSlide(@NonNull View bottomSheet, float slideOffset) {}
    });

    behavior.setState(BottomSheetBehavior.STATE_EXPANDED);
}

private void buildCaptionMenu(@NonNull List<String> availableLanguages) {
    if (captionPopupMenu == null) return;
    captionPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_CAPTION);
    captionPopupMenu.setOnDismissListener(this);

    final MenuItem captionOffItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION, 0, Menu.NONE, R.string.caption_none);
    captionOffItem.setOnMenuItemClickListener(menuItem -> {
        int textRendererIndex = player.getCaptionRendererIndex();
        if (textRendererIndex != RENDERER_UNAVAILABLE) {
            player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setRendererDisabled(textRendererIndex, true));
        }
        player.getPrefs().edit().remove(context.getString(R.string.caption_user_set_key)).apply();
        return true;
    });

    for (int i = 0; i < availableLanguages.size(); i++) {
        final String captionLanguage = availableLanguages.get(i);
        final MenuItem captionItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION, i + 1, Menu.NONE, captionLanguage);
        captionItem.setOnMenuItemClickListener(menuItem -> {
            int textRendererIndex = player.getCaptionRendererIndex();
            if (textRendererIndex != RENDERER_UNAVAILABLE) {
                player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters()
                    .setPreferredTextLanguages(captionLanguage, PlayerHelper.captionLanguageStemOf(captionLanguage))
                    .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
                    .setRendererDisabled(textRendererIndex, false));
                player.getPrefs().edit().putString(context.getString(R.string.caption_user_set_key), captionLanguage).apply();
            }
            return true;
        });
    }
    captionPopupMenu.setOnDismissListener(this);

    final int textRendererIndex = player.getCaptionRendererIndex();
    if (textRendererIndex == RENDERER_UNAVAILABLE) return;

    final String userPreferredLanguage = player.getPrefs().getString(context.getString(R.string.caption_user_set_key), null);
    if (userPreferredLanguage == null) {
        player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters().setRendererDisabled(textRendererIndex, true));
        return;
    }

    final List<String> selectedPreferredLanguages = player.getTrackSelector().getParameters().preferredTextLanguages;
    if (!selectedPreferredLanguages.contains(userPreferredLanguage)) {
        player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters()
            .setPreferredTextLanguages(userPreferredLanguage, PlayerHelper.captionLanguageStemOf(userPreferredLanguage))
            .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
            .setRendererDisabled(textRendererIndex, false));
    }
}

