private void updatePlaybackState(boolean playWhenReady, int playbackState) {
    if (currentState == STATE_PAUSED_SEEK) return;

    switch (playbackState) {
        case com.google.android.exoplayer2.Player.STATE_IDLE: 
            isPrepared = false; 
            break;
        case com.google.android.exoplayer2.Player.STATE_BUFFERING: 
            if (isPrepared) changeState(STATE_BUFFERING); 
            break;
        case com.google.android.exoplayer2.Player.STATE_READY: 
            if (!isPrepared) {
                isPrepared = true;
                onPrepared(playWhenReady);
            }
            changeState(playWhenReady ? STATE_PLAYING : STATE_PAUSED);
            break;
        case com.google.android.exoplayer2.Player.STATE_ENDED: 
            changeState(STATE_COMPLETED);
            saveStreamProgressStateCompleted();
            isPrepared = false;
            break;
    }
}

private static String processInlineImages(final String htmlContent, final EmailContent emailContent) {
    if (htmlContent == null || emailContent == null) {
        return htmlContent;
    }

    final Map<String, EmailAttachment> contentIdMap = new HashMap<>();
    for (final EmailAttachment attachment : emailContent.getAttachments()) {
        if (attachment.isEmbedded()
                && attachment.getContentId() != null
                && attachment.getData() != null) {
            contentIdMap.put(attachment.getContentId(), attachment);
        }
    }
    if (contentIdMap.isEmpty()) {
        return htmlContent;
    }

    final Pattern cidPattern = Pattern.compile(
            "(?i)<img[^>]*\\ssrc\\s*=\\s*['\"]cid:([^'\"]+)['\"][^>]*>",
            Pattern.CASE_INSENSITIVE);
    final Matcher matcher = cidPattern.matcher(htmlContent);
    final StringBuffer result = new StringBuffer();

    while (matcher.find()) {
        final String contentId = matcher.group(1);
        final EmailAttachment attachment = contentIdMap.get(contentId);

        if (attachment != null && attachment.getData() != null) {
            String mimeType = attachment.getContentType();
            if (mimeType == null || mimeType.isEmpty()) {
                final String filename = attachment.getFilename();
                if (filename != null) {
                    if (filename.toLowerCase().endsWith(".png")) {
                        mimeType = "image/png";
                    } else if (filename.toLowerCase().endsWith(".jpg")
                            || filename.toLowerCase().endsWith(".jpeg")) {
                        mimeType = "image/jpeg";
                    } else if (filename.toLowerCase().endsWith(".gif")) {
                        mimeType = "image/gif";
                    } else if (filename.toLowerCase().endsWith(".bmp")) {
                        mimeType = "image/bmp";
                    } else {
                        mimeType = "image/png";
                    }
                } else {
                    mimeType = "image/png";
                }
            }

            final String base64Data = Base64.getEncoder().encodeToString(attachment.getData());
            final String dataUri = "data:" + mimeType + ";base64," + base64Data;
            final String replacement = matcher.group(0).replaceFirst("cid:" + Pattern.quote(contentId), dataUri);
            matcher.appendReplacement(result, Matcher.quoteReplacement(replacement));
        } else {
            matcher.appendReplacement(result, Matcher.quoteReplacement(matcher.group(0)));
        }
    }
    matcher.appendTail(result);

    return result.toString();
}

private static void addAttachmentToInfo(
        final StringBuilder attachmentInfo,
        final String filename,
        final String contentType,
        final String encoding) {
    attachmentInfo
            .append("<div class=\"attachment-item\">")
            .append("<span class=\"attachment-icon\">")
            .append(MimeConstants.ATTACHMENT_MARKER)
            .append("</span> ")
            .append("<span class=\"attachment-name\">")
            .append(escapeHtml(filename))
            .append("</span>");

    if (!contentType.isEmpty() || !encoding.isEmpty()) {
        attachmentInfo.append(" <span class=\"attachment-details\">(");
        if (!contentType.isEmpty()) {
            attachmentInfo.append(escapeHtml(contentType));
        }
        if (!encoding.isEmpty()) {
            if (!contentType.isEmpty()) {
                attachmentInfo.append(", ");
            }
            attachmentInfo.append("encoding: ").append(escapeHtml(encoding));
        }
        attachmentInfo.append(")</span>");
    }
    attachmentInfo.append("</div>\n");
}

