@PostMapping(value = "/form-fields", consumes = "multipart/form-data")
@Operation(
        summary = "Get form field information",
        description = "Returns count and details of form fields. Input:PDF Output:JSON Type:SISO")
public Map<String, Object> getFormFields(@ModelAttribute PDFFile file) throws IOException {
    try (final PDDocument doc = pdfDocumentFactory.load(file.getFileInput())) {
        final Map<String, Object> info = new HashMap<>();
        final PDAcroForm form = doc.getDocumentCatalog().getAcroForm();

        if (form != null) {
            info.put("fieldCount", form.getFields().size());
            info.put("hasXFA", form.hasXFA());
            info.put("isSignaturesExist", form.isSignaturesExist());
        } else {
            info.put("fieldCount", 0);
            info.put("hasXFA", false);
            info.put("isSignaturesExist", false);
        }
        return info;
    }
}

private void checkAndRefreshExplorer() {
    if (!IS_WINDOWS) return;
    if (timeAt90Percent == -1) {
        timeAt90Percent = System.currentTimeMillis();
        stuckTimer = new Timer(1000, e -> {
            final long now = System.currentTimeMillis();
            if (now - timeAt90Percent > stuckThreshold) {
                try {
                    log.debug("Attempting Windows explorer refresh");
                    final String dir = System.getProperty("user.dir");

                    final Set<String> pids = new HashSet<>();
                    final ProcessBuilder listPb = new ProcessBuilder("cmd", "/c", "wmic", "process", 
                            "where", "name='explorer.exe'", "get", "ProcessId", "/format:csv");
                    final Process proc = listPb.start();
                    final BufferedReader reader = new BufferedReader(
                            new InputStreamReader(proc.getInputStream()));
                    String line;
                    while ((line = BoundedLineReader.readLine(reader, 5_000_000)) != null) {
                        if (line.matches(".*\\d+.*")) {
                            final String[] parts = line.trim().split(",");
                            if (parts.length >= 2) {
                                pids.add(parts[parts.length - 1].trim());
                            }
                        }
                    }
                    proc.waitFor(2, TimeUnit.SECONDS);

                    final ProcessBuilder pb = new ProcessBuilder("cmd", "/c", "start", 
                            "/min", "/b", "explorer.exe", dir);
                    pb.redirectErrorStream(true);
                    explorerProcess = pb.start();

                    final Timer cleanupTimer = new Timer(2000, cleanup -> {
                        try {
                            final ProcessBuilder findPb = new ProcessBuilder("cmd", "/c", "wmic", 
                                    "process", "where", "name='explorer.exe'", "get", 
                                    "ProcessId", "/format:csv");
                            final Process newProc = findPb.start();
                            final BufferedReader newReader = new BufferedReader(
                                    new InputStreamReader(newProc.getInputStream()));
                            String newLine;
                            while ((newLine = BoundedLineReader.readLine(newReader, 5_000_000)) != null) {
                                if (newLine.matches(".*\\d+.*")) {
                                    final String[] parts = newLine.trim().split(",");
                                    if (parts.length >= 2) {
                                        final String pid = parts[parts.length - 1].trim();
                                        if (!pids.contains(pid)) {
                                            log.debug("Found new explorer.exe PID: " + pid);
                                            final ProcessBuilder killPb = new ProcessBuilder(
                                                    "taskkill", "/PID", pid, "/F");
                                            killPb.redirectErrorStream(true);
                                            final Process killResult = killPb.start();
                                            killResult.waitFor(2, TimeUnit.SECONDS);
                                            log.debug("Terminated explorer: " + pid);
                                        }
                                    }
                                }
                            }
                            newProc.waitFor(2, TimeUnit.SECONDS);
                        } catch (Exception ex) {
                            log.error("Cleanup error", ex);
                        }
                    });
                    cleanupTimer.setRepeats(false);
                    cleanupTimer.start();
                    stuckTimer.stop();
                } catch (Exception ex) {
                    log.error("Refresh error", ex);
                }
            }
        });
        stuckTimer.setRepeats(true);
        stuckTimer.start();
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/compress-pdf")
@Operation(
        summary = "Optimize PDF file",
        description = "This endpoint accepts a PDF file and optimizes it. Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> optimizePdf(@ModelAttribute OptimizePdfRequest req) throws Exception {
    final MultipartFile inputFile = req.getFileInput();
    Integer level = req.getOptimizeLevel();
    final String sizeStr = req.getExpectedOutputSize();
    final Boolean grayscale = req.getGrayscale();
    if (sizeStr == null && level == null) {
        throw new Exception("Missing optimization parameters");
    }

    long expectedSize = 0L;
    boolean auto = false;
    if (sizeStr != null && sizeStr.length() > 1) {
        expectedSize = GeneralUtils.convertSizeToBytes(sizeStr);
        auto = true;
    }

    final Path orig = Files.createTempFile("original_", ".pdf");
    inputFile.transferTo(orig.toFile());
    final long inputSize = Files.size(orig);

    Path current = Files.createTempFile("working_", ".pdf");
    Files.copy(orig, current, StandardCopyOption.REPLACE_EXISTING);

    final List<Path> tmpFiles = new ArrayList<>();
    tmpFiles.add(orig);
    tmpFiles.add(current);
    try {
        if (auto) {
            final double ratio = expectedSize / (double) inputSize;
            level = determineOptimizeLevel(ratio);
        }

        boolean sizeOk = false;
        boolean imgDone = false;
        boolean extDone = false;

        while (!sizeOk && level <= 9) {
            if (!extDone) {
                boolean gsOk = false;

                if (isGhostscriptEnabled()) {
                    try {
                        applyGhostscriptCompression(req, level, current, tmpFiles);
                        log.info("Ghostscript applied");
                        gsOk = true;
                    } catch (IOException e) {
                        log.warn("Ghostscript failed");
                    }
                }

                if (!gsOk && isQpdfEnabled() && level <= 3) {
                    try {
                        applyQpdfCompression(req, level, current, tmpFiles);
                        log.info("QPDF applied");
                    } catch (IOException e) {
                        log.warn("QPDF failed");
                    }
                }

                if (!gsOk && !isQpdfEnabled()) {
                    log.info("No external tools available");
                }

                extDone = true;

                if (gsOk) {
                    imgDone = true;
                }
            }

            if ((level >= 4 || Boolean.TRUE.equals(grayscale)) && !imgDone) {
                final double scale = switch (level) {
                    case 4 -> 0.95;
                    case 5 -> 0.9;
                    case 6 -> 0.8;
                    case 7 -> 0.7;
                    case 8 -> 0.65;
                    case 9 -> 0.5;
                    default -> 1.0;
                };

                log.info("Image scale: {}", scale);
                final Path imgFile = compressImagesInPDF(
                        current, scale, 0.7f, Boolean.TRUE.equals(grayscale));
                tmpFiles.add(imgFile);
                current = imgFile;
                imgDone = true;
            }

            final long outputSize = Files.size(current);
            if (outputSize <= expectedSize || !auto) {
                sizeOk = true;
            } else {
                final int newLevel = incrementOptimizeLevel(level, outputSize, expectedSize);
                if (newLevel == level) {
                    if (auto) log.info("Max level reached");
                    sizeOk = true;
                } else {
                    imgDone = false;
                    extDone = false;
                    level = newLevel;
                }
            }
        }

        if (Files.size(current) >= inputSize) {
            log.warn("Optimized file larger - using original");
            current = orig;
        }

        final String outName = Filenames.toSimpleFileName(
                inputFile.getOriginalFilename()).replaceFirst("[.][^.]+$", "") + "_Optimized.pdf";

        return WebResponseUtils.pdfDocToWebResponse(
                pdfDocumentFactory.load(current.toFile()), outName);
    } finally {
        for (Path tmp : tmpFiles) {
            try {
                Files.deleteIfExists(tmp);
            } catch (IOException e) {
                log.warn("Delete failed: " + tmp, e);
            }
        }
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/get-info-on-pdf")
@Operation(summary = "Summary here", description = "desc. Input:PDF Output:JSON Type:SISO")
public ResponseEntity<byte[]> getPdfInfo(@ModelAttribute PDFFile req) throws IOException {
    final MultipartFile inputFile = req.getFileInput();
    try (final PDDocument doc = pdfDocumentFactory.load(inputFile, true)) {
        final ObjectMapper mapper = new ObjectMapper();
        final ObjectNode root = mapper.createObjectNode();

        final PDDocumentInformation info = doc.getDocumentInformation();
        final ObjectNode meta = mapper.createObjectNode();
        final ObjectNode basic = mapper.createObjectNode();
        final ObjectNode docInfo = mapper.createObjectNode();
        final ObjectNode compliancy = mapper.createObjectNode();
        final ObjectNode encryption = mapper.createObjectNode();
        final ObjectNode other = mapper.createObjectNode();

        meta.put("Title", info.getTitle());
        meta.put("Author", info.getAuthor());
        meta.put("Subject", info.getSubject());
        meta.put("Keywords", info.getKeywords());
        meta.put("Producer", info.getProducer());
        meta.put("Creator", info.getCreator());
        meta.put("CreationDate", formatDate(info.getCreationDate()));
        meta.put("ModificationDate", formatDate(info.getModificationDate()));
        root.set("Metadata", meta);

        final long size = inputFile.getSize();
        basic.put("FileSizeInBytes", size);

        final String text = new PDFTextStripper().getText(doc);
        final String[] words = text.split("\\s+");
        basic.put("WordCount", words.length);
        basic.put("ParagraphCount", text.split("\r\n|\r|\n").length);
        basic.put("CharacterCount", text.length());

        boolean hasCompression = false;
        String compressionType = "None";
        basic.put("Compression", hasCompression);
        if (hasCompression) basic.put("CompressionType", compressionType);

        basic.put("Language", doc.getDocumentCatalog().getLanguage());
        basic.put("Number of pages", doc.getNumberOfPages());

        final PDDocumentCatalog catalog = doc.getDocumentCatalog();
        docInfo.put("PDF version", doc.getVersion());
        docInfo.put("Trapped", info.getTrapped());
        docInfo.put("Page Mode", getPageModeDescription(catalog.getPageMode().name()));

        final PDAcroForm acroForm = catalog.getAcroForm();
        final ObjectNode formFields = mapper.createObjectNode();
        if (acroForm != null) {
            for (PDField field : acroForm.getFieldTree()) {
                formFields.put(field.getFullyQualifiedName(), field.getValueAsString());
            }
        }
        root.set("FormFields", formFields);

        final ObjectNode summary = generatePDFSummaryData(doc);
        if (summary != null && summary.size() > 0) {
            root.set("SummaryData", summary);
        }

        if (catalog.getNames() != null) {
            final PDEmbeddedFilesNameTreeNode efTree = catalog.getNames().getEmbeddedFiles();
            final ArrayNode embeddedFiles = mapper.createArrayNode();
            if (efTree != null) {
                final Map<String, PDComplexFileSpecification> efMap = efTree.getNames();
                if (efMap != null) {
                    for (Map.Entry<String, PDComplexFileSpecification> e : efMap.entrySet()) {
                        final ObjectNode fileNode = mapper.createObjectNode();
                        fileNode.put("Name", e.getKey());
                        final PDEmbeddedFile file = e.getValue().getEmbeddedFile();
                        if (file != null) {
                            fileNode.put("FileSize", file.getLength());
                        }
                        embeddedFiles.add(fileNode);
                    }
                }
            }
            other.set("EmbeddedFiles", embeddedFiles);
        }

        final ArrayNode attachments = mapper.createArrayNode();
        for (PDPage page : doc.getPages()) {
            for (PDAnnotation ann : page.getAnnotations()) {
                if (ann instanceof PDAnnotationFileAttachment fa) {
                    final ObjectNode attachNode = mapper.createObjectNode();
                    attachNode.put("Name", fa.getAttachmentName());
                    attachNode.put("Description", fa.getContents());
                    attachments.add(attachNode);
                }
            }
        }
        other.set("Attachments", attachments);

        final PDDocumentNameDictionary namesDict = catalog.getNames();
        final ArrayNode jsArray = mapper.createArrayNode();

        if (namesDict != null) {
            final PDJavascriptNameTreeNode jsDict = namesDict.getJavaScript();
            if (jsDict != null) {
                try {
                    final Map<String, PDActionJavaScript> jsMap = jsDict.getNames();
                    for (Map.Entry<String, PDActionJavaScript> e : jsMap.entrySet()) {
                        final ObjectNode jsNode = mapper.createObjectNode();
                        jsNode.put("JS Name", e.getKey());
                        final PDActionJavaScript js = e.getValue();
                        if (js != null) {
                            final String code = js.getAction();
                            if (code != null) {
                                jsNode.put("JS Script Length", code.length());
                            }
                        }
                        jsArray.add(jsNode);
                    }
                } catch (IOException e) {
                    log.error("JS error", e);
                }
            }
        }
        other.set("JavaScript", jsArray);

        final PDOptionalContentProperties ocProps = catalog.getOCProperties();
        final ArrayNode layers = mapper.createArrayNode();
        if (ocProps != null) {
            for (PDOptionalContentGroup ocg : ocProps.getOptionalContentGroups()) {
                final ObjectNode layerNode = mapper.createObjectNode();
                layerNode.put("Name", ocg.getName());
                layers.add(layerNode);
            }
        }
        other.set("Layers", layers);

        final PDStructureTreeRoot structRoot = catalog.getStructureTreeRoot();
        try {
            if (structRoot != null) {
                other.set("StructureTree", exploreStructureTree(structRoot.getKids()));
            }
        } catch (Exception e) {
            log.error("Structure error", e);
        }

        compliancy.put("IsPDF/ACompliant", checkForStandard(doc, "PDF/A"));
        compliancy.put("IsPDF/XCompliant", checkForStandard(doc, "PDF/X"));
        compliancy.put("IsPDF/ECompliant", checkForStandard(doc, "PDF/E"));
        compliancy.put("IsPDF/VTCompliant", checkForStandard(doc, "PDF/VT"));
        compliancy.put("IsPDF/UACompliant", checkForStandard(doc, "PDF/UA"));
        compliancy.put("IsPDF/BCompliant", checkForStandard(doc, "PDF/B"));
        compliancy.put("IsPDF/SECCompliant", checkForStandard(doc, "PDF/SEC"));

        final ArrayNode bookmarks = mapper.createArrayNode();
        final PDOutlineNode outline = catalog.getDocumentOutline();
        if (outline != null) {
            for (PDOutlineItem child : outline.children()) {
                addOutlinesToArray(child, bookmarks);
            }
        }
        other.set("Bookmarks/Outline/TOC", bookmarks);

        String xmp = null;
        final PDMetadata pdMeta = catalog.getMetadata();
        if (pdMeta != null) {
            try (final COSInputStream is = pdMeta.createInputStream()) {
                final DomXmpParser parser = new DomXmpParser();
                final XMPMetadata xmpMeta = parser.parse(is);
                final ByteArrayOutputStream os = new ByteArrayOutputStream();
                new XmpSerializer().serialize(xmpMeta, os, true);
                xmp = new String(os.toByteArray(), StandardCharsets.UTF_8);
            } catch (XmpParsingException e) {
                log.debug("XMP parse failed: {}", e.getMessage());
                try (final COSInputStream is = pdMeta.createInputStream()) {
                    xmp = new String(is.readAllBytes(), StandardCharsets.UTF_8);
                }
            } catch (IOException e) {
                log.error("XMP error", e);
            }
        }
        other.put("XMPMetadata", xmp);

        encryption.put("IsEncrypted", doc.isEncrypted());
        if (doc.isEncrypted()) {
            final PDEncryption enc = doc.getEncryption();
            encryption.put("EncryptionAlgorithm", enc.getFilter());
            encryption.put("KeyLength", enc.getLength());
        }

        final ObjectNode perms = mapper.createObjectNode();
        setNodePermissions(doc, perms);

        final ObjectNode pages = mapper.createObjectNode();
        for (int i = 0; i < doc.getNumberOfPages(); i++) {
            final ObjectNode pageInfo = mapper.createObjectNode();
            final PDPage page = doc.getPage(i);
            final PDRectangle mediaBox = page.getMediaBox();

            final float w = mediaBox.getWidth();
            final float h = mediaBox.getHeight();
            final ObjectNode sizeInfo = mapper.createObjectNode();
            getDimensionInfo(sizeInfo, w, h);
            sizeInfo.put("Standard Page", getPageSize(w, h));
            pageInfo.set("Size", sizeInfo);

            pageInfo.put("Rotation", page.getRotation());
            pageInfo.put("Page Orientation", getPageOrientation(w, h));

            pageInfo.put("MediaBox", mediaBox.toString());
            putBoxInfo(pageInfo, "CropBox", page.getCropBox());
            putBoxInfo(pageInfo, "BleedBox", page.getBleedBox());
            putBoxInfo(pageInfo, "TrimBox", page.getTrimBox());
            putBoxInfo(pageInfo, "ArtBox", page.getArtBox());

            final PDFTextStripper stripper = new PDFTextStripper();
            stripper.setStartPage(i + 1);
            stripper.setEndPage(i + 1);
            pageInfo.put("Text Characters Count", stripper.getText(doc).length());

            final List<PDAnnotation> anns = page.getAnnotations();
            int subtypeCount = 0;
            int contentsCount = 0;
            for (PDAnnotation ann : anns) {
                if (ann.getSubtype() != null) subtypeCount++;
                if (ann.getContents() != null) contentsCount++;
            }
            final ObjectNode annNode = mapper.createObjectNode();
            annNode.put("AnnotationsCount", anns.size());
            annNode.put("SubtypeCount", subtypeCount);
            annNode.put("ContentsCount", contentsCount);
            pageInfo.set("Annotations", annNode);

            final ArrayNode images = mapper.createArrayNode();
            final PDResources res = page.getResources();
            for (COSName name : res.getXObjectNames()) {
                final PDXObject xobj = res.getXObject(name);
                if (xobj instanceof PDImageXObject img) {
                    final ObjectNode imgNode = mapper.createObjectNode();
                    imgNode.put("Width", img.getWidth());
                    imgNode.put("Height", img.getHeight());
                    if (img.getMetadata() != null && img.getMetadata().getFile() != null
                            && img.getMetadata().getFile().getFile() != null) {
                        imgNode.put("Name", img.getMetadata().getFile().getFile());
                    }
                    if (img.getColorSpace() != null) {
                        imgNode.put("ColorSpace", img.getColorSpace().getName());
                    }
                    images.add(imgNode);
                }
            }
            pageInfo.set("Images", images);

            final ArrayNode links = mapper.createArrayNode();
            final Set<String> uris = new HashSet<>();
            for (PDAnnotation ann : anns) {
                if (ann instanceof PDAnnotationLink link) {
                    if (link.getAction() instanceof PDActionURI uriAction) {
                        uris.add(uriAction.getURI());
                    }
                }
            }
            for (String uri : uris) {
                final ObjectNode linkNode = mapper.createObjectNode();
                linkNode.put("URI", uri);
                links.add(linkNode);
            }
            pageInfo.set("Links", links);

            final ArrayNode fonts = mapper.createArrayNode();
            final Map<String, ObjectNode> fontMap = new HashMap<>();
            for (COSName fontName : res.getFontNames()) {
                final PDFont font = res.getFont(fontName);
                final ObjectNode fontNode = mapper.createObjectNode();
                fontNode.put("IsEmbedded", font.isEmbedded());
                fontNode.put("Name", font.getName());
                fontNode.put("Subtype", font.getType());

                final PDFontDescriptor desc = font.getFontDescriptor();
                if (desc != null) {
                    fontNode.put("ItalicAngle", desc.getItalicAngle());
                    final int flags = desc.getFlags();
                    fontNode.put("IsItalic", (flags & 1) != 0);
                    fontNode.put("IsBold", (flags & 64) != 0);
                    fontNode.put("IsFixedPitch", (flags & 2) != 0);
                    fontNode.put("IsSerif", (flags & 4) != 0);
                    fontNode.put("IsSymbolic", (flags & 8) != 0);
                    fontNode.put("IsScript", (flags & 16) != 0);
                    fontNode.put("IsNonsymbolic", (flags & 32) != 0);
                    fontNode.put("FontFamily", desc.getFontFamily());
                    fontNode.put("FontWeight", desc.getFontWeight());
                }

                final String key = fontNode.toString();
                if (fontMap.containsKey(key)) {
                    final ObjectNode existing = fontMap.get(key);
                    existing.put("Count", existing.get("Count").asInt() + 1);
                } else {
                    fontNode.put("Count", 1);
                    fontMap.put(key, fontNode);
                }
            }
            for (ObjectNode fn : fontMap.values()) fonts.add(fn);
            pageInfo.set("Fonts", fonts);

            final ArrayNode colorSpaces = mapper.createArrayNode();
            for (COSName csName : res.getColorSpaceNames()) {
                final PDColorSpace cs = res.getColorSpace(csName);
                if (cs instanceof PDICCBased icc) {
                    final ObjectNode iccNode = mapper.createObjectNode();
                    iccNode.put("ICC Profile Length", icc.getPDStream().toByteArray().length);
                    colorSpaces.add(iccNode);
                }
            }
            pageInfo.set("Color Spaces & ICC Profiles", colorSpaces);

            final ObjectNode xobjCounts = mapper.createObjectNode();
            final Map<String, Integer> xobjMap = new HashMap<>();
            for (COSName xobjName : res.getXObjectNames()) {
                final PDXObject xobj = res.getXObject(xobjName);
                final String type = xobj instanceof PDImageXObject ? "Image" :
                                   xobj instanceof PDFormXObject ? "Form" : "Other";
                xobjMap.put(type, xobjMap.getOrDefault(type, 0) + 1);
            }
            for (Map.Entry<String, Integer> e : xobjMap.entrySet()) {
                xobjCounts.put(e.getKey(), e.getValue());
            }
            pageInfo.set("XObjectCounts", xobjCounts);

            final ArrayNode media = mapper.createArrayNode();
            for (PDAnnotation ann : anns) {
                if ("RichMedia".equals(ann.getSubtype())) {
                    media.add(mapper.createObjectNode());
                }
            }
            pageInfo.set("Multimedia", media);

            pages.set("Page " + (i + 1), pageInfo);
        }

        root.set("BasicInfo", basic);
        root.set("DocumentInfo", docInfo);
        root.set("Compliancy", compliancy);
        root.set("Encryption", encryption);
        root.set("Permissions", perms);
        root.set("Other", other);
        root.set("PerPageInfo", pages);

        final String json = mapper.writerWithDefaultPrettyPrinter().writeValueAsString(root);
        return WebResponseUtils.bytesToWebResponse(
                json.getBytes(StandardCharsets.UTF_8),
                "response.json",
                MediaType.APPLICATION_JSON);
    } catch (Exception e) {
        log.error("Processing error", e);
        return null;
    }
}

public static boolean isBlankImage(BufferedImage img, int threshold, 
                                  double whitePercent, int blurSize) {
    if (img == null) {
        log.info("Null image");
        return false;
    }

    int whitePixels = 0;
    final int total = img.getWidth() * img.getHeight();

    for (int y = 0; y < img.getHeight(); y++) {
        for (int x = 0; x < img.getWidth(); x++) {
            final int color = img.getRGB(x, y) & 0xFF;
            if (color >= 255 - threshold) whitePixels++;
        }
    }

    final double percent = (whitePixels / (double) total) * 100;
    log.info("White pixels: {:.2f}%", percent);
    return percent >= whitePercent;
}

@PostMapping(consumes = "multipart/form-data", value = "/filter-page-count")
@Operation(
        summary = "Checks if a PDF is greater, less or equal to a setPageCount",
        description = "Input:PDF Output:Boolean Type:SISO")
public ResponseEntity<byte[]> pageCount(@ModelAttribute PDFComparisonAndCount req) 
        throws IOException, InterruptedException {
    final MultipartFile file = req.getFileInput();
    final int count = req.getPageCount();
    final String comp = req.getComparator();

    final PDDocument doc = pdfDocumentFactory.load(file);
    final int actual = doc.getNumberOfPages();

    boolean valid = false;
    switch (comp) {
        case "Greater": valid = actual > count; break;
        case "Equal": valid = actual == count; break;
        case "Less": valid = actual < count; break;
        default: throw ExceptionUtils.createInvalidArgumentException("comparator", comp);
    }

    return valid ? WebResponseUtils.multiPartFileToWebResponse(file) : null;
}

private void handleSplitBySize(PDDocument srcDoc, long maxBytes, 
                              ZipOutputStream zipOut, String baseName) throws IOException {
    log.debug("Splitting by size: maxBytes={}", maxBytes);
    PDDocument currDoc = pdfDocumentFactory.createNewDocumentBasedOnOldDocument(srcDoc);
    int part = 1;
    int total = srcDoc.getNumberOfPages();
    int added = 0;
    final int checkFreq = 5;

    for (int i = 0; i < total; i++) {
        final PDPage page = srcDoc.getPage(i);
        log.debug("Processing page {}/{}", i + 1, total);
        currDoc.addPage(new PDPage(page.getCOSObject()));
        added++;

        final boolean check = (added % checkFreq == 0) || (i == total - 1) || (added >= 20);
        if (check) {
            log.debug("Size check after {} pages", added);
            final ByteArrayOutputStream tmp = new ByteArrayOutputStream();
            currDoc.save(tmp);
            final long size = tmp.size();
            log.debug("Current size: {} (max {})", size, maxBytes);

            if (size > maxBytes) {
                if (currDoc.getNumberOfPages() > 1) {
                    currDoc.removePage(currDoc.getNumberOfPages() - 1);
                    i--;
                    log.debug("Removed last page");
                }
                log.debug("Saving part {} with {} pages", part, currDoc.getNumberOfPages());
                saveDocumentToZip(currDoc, zipOut, baseName, part++);
                currDoc = new PDDocument();
                added = 0;
            } else if (i < total - 1) {
                if (size < maxBytes * 0.75 && added > 0) {
                    final int lookahead = Math.min(5, total - i - 1);
                    if (lookahead > 0) {
                        log.debug("Testing {} pages", lookahead);
                        final PDDocument testDoc = new PDDocument();
                        for (int j = 0; j < currDoc.getNumberOfPages(); j++) {
                            testDoc.addPage(new PDPage(currDoc.getPage(j).getCOSObject()));
                        }

                        int extra = 0;
                        for (int j = 0; j < lookahead; j++) {
                            final int idx = i + 1 + j;
                            testDoc.addPage(new PDPage(srcDoc.getPage(idx).getCOSObject()));
                            final ByteArrayOutputStream testStream = new ByteArrayOutputStream();
                            testDoc.save(testStream);
                            if (testStream.size() <= maxBytes) {
                                log.debug("Can add page {}", idx + 1);
                                extra++;
                            } else {
                                log.debug("Cannot add page {}", idx + 1);
                                break;
                            }
                        }
                        testDoc.close();

                        if (extra > 0) {
                            log.debug("Adding {} pages", extra);
                            for (int j = 0; j < extra; j++) {
                                final int idx = i + 1 + j;
                                currDoc.addPage(new PDPage(srcDoc.getPage(idx).getCOSObject()));
                            }
                            i += extra;
                            added += extra;
                        }
                    }
                }
            }
        }
    }

    if (currDoc.getNumberOfPages() > 0) {
        log.debug("Saving final part {} with {} pages", part, currDoc.getNumberOfPages());
        saveDocumentToZip(currDoc, zipOut, baseName, part);
    }
    log.debug("Split complete: {} parts", part);
}

public static File convertMultipartFileToFile(MultipartFile mFile) throws IOException {
    String tmpDir = System.getenv("STIRLING_TEMPFILES_DIRECTORY");
    if (tmpDir == null || tmpDir.isEmpty()) {
        tmpDir = System.getProperty("stirling.tempfiles.directory");
    }

    final File file;
    if (tmpDir != null && !tmpDir.isEmpty()) {
        final Path dir = Path.of(tmpDir);
        if (!Files.exists(dir)) Files.createDirectories(dir);
        file = Files.createTempFile(dir, "stirling-pdf-", null).toFile();
    } else {
        final Path dir = Path.of(System.getProperty("java.io.tmpdir"), "stirling-pdf");
        if (!Files.exists(dir)) Files.createDirectories(dir);
        file = Files.createTempFile(dir, "stirling-pdf-", null).toFile();
    }

    try (InputStream in = mFile.getInputStream();
         FileOutputStream out = new FileOutputStream(file)) {
        final byte[] buf = new byte[8192];
        int len;
        while ((len = in.read(buf)) != -1) {
            out.write(buf, 0, len);
        }
    }
    return file;
}

private void cleanupTempFiles() {
    try {
        final Set<Path> files = registry.getAllRegisteredFiles();
        int deleted = 0;

        for (Path file : files) {
            try {
                if (Files.exists(file)) {
                    Files.deleteIfExists(file);
                    deleted++;
                }
            } catch (IOException e) {
                log.warn("Delete failed: {}", file, e);
            }
        }

        final Set<Path> dirs = registry.getTempDirectories();
        for (Path dir : dirs) {
            try {
                if (Files.exists(dir)) {
                    GeneralUtils.deleteDirectory(dir);
                    deleted++;
                }
            } catch (IOException e) {
                log.warn("Delete failed: {}", dir, e);
            }
        }

        log.info("Deleted {} temp items", deleted);
        registry.clear();
    } catch (Exception e) {
        log.error("Cleanup error", e);
    }
}

private Object convertAttrValue(String attributeName, Object originAttrValue) {
    Object attrValue = originAttrValue;

    try {
        if (originAttrValue instanceof ObjectName) {
            attrValue = String.valueOf(originAttrValue);
        } else if (attrValue instanceof CompositeData) {
            CompositeData compositeData = (CompositeData) attrValue;
            attrValue = convertCompositeData(attributeName, compositeData);
        } else if (attrValue instanceof CompositeData[]) {
            CompositeData[] compositeDataArray = (CompositeData[]) attrValue;
            List<Map<String, Object>> dataList = new ArrayList<>(compositeDataArray.length);
            for (CompositeData compositeData : compositeDataArray) {
                dataList.add(convertCompositeData(attributeName, compositeData));
            }
            attrValue = dataList;
        } else if (attrValue instanceof TabularData) {
            TabularData tabularData = (TabularData) attrValue;
            Collection<CompositeData> compositeDataList = (Collection<CompositeData>) tabularData.values();
            List<Map<String, Object>> dataList = new ArrayList<>(compositeDataList.size());
            for (CompositeData compositeData : compositeDataList) {
                dataList.add(convertCompositeData(attributeName, compositeData));
            }
            attrValue = dataList;
        }
    } catch (Throwable e) {
        log.error("convert mbean attribute error, attribute: {}={}", attributeName, originAttrValue, e);
        attrValue = String.valueOf(originAttrValue);
    }
    return attrValue;
}

public static int getItemCount(ResultModel model) {
    if (model instanceof Countable) {
        return ((Countable) model).size();
    }

    Class modelClass = model.getClass();
    List<Field> fields = modelFieldMap.get(modelClass.getName());
    if (fields == null) {
        fields = new ArrayList<>();
        Field[] declaredFields = modelClass.getDeclaredFields();
        for (Field field : declaredFields) {
            Class<?> fieldClass = field.getType();
            if (Collection.class.isAssignableFrom(fieldClass)
                    || Map.class.isAssignableFrom(fieldClass)
                    || Countable.class.isAssignableFrom(fieldClass)
                    || fieldClass.isArray()) {
                field.setAccessible(true);
                fields.add(field);
            }
        }
        List<Field> oldFields = modelFieldMap.putIfAbsent(modelClass.getName(), fields);
        if (oldFields != null) {
            fields = oldFields;
        }
    }

    int count = 0;
    try {
        for (Field field : fields) {
            if (!field.isAccessible()) {
                field.setAccessible(true);
            }
            Object value = field.get(model);
            if (value != null) {
                if (value instanceof Collection) {
                    count += ((Collection) value).size();
                } else if (value.getClass().isArray()) {
                    count += Array.getLength(value);
                } else if (value instanceof Map) {
                    count += ((Map) value).size();
                } else if (value instanceof Countable) {
                    count += ((Countable) value).size();
                }
            }
        }
    } catch (Exception e) {
        log.error("get item count of result model failed, model: {}", JSON.toJSONString(model), e);
    }

    return count > 0 ? count : 1;
}

public void evictSessions() {
    long now = System.currentTimeMillis();
    List<Session> toClose = new ArrayList<>();
    for (Session session : sessions.values()) {
        if (now - session.getLastAccessTime() > sessionTimeoutMillis && session.getForegroundJob() == null) {
            toClose.add(session);
        }
        evictConsumers(session);
    }
    for (Session session : toClose) {
        Job job = session.getForegroundJob();
        if (job != null) {
            job.interrupt();
        }
        long timeOutInMinutes = sessionTimeoutMillis / 1000 / 60;
        String reason = "session is inactive for " + timeOutInMinutes + " min(s).";
        SharingResultDistributor resultDistributor = session.getResultDistributor();
        if (resultDistributor != null) {
            resultDistributor.appendResult(new MessageModel(reason));
        }
        this.removeSession(session.getSessionId());
        log.info("Removing inactive session: {}, last access time: {}", session.getSessionId(), session.getLastAccessTime());
    }
}

