private ApiResponse processExecRequest(ApiRequest apiRequest, Session session) {
    boolean oneTimeAccess = session.get(ONETIME_SESSION_KEY) != null;

    try {
        String commandLine = apiRequest.getCommand();
        Map<String, Object> body = new TreeMap<>();
        body.put("command", commandLine);

        ApiResponse response = new ApiResponse();
        response.setSessionId(session.getSessionId()).setBody(body);

        if (!session.tryLock()) {
            response.setState(ApiState.REFUSED).setMessage("Another command is executing.");
            return response;
        }

        int lock = session.getLock();
        PackingResultDistributor packingResultDistributor = null;
        Job job = null;
        try {
            Job foregroundJob = session.getForegroundJob();
            if (foregroundJob != null) {
                response.setState(ApiState.REFUSED).setMessage("Another job is running.");
                log.info("Another job is running, jobId: {}", foregroundJob.id());
                return response;
            }

            packingResultDistributor = new PackingResultDistributorImpl(session);
            job = this.createJob(commandLine, session, packingResultDistributor);
            session.setForegroundJob(job);
            updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);

            job.run();
        } catch (Throwable e) {
            log.error("Exec command failed: {}, command: {}", e.getMessage(), commandLine, e);
            response.setState(ApiState.FAILED).setMessage("Exec command failed:" + e.getMessage());
            return response;
        } finally {
            if (session.getLock() == lock) {
                session.unLock();
            }
        }

        Integer timeout = apiRequest.getExecTimeout();
        if (timeout == null || timeout <= 0) {
            timeout = DEFAULT_EXEC_TIMEOUT;
        }
        boolean timeExpired = !waitForJob(job, timeout);
        if (timeExpired) {
            log.warn("Job exceeded time limit, force interrupt, jobId: {}", job.id());
            job.interrupt();
            response.setState(ApiState.INTERRUPTED).setMessage("Job exceeded time limit, force interrupt");
        } else {
            response.setState(ApiState.SUCCEEDED);
        }

        body.put("jobId", job.id());
        body.put("jobStatus", job.status());
        body.put("timeExpired", timeExpired);
        if (timeExpired) {
            body.put("timeout", timeout);
        }
        body.put("results", packingResultDistributor.getResults());

        response.setSessionId(session.getSessionId()).setBody(body);
        return response;
    } finally {
        if (oneTimeAccess) {
            sessionManager.removeSession(session.getSessionId());
        }
    }
}

private String renderLoggerInfo(Map<String, Map<String, Object>> loggerInfos, int width) {
    StringBuilder sb = new StringBuilder(8192);

    for (Map.Entry<String, Map<String, Object>> entry : loggerInfos.entrySet()) {
        Map<String, Object> info = entry.getValue();

        TableElement table = new TableElement(2, 10).leftCellPadding(1).rightCellPadding(1);
        TableElement appendersTable = new TableElement().rightCellPadding(1);

        Class<?> clazz = (Class<?>) info.get(LoggerHelper.clazz);
        table.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.name)))
             .row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label("" + clazz.getName()))
             .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.classLoader)))
             .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.classLoaderHash)))
             .row(label(LoggerHelper.level).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.level)));

        if (info.get(LoggerHelper.effectiveLevel) != null) {
            table.row(label(LoggerHelper.effectiveLevel).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.effectiveLevel)));
        }

        if (info.get(LoggerHelper.config) != null) {
            table.row(label(LoggerHelper.config).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.config)));
        }

        table.row(label(LoggerHelper.additivity).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.additivity)))
             .row(label(LoggerHelper.codeSource).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.codeSource)));

        @SuppressWarnings("unchecked")
        List<Map<String, Object>> appenders = (List<Map<String, Object>>) info.get(LoggerHelper.appenders);
        if (appenders != null && !appenders.isEmpty()) {
            for (Map<String, Object> appenderInfo : appenders) {
                Class<?> appenderClass = (Class<?>) appenderInfo.get(LoggerHelper.clazz);

                appendersTable.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + appenderInfo.get(LoggerHelper.name)));
                appendersTable.row(label(LoggerHelper.clazz), label("" + appenderClass.getName()));
                appendersTable.row(label(LoggerHelper.classLoader), label("" + info.get(LoggerHelper.classLoader)));
                appendersTable.row(label(LoggerHelper.classLoaderHash), label("" + info.get(LoggerHelper.classLoaderHash)));
                if (appenderInfo.get(LoggerHelper.file) != null) {
                    appendersTable.row(label(LoggerHelper.file), label("" + appenderInfo.get(LoggerHelper.file)));
                }
                if (appenderInfo.get(LoggerHelper.target) != null) {
                    appendersTable.row(label(LoggerHelper.target), label("" + appenderInfo.get(LoggerHelper.target)));
                }
                if (appenderInfo.get(LoggerHelper.blocking) != null) {
                    appendersTable.row(label(LoggerHelper.blocking), label("" + appenderInfo.get(LoggerHelper.blocking)));
                }
                if (appenderInfo.get(LoggerHelper.appenderRef) != null) {
                    appendersTable.row(label(LoggerHelper.appenderRef), label("" + appenderInfo.get(LoggerHelper.appenderRef)));
                }
            }
            table.row(label("appenders").style(Decoration.bold.bold()), appendersTable);
        }

        sb.append(RenderUtil.render(table, width)).append('\n');
    }
    return sb.toString();
}

private void renderNode(StringBuilder sb, TraceNode node, Ansi highlighted) {
    if (isPrintCost && node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        String costStr = renderCost(methodNode);
        if (node == maxCostNode) {
            sb.append(highlighted.a(costStr).reset().toString());
        } else {
            sb.append(costStr);
        }
    }

    if (node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        sb.append(methodNode.getClassName()).append(":").append(methodNode.getMethodName()).append("()");
        if (methodNode.getLineNumber() != -1) {
            sb.append(" #").append(methodNode.getLineNumber());
        }
    } else if (node instanceof ThreadNode) {
        ThreadNode threadNode = (ThreadNode) node;
        sb.append(String.format(
            "ts=%s;thread_name=%s;id=%d;is_daemon=%s;priority=%d;TCCL=%s",
            DateUtils.formatDateTime(threadNode.getTimestamp()),
            threadNode.getThreadName(),
            threadNode.getThreadId(),
            threadNode.isDaemon(),
            threadNode.getPriority(),
            threadNode.getClassloader()
        ));

        if (threadNode.getTraceId() != null) {
            sb.append(";trace_id=").append(threadNode.getTraceId());
        }
        if (threadNode.getRpcId() != null) {
            sb.append(";rpc_id=").append(threadNode.getRpcId());
        }
    } else if (node instanceof ThrowNode) {
        ThrowNode throwNode = (ThrowNode) node;
        sb.append("throw:").append(throwNode.getException())
          .append(" #").append(throwNode.getLineNumber())
          .append(" [").append(throwNode.getMessage()).append("]");
    } else {
        throw new UnsupportedOperationException("unknown trace node: " + node.getClass());
    }
}

private static String processInlineImages(final String htmlContent, final EmailContent emailContent) {
    if (htmlContent == null || emailContent == null) {
        return htmlContent;
    }

    final Map<String, EmailAttachment> contentIdMap = new HashMap<>();
    for (final EmailAttachment attachment : emailContent.getAttachments()) {
        if (attachment.isEmbedded()
                && attachment.getContentId() != null
                && attachment.getData() != null) {
            contentIdMap.put(attachment.getContentId(), attachment);
        }
    }

    if (contentIdMap.isEmpty()) {
        return htmlContent;
    }

    final Pattern cidPattern = Pattern.compile(
            "(?i)<img[^>]*\\ssrc\\s*=\\s*['\"]cid:([^'\"]+)['\"][^>]*>",
            Pattern.CASE_INSENSITIVE);
    final Matcher matcher = cidPattern.matcher(htmlContent);
    final StringBuffer result = new StringBuffer();

    while (matcher.find()) {
        final String contentId = matcher.group(1);
        final EmailAttachment attachment = contentIdMap.get(contentId);

        if (attachment != null && attachment.getData() != null) {
            String mimeType = attachment.getContentType();
            if (mimeType == null || mimeType.isEmpty()) {
                final String filename = attachment.getFilename();
                if (filename != null) {
                    final String lowerName = filename.toLowerCase();
                    if (lowerName.endsWith(".png")) {
                        mimeType = "image/png";
                    } else if (lowerName.endsWith(".jpg") || lowerName.endsWith(".jpeg")) {
                        mimeType = "image/jpeg";
                    } else if (lowerName.endsWith(".gif")) {
                        mimeType = "image/gif";
                    } else if (lowerName.endsWith(".bmp")) {
                        mimeType = "image/bmp";
                    } else {
                        mimeType = "image/png";
                    }
                } else {
                    mimeType = "image/png";
                }
            }

            final String base64Data = Base64.getEncoder().encodeToString(attachment.getData());
            final String dataUri = "data:" + mimeType + ";base64," + base64Data;
            final String replacement = matcher.group(0).replaceFirst("cid:" + Pattern.quote(contentId), dataUri);
            matcher.appendReplacement(result, Matcher.quoteReplacement(replacement));
        } else {
            matcher.appendReplacement(result, Matcher.quoteReplacement(matcher.group(0)));
        }
    }
    matcher.appendTail(result);

    return result.toString();
}

private static String generateEnhancedEmailHtml(final EmailContent content, final EmlToPdfRequest request) {
    final StringBuilder html = new StringBuilder();

    html.append("<!DOCTYPE html>\n")
        .append("<html><head><meta charset=\"UTF-8\">\n")
        .append("<title>").append(escapeHtml(content.getSubject())).append("</title>\n")
        .append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n</head><body>\n")
        .append("<div class=\"email-container\">\n")
        .append("<div class=\"email-header\">\n")
        .append("<h1>").append(escapeHtml(content.getSubject())).append("</h1>\n")
        .append("<div class=\"email-meta\">\n")
        .append("<div><strong>From:</strong> ").append(escapeHtml(content.getFrom())).append("</div>\n")
        .append("<div><strong>To:</strong> ").append(escapeHtml(content.getTo())).append("</div>\n");

    if (content.getDate() != null) {
        html.append("<div><strong>Date:</strong> ")
            .append(formatEmailDate(content.getDate()))
            .append("</div>\n");
    }
    html.append("</div></div>\n")
        .append("<div class=\"email-body\">\n");

    if (content.getHtmlBody() != null && !content.getHtmlBody().trim().isEmpty()) {
        html.append(processEmailHtmlBody(content.getHtmlBody(), content));
    } else if (content.getTextBody() != null && !content.getTextBody().trim().isEmpty()) {
        html.append("<div class=\"text-body\">")
            .append(convertTextToHtml(content.getTextBody()))
            .append("</div>");
    } else {
        html.append("<div class=\"no-content\"><p><em>No content available</em></p></div>");
    }
    html.append("</div>\n");

    final int attachmentCount = content.getAttachmentCount();
    final List<EmailAttachment> attachments = content.getAttachments();
    if (attachmentCount > 0 || !attachments.isEmpty()) {
        html.append("<div class=\"attachment-section\">\n")
            .append("<h3>Attachments (")
            .append(attachmentCount > 0 ? attachmentCount : attachments.size())
            .append(")</h3>\n");

        if (!attachments.isEmpty()) {
            for (final EmailAttachment attachment : attachments) {
                final String uniqueId = generateUniqueAttachmentId(attachment.getFilename());
                if (attachment.getEmbeddedFilename() == null) {
                    attachment.setEmbeddedFilename(attachment.getFilename());
                }

                html.append("<div class=\"attachment-item\" id=\"")
                    .append(uniqueId)
                    .append("\"><span class=\"attachment-icon\">")
                    .append(MimeConstants.ATTACHMENT_MARKER)
                    .append("</span> <span class=\"attachment-name\">")
                    .append(escapeHtml(safeMimeDecode(attachment.getFilename())))
                    .append("</span> <span class=\"attachment-details\">(")
                    .append(formatFileSize(attachment.getSizeBytes()));

                if (attachment.getContentType() != null && !attachment.getContentType().isEmpty()) {
                    html.append(", ").append(escapeHtml(attachment.getContentType()));
                }
                html.append(")</span></div>\n");
            }
        }

        final String note = request.isIncludeAttachments()
            ? "<p><em>Attachments are embedded in the file.</em></p>\n"
            : "<p><em>Attachment information displayed - files not included in PDF.</em></p>\n";
        html.append("<div class=\"attachment-info-note\">").append(note).append("</div>\n")
            .append("</div>\n");
    }

    html.append("</div>\n</body></html>");
    return html.toString();
}

