public List<PDDocument> splitPdfPages(
        final PDDocument document, 
        final int horizontalDivisions, 
        final int verticalDivisions) throws IOException {

    final List<PDDocument> splitDocuments = new ArrayList<>();

    for (final PDPage originalPage : document.getPages()) {
        final PDRectangle originalMediaBox = originalPage.getMediaBox();
        final float width = originalMediaBox.getWidth();
        final float height = originalMediaBox.getHeight();
        final float subPageWidth = width / horizontalDivisions;
        final float subPageHeight = height / verticalDivisions;

        final LayerUtility layerUtility = new LayerUtility(document);

        for (int i = 0; i < horizontalDivisions; i++) {
            for (int j = 0; j < verticalDivisions; j++) {
                final PDDocument subDoc = new PDDocument();
                final PDPage subPage = new PDPage(new PDRectangle(subPageWidth, subPageHeight));
                subDoc.addPage(subPage);

                final PDFormXObject form = layerUtility.importPageAsForm(
                    document, document.getPages().indexOf(originalPage));

                try (final PDPageContentStream contentStream = new PDPageContentStream(
                        subDoc, subPage, AppendMode.APPEND, true, true)) {
                    final float translateX = -subPageWidth * i;
                    final float translateY = -subPageHeight * (verticalDivisions - 1 - j);

                    contentStream.saveGraphicsState();
                    contentStream.addRect(0, 0, subPageWidth, subPageHeight);
                    contentStream.clip();
                    contentStream.transform(new Matrix(1, 0, 0, 1, translateX, translateY));
                    contentStream.drawForm(form);
                    contentStream.restoreGraphicsState();
                }

                splitDocuments.add(subDoc);
            }
        }
    }
    return splitDocuments;
}

public boolean isEndpointEnabled(final String endpoint) {
    final String original = endpoint;
    final String endpointToCheck = endpoint.startsWith("/") 
        ? endpoint.substring(1) 
        : endpoint;

    // Rule 1: Explicit flag wins
    final Boolean explicitStatus = endpointStatuses.get(endpointToCheck);
    if (Boolean.FALSE.equals(explicitStatus)) {
        log.debug("isEndpointEnabled('{}') -> false (explicitly disabled)", original);
        return false;
    }

    // Rule 2: Functional-group override
    for (final String group : endpointGroups.keySet()) {
        if (disabledGroups.contains(group) 
                && endpointGroups.get(group).contains(endpointToCheck)
                && !isToolGroup(group)) {
            log.debug(
                "isEndpointEnabled('{}') -> false (functional group '{}' disabled)",
                original, group);
            return false;
        }
    }

    // Rule 3: Tool-group fallback
    final Set<String> alternatives = endpointAlternatives.get(endpointToCheck);
    if (alternatives != null && !alternatives.isEmpty()) {
        final boolean hasEnabledToolGroup = alternatives.stream()
            .anyMatch(toolGroup -> !disabledGroups.contains(toolGroup));
        log.debug(
            "isEndpointEnabled('{}') -> {} (tool groups check)",
            original, hasEnabledToolGroup);
        return hasEnabledToolGroup;
    }

    // Rule 4: Single-dependency check
    for (final String group : endpointGroups.keySet()) {
        if (isToolGroup(group)
                && disabledGroups.contains(group)
                && endpointGroups.get(group).contains(endpointToCheck)) {
            log.debug(
                "isEndpointEnabled('{}') -> false (single tool group '{}' disabled, no alternatives)",
                original, group);
            return false;
        }
    }

    // Default
    final boolean enabled = !Boolean.FALSE.equals(explicitStatus);
    log.debug("isEndpointEnabled('{}') -> {} (default)", original, enabled);
    return enabled;
}

private void setupLoadHandler() {
    final long initStartTime = System.currentTimeMillis();
    log.info("Setting up load handler at: {}", initStartTime);

    client.addLoadHandler(new CefLoadHandlerAdapter() {
        @Override
        public void onLoadingStateChange(
                final CefBrowser browser,
                final boolean isLoading,
                final boolean canGoBack,
                final boolean canGoForward) {
            log.debug(
                "Loading state change - isLoading: {}, canGoBack: {}, canGoForward: {},"
                + " browserInitialized: {}, Time elapsed: {}ms",
                isLoading, canGoBack, canGoForward, browserInitialized,
                System.currentTimeMillis() - initStartTime);

            if (!isLoading && !browserInitialized) {
                log.info(
                    "Browser finished loading, preparing to initialize UI components");
                browserInitialized = true;
                SwingUtilities.invokeLater(() -> initUI(browser));
            }
        }
    });
}

private void initUI(final CefBrowser browser) {
    try {
        if (loadingWindow == null) return;

        log.info("Starting UI initialization sequence");
        loadingWindow.setVisible(false);
        loadingWindow.dispose();
        loadingWindow = null;
        log.info("Loading window disposed");

        // Reconfigure main frame
        frame.setVisible(false);
        frame.dispose();
        frame.setOpacity(1.0f);
        frame.setUndecorated(false);
        frame.pack();
        frame.setSize(UIScaling.scaleWidth(1280), UIScaling.scaleHeight(800));
        frame.setLocationRelativeTo(null);
        log.debug("Frame reconfigured");

        // Show main frame
        frame.setVisible(true);
        frame.requestFocus();
        frame.toFront();
        log.info("Main frame displayed and focused");

        // Schedule browser focus
        final Timer focusTimer = new Timer(100, e -> {
            try {
                browser.getUIComponent().requestFocus();
                log.info("Browser component focused");
            } catch (final Exception ex) {
                log.error("Error focusing browser", ex);
            }
        });
        focusTimer.setRepeats(false);
        focusTimer.start();

    } catch (final Exception e) {
        log.error("Error during UI initialization", e);
        cleanupUI();
    }
}

private void cleanupUI() {
    if (loadingWindow != null) {
        loadingWindow.dispose();
        loadingWindow = null;
    }
    if (frame != null) {
        frame.setVisible(true);
        frame.requestFocus();
    }
}

private void processWithTesseract(
        @NonNull final List<String> selectedLanguages,
        @NonNull final String ocrType,
        @NonNull final Path tempInputFile,
        @NonNull final Path tempOutputFile) throws IOException, InterruptedException {

    try (final TempDirectory tempDir = new TempDirectory(tempFileManager)) {
        final File tempOutputDir = new File(tempDir.getPath().toFile(), "output");
        final File tempImagesDir = new File(tempDir.getPath().toFile(), "images");
        final File finalOutputFile = new File(tempDir.getPath().toFile(), "final_output.pdf");

        tempOutputDir.mkdirs();
        tempImagesDir.mkdirs();

        final PDFMergerUtility merger = new PDFMergerUtility();
        merger.setDestinationFileName(finalOutputFile.toString());

        try (final PDDocument document = pdfDocumentFactory.load(tempInputFile.toFile())) {
            final PDFRenderer pdfRenderer = new PDFRenderer(document);
            final int pageCount = document.getNumberOfPages();

            for (int pageNum = 0; pageNum < pageCount; pageNum++) {
                final PDPage page = document.getPage(pageNum);
                final boolean hasText = checkForText(page);
                final boolean shouldOcr = determineOcrNeed(ocrType, hasText);
                final File pageOutputPath = new File(
                    tempOutputDir, String.format("page_%d.pdf", pageNum));

                if (shouldOcr) {
                    ocrPage(
                        pdfRenderer, pageNum, selectedLanguages, 
                        tempImagesDir, tempOutputDir, merger, pageOutputPath);
                } else {
                    saveOriginalPage(page, merger, pageOutputPath);
                }
            }
        }

        merger.mergeDocuments(null);
        Files.copy(
            finalOutputFile.toPath(), tempOutputFile,
            StandardCopyOption.REPLACE_EXISTING);
    }
}

private boolean checkForText(final PDPage page) throws IOException {
    try (final PDDocument tempDoc = new PDDocument()) {
        tempDoc.addPage(page);
        return !new PDFTextStripper().getText(tempDoc).trim().isEmpty();
    }
}

private boolean determineOcrNeed(final String ocrType, final boolean hasText) {
    return switch (ocrType) {
        case "skip-text" -> !hasText;
        case "force-ocr" -> true;
        default -> true;
    };
}

private void ocrPage(
        final PDFRenderer pdfRenderer,
        final int pageNum,
        final List<String> selectedLanguages,
        final File tempImagesDir,
        final File tempOutputDir,
        final PDFMergerUtility merger,
        final File pageOutputPath) throws IOException, InterruptedException {

    final BufferedImage image = pdfRenderer.renderImageWithDPI(pageNum, 300);
    final File imagePath = new File(tempImagesDir, String.format("page_%d.png", pageNum));
    ImageIO.write(image, "png", imagePath);

    final List<String> command = List.of(
        "tesseract",
        imagePath.toString(),
        new File(tempOutputDir, String.format("page_%d", pageNum)).toString(),
        "-l", String.join("+", selectedLanguages),
        "pdf"
    );

    final ProcessExecutorResult result = ProcessExecutor.getInstance(
        ProcessExecutor.Processes.TESSERACT).runCommandWithOutputHandling(command);

    if (result.getRc() != 0) {
        throw ExceptionUtils.createRuntimeException(
            "error.commandFailed",
            "{0} command failed with exit code: {1}",
            null, "Tesseract", result.getRc());
    }
    merger.addSource(pageOutputPath);
}

private void saveOriginalPage(
        final PDPage page,
        final PDFMergerUtility merger,
        final File pageOutputPath) throws IOException {
    try (final PDDocument pageDoc = new PDDocument()) {
        pageDoc.addPage(page);
        pageDoc.save(pageOutputPath);
        merger.addSource(pageOutputPath);
    }
}

private boolean processJWTLicensePayload(
        @NonNull final String payload, 
        @NonNull final LicenseContext context) {
    try {
        log.info("Processing license payload: {}", payload);
        final JSONObject licenseData = new JSONObject(payload);

        JSONObject licenseObj = licenseData.optJSONObject("license");
        if (licenseObj == null) {
            final String id = licenseData.optString("id", null);
            if (id == null) {
                log.error("License data not found in payload");
                return false;
            }
            licenseObj = licenseData;
        }

        final String licenseId = licenseObj.optString("id", "unknown");
        log.info("Processing license with ID: {}", licenseId);

        processLicenseAttributes(context, licenseObj);
        processPolicyInformation(licenseData, context);

        return true;
    } catch (final Exception e) {
        log.error("Error processing license payload: {}", e.getMessage(), e);
        return false;
    }
}

private void processLicenseAttributes(
        final LicenseContext context, 
        final JSONObject licenseObj) {
    context.isFloatingLicense = licenseObj.optBoolean("floating", false);
    context.maxMachines = licenseObj.optInt("maxMachines", 1);

    if (context.isFloatingLicense) {
        log.info(
            "Detected floating license with max machines: {}", 
            context.maxMachines);
    }

    final String expiryStr = licenseObj.optString("expiry", null);
    if (expiryStr != null && !"null".equals(expiryStr)) {
        final Instant expiry = Instant.parse(expiryStr);
        if (Instant.now().isAfter(expiry)) {
            log.error("License has expired on {}", expiryStr);
            throw new LicenseExpiredException();
        }
        log.info("License valid until {}", expiryStr);
    } else {
        log.info("License has no expiration date");
    }
}

private void processPolicyInformation(
        final JSONObject licenseData, 
        final LicenseContext context) {

    final JSONObject accountObj = licenseData.optJSONObject("account");
    if (accountObj != null) {
        final String accountId = accountObj.optString("id", "unknown");
        log.info("License belongs to account: {}", accountId);
        if (!ACCOUNT_ID.equals(accountId)) {
            log.warn("License account ID does not match expected account ID");
        }
    }

    final JSONObject policyObj = licenseData.optJSONObject("policy");
    if (policyObj == null) return;

    final String policyId = policyObj.optString("id", "unknown");
    log.info("License uses policy: {}", policyId);

    // Policy settings take precedence
    final boolean policyFloating = policyObj.optBoolean("floating", false);
    final int policyMaxMachines = policyObj.optInt("maxMachines", 1);

    if (policyFloating) {
        context.isFloatingLicense = true;
        context.maxMachines = policyMaxMachines;
        log.info(
            "Policy defines floating license with max machines: {}", 
            context.maxMachines);
    }

    final int users = policyObj.optInt("users", 1);
    context.isEnterpriseLicense = policyObj.optBoolean("isEnterprise", false);
    applicationProperties.getPremium().setMaxUsers(
        users > 0 ? users : getUsersFromMetadata(policyObj));
}

private int getUsersFromMetadata(final JSONObject policyObj) {
    final Object metadataObj = policyObj.opt("metadata");
    if (!(metadataObj instanceof JSONObject)) return 1;

    final JSONObject metadata = (JSONObject) metadataObj;
    final int users = metadata.optInt("users", 1);
    context.isEnterpriseLicense = metadata.optBoolean("isEnterprise", false);
    log.info("License allows for {} users (from metadata)", users);
    return users;
}

@PostMapping(value = "/add-page-numbers", consumes = "multipart/form-data")
@Operation(
        summary = "Add page numbers to a PDF document",
        description = "This operation takes an input PDF file and adds page numbers to it."
                + " Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> addPageNumbers(
        @ModelAttribute final AddPageNumbersRequest request) throws IOException {

    final MultipartFile file = request.getFileInput();
    final String customMargin = request.getCustomMargin();
    final int position = request.getPosition();
    int pageNumber = request.getStartingNumber();
    String pagesToNumber = request.getPagesToNumber();
    String customText = request.getCustomText();
    final float fontSize = request.getFontSize();
    final String fontType = request.getFontType();

    if (pagesToNumber == null || pagesToNumber.isEmpty()) {
        pagesToNumber = "all";
    }
    if (customText == null || customText.isEmpty()) {
        customText = "{n}";
    }

    try (final PDDocument document = pdfDocumentFactory.load(file)) {
        final float marginFactor = determineMarginFactor(customMargin);
        final List<Integer> pagesToNumberList = GeneralUtils.parsePageList(
            pagesToNumber.split(","), document.getNumberOfPages());

        for (final int i : pagesToNumberList) {
            addPageNumber(
                document, document.getPage(i), position, pageNumber++, 
                file.getOriginalFilename(), customText, fontSize, 
                fontType, marginFactor);
        }

        final ByteArrayOutputStream baos = new ByteArrayOutputStream();
        document.save(baos);
        return WebResponseUtils.bytesToWebResponse(
            baos.toByteArray(),
            Filenames.toSimpleFileName(file.getOriginalFilename())
                .replaceFirst("[.][^.]+$", "") + "_numbersAdded.pdf",
            MediaType.APPLICATION_PDF);
    }
}

private float determineMarginFactor(final String customMargin) {
    return switch (customMargin.toLowerCase()) {
        case "small" -> 0.02f;
        case "large" -> 0.05f;
        case "x-large" -> 0.075f;
        case "medium", default -> 0.035f;
    };
}

private void addPageNumber(
        final PDDocument document,
        final PDPage page,
        final int position,
        final int pageNumber,
        final String originalFilename,
        final String customText,
        final float fontSize,
        final String fontType,
        final float marginFactor) throws IOException {

    final PDRectangle pageSize = page.getMediaBox();
    final String text = customText
        .replace("{n}", String.valueOf(pageNumber))
        .replace("{total}", String.valueOf(document.getNumberOfPages()))
        .replace(
            "{filename}", 
            Filenames.toSimpleFileName(originalFilename).replaceFirst("[.][^.]+$", ""));

    final PDType1Font currentFont = switch (fontType.toLowerCase()) {
        case "courier" -> new PDType1Font(Standard14Fonts.FontName.COURIER);
        case "times" -> new PDType1Font(Standard14Fonts.FontName.TIMES_ROMAN);
        default -> new PDType1Font(Standard14Fonts.FontName.HELVETICA);
    };

    final Point2D.Float point = calculateTextPosition(position, pageSize, marginFactor, text, currentFont, fontSize);

    try (final PDPageContentStream contentStream = new PDPageContentStream(
            document, page, PDPageContentStream.AppendMode.APPEND, true, true)) {
        contentStream.beginText();
        contentStream.setFont(currentFont, fontSize);
        contentStream.newLineAtOffset(point.x, point.y);
        contentStream.showText(text);
        contentStream.endText();
    }
}

private Point2D.Float calculateTextPosition(
        final int position,
        final PDRectangle pageSize,
        final float marginFactor,
        final String text,
        final PDType1Font font,
        final float fontSize) {

    if (position == 5) { // Center position
        final float textWidth = font.getStringWidth(text) / 1000 * fontSize;
        final float ascent = font.getFontDescriptor().getAscent() / 1000 * fontSize;
        final float descent = font.getFontDescriptor().getDescent() / 1000 * fontSize;
        return new Point2D.Float(
            pageSize.getLowerLeftX() + (pageSize.getWidth() - textWidth) / 2,
            pageSize.getLowerLeftY() + (pageSize.getHeight() - (ascent + descent)) / 2
        );
    }

    final int xGroup = (position - 1) % 3;
    final int yGroup = 2 - (position - 1) / 3;

    final float x = switch (xGroup) {
        case 0 -> pageSize.getLowerLeftX() + marginFactor * pageSize.getWidth();
        case 1 -> pageSize.getLowerLeftX() + pageSize.getWidth() / 2;
        default -> pageSize.getUpperRightX() - marginFactor * pageSize.getWidth();
    };

    final float y = switch (yGroup) {
        case 0 -> pageSize.getLowerLeftY() + marginFactor * pageSize.getHeight();
        case 1 -> pageSize.getLowerLeftY() + pageSize.getHeight() / 2;
        default -> pageSize.getUpperRightY() - marginFactor * pageSize.getHeight();
    };

    return new Point2D.Float(x, y);
}

public int calculateDynamicQueueCapacity(
        final int baseCapacity, 
        final int minCapacity) {
    final ResourceMetrics metrics = latestMetrics.get();
    final ResourceStatus status = currentStatus.get();

    double capacityFactor = switch (status) {
        case OK -> 1.0;
        case WARNING -> 0.6;
        case CRITICAL -> 0.3;
    };

    if (metrics.memoryUsage > 0.8) {
        capacityFactor *= 0.5;
    }

    final int capacity = (int) Math.max(
        minCapacity, Math.ceil(baseCapacity * capacityFactor));

    log.debug(
        "Dynamic queue capacity: {} (base: {}, factor: {:.2f}, status: {})",
        capacity, baseCapacity, capacityFactor, status);

    return capacity;
}

public StreamCacheCreateFunction getStreamCacheFunction(final long contentSize) {
    final long maxMemory = Runtime.getRuntime().maxMemory();
    final long totalMemory = Runtime.getRuntime().totalMemory();
    final long freeMemory = Runtime.getRuntime().freeMemory();
    final long usedMemory = totalMemory - freeMemory;
    final double freeMemoryPercent = (double) (maxMemory - usedMemory) / maxMemory * 100;
    final long actualFreeMemory = maxMemory - usedMemory;

    log.debug(
        "Memory status - Free: {}MB ({}%), Used: {}MB, Max: {}MB",
        actualFreeMemory / (1024 * 1024),
        String.format("%.2f", freeMemoryPercent),
        usedMemory / (1024 * 1024),
        maxMemory / (1024 * 1024));

    if (freeMemoryPercent < MIN_FREE_MEMORY_PERCENTAGE
            || actualFreeMemory < MIN_FREE_MEMORY_BYTES) {
        log.debug(
            "Low memory detected ({}%), forcing file-based cache",
            String.format("%.2f", freeMemoryPercent));
        return createScratchFileCacheFunction(MemoryUsageSetting.setupTempFileOnly());
    }

    return contentSize < SMALL_FILE_THRESHOLD 
        ? handleSmallFile(contentSize)
        : handleLargeFile(contentSize);
}

private StreamCacheCreateFunction handleSmallFile(final long contentSize) {
    log.debug("Using memory-only cache for small document ({}KB)", contentSize / 1024);
    return IOUtils.createMemoryOnlyStreamCache();
}

private StreamCacheCreateFunction handleLargeFile(final long contentSize) {
    if (contentSize < LARGE_FILE_THRESHOLD) {
        log.debug(
            "Using mixed memory/file cache for medium document ({}MB)",
            contentSize / (1024 * 1024));
        return createScratchFileCacheFunction(MemoryUsageSetting.setupMixed(LARGE_FILE_USAGE));
    }
    log.debug("Using file-based cache for large document");
    return createScratchFileCacheFunction(MemoryUsageSetting.setupTempFileOnly());
}

@Override
public void onStopTrackingTouch(final SeekBar seekBar) {
    if (DEBUG) {
        Log.d(TAG, "onStopTrackingTouch() called with: seekBar = [" + seekBar + "]");
    }

    player.seekTo(seekBar.getProgress());
    if (player.getExoPlayer().getDuration() == seekBar.getProgress()) {
        player.getExoPlayer().play();
    }

    binding.playbackCurrentTime.setText(getTimeString(seekBar.getProgress()));
    animate(binding.currentDisplaySeek, false, 200, AnimationType.SCALE_AND_ALPHA);
    animate(binding.currentSeekbarPreviewThumbnail, false, 200, AnimationType.SCALE_AND_ALPHA);

    if (player.getCurrentState() == STATE_PAUSED_SEEK) {
        player.changeState(STATE_BUFFERING);
    }
    if (!player.isProgressLoopRunning()) {
        player.startProgressLoop();
    }

    showControlsThenHide();
}

@Override
public boolean onMenuItemClick(@NonNull final MenuItem menuItem) {
    if (DEBUG) {
        Log.d(TAG, "onMenuItemClick() called with: "
                + "menuItem = [" + menuItem + "], "
                + "menuItem.getItemId = [" + menuItem.getItemId() + "]");
    }

    if (menuItem.getGroupId() == POPUP_MENU_ID_QUALITY) {
        onQualityItemClick(menuItem);
        return true;
    } else if (menuItem.getGroupId() == POPUP_MENU_ID_AUDIO_TRACK) {
        onAudioTrackItemClick(menuItem);
        return true;
    } else if (menuItem.getGroupId() == POPUP_MENU_ID_PLAYBACK_SPEED) {
        final int speedIndex = menuItem.getItemId();
        final float speed = PLAYBACK_SPEEDS[speedIndex];

        player.setPlaybackSpeed(speed);
        binding.playbackSpeed.setText(formatSpeed(speed));
    }

    return false;
}

private void monitorSubscription(final ChannelInfo info) {
    final Consumer<Throwable> onError = (final Throwable throwable) -> {
        animate(binding.channelSubscribeButton, false, 100);
        showSnackBarError(new ErrorInfo(throwable, UserAction.SUBSCRIPTION_GET,
                "Get subscription status", currentInfo));
    };

    final Observable<List<SubscriptionEntity>> observable = subscriptionManager
            .subscriptionTable()
            .getSubscriptionFlowable(info.getServiceId(), info.getUrl())
            .toObservable();

    disposables.add(observable
            .observeOn(AndroidSchedulers.mainThread())
            .subscribe(getSubscribeUpdateMonitor(info), onError));

    disposables.add(observable
            .map(List::isEmpty)
            .distinctUntilChanged()
            .observeOn(AndroidSchedulers.mainThread())
            .subscribe(isEmpty -> updateSubscribeButton(!isEmpty), onError));

    disposables.add(observable
            .map(List::isEmpty)
            .distinctUntilChanged()
            .skip(1)
            .filter(x -> NotificationHelper.areNewStreamsNotificationsEnabled(requireContext()))
            .observeOn(AndroidSchedulers.mainThread())
            .subscribe(isEmpty -> {
                if (!isEmpty) {
                    showNotifySnackbar();
                }
            }, onError));
}

private void runOnVisible(final Consumer<AppCompatActivity> runnable) {
    getActivityContext().ifPresentOrElse(context -> {
        if (getLifecycle().getCurrentState().isAtLeast(Lifecycle.State.STARTED)) {
            context.runOnUiThread(() -> {
                runnable.accept(context);
                inFlight(false);
            });
        } else {
            getLifecycle().addObserver(new DefaultLifecycleObserver() {
                @Override
                public void onResume(@NonNull final LifecycleOwner owner) {
                    getLifecycle().removeObserver(this);
                    getActivityContext().ifPresentOrElse(ctx ->
                            ctx.runOnUiThread(() -> {
                                runnable.accept(ctx);
                                inFlight(false);
                            }),
                            () -> inFlight(false)
                    );
                }
            });
            if (Build.VERSION.SDK_INT < Build.VERSION_CODES.Q
                    && !context.isChangingConfigurations()) {
                final Intent i = new Intent(context, RouterActivity.class);
                i.setFlags(Intent.FLAG_ACTIVITY_REORDER_TO_FRONT);
                startActivity(i);
            }
        }
    }, () -> inFlight(false));
}

public static void showMetaInfoInTextView(@Nullable final List<MetaInfo> metaInfos,
        final TextView metaInfoTextView,
        final View metaInfoSeparator,
        final CompositeDisposable disposables) {
    final Context context = metaInfoTextView.getContext();
    if (metaInfos == null || metaInfos.isEmpty()
            || !PreferenceManager.getDefaultSharedPreferences(context).getBoolean(
            context.getString(R.string.show_meta_info_key), true)) {
        metaInfoTextView.setVisibility(View.GONE);
        metaInfoSeparator.setVisibility(View.GONE);
    } else {
        final StringBuilder stringBuilder = new StringBuilder();
        for (final MetaInfo metaInfo : metaInfos) {
            if (!isNullOrEmpty(metaInfo.getTitle())) {
                stringBuilder.append("<b>").append(metaInfo.getTitle()).append("</b>")
                        .append(Localization.DOT_SEPARATOR);
            }

            String content = metaInfo.getContent().getContent().trim();
            if (content.endsWith(".")) {
                content = content.substring(0, content.length() - 1);
            }
            stringBuilder.append(content);

            for (int i = 0; i < metaInfo.getUrls().size(); i++) {
                if (i == 0) {
                    stringBuilder.append(Localization.DOT_SEPARATOR);
                } else {
                    stringBuilder.append("<br/><br/>");
                }

                stringBuilder
                        .append("<a href=\"").append(metaInfo.getUrls().get(i)).append("\">")
                        .append(capitalizeIfAllUppercase(metaInfo.getUrlTexts().get(i).trim()))
                        .append("</a>");
            }
        }

        metaInfoSeparator.setVisibility(View.VISIBLE);
        TextLinkifier.fromHtml(metaInfoTextView, stringBuilder.toString(),
                HtmlCompat.FROM_HTML_SEPARATOR_LINE_BREAK_HEADING, null, null, disposables,
                SET_LINK_MOVEMENT_METHOD);
    }
}

@Override
public void onPlaybackSynchronize(@NonNull final PlayQueueItem item, final boolean wasBlocked) {
    if (DEBUG) {
        Log.d(TAG, "Playback - onPlaybackSynchronize(was blocked: " + wasBlocked
                + ") called with item=[" + item.getTitle() + "], url=[" + item.getUrl() + "]");
    }
    if (exoPlayerIsNull() || playQueue == null || currentItem == item) {
        return;
    }

    final int playQueueIndex = playQueue.indexOf(item);
    final int playlistIndex = simpleExoPlayer.getCurrentMediaItemIndex();
    final int playlistSize = simpleExoPlayer.getCurrentTimeline().getWindowCount();
    final boolean removeThumbnailBeforeSync = currentItem == null
            || currentItem.getServiceId() != item.getServiceId()
            || !currentItem.getUrl().equals(item.getUrl());

    currentItem = item;

    if (playQueueIndex != playQueue.getIndex()) {
        Log.e(TAG, "Playback - Play Queue may be not in sync: item index=["
                + playQueueIndex + "], " + "queue index=[" + playQueue.getIndex() + "]");
    } else if ((playlistSize > 0 && playQueueIndex >= playlistSize) || playQueueIndex < 0) {
        Log.e(TAG, "Playback - Trying to seek to invalid index=[" + playQueueIndex
                + "] with playlist length=[" + playlistSize + "]");
    } else if (wasBlocked || playlistIndex != playQueueIndex || !isPlaying()) {
        if (DEBUG) {
            Log.d(TAG, "Playback - Rewinding to correct index=[" + playQueueIndex + "], "
                    + "from=[" + playlistIndex + "], size=[" + playlistSize + "].");
        }

        if (removeThumbnailBeforeSync) {
            onThumbnailLoaded(null);
        }

        if (item.getRecoveryPosition() != PlayQueueItem.RECOVERY_UNSET) {
            simpleExoPlayer.seekTo(playQueueIndex, item.getRecoveryPosition());
            playQueue.unsetRecovery(playQueueIndex);
        } else {
            simpleExoPlayer.seekToDefaultPosition(playQueueIndex);
        }
    }
}

@Nullable
static String choosePreferredImage(@NonNull final List<Image> images,
        final PreferredImageQuality nonNoneQuality) {
    final double widthOverHeight = images.stream()
            .filter(image -> image.getHeight() != HEIGHT_UNKNOWN
                    && image.getWidth() != WIDTH_UNKNOWN)
            .mapToDouble(image -> ((double) image.getWidth()) / image.getHeight())
            .findFirst()
            .orElse(1.0);

    final Image.ResolutionLevel preferredLevel = nonNoneQuality.toResolutionLevel();
    final Comparator<Image> initialComparator = Comparator
            .<Image>comparingInt(i -> {
                if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.UNKNOWN) {
                    return 3;
                } else if (i.getEstimatedResolutionLevel() == preferredLevel) {
                    return 0;
                } else if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.MEDIUM) {
                    return 1;
                } else {
                    return 2;
                }
            })
            .thenComparing(image ->
                    image.getHeight() == HEIGHT_UNKNOWN && image.getWidth() == WIDTH_UNKNOWN);

    final Comparator<Image> finalComparator = switch (nonNoneQuality) {
        case NONE -> initialComparator;
        case LOW -> initialComparator.thenComparingDouble(image -> {
            final double pixelCount = estimatePixelCount(image, widthOverHeight);
            return Math.abs(pixelCount - BEST_LOW_H * BEST_LOW_H * widthOverHeight);
        });
        case MEDIUM -> initialComparator.thenComparingDouble(image -> {
            final double pixelCount = estimatePixelCount(image, widthOverHeight);
            return Math.abs(pixelCount - BEST_MEDIUM_H * BEST_MEDIUM_H * widthOverHeight);
        });
        case HIGH -> initialComparator.thenComparingDouble(
                i -> -estimatePixelCount(i, widthOverHeight));
    };

    return images.stream()
            .min(finalComparator)
            .map(Image::getUrl)
            .orElse(null);
}

private void updateTabLayoutPosition() {
    final ScrollableTabLayout tabLayout = binding.mainTabLayout;
    final ViewPager viewPager = binding.pager;
    final boolean bottom = mainTabsPositionBottom;

    final var tabParams = (RelativeLayout.LayoutParams) tabLayout.getLayoutParams();
    final var pagerParams = (RelativeLayout.LayoutParams) viewPager.getLayoutParams();

    tabParams.removeRule(bottom ? ALIGN_PARENT_TOP : ALIGN_PARENT_BOTTOM);
    tabParams.addRule(bottom ? ALIGN_PARENT_BOTTOM : ALIGN_PARENT_TOP);
    pagerParams.removeRule(bottom ? BELOW : ABOVE);
    pagerParams.addRule(bottom ? ABOVE : BELOW, R.id.main_tab_layout);
    tabLayout.setSelectedTabIndicatorGravity(
            bottom ? INDICATOR_GRAVITY_TOP : INDICATOR_GRAVITY_BOTTOM);

    tabLayout.setLayoutParams(tabParams);
    viewPager.setLayoutParams(pagerParams);

    tabLayout.setBackgroundColor(ThemeHelper.resolveColorFromAttr(requireContext(),
            bottom ? android.R.attr.windowBackground : R.attr.colorPrimary));

    @ColorInt final int iconColor = bottom
            ? ThemeHelper.resolveColorFromAttr(requireContext(), android.R.attr.colorAccent)
            : Color.WHITE;
    tabLayout.setTabRippleColor(ColorStateList.valueOf(iconColor).withAlpha(32));
    tabLayout.setTabIconTint(ColorStateList.valueOf(iconColor));
    tabLayout.setSelectedTabIndicatorColor(iconColor);
}

