    final float endScreenHeight = calculateMaxEndScreenThumbnailHeight(thumbnail);
    final Bitmap endScreenBitmap = BitmapCompat.createScaledBitmap(
            thumbnail,
            (int) (thumbnail.getWidth() / (thumbnail.getHeight() / endScreenHeight)),
            (int) endScreenHeight,
            null,
