private HttpResponse readFileFromResource(FullHttpRequest request, String path) throws IOException {
  DefaultFullHttpResponse fullResp = null;
  InputStream in = null;
  try {
    URL res = HttpTtyConnection.class.getResource("/com/taobao/arthas/core/http" + path);
    if (res != null) {
      fullResp = new DefaultFullHttpResponse(request.protocolVersion(), HttpResponseStatus.OK);
      in = res.openStream();
      byte[] tmp = new byte[256];
      for (int l = 0; l != -1; l = in.read(tmp)) {
        fullResp.content().writeBytes(tmp, 0, l);
      }
      int li = path.lastIndexOf('.');
      if (li != -1 && li != path.length() - 1) {
        String ext = path.substring(li + 1);
        String contentType;
        if ("html".equals(ext)) {
          contentType = "text/html";
        } else if ("js".equals(ext)) {
          contentType = "application/javascript";
        } else if ("css".equals(ext)) {
          contentType = "text/css";
        } else {
          contentType = null;
        }
        if (contentType != null) {
          fullResp.headers().set(HttpHeaderNames.CONTENT_TYPE, contentType);
        }
      }
    }
  } finally {
    IOUtils.close(in);
  }
  return fullResp;
}

@Override
public void draw(CommandProcess process, StackModel result) {
  StringBuilder sb = new StringBuilder();
  sb.append(ThreadUtil.getThreadTitle(result)).append("\n");

  StackTraceElement[] stackTraceElements = result.getStackTrace();
  StackTraceElement locationStackTraceElement = stackTraceElements[0];
  String locationString = String.format("    @%s.%s()", locationStackTraceElement.getClassName(),
      locationStackTraceElement.getMethodName());
  sb.append(locationString).append("\n");

  int skip = 1;
  for (int index = skip; index < stackTraceElements.length; index++) {
    StackTraceElement ste = stackTraceElements[index];
    sb.append("        at ")
        .append(ste.getClassName())
        .append(".")
        .append(ste.getMethodName())
        .append("(")
        .append(ste.getFileName())
        .append(":")
        .append(ste.getLineNumber())
        .append(")\n");
  }
  process.write("ts=" + DateUtils.formatDateTime(result.getTs()) + ";" + sb.toString() + "\n");
}

private ApiResponse processExecRequest(ApiRequest apiRequest, Session session) {
  boolean oneTimeAccess = false;
  if (session.get(ONETIME_SESSION_KEY) != null) {
    oneTimeAccess = true;
  }

  try {
    String commandLine = apiRequest.getCommand();
    Map<String, Object> body = new TreeMap<String, Object>();
    body.put("command", commandLine);

    ApiResponse response = new ApiResponse();
    response.setSessionId(session.getSessionId()).setBody(body);

    if (!session.tryLock()) {
      response.setState(ApiState.REFUSED).setMessage("Another command is executing.");
      return response;
    }

    int lock = session.getLock();
    PackingResultDistributor packingResultDistributor = null;
    Job job = null;
    try {
      Job foregroundJob = session.getForegroundJob();
      if (foregroundJob != null) {
        response.setState(ApiState.REFUSED).setMessage("Another job is running.");
        logger.info("Another job is running, jobId: {}", foregroundJob.id());
        return response;
      }

      packingResultDistributor = new PackingResultDistributorImpl(session);
      job = this.createJob(commandLine, session, packingResultDistributor);
      session.setForegroundJob(job);
      updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);
      job.run();
    } catch (Throwable e) {
      logger.error("Exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
      response.setState(ApiState.FAILED).setMessage("Exec command failed:" + e.getMessage());
      return response;
    } finally {
      if (session.getLock() == lock) {
        session.unLock();
      }
    }

    Integer timeout = apiRequest.getExecTimeout();
    if (timeout == null || timeout <= 0) {
      timeout = DEFAULT_EXEC_TIMEOUT;
    }
    boolean timeExpired = !waitForJob(job, timeout);
    if (timeExpired) {
      logger.warn("Job is exceeded time limit, force interrupt it, jobId: {}", job.id());
      job.interrupt();
      response.setState(ApiState.INTERRUPTED).setMessage("The job is exceeded time limit, force interrupt");
    } else {
      response.setState(ApiState.SUCCEEDED);
    }

    body.put("jobId", job.id());
    body.put("jobStatus", job.status());
    body.put("timeExpired", timeExpired);
    if (timeExpired) {
      body.put("timeout", timeout);
    }
    body.put("results", packingResultDistributor.getResults());

    response.setSessionId(session.getSessionId()).setBody(body);
    return response;
  } finally {
    if (oneTimeAccess) {
      sessionManager.removeSession(session.getSessionId());
    }
  }
}

public static String drawThreadInfo(List<ThreadVO> threads, int width, int height) {
  TableElement table = new TableElement(1, 6, 3, 2, 2, 2, 2, 2, 2, 2).overflow(Overflow.HIDDEN).rightCellPadding(1);

  table.add(new RowElement().style(Decoration.bold.fg(Color.black).bg(Color.white)).add(
      "ID", "NAME", "GROUP", "PRIORITY", "STATE", "%CPU", "DELTA_TIME", "TIME", "INTERRUPTED", "DAEMON"));

  int count = 0;
  for (ThreadVO thread : threads) {
    Color color = colorMapping.get(thread.getState());
    String time = formatTimeMills(thread.getTime());
    String deltaTime = formatTimeMillsToSeconds(thread.getDeltaTime());
    double cpu = thread.getCpu();

    LabelElement daemonLabel = new LabelElement(thread.isDaemon());
    if (!thread.isDaemon()) {
      daemonLabel.setStyle(Style.style(Color.magenta));
    }
    LabelElement stateElement;
    if (thread.getState() != null) {
      stateElement = new LabelElement(thread.getState()).style(color.fg());
    } else {
      stateElement = new LabelElement("-");
    }
    table.row(
        new LabelElement(thread.getId()),
        new LabelElement(thread.getName()),
        new LabelElement(thread.getGroup() != null ? thread.getGroup() : "-"),
        new LabelElement(thread.getPriority()),
        stateElement,
        new LabelElement(cpu),
        new LabelElement(deltaTime),
        new LabelElement(time),
        new LabelElement(thread.isInterrupted()),
        daemonLabel
    );
    if (++count >= height) {
      break;
    }
  }
  return RenderUtil.render(table, width, height);
}

private ApiResponse dispatchRequest(ApiAction action, ApiRequest apiRequest, Session session) throws ApiException {
  switch (action) {
    case EXEC:
      return processExecRequest(apiRequest, session);
    case ASYNC_EXEC:
      return processAsyncExecRequest(apiRequest, session);
    case INTERRUPT_JOB:
      return processInterruptJob(apiRequest, session);
    case PULL_RESULTS:
      return processPullResultsRequest(apiRequest, session);
    case SESSION_INFO:
      return processSessionInfoRequest(apiRequest, session);
    case JOIN_SESSION:
      return processJoinSessionRequest(apiRequest, session);
    case CLOSE_SESSION:
      return processCloseSessionRequest(apiRequest, session);
    case INIT_SESSION:
      break;
  }
  return null;
}

public static Pair<String, NavigableMap<Integer, Integer>> decompileWithMappings(String classFilePath,
    String methodName, boolean hideUnicode, boolean printLineNumber) {
  final StringBuilder sb = new StringBuilder(8192);
  final NavigableMap<Integer, Integer> lineMapping = new TreeMap<>();

  OutputSinkFactory mySink = new OutputSinkFactory() {
    @Override
    public List<SinkClass> getSupportedSinks(SinkType sinkType, Collection<SinkClass> collection) {
      return Arrays.asList(SinkClass.STRING, SinkClass.DECOMPILED, SinkClass.DECOMPILED_MULTIVER,
          SinkClass.EXCEPTION_MESSAGE, SinkClass.LINE_NUMBER_MAPPING);
    }

    @Override
    public <T> Sink<T> getSink(final SinkType sinkType, final SinkClass sinkClass) {
      return new Sink<T>() {
        @Override
        public void write(T sinkable) {
          if (sinkType == SinkType.PROGRESS) {
            return;
          }
          if (sinkType == SinkType.LINENUMBER) {
            LineNumberMapping mapping = (LineNumberMapping) sinkable;
            NavigableMap<Integer, Integer> classFileMappings = mapping.getClassFileMappings();
            NavigableMap<Integer, Integer> mappings = mapping.getMappings();
            if (classFileMappings != null && mappings != null) {
              for (Entry<Integer, Integer> entry : mappings.entrySet()) {
                Integer srcLineNumber = classFileMappings.get(entry.getKey());
                lineMapping.put(entry.getValue(), srcLineNumber);
              }
            }
            return;
          }
          sb.append(sinkable);
        }
      };
    }
  };

  HashMap<String, String> options = new HashMap<>();
  options.put("showversion", "false");
  options.put("hideutf", String.valueOf(hideUnicode));
  options.put("trackbytecodeloc", "true");
  if (!StringUtils.isBlank(methodName)) {
    options.put("methodname", methodName);
  }

  CfrDriver driver = new CfrDriver.Builder().withOptions(options).withOutputSink(mySink).build();
  List<String> toAnalyse = new ArrayList<>();
  toAnalyse.add(classFilePath);
  driver.analyse(toAnalyse);

  String resultCode = sb.toString();
  if (printLineNumber && !lineMapping.isEmpty()) {
    resultCode = addLineNumber(resultCode, lineMapping);
  }

  return Pair.make(resultCode, lineMapping);
}

public static String nullSafeToString(char[] array) {
  if (array == null) {
    return "null";
  } else {
    int length = array.length;
    if (length == 0) {
      return "{}";
    } else {
      StringBuilder sb = new StringBuilder("{");
      for (int i = 0; i < length; ++i) {
        if (i > 0) {
          sb.append(", ");
        }
        sb.append("\'").append(array[i]).append("\'");
      }
      sb.append("}");
      return sb.toString();
    }
  }
}

private void renderObject(Object obj, int deep, int expand, final StringBuilder buf)
    throws ObjectTooLargeException {
  if (obj == null) {
    appendStringBuilder(buf, "null");
  } else {
    final Class<?> clazz = obj.getClass();
    final String className = clazz.getSimpleName();

    if (Integer.class.isInstance(obj)
        || Long.class.isInstance(obj)
        || Float.class.isInstance(obj)
        || Double.class.isInstance(obj)
        || Short.class.isInstance(obj)
        || Byte.class.isInstance(obj)
        || Boolean.class.isInstance(obj)) {
      appendStringBuilder(buf, format("@%s[%s]", className, obj));
    } else if (Character.class.isInstance(obj)) {
      final Character c = (Character) obj;
      if (c >= 32 && c <= 126) {
        appendStringBuilder(buf, format("@%s[%s]", className, c));
      } else if (ASCII_MAP.containsKey((byte) c.charValue())) {
        appendStringBuilder(buf, format("@%s[%s]", className, ASCII_MAP.get((byte) c.charValue())));
      } else {
        appendStringBuilder(buf, format("@%s[%s]", className, c));
      }
    } else if (String.class.isInstance(obj)) {
      appendStringBuilder(buf, "@");
      appendStringBuilder(buf, className);
      appendStringBuilder(buf, "[");
      for (Character c : ((String) obj).toCharArray()) {
        switch (c) {
          case '\n':
            appendStringBuilder(buf, "\\n");
            break;
          case '\r':
            appendStringBuilder(buf, "\\r");
            break;
          default:
            appendStringBuilder(buf, c.toString());
        }
      }
      appendStringBuilder(buf, "]");
    } else if (Collection.class.isInstance(obj)) {
      @SuppressWarnings("unchecked")
      final Collection<Object> collection = (Collection<Object>) obj;
      if (!isExpand(deep, expand) || collection.isEmpty()) {
        appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]",
            className, collection.isEmpty(), collection.size()));
      } else {
        appendStringBuilder(buf, format("@%s[", className));
        for (Object e : collection) {
          appendStringBuilder(buf, "\n");
          for (int i = 0; i < deep + 1; i++) {
            appendStringBuilder(buf, TAB);
          }
          renderObject(e, deep + 1, expand, buf);
          appendStringBuilder(buf, ",");
        }
        appendStringBuilder(buf, "\n");
        for (int i = 0; i < deep; i++) {
          appendStringBuilder(buf, TAB);
        }
        appendStringBuilder(buf, "]");
      }
    } else if (Map.class.isInstance(obj)) {
      @SuppressWarnings("unchecked")
      final Map<Object, Object> map = (Map<Object, Object>) obj;
      if (!isExpand(deep, expand) || map.isEmpty()) {
        appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]",
            className, map.isEmpty(), map.size()));
      } else {
        appendStringBuilder(buf, format("@%s[", className));
        for (Map.Entry<Object, Object> entry : map.entrySet()) {
          appendStringBuilder(buf, "\n");
          for (int i = 0; i < deep + 1; i++) {
            appendStringBuilder(buf, TAB);
          }
          renderObject(entry.getKey(), deep + 1, expand, buf);
          appendStringBuilder(buf, ":");
          renderObject(entry.getValue(), deep + 1, expand, buf);
          appendStringBuilder(buf, ",");
        }
        appendStringBuilder(buf, "\n");
        for (int i = 0; i < deep; i++) {
          appendStringBuilder(buf, TAB);
        }
        appendStringBuilder(buf, "]");
      }
    } else if (obj.getClass().isArray()) {
      final String typeName = obj.getClass().getSimpleName();
      if (typeName.equals("int[]")) {
        handleIntArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("long[]")) {
        handleLongArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("short[]")) {
        handleShortArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("float[]")) {
        handleFloatArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("double[]")) {
        handleDoubleArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("boolean[]")) {
        handleBooleanArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("char[]")) {
        handleCharArray(obj, deep, expand, buf, typeName, className);
      } else if (typeName.equals("byte[]")) {
        handleByteArray(obj, deep, expand, buf, typeName, className);
      } else {
        handleObjectArray(obj, deep, expand, buf, typeName, className);
      }
    } else if (Throwable.class.isInstance(obj)) {
      if (!isExpand(deep, expand)) {
        appendStringBuilder(buf, format("@%s[%s]", className, obj));
      } else {
        final Throwable throwable = (Throwable) obj;
        final StringWriter sw = new StringWriter();
        final PrintWriter pw = new PrintWriter(sw);
        throwable.printStackTrace(pw);
        appendStringBuilder(buf, sw.toString());
      }
    } else if (Date.class.isInstance(obj)) {
      appendStringBuilder(buf, format("@%s[%s]", className, 
          new SimpleDateFormat("yyyy-MM-dd HH:mm:ss,SSS").format(obj)));
    } else if (obj instanceof Enum<?>) {
      appendStringBuilder(buf, format("@%s[%s]", className, obj));
    } else {
      if (!isExpand(deep, expand)) {
        appendStringBuilder(buf, format("@%s[%s]", className, obj));
      } else {
        appendStringBuilder(buf, format("@%s[", className));
        final List<Field> fields;
        Class<?> objClass = obj.getClass();
        if (GlobalOptions.printParentFields) {
          fields = new ArrayList<>();
          while (objClass != null) {
            fields.addAll(Arrays.asList(objClass.getDeclaredFields()));
            objClass = objClass.getSuperclass();
          }
        } else {
          fields = new ArrayList<>(Arrays.asList(objClass.getDeclaredFields()));
        }
        for (Field field : fields) {
          field.setAccessible(true);
          try {
            final Object value = field.get(obj);
            appendStringBuilder(buf, "\n");
            for (int i = 0; i < deep + 1; i++) {
              appendStringBuilder(buf, TAB);
            }
            appendStringBuilder(buf, field.getName());
            appendStringBuilder(buf, "=");
            renderObject(value, deep + 1, expand, buf);
            appendStringBuilder(buf, ",");
          } catch (ObjectTooLargeException t) {
            buf.append("...");
            break;
          } catch (Throwable t) {
            // ignore
          }
        }
        appendStringBuilder(buf, "\n");
        for (int i = 0; i < deep; i++) {
          appendStringBuilder(buf, TAB);
        }
        appendStringBuilder(buf, "]");
      }
    }
  }
}

