public static Map<String, String> removeDashKey(Map<String, String> map) {
  Map<String, String> result = new HashMap<>(map.size());
  for (Entry<String, String> entry : map.entrySet()) {
    String key = entry.getKey();
    if (key.contains("-")) {
      StringBuilder sb = new StringBuilder(key.length());
      for (int i = 0; i < key.length(); i++) {
        if (key.charAt(i) == '-' && (i + 1 < key.length()) && Character.isAlphabetic(key.charAt(i + 1))) {
          ++i;
          char upperChar = Character.toUpperCase(key.charAt(i));
          sb.append(upperChar);
        } else {
          sb.append(key.charAt(i));
        }
      }
      key = sb.toString();
    }
    result.put(key, entry.getValue());
  }
  return result;
}

final void advance() {
  if (nextEntry != null && (nextEntry = nextEntry.next) != null) {
    return;
  }

  while (nextTableIndex >= 0) {
    if ((nextEntry = currentTable[nextTableIndex--]) != null) {
      return;
    }
  }

  while (nextSegmentIndex >= 0) {
    Segment<K, V> seg = segments[nextSegmentIndex--];
    if (seg.count != 0) {
      currentTable = seg.table;
      for (int j = currentTable.length - 1; j >= 0; --j) {
        if ((nextEntry = currentTable[j]) != null) {
          nextTableIndex = j - 1;
          return;
        }
      }
    }
  }
}

public static boolean containsConstant(Enum<?>[] enumValues, String constant, boolean caseSensitive) {
  for (Enum candidate : enumValues) {
    if (caseSensitive) {
      if (candidate.toString().equals(constant)) {
        return true;
      }
    } else if (candidate.toString().equalsIgnoreCase(constant)) {
      return true;
    }
  }
  return false;
}

synchronized public long size() throws IOException {
    if (log.isTraceEnabled()) {
        log.trace("size() called");
    }

    if (this.endtime >= src.getEndTime()) {
        return src.size() - skippedAtStart;
    }

    long pos = in.getPosition();

    if (log.isTraceEnabled()) {
        log.trace("saved pos: {}", pos);
    }

    LogEntry e;

    LogSkipList.Mark lastseg = src.getSkipList().findMarkBefore(this.endtime);
    in.seek(lastseg.getBytes());
    buf = "";  // clear buf to avoid pre-seek data
    long count = lastseg.getEntriesSkipped() - skippedAtStart; 

    while ((e = readNextEntry()) != null) {
        if (log.isTraceEnabled()) {
            //log.trace("Entry: {}", e);
        }
        if (e.getTimestamp() > this.endtime) {
            break;
        }
        count++;
    }
    in.seek(pos);
    buf = "";

    if (log.isTraceEnabled()) {
        log.trace("size: {}", count);
    }

    return count;
}

void handleWrite(SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        sock.write(outgoingBuffers.toArray(bufferList));

        ByteBuffer bb;
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }
            outgoingBuffers.remove();
        }
    } else {
        directBuffer.clear();

        for (ByteBuffer b : outgoingBuffers) {
            if (directBuffer.remaining() < b.remaining()) {
                b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
            }
            int p = b.position();
            directBuffer.put(b);
            b.position(p);
            if (directBuffer.remaining() == 0) {
                break;
            }
        }
        directBuffer.flip();

        int sent = sock.write(directBuffer);
        ByteBuffer bb;

        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (sent < bb.remaining()) {
                bb.position(bb.position() + sent);
                break;
            }
            sent -= bb.remaining();
            outgoingBuffers.remove();
        }
    }
}

private void select() {
    try {
        selector.select();

        Set<SelectionKey> selected = selector.selectedKeys();
        ArrayList<SelectionKey> selectedList = new ArrayList<>(selected);
        Collections.shuffle(selectedList);

        Iterator<SelectionKey> selectedKeys = selectedList.iterator();
        while (!stopped && selectedKeys.hasNext()) {
            SelectionKey key = selectedKeys.next();
            selected.remove(key);

            if (!key.isValid()) {
                cleanupSelectionKey(key);
                continue;
            }

            if (key.isReadable() || key.isWritable()) {
                handleIO(key);
            } else {
                log.warn("Unexpected ops in select: {}", key.readyOps());
            }
        }
    } catch (IOException e) {
        log.warn("Ignoring IOException during selection", e);
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;

    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            if (!tmpFile.renameTo(origFile)) {
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException(
                        "Could not rename temporary file " + tmpFile + " to " + origFile
                    );
                }
            }
        } else {
            if (!triedToClose) {
                IOUtils.closeStream(out);
            }
            if (!tmpFile.delete()) {
                log.warn("Unable to delete tmp file: {}", tmpFile);
            }
        }
    }
}

public void doWork() throws InterruptedException {
    if (!key.isValid()) {
        selectorThread.cleanupSelectionKey(key);
        return;
    }

    if (key.isReadable() || key.isWritable()) {
        cnxn.doIO(key);

        if (stopped) {
            cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            return;
        }
        if (!key.isValid()) {
            selectorThread.cleanupSelectionKey(key);
            return;
        }
        touchCnxn(cnxn);
    }

    cnxn.enableSelectable();
    if (!selectorThread.addInterestOpsUpdateRequest(key)) {
        cnxn.close(ServerCnxn.DisconnectReason.CONNECTION_MODE_CHANGED);
    }
}

public String getStatus(String name, long timeout) 
        throws KeeperException, InterruptedException {

    Stat stat = new Stat();
    byte[] data = null;
    long endTime = Time.currentElapsedTime() + timeout;
    KeeperException lastException = null;

    for (int i = 0; i < maxTries && endTime > Time.currentElapsedTime(); i++) {
        try {
            data = zk.getData(reportsNode + '/' + name, false, stat);
            if (log.isDebugEnabled()) {
                log.debug("Got Data: {}", (data == null) ? "null" : new String(data));
            }
            lastException = null;
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        } catch (NoNodeException e) {
            final Object eventObj = new Object();
            synchronized (eventObj) {
                Stat eStat = zk.exists(reportsNode + '/' + name, event -> {
                    synchronized (eventObj) {
                        eventObj.notifyAll();
                    }
                });
                if (eStat == null) {
                    eventObj.wait(endTime - Time.currentElapsedTime());
                }
            }
            lastException = e;
        }
    }

    if (lastException != null) {
        throw lastException;
    }
    return new String(data);
}

public static void main(String[] args) throws IOException {
    MergedLogSource source = new MergedLogSource(args);

    try (
        PrintStream ps_ms = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-ms.out")));
        PrintStream ps_sec = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-sec.out")));
        PrintStream ps_min = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-min.out")));
        PrintStream ps_hour = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-hour.out")))
    ) {
        LogIterator iter;
        log.info("Source: {}", source);

        iter = source.iterator();
        long currentms = 0;
        long currentsec = 0;
        long currentmin = 0;
        long currenthour = 0;
        Set<Long> zxids_ms = new HashSet<>();
        long zxid_sec = 0;
        long zxid_min = 0;
        long zxid_hour = 0;

        while (iter.hasNext()) {
            LogEntry e = iter.next();
            TransactionEntry cxn = (TransactionEntry) e;

            long ms = cxn.getTimestamp();
            long sec = ms / MS_PER_SEC;
            long min = ms / MS_PER_MIN;
            long hour = ms / MS_PER_HOUR;

            if (currentms != ms && currentms != 0) {
                ps_ms.println(currentms + " " + zxids_ms.size());
                zxid_sec += zxids_ms.size();
                zxid_min += zxids_ms.size();
                zxid_hour += zxids_ms.size();
                zxids_ms.clear();
            }

            if (currentsec != sec && currentsec != 0) {
                ps_sec.println(currentsec * MS_PER_SEC + " " + zxid_sec);
                zxid_sec = 0;
            }

            if (currentmin != min && currentmin != 0) {
                ps_min.println(currentmin * MS_PER_MIN + " " + zxid_min);
                zxid_min = 0;
            }

            if (currenthour != hour && currenthour != 0) {
                ps_hour.println(currenthour * MS_PER_HOUR + " " + zxid_hour);
                zxid_hour = 0;
            }

            currentms = ms;
            currentsec = sec;
            currentmin = min;
            currenthour = hour;
            zxids_ms.add(cxn.getZxid());
        }
    }
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                log.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                log.warn("Ignoring unexpected exception", e);
            }
        }

        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }

        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        NIOServerCnxnFactory.this.stop();
        log.info("selector thread exited");
    }
}

synchronized public static ZooKeeper getClient(
        String contextPath, String session, int expireTime) throws IOException {

    final String connectionId = concat(contextPath, session);
    ZooKeeper zk = zkMap.get(connectionId);

    if (zk == null) {
        log.info("creating new connection for: '{}'", connectionId);
        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", 
                String.format("%s:%s", p.getKey(), p.getValue()).getBytes()
            );
        }

        zkMap.put(connectionId, zk);

        if (session != null) {
            zkSessionTimers.put(connectionId, 
                new SessionTimerTask(expireTime, session, contextPath, timer)
            );
        }
    }
    return zk;
}

public InputStream getView(int size) {
    if (view != null) {
        viewSize = size;
        return view;
    }

    view = new InputStream() {
        @Override
        public int read() throws IOException {
            if (viewSize < 1) return -1;
            int res = DataReader.this.read();
            if (res > 0) viewSize--;
            return res;
        }

        @Override
        public int read(byte[] buffer) throws IOException {
            return read(buffer, 0, buffer.length);
        }

        @Override
        public int read(byte[] buffer, int offset, int count) throws IOException {
            if (viewSize < 1) return -1;
            int res = DataReader.this.read(buffer, offset, Math.min(viewSize, count));
            viewSize -= res;
            return res;
        }

        @Override
        public long skip(long amount) throws IOException {
            if (viewSize < 1) return 0;
            int res = (int) DataReader.this.skipBytes(Math.min(amount, viewSize));
            viewSize -= res;
            return res;
        }

        @Override
        public int available() {
            return viewSize;
        }

        @Override
        public void close() {
            viewSize = 0;
        }

        @Override
        public boolean markSupported() {
            return false;
        }
    };

    viewSize = size;
    return view;
}

private List<Object> getSpecialItems() {
    synchronized (DownloadManager.this) {
        List<Mission> pending = new ArrayList<>(mMissionsPending);
        List<Mission> finished = new ArrayList<>(mMissionsFinished);
        List<Mission> remove = new ArrayList<>(hidden);

        remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

        int fakeTotal = pending.size();
        if (fakeTotal > 0) fakeTotal++;
        fakeTotal += finished.size();
        if (finished.size() > 0) fakeTotal++;

        List<Object> list = new ArrayList<>(fakeTotal);
        if (!pending.isEmpty()) {
            list.add(PENDING);
            list.addAll(pending);
        }
        if (!finished.isEmpty()) {
            list.add(FINISHED);
            list.addAll(finished);
        }

        hasFinished = !finished.isEmpty();
        return list;
    }
}

private void loadPendingMissions(Context ctx) {
    File[] subs = mPendingMissionsDir.listFiles();

    if (subs == null) {
        Log.e(TAG, "listFiles() returned null");
        return;
    }
    if (subs.length < 1) return;
    if (DEBUG) Log.d(TAG, "Loading pending downloads from: " + mPendingMissionsDir.getAbsolutePath());

    File tempDir = pickAvailableTemporalDir(ctx);
    Log.i(TAG, "Using '" + tempDir + "' as temporal directory");

    for (File sub : subs) {
        if (!sub.isFile() || sub.getName().equals(".tmp")) continue;

        DownloadMission mis = Utility.readFromFile(sub);
        if (mis == null || mis.isFinished() || mis.hasInvalidStorage()) {
            sub.delete();
            continue;
        }

        mis.threads = new Thread[0];
        boolean exists;

        try {
            mis.storage = StoredFileHelper.deserialize(mis.storage, ctx);
            exists = !mis.storage.isInvalid() && mis.storage.existsAsFile();
        } catch (Exception ex) {
            Log.e(TAG, "Failed to load file source: " + mis.storage, ex);
            mis.storage.invalidate();
            exists = false;
        }

        if (mis.isPsRunning()) {
            if (mis.psAlgorithm.worksOnSameFile && exists && mis.storage.isDirect() && !mis.storage.delete()) {
                Log.w(TAG, "Unable to delete incomplete file: " + sub);
            }
            mis.psState = 0;
            mis.errCode = DownloadMission.ERROR_POSTPROCESSING_STOPPED;
        } else if (!exists) {
            tryRecover(mis);
            if (mis.isInitialized()) mis.resetState(true, true, DownloadMission.ERROR_PROGRESS_LOST);
        }

        if (mis.psAlgorithm != null) {
            mis.psAlgorithm.cleanupTemporalDir();
            mis.psAlgorithm.setTemporalDir(tempDir);
        }

        mis.metadata = sub;
        mis.maxRetry = mPrefMaxRetry;
        mis.mHandler = mHandler;
        mMissionsPending.add(mis);
    }

    if (mMissionsPending.size() > 1) {
        Collections.sort(mMissionsPending, Comparator.comparingLong(Mission::getTimestamp));
    }
}

public void updateMission(Mission mission) {
    ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    SQLiteDatabase database = getWritableDatabase();
    String ts = String.valueOf(mission.timestamp);

    int rowsAffected;
    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[]{ts});
        } else {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_PATH + " = ?", 
                new String[]{mission.storage.getUri().toString()});
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e("FinishedMissionStore", "Expected 1 affected row, got " + rowsAffected);
    }
}

private void startMission(Intent intent) {
    String[] urls = intent.getStringArrayExtra(EXTRA_URLS);
    Uri path = IntentCompat.getParcelableExtra(intent, EXTRA_PATH, Uri.class);
    Uri parentPath = IntentCompat.getParcelableExtra(intent, EXTRA_PARENT_PATH, Uri.class);
    int threads = intent.getIntExtra(EXTRA_THREADS, 1);
    char kind = intent.getCharExtra(EXTRA_KIND, '?');
    String psName = intent.getStringExtra(EXTRA_POSTPROCESSING_NAME);
    String[] psArgs = intent.getStringArrayExtra(EXTRA_POSTPROCESSING_ARGS);
    String source = intent.getStringExtra(EXTRA_SOURCE);
    long nearLength = intent.getLongExtra(EXTRA_NEAR_LENGTH, 0);
    String tag = intent.getStringExtra(EXTRA_STORAGE_TAG);
    final var recovery = IntentCompat.getParcelableArrayListExtra(intent, EXTRA_RECOVERY_INFO, MissionRecoveryInfo.class);
    Objects.requireNonNull(recovery);

    StoredFileHelper storage;
    try {
        storage = new StoredFileHelper(this, parentPath, path, tag);
    } catch (IOException e) {
        throw new RuntimeException(e);
    }

    Postprocessing ps = null;
    if (psName != null) {
        ps = Postprocessing.getAlgorithm(psName, psArgs);
    }

    DownloadMission mission = new DownloadMission(urls, storage, kind, ps);
    mission.threadCount = threads;
    mission.source = source;
    mission.nearLength = nearLength;
    mission.recoveryInfo = recovery.toArray(new MissionRecoveryInfo[0]);

    if (ps != null) {
        ps.setTemporalDir(DownloadManager.pickAvailableTemporalDir(this));
    }

    handleConnectivityState(true);
    mManager.startMission(mission);
}

@Override
public void run() {
    if (mMission.current > 0) mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();
                    if (Thread.interrupted()) return;

                    long length = Utility.getTotalContentLength(mConn);
                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }
                    if (length > 0) finalLength += length;
                    if (length < lowestSize) lowestSize = length;
                }

                mMission.nearLength = finalLength;
                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    mMission.offsets[0] = (lowestSize < 1) ? 
                        RESERVE_SPACE_DEFAULT : 
                        Math.min(lowestSize, RESERVE_SPACE_MAXIMUM);
                }
            } else {
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();
                if (!mMission.running || Thread.interrupted()) return;

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            if (mMission.length == -1 && httpCode == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;
            } else {
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();
                if (!mMission.running || Thread.interrupted()) return;

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {
                        mMission.blocks = (mMission.threadCount > 1) ? 
                            new int[(int) (mMission.length / DownloadMission.BLOCK_SIZE) + 1] : 
                            new int[0];
                    } else {
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;
                    }
                }
                if (!mMission.running || Thread.interrupted()) return;
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }
            if (!mMission.running || Thread.interrupted()) return;

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) return;

            if (e instanceof DownloadMission.HttpError && 
                ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "Initializer failed", e);
                mMission.notifyError(e);
                return;
            }
            Log.e(TAG, "Initializer failed, retrying", e);
        }
    }
    mMission.start();
}

public void pause() {
    if (!running) return;
    if (isPsRunning()) {
        if (DEBUG) Log.w(TAG, "Pause during post-processing is not applicable");
        return;
    }

    running = false;
    notify(DownloadManagerService.MESSAGE_PAUSED);

    if (init != null && init.isAlive()) {
        init.interrupt();
        synchronized (LOCK) {
            resetState(false, true, ERROR_NOTHING);
        }
        return;
    }

    if (DEBUG && unknownLength) {
        Log.w(TAG, "Pausing non-resumable download (server doesn't support range requests)");
    }
    init = null;
    pauseThreads();
}

boolean runMissions() {
    synchronized (this) {
        if (mMissionsPending.isEmpty()) return false;
        if (!canDownloadInCurrentNetwork()) return false;
        if (mPrefQueueLimit) {
            for (DownloadMission mission : mMissionsPending) {
                if (mission.running) return true;
            }
        }

        boolean flag = false;
        for (DownloadMission mission : mMissionsPending) {
            if (mission.running || !mission.enqueued || mission.isFinished()) continue;
            resumeMission(mission);
            if (mission.errCode != DownloadMission.ERROR_NOTHING) continue;
            if (mPrefQueueLimit) return true;
            flag = true;
        }
        return flag;
    }
}

private void notifyPostProcessing(int state) {
    String action = switch (state) {
        case 1 -> "Running";
        case 2 -> "Completed";
        default -> "Failed";
    };

    Log.d(TAG, action + " postprocessing on " + storage.getName());
    if (state == 2) {
        psState = state;
        return;
    }

    synchronized (LOCK) {
        psState = state;
        writeThisToFile();
    }
}

private static String decodeQRCode(BufferedImage bufferedImage) {
    LuminanceSource source;

    if (bufferedImage.getRaster().getDataBuffer() instanceof DataBufferByte dataBufferByte) {
        byte[] pixels = dataBufferByte.getData();
        source = new PlanarYUVLuminanceSource(
            pixels,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            0, 0,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            false
        );
    } else if (bufferedImage.getRaster().getDataBuffer() instanceof DataBufferInt dataBufferInt) {
        int[] pixels = dataBufferInt.getData();
        byte[] newPixels = new byte[pixels.length];
        for (int i = 0; i < pixels.length; i++) {
            newPixels[i] = (byte) (pixels[i] & 0xff);
        }
        source = new PlanarYUVLuminanceSource(
            newPixels,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            0, 0,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            false
        );
    } else {
        throw new IllegalArgumentException(
            "BufferedImage must have 8-bit gray scale, 24-bit RGB, 32-bit ARGB (packed int), " +
            "byte gray, or 3-byte/4-byte RGB image data"
        );
    }

    BinaryBitmap bitmap = new BinaryBitmap(new HybridBinarizer(source));
    try {
        Result result = new MultiFormatReader().decode(bitmap);
        return result.getText();
    } catch (NotFoundException e) {
        return null; // No QR code found
    }
}

private void executeJob(QueuedJob job) {
    if (job.cancelled) {
        log.debug("Job {} was cancelled, not executing", job.jobId);
        return;
    }

    jobExecutor.execute(() -> {
        log.debug("Executing queued job {} (queued at {})", job.jobId, job.queuedAt);
        try {
            Object result = executeWithTimeout(job.work, job.timeoutMs);
            job.future.complete(
                result instanceof ResponseEntity 
                    ? (ResponseEntity<?>) result 
                    : ResponseEntity.ok(result)
            );
        } catch (Exception e) {
            log.error("Error executing queued job {}: {}", job.jobId, e.getMessage(), e);
            job.future.completeExceptionally(e);
        }
    });
}

@DeleteMapping("/api/v1/general/job/{jobId}")
public ResponseEntity<?> cancelJob(@PathVariable("jobId") String jobId) {
    log.debug("Request to cancel job: {}", jobId);

    Object sessionJobIds = request.getSession().getAttribute("userJobIds");
    if (sessionJobIds == null 
        || !(sessionJobIds instanceof java.util.Set) 
        || !((java.util.Set<?>) sessionJobIds).contains(jobId)) {
        log.warn("Unauthorized attempt to cancel job: {}", jobId);
        return ResponseEntity.status(403)
            .body(Map.of("message", "You are not authorized to cancel this job"));
    }

    boolean cancelled = false;
    int queuePosition = -1;

    if (jobQueue.isJobQueued(jobId)) {
        queuePosition = jobQueue.getJobPosition(jobId);
        cancelled = jobQueue.cancelJob(jobId);
        log.info("Cancelled queued job: {} (was at position {})", job.jobId, queuePosition);
    }

    if (!cancelled) {
        JobResult result = taskManager.getJobResult(jobId);
        if (result != null && !result.isComplete()) {
            taskManager.setError(jobId, "Job was cancelled by user");
            cancelled = true;
            log.info("Marked job as cancelled in TaskManager: {}", jobId);
        }
    }

    if (cancelled) {
        return ResponseEntity.ok(Map.of(
            "message", "Job cancelled successfully",
            "wasQueued", queuePosition >= 0,
            "queuePosition", queuePosition >= 0 ? queuePosition : "n/a"
        ));
    } else {
        JobResult result = taskManager.getJobResult(jobId);
        if (result == null) {
            return ResponseEntity.notFound().build();
        } else if (result.isComplete()) {
            return ResponseEntity.badRequest()
                .body(Map.of("message", "Cannot cancel job that is already complete"));
        } else {
            return ResponseEntity.internalServerError()
                .body(Map.of("message", "Failed to cancel job for unknown reason"));
        }
    }
}

public ResponseEntity<String> printFile(@ModelAttribute PrintFileRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    String originalFilename = file.getOriginalFilename();
    if (originalFilename != null && (originalFilename.contains("..") || Paths.get(originalFilename).isAbsolute())) {
        throw new IOException("Invalid file path detected: " + originalFilename);
    }

    String printerName = request.getPrinterName();
    String contentType = file.getContentType();
    try {
        PrintService selectedService = Arrays.stream(PrintServiceLookup.lookupPrintServices(null, null))
            .filter(service -> service.getName().toLowerCase().contains(printerName))
            .findFirst()
            .orElseThrow(() -> new IllegalArgumentException("No matching printer found"));

        log.info("Selected Printer: {}", selectedService.getName());

        if ("application/pdf".equals(contentType)) {
            try (PDDocument document = Loader.loadPDF(file.getBytes())) {
                PrinterJob job = PrinterJob.getPrinterJob();
                job.setPrintService(selectedService);
                job.setPageable(new PDFPageable(document));
                job.print();
            }
        } else if (contentType != null && contentType.startsWith("image/")) {
            BufferedImage image = ImageIO.read(file.getInputStream());
            PrinterJob job = PrinterJob.getPrinterJob();
            job.setPrintService(selectedService);
            job.setPrintable((graphics, pageFormat, pageIndex) -> {
                if (pageIndex != 0) return NO_SUCH_PAGE;
                Graphics2D g2d = (Graphics2D) graphics;
                g2d.translate(pageFormat.getImageableX(), pageFormat.getImageableY());
                g2d.drawImage(
                    image, 
                    0, 
                    0, 
                    (int) pageFormat.getImageableWidth(), 
                    (int) pageFormat.getImageableHeight(), 
                    null
                );
                return PAGE_EXISTS;
            });
            job.print();
        }
        return ResponseEntity.ok("File printed successfully to " + selectedService.getName());
    } catch (Exception e) {
        log.error("Failed to print: {}", e.getMessage());
        return ResponseEntity.badRequest().body(e.getMessage());
    }
}

@PostMapping(value = "/auto-split-pdf", consumes = "multipart/form-data")
@Operation(
    summary = "Auto split PDF pages into separate documents",
    description = "Accepts PDF file, scans each page for QR code, splits document at boundaries. Output: ZIP of PDFs"
)
public ResponseEntity<byte[]> autoSplitPdf(@ModelAttribute AutoSplitPdfRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    boolean duplexMode = Boolean.TRUE.equals(request.getDuplexMode());
    List<PDDocument> splitDocuments = new ArrayList<>();

    try (PDDocument document = pdfDocumentFactory.load(file.getInputStream())) {
        PDFRenderer pdfRenderer = new PDFRenderer(document);
        pdfRenderer.setSubsamplingAllowed(true);

        for (int page = 0; page < document.getNumberOfPages(); ++page) {
            BufferedImage bim = pdfRenderer.renderImageWithDPI(page, 150);
            String result = decodeQRCode(bim);
            boolean isValidQrCode = VALID_QR_CONTENTS.contains(result);
            log.debug("Detected QR code {}, valid={}", result, isValidQrCode);

            if (isValidQrCode && page != 0) {
                splitDocuments.add(new PDDocument());
            }

            if (!splitDocuments.isEmpty() && !isValidQrCode) {
                splitDocuments.get(splitDocuments.size() - 1).addPage(document.getPage(page));
            } else if (page == 0) {
                PDDocument firstDoc = new PDDocument();
                firstDoc.addPage(document.getPage(page));
                splitDocuments.add(firstDoc);
            }

            if (duplexMode && isValidQrCode) {
                page++;
            }
        }

        splitDocuments.removeIf(doc -> doc.getNumberOfPages() == 0);
        String filename = Filenames.toSimpleFileName(file.getOriginalFilename()).replaceFirst("[.][^.]+$", "");
        Path zipFile = Files.createTempFile("split_documents", ".zip");

        try (ZipOutputStream zipOut = new ZipOutputStream(Files.newOutputStream(zipFile))) {
            for (int i = 0; i < splitDocuments.size(); i++) {
                PDDocument doc = splitDocuments.get(i);
                String entryName = filename + "_" + (i + 1) + ".pdf";
                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                doc.save(baos);
                zipOut.putNextEntry(new ZipEntry(entryName));
                zipOut.write(baos.toByteArray());
                zipOut.closeEntry();
                doc.close();
            }
        }

        byte[] data = Files.readAllBytes(zipFile);
        return WebResponseUtils.bytesToWebResponse(
            data, filename + ".zip", MediaType.APPLICATION_OCTET_STREAM
        );
    } catch (Exception e) {
        log.error("Error in auto split", e);
        throw e;
    } finally {
        splitDocuments.forEach(doc -> {
            try { doc.close(); } 
            catch (IOException ex) { log.error("Error closing split document", ex); }
        });
        try { Files.deleteIfExists(zipFile); } 
        catch (IOException ex) { log.error("Error deleting temporary zip", ex); }
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/auto-rename")
@Operation(
    summary = "Extract header from PDF file",
    description = "Accepts PDF file, extracts title/header using heuristics"
)
public ResponseEntity<byte[]> extractHeader(@ModelAttribute ExtractHeaderRequest request) throws Exception {
    MultipartFile file = request.getFileInput();
    boolean useFirstTextFallback = Boolean.TRUE.equals(request.getUseFirstTextAsFallback());

    try (PDDocument document = pdfDocumentFactory.load(file)) {
        PDFTextStripper reader = new HeaderExtractionStripper(useFirstTextFallback);
        String header = reader.getText(document);

        if (header != null && header.length() < 255) {
            String sanitized = header.replaceAll("[/\\\\?%*:|\"<>]", "").trim();
            return WebResponseUtils.pdfDocToWebResponse(document, sanitized + ".pdf");
        } else {
            log.info("File has no valid title");
            return WebResponseUtils.pdfDocToWebResponse(
                document, Filenames.toSimpleFileName(file.getOriginalFilename())
            );
        }
    }
}

private static class HeaderExtractionStripper extends PDFTextStripper {
    private static final int LINE_LIMIT = 100;
    private final boolean useFirstTextFallback;
    private List<LineInfo> lineInfos = new ArrayList<>();
    private StringBuilder lineBuilder;
    private float lastY = -1;
    private float maxFontSizeInLine;
    private int lineCount;

    HeaderExtractionStripper(boolean useFirstTextFallback) throws IOException {
        this.useFirstTextFallback = useFirstTextFallback;
    }

    @Override
    protected void processTextPosition(TextPosition text) {
        if (lastY != text.getY() && lineCount < LINE_LIMIT) {
            processLine();
            lineBuilder = new StringBuilder(text.getUnicode());
            maxFontSizeInLine = text.getFontSizeInPt();
            lastY = text.getY();
            lineCount++;
        } else if (lineCount < LINE_LIMIT) {
            lineBuilder.append(text.getUnicode());
            maxFontSizeInLine = Math.max(maxFontSizeInLine, text.getFontSizeInPt());
        }
    }

    private void processLine() {
        if (lineBuilder.length() > 0 && lineCount < LINE_LIMIT) {
            lineInfos.add(new LineInfo(lineBuilder.toString(), maxFontSizeInLine));
        }
    }

    @Override
    public String getText(PDDocument doc) throws IOException {
        resetState();
        super.getText(doc);
        processLine();

        List<LineInfo> merged = mergeLines();
        merged.sort(Comparator.comparing((LineInfo li) -> li.fontSize).reversed());

        return merged.isEmpty() 
            ? null 
            : (useFirstTextFallback 
                ? merged.get(merged.size() - 1).text 
                : merged.get(0).text);
    }

    private void resetState() {
        lineInfos.clear();
        lineBuilder = new StringBuilder();
        lastY = -1;
        maxFontSizeInLine = 0f;
        lineCount = 0;
    }

    private List<LineInfo> mergeLines() {
        List<LineInfo> merged = new ArrayList<>();
        for (int i = 0; i < lineInfos.size(); i++) {
            String text = lineInfos.get(i).text;
            float fontSize = lineInfos.get(i).fontSize;
            while (i + 1 < lineInfos.size() && lineInfos.get(i+1).fontSize == fontSize) {
                text += " " + lineInfos.get(++i).text;
            }
            merged.add(new LineInfo(text, fontSize));
        }
        return merged;
    }

    private static class LineInfo {
        final String text;
        final float fontSize;

