public CompletableFuture<ResponseEntity<?>> queueJob(
        String jobId, int resourceWeight, Supplier<Object> work, long timeoutMs) {

    CompletableFuture<ResponseEntity<?>> future = new CompletableFuture<>();
    QueuedJob job = new QueuedJob(jobId, resourceWeight, work, timeoutMs, Instant.now(), future, false);
    jobMap.put(jobId, job);
    totalQueuedJobs++;

    synchronized (queueLock) {
        try {
            if (!jobQueue.offer(job, 5, TimeUnit.SECONDS)) {
                log.warn("Queue full, rejecting job {}", jobId);
                rejectedJobs++;
                future.completeExceptionally(new RuntimeException("Job queue full"));
                jobMap.remove(jobId);
                return future;
            }
            log.debug("Job {} queued (weight: {}, queue size: {})", jobId, resourceWeight, jobQueue.size());
            return future;
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            future.completeExceptionally(new RuntimeException("Job queue interrupted"));
            jobMap.remove(jobId);
            return future;
        }
    }
}

private void applyQpdfCompression(
        OptimizePdfRequest request, int optimizeLevel, Path currentFile, List<Path> tempFiles) throws IOException {

    long preQpdfSize = Files.size(currentFile);
    log.info("Pre-QPDF file size: {}", GeneralUtils.formatBytes(preQpdfSize));

    int qpdfCompressionLevel = optimizeLevel == 1 ? 5 : 9;
    Path qpdfOutputFile = Files.createTempFile("qpdf_output_", ".pdf");
    tempFiles.add(qpdfOutputFile);

    List<String> command = new ArrayList<>();
    command.add("qpdf");
    if (request.getNormalize()) command.add("--normalize-content=y");
    if (request.getLinearize()) command.add("--linearize");
    command.add("--recompress-flate");
    command.add("--compression-level=" + qpdfCompressionLevel);
    command.add("--compress-streams=y");
    command.add("--object-streams=generate");
    command.add(currentFile.toString());
    command.add(qpdfOutputFile.toString());

    try {
        ProcessExecutorResult result = ProcessExecutor.getInstance(ProcessExecutor.Processes.QPDF)
            .runCommandWithOutputHandling(command);

        Files.copy(qpdfOutputFile, currentFile, StandardCopyOption.REPLACE_EXISTING);
        long postQpdfSize = Files.size(currentFile);
        double reduction = 100.0 - ((postQpdfSize * 100.0) / preQpdfSize);
        log.info("Post-QPDF size: {} (reduced by {}%)", 
                 GeneralUtils.formatBytes(postQpdfSize), 
                 String.format("%.1f", reduction));
    } catch (Exception e) {
        if (!(e instanceof ProcessExecutionException) || ((ProcessExecutionException)e).getExitValue() != 3) {
            throw new IOException("QPDF command failed", e);
        }
        log.warn("QPDF compression failed, continuing", e);
    }
}

private void handleCookiesFromUrl(@Nullable final String url) {
    if (MainActivity.DEBUG) {
        Log.d(TAG, "handleCookiesFromUrl: url=" + (url == null ? "null" : url));
    }

    if (url == null) return;

    final String cookies = CookieManager.getInstance().getCookie(url);
    handleCookies(cookies);

    // sometimes cookies are inside the url
    final int abuseStart = url.indexOf("google_abuse=");
    if (abuseStart != -1) {
        final int abuseEnd = url.indexOf("+path");
        try {
            handleCookies(Utils.decodeUrlUtf8(url.substring(abuseStart + 13, abuseEnd)));
        } catch (final StringIndexOutOfBoundsException e) {
            if (MainActivity.DEBUG) {
                Log.e(TAG, String.format(
                    "handleCookiesFromUrl: invalid google abuse starting at %d and ending at %d for url %s",
                    abuseStart, abuseEnd, url), e);
            }
        }
    }
}

