@Override
protected void subscribeActual(@NonNull final MaybeObserver<? super T> observer) {
    final MaybeDisposable<T> md = new MaybeDisposable<>(observer, this);
    observer.onSubscribe(md);
    if (add(md)) {
        if (md.isDisposed()) {
            remove(md);
        }
    } else {
        @Nullable final Throwable ex = error;
        if (ex != null) {
            observer.onError(ex);
            return;
        }

        @Nullable final T v = value;
        if (v == null) {
            observer.onComplete();
        } else {
            observer.onSuccess(v);
        }
    }
}

@Override
protected void subscribeActual(@NonNull final CompletableObserver observer) {
    final Disposable d = Disposable.empty();
    observer.onSubscribe(d);
    if (d.isDisposed()) {
        return;
    }

    try {
        run.run();
    } catch (final Throwable e) {
        Exceptions.throwIfFatal(e);
        if (d.isDisposed()) {
            RxJavaPlugins.onError(e);
        } else {
            observer.onError(e);
        }
        return;
    }

    if (!d.isDisposed()) {
        observer.onComplete();
    }
}

@Override
public void onNext(@NonNull final T value) {
    if (upstream == DisposableHelper.DISPOSED) {
        return;
    }

    final Iterator<? extends R> it;
    try {
        it = mapper.apply(value).iterator();
    } catch (final Throwable ex) {
        Exceptions.throwIfFatal(ex);
        upstream.dispose();
        onError(ex);
        return;
    }

    final Observer<? super R> a = downstream;

    for (;;) {
        final boolean b;
        try {
            b = it.hasNext();
        } catch (final Throwable ex) {
            Exceptions.throwIfFatal(ex);
            upstream.dispose();
            onError(ex);
            return;
        }

        if (!b) {
            break;
        }

        final R v;
        try {
            v = Objects.requireNonNull(it.next(), "The iterator returned a null value");
        } catch (final Throwable ex) {
            Exceptions.throwIfFatal(ex);
            upstream.dispose();
            onError(ex);
            return;
        }

        a.onNext(v);
    }
}

@Override
public boolean tryOnNext(@NonNull final T t) {
    if (done) {
        return false;
    }

    long retries = 0L;
    for (;;) {
        final boolean b;
        try {
            b = predicate.test(t);
        } catch (final Throwable ex) {
            Exceptions.throwIfFatal(ex);
            final ParallelFailureHandling h;
            try {
                h = Objects.requireNonNull(
                    errorHandler.apply(++retries, ex),
                    "The errorHandler returned a null ParallelFailureHandling"
                );
            } catch (final Throwable exc) {
                Exceptions.throwIfFatal(exc);
                cancel();
                onError(new CompositeException(ex, exc));
                return false;
            }

            switch (h) {
                case RETRY:
                    continue;
                case SKIP:
                    return false;
                case STOP:
                    cancel();
                    onComplete();
                    return false;
                default:
                    cancel();
                    onError(ex);
                    return false;
            }
        }

        return b && downstream.tryOnNext(t);
    }
}

@Override
@NonNull
public synchronized Throwable getCause() { // NOPMD
    if (cause != null) {
        return cause;
    }

    final int exceptionCount = exceptions.size();
    if (exceptionCount == 1) {
        cause = exceptions.get(0);
        return cause;
    }

    final String separator = System.getProperty("line.separator");
    final Map<Throwable, Boolean> seenCauses = new IdentityHashMap<>();
    final StringBuilder aggregateMessage = new StringBuilder();

    aggregateMessage
        .append("Multiple exceptions (")
        .append(exceptionCount)
        .append(")")
        .append(separator);

    for (final Throwable inner : exceptions) {
        int depth = 0;
        Throwable current = inner;
        while (current != null) {
            for (int i = 0; i < depth; i++) {
                aggregateMessage.append("  ");
            }
            aggregateMessage.append("|-- ");
            aggregateMessage.append(current.getClass().getCanonicalName()).append(": ");

            final String innerMessage = current.getMessage();
            if (innerMessage != null && innerMessage.contains(separator)) {
                aggregateMessage.append(separator);
                for (final String line : innerMessage.split(separator)) {
                    for (int i = 0; i < depth + 2; i++) {
                        aggregateMessage.append("  ");
                    }
                    aggregateMessage.append(line).append(separator);
                }
            } else {
                aggregateMessage.append(innerMessage).append(separator);
            }

            for (int i = 0; i < depth + 2; i++) {
                aggregateMessage.append("  ");
            }
            final StackTraceElement[] st = current.getStackTrace();
            if (st.length > 0) {
                aggregateMessage.append("at ").append(st[0]).append(separator);
            }

            if (!seenCauses.containsKey(current)) {
                seenCauses.put(current, true);
                current = current.getCause();
                depth++;
            } else {
                current = current.getCause();
                if (current != null) {
                    for (int i = 0; i < depth + 2; i++) {
                        aggregateMessage.append("  ");
                    }
                    aggregateMessage
                        .append("|-- (cause not expanded again) ")
                        .append(current.getClass().getCanonicalName())
                        .append(": ")
                        .append(current.getMessage())
                        .append(separator);
                }
                break;
            }
        }
    }

    cause = new ExceptionOverview(aggregateMessage.toString().trim());
    return cause;
}

