            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                a.onError(ex);
                return;
            }

            a.onNext(v);

            if (cancelled) {
                return;
            }

            boolean b;

            try {
                b = iterator.hasNext();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                a.onError(ex);
                return;
            }

            if (!b) {
                a.onComplete();
                return;
