public synchronized EnhancerAffect enhance(final Instrumentation inst, int maxNumOfMatchedClass)
        throws UnmodifiableClassException {
    // get matched classes
    this.matchingClasses = GlobalOptions.isDisableSubClass
            ? SearchUtils.searchClass(inst, classNameMatcher)
            : SearchUtils.searchSubClass(inst, SearchUtils.searchClass(inst, classNameMatcher));

    if (matchingClasses.size() > maxNumOfMatchedClass) {
        affect.setOverLimitMsg(
                "The number of matched classes is " + matchingClasses.size() + ", greater than the limit value "
                        + maxNumOfMatchedClass + ". Try to change the limit with option '-m <arg>'.");
        return affect;
    }

    // filter classes that cannot be enhanced
    List<Pair<Class<?>, String>> filteredList = filter(matchingClasses);
    if (!filteredList.isEmpty()) {
        for (Pair<Class<?>, String> filtered : filteredList) {
            logger.info("ignore class: {}, reason: {}", filtered.getFirst().getName(), filtered.getSecond());
        }
    }

    logger.info("enhance matched classes: {}", matchingClasses);

    affect.setTransformer(this);

    try {
        ArthasBootstrap.getInstance().getTransformerManager().addTransformer(this, isTracing);

        // batch enhance
        if (GlobalOptions.isBatchReTransform) {
            final int size = matchingClasses.size();
            final Class<?>[] classArray = new Class<?>[size];
            arraycopy(matchingClasses.toArray(), 0, classArray, 0, size);
            if (classArray.length > 0) {
                inst.retransformClasses(classArray);
                logger.info("Success to batch transform classes: {}", Arrays.toString(classArray));
            }
        } else {
            // enhance one by one
            for (Class<?> clazz : matchingClasses) {
                try {
                    inst.retransformClasses(clazz);
                    logger.info("Success to transform class: {}", clazz);
                } catch (Throwable t) {
                    logger.warn("retransform {} failed.", clazz, t);
                    if (t instanceof UnmodifiableClassException) {
                        throw (UnmodifiableClassException) t;
                    } else if (t instanceof RuntimeException) {
                        throw (RuntimeException) t;
                    } else {
                        throw new RuntimeException(t);
                    }
                }
            }
        }
    } catch (Throwable e) {
        logger.error("Enhancer error, matchingClasses: {}", matchingClasses, e);
        affect.setThrowable(e);
    }

    return affect;
}

public static DefaultFullHttpResponse directView(
        File dir, String path, FullHttpRequest request, ChannelHandlerContext ctx) throws IOException {
    if (path.startsWith("/")) {
        path = path.substring(1);
    }

    // path maybe: arthas-output/20201225-203454.svg
    // use dir's parent to remove prefix
    File file = new File(dir.getParent(), path);
    HttpVersion version = request.protocolVersion();

    if (!isSubFile(dir, file)) {
        return null;
    }

    DefaultFullHttpResponse fullResp = new DefaultFullHttpResponse(version, HttpResponseStatus.OK);

    if (file.isDirectory()) {
        if (!path.endsWith("/")) {
            fullResp.setStatus(HttpResponseStatus.FOUND).headers().set(
                    HttpHeaderNames.LOCATION,
                    "/" + path + "/"
            );
        }
        String renderResult = renderDir(file, !isSameFile(dir, file));
        fullResp.content().writeBytes(renderResult.getBytes("utf-8"));
        fullResp.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/html; charset=utf-8");
        ctx.write(fullResp);
        ChannelFuture future = ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT);
        future.addListener(ChannelFutureListener.CLOSE);
        return fullResp;
    }

    logger.info("get file now. file: {}", file.getPath());
    if (file.isHidden() || !file.exists() || file.isDirectory() || !file.isFile()) {
        return null;
    }

    long fileLength = file.length();
    if (fileLength < MIN_NETTY_DIRECT_SEND_SIZE) {
        FileInputStream fileInputStream = new FileInputStream(file);
        try {
            byte[] content = IOUtils.getBytes(fileInputStream);
            fullResp.content().writeBytes(content);
            HttpUtil.setContentLength(fullResp, fullResp.content().readableBytes());
        } finally {
            IOUtils.close(fileInputStream);
        }

        ChannelFuture channelFuture = ctx.writeAndFlush(fullResp);
        channelFuture.addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                ChannelFuture lastContentFuture = ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT);
                lastContentFuture.addListener(ChannelFutureListener.CLOSE);
            } else {
                future.channel().close();
            }
        });
        return fullResp;
    }

    logger.info("file {} size bigger than {}, send by future.", file.getName(), MIN_NETTY_DIRECT_SEND_SIZE);
    HttpResponse response = new DefaultHttpResponse(HTTP_1_1, OK);
    HttpUtil.setContentLength(response, fileLength);
    setContentTypeHeader(response, file);
    setDateAndCacheHeaders(response, file);
    if (HttpUtil.isKeepAlive(request)) {
        response.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.KEEP_ALIVE);
    }

    // Write the initial line and the header.
    ctx.write(response);

    // Write the content.
    ChannelFuture sendFileFuture;
    ChannelFuture lastContentFuture;
    RandomAccessFile raf = new RandomAccessFile(file, "r"); // will be closed by netty

    if (ctx.pipeline().get(SslHandler.class) == null) {
        sendFileFuture = ctx.write(new DefaultFileRegion(raf.getChannel(), 0, fileLength), ctx.newProgressivePromise());
        // Write the end marker.
        lastContentFuture = ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT);
    } else {
        sendFileFuture = ctx.writeAndFlush(
                new HttpChunkedInput(new ChunkedFile(raf, 0, fileLength, 8192)),
                ctx.newProgressivePromise()
        );
        // HttpChunkedInput will write the end marker (LastHttpContent) for us.
        lastContentFuture = sendFileFuture;
    }

    sendFileFuture.addListener(new ChannelProgressiveFutureListener() {
        @Override
        public void operationProgressed(ChannelProgressiveFuture future, long progress, long total) {
            if (total < 0) { // total unknown
                logger.info("{} Transfer progress: {}", future.channel(), progress);
            } else {
                logger.info("{} Transfer progress: {} / {}", future.channel(), progress, total);
            }
        }

        @Override
        public void operationComplete(ChannelProgressiveFuture future) {
            logger.info("{} Transfer complete.", future.channel());
        }
    });

    // Decide whether to close the connection or not.
    if (!HttpUtil.isKeepAlive(request)) {
        // Close the connection when the whole content is written out.
        lastContentFuture.addListener(ChannelFutureListener.CLOSE);
    }

    return fullResp;
}

@Override
public byte[] transform(
        final ClassLoader inClassLoader,
        String className,
        Class<?> classBeingRedefined,
        ProtectionDomain protectionDomain,
        byte[] classfileBuffer) throws IllegalClassFormatException {
    try {
        // Check if classloader can load SpyAPI, or skip enhancement
        try {
            if (inClassLoader != null) {
                inClassLoader.loadClass(SpyAPI.class.getName());
            }
        } catch (Throwable e) {
            logger.error(
                    "the classloader can not load SpyAPI, ignore it. classloader: {}, className: {}",
                    inClassLoader.getClass().getName(), className, e);
            return null;
        }

        // Filter again because new classes may be created during transform
        if (matchingClasses != null && !matchingClasses.contains(classBeingRedefined)) {
            return null;
        }

        // keep origin class reader for bytecode optimizations, avoiding JVM metaspace OOM.
        ClassNode classNode = new ClassNode(Opcodes.ASM9);
        ClassReader classReader = AsmUtils.toClassNode(classfileBuffer, classNode);
        // remove JSR https://github.com/alibaba/arthas/issues/1304
        classNode = AsmUtils.removeJSRInstructions(classNode);

        // generate enhanced bytecode
        DefaultInterceptorClassParser defaultInterceptorClassParser = new DefaultInterceptorClassParser();

        final List<InterceptorProcessor> interceptorProcessors = new ArrayList<>();
        interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyInterceptor1.class));
        interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyInterceptor2.class));
        interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyInterceptor3.class));

        if (this.isTracing) {
            if (!this.skipJDKTrace) {
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceInterceptor1.class));
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceInterceptor2.class));
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceInterceptor3.class));
            } else {
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceExcludeJDKInterceptor1.class));
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceExcludeJDKInterceptor2.class));
                interceptorProcessors.addAll(defaultInterceptorClassParser.parse(SpyTraceExcludeJDKInterceptor3.class));
            }
        }

        List<MethodNode> matchedMethods = new ArrayList<>();
        for (MethodNode methodNode : classNode.methods) {
            if (!isIgnore(methodNode, methodNameMatcher)) {
                matchedMethods.add(methodNode);
            }
        }

        // https://github.com/alibaba/arthas/issues/1690
        if (AsmUtils.isEnhancerByCGLIB(className)) {
            for (MethodNode methodNode : matchedMethods) {
                if (AsmUtils.isConstructor(methodNode)) {
                    AsmUtils.fixConstructorExceptionTable(methodNode);
                }
            }
        }

        // Used to check if spy functions already exist to avoid duplicate insertion
        GroupLocationFilter groupLocationFilter = new GroupLocationFilter();

        LocationFilter enterFilter = new InvokeContainLocationFilter(
                Type.getInternalName(SpyAPI.class), "atEnter", LocationType.ENTER);
        LocationFilter existFilter = new InvokeContainLocationFilter(
                Type.getInternalName(SpyAPI.class), "atExit", LocationType.EXIT);
        LocationFilter exceptionFilter = new InvokeContainLocationFilter(
                Type.getInternalName(SpyAPI.class), "atExceptionExit", LocationType.EXCEPTION_EXIT);

        groupLocationFilter.addFilter(enterFilter);
        groupLocationFilter.addFilter(existFilter);
        groupLocationFilter.addFilter(exceptionFilter);

        LocationFilter invokeBeforeFilter = new InvokeCheckLocationFilter(
                Type.getInternalName(SpyAPI.class), "atBeforeInvoke", LocationType.INVOKE);
        LocationFilter invokeAfterFilter = new InvokeCheckLocationFilter(
                Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_COMPLETED);
        LocationFilter invokeExceptionFilter = new InvokeCheckLocationFilter(
                Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_EXCEPTION_EXIT);
        groupLocationFilter.addFilter(invokeBeforeFilter);
        groupLocationFilter.addFilter(invokeAfterFilter);
        groupLocationFilter.addFilter(invokeExceptionFilter);

        for (MethodNode methodNode : matchedMethods) {
            if (AsmUtils.isNative(methodNode)) {
                logger.info("ignore native method: {}",
                        AsmUtils.methodDeclaration(Type.getObjectType(classNode.name), methodNode));
                continue;
            }

            // If atBeforeInvoke exists, trace already present: only register listener
            if (AsmUtils.containsMethodInsnNode(methodNode, Type.getInternalName(SpyAPI.class), "atBeforeInvoke")) {
                for (AbstractInsnNode insnNode = methodNode.instructions.getFirst();
                     insnNode != null;
                     insnNode = insnNode.getNext()) {
                    if (insnNode instanceof MethodInsnNode) {
                        final MethodInsnNode methodInsnNode = (MethodInsnNode) insnNode;

                        if (this.skipJDKTrace) {
                            if (methodInsnNode.owner.startsWith("java/")) {
                                continue;
                            }
                        }

                        // skip boxing types
                        if (AsmOpUtils.isBoxType(Type.getObjectType(methodInsnNode.owner))) {
                            continue;
                        }

                        AdviceListenerManager.registerTraceAdviceListener(
                                inClassLoader, className,
                                methodInsnNode.owner, methodInsnNode.name, methodInsnNode.desc, listener);
                    }
                }
            } else {
                MethodProcessor methodProcessor = new MethodProcessor(classNode, methodNode, groupLocationFilter);
                for (InterceptorProcessor interceptor : interceptorProcessors) {
                    try {
                        List<Location> locations = interceptor.process(methodProcessor);
                        for (Location location : locations) {
                            if (location instanceof MethodInsnNodeWare) {
                                MethodInsnNodeWare methodInsnNodeWare = (MethodInsnNodeWare) location;
                                MethodInsnNode methodInsnNode = methodInsnNodeWare.methodInsnNode();

                                AdviceListenerManager.registerTraceAdviceListener(
                                        inClassLoader, className,
                                        methodInsnNode.owner, methodInsnNode.name, methodInsnNode.desc, listener);
                            }
                        }
                    } catch (Throwable e) {
                        logger.error(
                                "enhancer error, class: {}, method: {}, interceptor: {}",
                                classNode.name, methodNode.name, interceptor.getClass().getName(), e);
                    }
                }
            }

            // enter/exit always register listener
            AdviceListenerManager.registerAdviceListener(
                    inClassLoader, className, methodNode.name, methodNode.desc, listener);
            affect.addMethodAndCount(inClassLoader, className, methodNode.name, methodNode.desc);
        }

        // https://github.com/alibaba/arthas/issues/1223 , V1_5 major version is 49
        if (AsmUtils.getMajorVersion(classNode.version) < 49) {
            classNode.version = AsmUtils.setMajorVersion(classNode.version, 49);
        }

        byte[] enhanceClassByteArray = AsmUtils.toBytes(classNode, inClassLoader, classReader);

        // enhance success, record class
        classBytesCache.put(classBeingRedefined, new Object());

        // dump the class
        dumpClassIfNecessary(className, enhanceClassByteArray, affect);

        // success count
        affect.cCnt(1);

        return enhanceClassByteArray;
    } catch (Throwable t) {
        logger.warn("transform loader[{}]:class[{}] failed.", inClassLoader, className, t);
        affect.setThrowable(t);
    }

    return null;
}

public static boolean completeMethodName(Completion completion) {
    List<CliToken> tokens = completion.lineTokens();
    String lastToken = tokens.get(tokens.size() - 1).value();

    if (StringUtils.isBlank(lastToken)) {
        lastToken = "";
    }

    // retrieve the class name
    String className;
    if (StringUtils.isBlank(lastToken)) {
        // tokens = { " ", "CLASS_NAME", " "}
        className = tokens.get(tokens.size() - 2).value();
    } else {
        // tokens = { " ", "CLASS_NAME", " ", "PARTIAL_METHOD_NAME"}
        className = tokens.get(tokens.size() - 3).value();
    }

    Set<Class<?>> results = SearchUtils.searchClassOnly(completion.session().getInstrumentation(), className, 2);
    if (results.size() != 1) {
        // no class found or multiple class found
        completion.complete(Collections.emptyList());
        return true;
    }

    Class<?> clazz = results.iterator().next();

    List<String> res = new ArrayList<>();
    for (Method method : clazz.getDeclaredMethods()) {
        if (StringUtils.isBlank(lastToken)) {
            res.add(method.getName());
        } else if (method.getName().startsWith(lastToken)) {
            res.add(method.getName());
        }
    }
    res.add("<init>");

    if (res.size() == 1) {
        completion.complete(res.get(0).substring(lastToken.length()), true);
        return true;
    } else {
        CompletionUtils.complete(completion, res);
        return true;
    }
}

private void agentRegister(ChannelHandlerContext ctx, HandshakeComplete handshake, String requestUri)
        throws URISyntaxException {
    QueryStringDecoder queryDecoder = new QueryStringDecoder(requestUri);
    Map<String, List<String>> parameters = queryDecoder.parameters();

    String appName = null;
    List<String> appNameList = parameters.get(URIConstans.APP_NAME);
    if (appNameList != null && !appNameList.isEmpty()) {
        appName = appNameList.get(0);
    }

    // generate a random agent id
    String id;
    if (appName != null) {
        // if app name provided, prefix id with app name for easier management
        id = appName + "_" + RandomStringUtils.random(20, true, true).toUpperCase();
    } else {
        id = RandomStringUtils.random(20, true, true).toUpperCase();
    }

    // prefer id from agent if provided
    List<String> idList = parameters.get(URIConstans.ID);
    if (idList != null && !idList.isEmpty()) {
        id = idList.get(0);
    }

    String arthasVersion = null;
    List<String> arthasVersionList = parameters.get(URIConstans.ARTHAS_VERSION);
    if (arthasVersionList != null && !arthasVersionList.isEmpty()) {
        arthasVersion = arthasVersionList.get(0);
    }

    final String finalId = id;

    URI responseUri = UriComponentsBuilder.newInstance()
            .scheme(URIConstans.RESPONSE)
            .path("/")
            .queryParam(URIConstans.METHOD, MethodConstants.AGENT_REGISTER)
            .queryParam(URIConstans.ID, id)
            .build()
            .encode()
            .toUri();

    AgentInfo info = new AgentInfo();

    // might be behind nginx proxy
    HttpHeaders headers = handshake.requestHeaders();
    String host = HttpUtils.findClientIP(headers);

    if (host == null) {
        SocketAddress remoteAddress = ctx.channel().remoteAddress();
        if (remoteAddress instanceof InetSocketAddress) {
            InetSocketAddress inetSocketAddress = (InetSocketAddress) remoteAddress;
            info.setHost(inetSocketAddress.getHostString());
            info.setPort(inetSocketAddress.getPort());
        }
    } else {
        info.setHost(host);
        Integer port = HttpUtils.findClientPort(headers);
        if (port != null) {
            info.setPort(port);
        }
    }

    info.setChannelHandlerContext(ctx);
    if (arthasVersion != null) {
        info.setArthasVersion(arthasVersion);
    }

    tunnelServer.addAgent(id, info);
    ctx.channel().closeFuture().addListener(new GenericFutureListener<Future<? super Void>>() {
        @Override
        public void operationComplete(Future<? super Void> future) throws Exception {
            tunnelServer.removeAgent(finalId);
        }
    });

    ctx.channel().writeAndFlush(new TextWebSocketFrame(responseUri.toString()));
}

@Override
public int[] startSubnegotiationLocal() {
    int nCompoundWindowSize = m_nWidth * 0x10000 + m_nHeight;
    int nResponseSize = 5;
    int nIndex;
    int nShift;
    int nTurnedOnBits;

    if ((m_nWidth % 0x100) == 0xFF) {
        nResponseSize += 1;
    }
    if ((m_nWidth / 0x100) == 0xFF) {
        nResponseSize += 1;
    }
    if ((m_nHeight % 0x100) == 0xFF) {
        nResponseSize += 1;
    }
    if ((m_nHeight / 0x100) == 0xFF) {
        nResponseSize += 1;
    }

    // allocate response array
    int[] response = new int[nResponseSize];

    // Build response array.
    // 1. put option name.
    // 2. loop through Window size and fill the values,
    // 3. duplicate 'ff' if needed.
    response[0] = WINDOW_SIZE; // 1

    for (nIndex = 1, nShift = 24; nIndex < nResponseSize; nIndex++, nShift -= 8) { // 2
        nTurnedOnBits = 0xFF;
        nTurnedOnBits <<= nShift;
        response[nIndex] = (nCompoundWindowSize & nTurnedOnBits) >>> nShift;

        if (response[nIndex] == 0xff) { // 3
            nIndex++;
            response[nIndex] = 0xff;
        }
    }

    return response;
}

@SuppressWarnings({ "unchecked", "restriction" })
public static URL[] getUrls(ClassLoader classLoader) {
    if (classLoader instanceof URLClassLoader) {
        try {
            return ((URLClassLoader) classLoader).getURLs();
        } catch (Throwable e) {
            logger.error("classLoader: {} getUrls error", classLoader, e);
        }
    }

    // jdk9+
    if (classLoader.getClass().getName().startsWith("jdk.internal.loader.ClassLoaders$")) {
        try {
            Field field = sun.misc.Unsafe.class.getDeclaredField("theUnsafe");
            field.setAccessible(true);
            sun.misc.Unsafe unsafe = (sun.misc.Unsafe) field.get(null);

            Class<?> ucpOwner = classLoader.getClass();
            Field ucpField = null;

            // jdk 9~15: jdk.internal.loader.ClassLoaders$AppClassLoader.ucp
            // jdk 16~17: jdk.internal.loader.BuiltinClassLoader.ucp
            while (ucpField == null && !ucpOwner.getName().equals("java.lang.Object")) {
                try {
                    ucpField = ucpOwner.getDeclaredField("ucp");
                } catch (NoSuchFieldException ex) {
                    ucpOwner = ucpOwner.getSuperclass();
                }
            }
            if (ucpField == null) {
                return null;
            }

            long ucpFieldOffset = unsafe.objectFieldOffset(ucpField);
            Object ucpObject = unsafe.getObject(classLoader, ucpFieldOffset);
            if (ucpObject == null) {
                return null;
            }

            // jdk.internal.loader.URLClassPath.path
            Field pathField = ucpField.getType().getDeclaredField("path");
            if (pathField == null) {
                return null;
            }
            long pathFieldOffset = unsafe.objectFieldOffset(pathField);
            ArrayList<URL> path = (ArrayList<URL>) unsafe.getObject(ucpObject, pathFieldOffset);

            return path.toArray(new URL[path.size()]);
        } catch (Throwable e) {
            // ignore
            return null;
        }
    }
    return null;
}

private Minf parseMinf(final Box ref) throws IOException {
    final Minf obj = new Minf();
    Box b;
    while ((b = untilAnyBox(ref)) != null) {
        switch (b.type) {
            case ATOM_DINF:
                obj.dinf = readFullBox(b);
                break;
            case ATOM_STBL:
                obj.stblStsd = parseStbl(b);
                break;
            case ATOM_VMHD:
            case ATOM_SMHD:
                obj.mhd = readFullBox(b);
                break;
            default:
                break;
        }
        ensure(b);
    }
    return obj;
}

private Moov parseMoov(final Box ref) throws IOException {
    Box b = readBox(ATOM_MVHD);
    final Moov moov = new Moov();
    moov.mvhd = parseMvhd();
    ensure(b);

    final ArrayList<Trak> tmp = new ArrayList<>((int) moov.mvhd.nextTrackId);
    while ((b = untilBox(ref, ATOM_TRAK, ATOM_MVEX)) != null) {
        switch (b.type) {
            case ATOM_TRAK:
                tmp.add(parseTrak(b));
                break;
            case ATOM_MVEX:
                moov.mvexTrex = parseMvex(b, (int) moov.mvhd.nextTrackId);
                break;
            default:
                break;
        }
        ensure(b);
    }

    moov.trak = tmp.toArray(new Trak[0]);
    return moov;
}

private ArrayList<Object> getSpecialItems() {
    synchronized (DownloadManager.this) {
        ArrayList<Mission> pending = new ArrayList<>(mMissionsPending);
        ArrayList<Mission> finished = new ArrayList<>(mMissionsFinished);
        List<Mission> remove = new ArrayList<>(hidden);

        // hide missions (if required)
        remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

        int fakeTotal = pending.size();
        if (fakeTotal > 0) {
            fakeTotal++;
        }

        fakeTotal += finished.size();
        if (finished.size() > 0) {
            fakeTotal++;
        }

        ArrayList<Object> list = new ArrayList<>(fakeTotal);
        if (pending.size() > 0) {
            list.add(PENDING);
            list.addAll(pending);
        }
        if (finished.size() > 0) {
            list.add(FINISHED);
            list.addAll(finished);
        }

        hasFinished = finished.size() > 0;
        return list;
    }
}

@Override
public void run() {
    if (mMission.current > 0) {
        mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);
    }

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) {
                        return;
                    }
                    long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) {
                        finalLength += length;
                    }
                    if (length < lowestSize) {
                        lowestSize = length;
                    }
                }

                mMission.nearLength = finalLength;

                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    if (lowestSize < 1) {
                        mMission.offsets[0] = RESERVE_SPACE_DEFAULT;
                    } else {
                        mMission.offsets[0] = lowestSize < RESERVE_SPACE_MAXIMUM ? lowestSize : RESERVE_SPACE_MAXIMUM;
                    }
                }
            } else {
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;

                if (DEBUG) {
                    Log.d(TAG, "falling back (unknown length)");
                }
            } else {
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {
                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) {
                                count++;
                            }
                            mMission.blocks = new int[count];
                        } else {
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }

                        if (DEBUG) {
                            Log.d(TAG, "http response code = " + mConn.getResponseCode());
                        }
                    } else {
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;

                        if (DEBUG) {
                            Log.d(TAG, "falling back due http response code = " + mConn.getResponseCode());
                        }
                    }
                }

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) {
                return;
            }

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) {
                return;
            }

            if (e instanceof DownloadMission.HttpError && ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }

            Log.e(TAG, "initializer failed, retrying", e);
        }
    }

    mMission.start();
}

@SuppressLint("DefaultLocale")
private void updateProgress(ViewHolderItem h) {
    if (h == null || h.item == null || h.item.mission instanceof FinishedMission) {
        return;
    }

    DownloadMission mission = (DownloadMission) h.item.mission;
    double done = mission.done;
    long length = mission.getLength();
    long now = System.currentTimeMillis();
    boolean hasError = mission.errCode != ERROR_NOTHING;

    h.progress.setMarquee(mission.isRecovering() || (!hasError && (!mission.isInitialized() || mission.unknownLength)));

    double progress;
    if (mission.unknownLength) {
        progress = Double.NaN;
        h.progress.setProgress(0.0f);
    } else {
        progress = done / length;
    }

    if (hasError) {
        h.progress.setProgress(isNotFinite(progress) ? 1d : progress);
        h.status.setText(R.string.msg_error);
    } else if (isNotFinite(progress)) {
        h.status.setText(UNDEFINED_PROGRESS);
    } else {
        h.status.setText(String.format("%.2f%%", progress * 100));
        h.progress.setProgress(progress);
    }

    @StringRes int state;
    String sizeStr = Utility.formatBytes(length).concat("  ");

    if (mission.isPsFailed() || mission.errCode == ERROR_POSTPROCESSING_HOLD) {
        h.size.setText(sizeStr);
        return;
    } else if (!mission.running) {
        state = mission.enqueued ? R.string.queued : R.string.paused;
    } else if (mission.isPsRunning()) {
        state = R.string.post_processing;
    } else if (mission.isRecovering()) {
        state = R.string.recovering;
    } else {
        state = 0;
    }

    if (state != 0) {
        h.size.setText(sizeStr.concat("(").concat(mContext.getString(state)).concat(")"));
        h.resetSpeedMeasure();
        return;
    }

    if (h.lastTimestamp < 0) {
        h.size.setText(sizeStr);
        h.lastTimestamp = now;
        h.lastDone = done;
        return;
    }

    long deltaTime = now - h.lastTimestamp;
    double deltaDone = done - h.lastDone;

    if (h.lastDone > done) {
        h.lastDone = done;
        h.size.setText(sizeStr);
        return;
    }

    if (deltaDone > 0 && deltaTime > 0) {
        float speed = (float) ((deltaDone * 1000d) / deltaTime);
        float averageSpeed = speed;

        if (h.lastSpeedIdx < 0) {
            Arrays.fill(h.lastSpeed, speed);
            h.lastSpeedIdx = 0;
        } else {
            for (int i = 0; i < h.lastSpeed.length; i++) {
                averageSpeed += h.lastSpeed[i];
            }
            averageSpeed /= h.lastSpeed.length + 1.0f;
        }

        String speedStr = Utility.formatSpeed(averageSpeed);
        String etaStr;

        if (mission.unknownLength) {
            etaStr = "";
        } else {
            long eta = (long) Math.ceil((length - done) / averageSpeed);
            etaStr = Utility.formatBytes((long) done) + "/" + Utility.stringifySeconds(eta) + "  ";
        }

        h.size.setText(sizeStr.concat(etaStr).concat(speedStr));

        h.lastTimestamp = now;
        h.lastDone = done;
        h.lastSpeed[h.lastSpeedIdx++] = speed;

        if (h.lastSpeedIdx >= h.lastSpeed.length) {
            h.lastSpeedIdx = 0;
        }
    }
}

private Trun parseTrun() throws IOException {
    final Trun obj = new Trun();
    obj.bFlags = stream.readInt();
    obj.entryCount = stream.readInt(); // unsigned int

    obj.entriesRowSize = 0;
    if (hasFlag(obj.bFlags, 0x0100)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0200)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0400)) {
        obj.entriesRowSize += 4;
    }
    if (hasFlag(obj.bFlags, 0x0800)) {
        obj.entriesRowSize += 4;
    }
    obj.bEntries = new byte[obj.entriesRowSize * obj.entryCount];

    if (hasFlag(obj.bFlags, 0x0001)) {
        obj.dataOffset = stream.readInt();
    }
    if (hasFlag(obj.bFlags, 0x0004)) {
        obj.bFirstSampleFlags = stream.readInt();
    }

    stream.read(obj.bEntries);

    for (int i = 0; i < obj.entryCount; i++) {
        final TrunEntry entry = obj.getEntry(i);
        if (hasFlag(obj.bFlags, 0x0100)) {
            obj.chunkDuration += entry.sampleDuration;
        }
        if (hasFlag(obj.bFlags, 0x0200)) {
            obj.chunkSize += entry.sampleSize;
        }
        if (hasFlag(obj.bFlags, 0x0800)) {
            if (!hasFlag(obj.bFlags, 0x0100)) {
                obj.chunkDuration += entry.sampleCompositionTimeOffset;
            }
        }
    }

    return obj;
}

private void makeMvhd(final long longestTrack) throws IOException {
    auxWrite(new byte[] {
            0x00, 0x00, 0x00, 0x78, 0x6D, 0x76, 0x68, 0x64, 0x01, 0x00, 0x00, 0x00
    });
    auxWrite(ByteBuffer.allocate(28)
            .putLong(time)
            .putLong(time)
            .putInt(DEFAULT_TIMESCALE)
            .putLong(longestTrack)
            .array()
    );

    auxWrite(new byte[] {
            0x00, 0x01, 0x00, 0x00, 0x01, 0x00,
            0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
            0x00, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
            0x00, 0x00, 0x00, 0x00,
            0x40, 0x00, 0x00, 0x00
    });
    auxWrite(new byte[24]);
    auxWrite(ByteBuffer.allocate(4)
            .putInt(tracks.length + 1)
            .array()
    );
}

public Mp4DashChunk getNextChunk(final boolean infoOnly) throws IOException {
    final Mp4Track track = tracks[selectedTrack];

    while (stream.available()) {
        if (chunkZero) {
            ensure(box);
            if (!stream.available()) {
                break;
            }
            box = readBox();
        } else {
            chunkZero = true;
        }

        switch (box.type) {
            case ATOM_MOOF:
                if (moof != null) {
                    throw new IOException("moof found without mdat");
                }

                moof = parseMoof(box, track.trak.tkhd.trackId);

                if (moof.traf != null) {
                    if (hasFlag(moof.traf.trun.bFlags, 0x0001)) {
                        moof.traf.trun.dataOffset -= box.size + 8;
                        if (moof.traf.trun.dataOffset < 0) {
                            throw new IOException("trun box has wrong data offset, points outside of concurrent mdat box");
                        }
                    }

                    if (moof.traf.trun.chunkSize < 1) {
                        if (hasFlag(moof.traf.tfhd.bFlags, 0x10)) {
                            moof.traf.trun.chunkSize = moof.traf.tfhd.defaultSampleSize * moof.traf.trun.entryCount;
                        } else {
                            moof.traf.trun.chunkSize = (int) (box.size - 8);
                        }
                    }
                    if (!hasFlag(moof.traf.trun.bFlags, 0x900) && moof.traf.trun.chunkDuration == 0) {
                        if (hasFlag(moof.traf.tfhd.bFlags, 0x20)) {
                            moof.traf.trun.chunkDuration = moof.traf.tfhd.defaultSampleDuration * moof.traf.trun.entryCount;
                        }
                    }
                }
                break;
            case ATOM_MDAT:
                if (moof == null) {
                    throw new IOException("mdat found without moof");
                }

                if (moof.traf == null) {
                    moof = null;
                    continue;
                }

                final Mp4DashChunk chunk = new Mp4DashChunk();
                chunk.moof = moof;
                if (!infoOnly) {
                    chunk.data = stream.getView(moof.traf.trun.chunkSize);
                }

                moof = null;

                stream.skipBytes(chunk.moof.traf.trun.dataOffset);
                return chunk;
            default:
                break;
        }
    }

    return null;
}

public void updateMission(Mission mission) {
    ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    SQLiteDatabase database = getWritableDatabase();
    String ts = String.valueOf(mission.timestamp);

    int rowsAffected;

    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[] { ts });
        } else {
            rowsAffected = database.update(
                    FINISHED_TABLE_NAME,
                    values,
                    KEY_PATH + " = ?",
                    new String[] { mission.storage.getUri().toString() }
            );
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e("FinishedMissionStore", "Expected 1 row to be affected by update but got " + rowsAffected);
    }
}

private void animatePopupOverlayAndFinishService() {
    int targetTranslationY = (int) (closeOverlayBinding.closeButton.getRootView().getHeight()
            - closeOverlayBinding.closeButton.getY());

    closeOverlayBinding.closeButton.animate().setListener(null).cancel();
    closeOverlayBinding.closeButton.animate()
            .setInterpolator(new AnticipateInterpolator())
            .translationY(targetTranslationY)
            .setDuration(400)
            .setListener(new AnimatorListenerAdapter() {
                @Override
                public void onAnimationCancel(Animator animation) {
                    end();
                }

                @Override
                public void onAnimationEnd(Animator animation) {
                    end();
                }

                private void end() {
                    windowManager.removeView(closeOverlayBinding.getRoot());
                    closeOverlayBinding = null;
                    player.getService().destroyPlayerAndStopService();
                }
            }).start();
}

public void play() {
    if (DEBUG) {
        Log.d(TAG, "play() called");
    }
    if (audioReactor == null || playQueue == null || exoPlayerIsNull()) {
        return;
    }

    if (!isMuted()) {
        audioReactor.requestAudioFocus();
    }

    if (currentState == STATE_COMPLETED) {
        if (playQueue.getIndex() == 0) {
            seekToDefault();
        } else {
            playQueue.setIndex(0);
        }
    }

    simpleExoPlayer.play();
    saveStreamProgressState();
}

synchronized private NotificationCompat.Builder createNotification() {
    if (DEBUG) {
        Log.d(TAG, "createNotification()");
    }
    notificationManager = NotificationManagerCompat.from(player.getContext());
    NotificationCompat.Builder builder =
            new NotificationCompat.Builder(player.getContext(),
                    player.getContext().getString(R.string.notification_channel_id));
    MediaStyle mediaStyle = new MediaStyle();

    // setup media style (compact notification slots and media session)
    if (Build.VERSION.SDK_INT < Build.VERSION_CODES.TIRAMISU) {
        // notification actions are ignored on Android 13+, and are replaced by code in
        // MediaSessionPlayerUi
        int[] compactSlots = initializeNotificationSlots();
        mediaStyle.setShowActionsInCompactView(compactSlots);
    }
    player.UIs()
            .get(MediaSessionPlayerUi.class)
            .flatMap(MediaSessionPlayerUi::getSessionToken)
            .ifPresent(mediaStyle::setMediaSession);

    // setup notification builder
    builder.setStyle(mediaStyle)
            .setPriority(NotificationCompat.PRIORITY_HIGH)
            .setVisibility(NotificationCompat.VISIBILITY_PUBLIC)
            .setCategory(NotificationCompat.CATEGORY_TRANSPORT)
            .setShowWhen(false)
            .setSmallIcon(R.drawable.ic_newpipe_triangle_white)
            .setColor(ContextCompat.getColor(player.getContext(),
                    R.color.dark_background_color))
            .setColorized(player.getPrefs().getBoolean(
                    player.getContext().getString(R.string.notification_colorize_key), true))
            .setDeleteIntent(PendingIntentCompat.getBroadcast(player.getContext(),
                    NOTIFICATION_ID, new Intent(ACTION_CLOSE), FLAG_UPDATE_CURRENT, false));

    // set the initial value for the video thumbnail, updatable with updateNotificationThumbnail
    setLargeIcon(builder);

    return builder;
}

private void onBroadcastReceived(Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }

    switch (intent.getAction()) {
        case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
            pause();
            break;
        case ACTION_CLOSE:
            service.destroyPlayerAndStopService();
            break;
        case ACTION_PLAY_PAUSE:
            playPause();
            break;
        case ACTION_PLAY_PREVIOUS:
            playPrevious();
            break;
        case ACTION_PLAY_NEXT:
            playNext();
            break;
        case ACTION_FAST_REWIND:
            fastRewind();
            break;
        case ACTION_FAST_FORWARD:
            fastForward();
            break;
        case ACTION_REPEAT:
            cycleNextRepeatMode();
            break;
        case ACTION_SHUFFLE:
            toggleShuffleModeEnabled();
            break;
        case Intent.ACTION_CONFIGURATION_CHANGED:
            if (DEBUG) {
                Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
            }
            break;
    }

    UIs.call(playerUi -> playerUi.onBroadcastReceived(intent));
}

protected void addImagesMetadataItem(LayoutInflater inflater,
                                     LinearLayout layout,
                                     @StringRes int type,
                                     List<Image> images) {
    String preferredImageUrl = ImageStrategy.choosePreferredImage(images);
    if (preferredImageUrl == null) {
        return; // null will be returned in case there is no image
    }

    ItemMetadataBinding itemBinding = ItemMetadataBinding.inflate(inflater, layout, false);
    itemBinding.metadataTypeView.setText(type);

    SpannableStringBuilder urls = new SpannableStringBuilder();
    for (Image image : images) {
        if (urls.length() != 0) {
            urls.append(", ");
        }
        int entryBegin = urls.length();

        if (image.getHeight() != Image.HEIGHT_UNKNOWN
                || image.getWidth() != Image.WIDTH_UNKNOWN
                // if even the resolution level is unknown, ?x? will be shown
                || image.getEstimatedResolutionLevel() == Image.ResolutionLevel.UNKNOWN) {
            urls.append(imageSizeToText(image.getHeight()));
            urls.append('x');
            urls.append(imageSizeToText(image.getWidth()));
        } else {
            switch (image.getEstimatedResolutionLevel()) {
                case LOW:
                    urls.append(getString(R.string.image_quality_low));
                    break;
                case MEDIUM:
                    urls.append(getString(R.string.image_quality_medium));
                    break;
                case HIGH:
                    urls.append(getString(R.string.image_quality_high));
                    break;
                default: {
                    // unreachable, Image.ResolutionLevel.UNKNOWN is already filtered out
                }
            }
        }

        urls.setSpan(new ClickableSpan() {
            @Override
            public void onClick(@NonNull View widget) {
                ShareUtils.openUrlInBrowser(requireContext(), image.getUrl());
            }
        }, entryBegin, urls.length(), Spanned.SPAN_EXCLUSIVE_EXCLUSIVE);

        if (preferredImageUrl.equals(image.getUrl())) {
            urls.setSpan(new StyleSpan(Typeface.BOLD), entryBegin, urls.length(),
                    Spanned.SPAN_EXCLUSIVE_EXCLUSIVE);
        }
    }

    itemBinding.metadataContentView.setText(urls);
    itemBinding.metadataContentView.setMovementMethod(LinkMovementMethod.getInstance());
    layout.addView(itemBinding.getRoot());
}

private void openDetailFragmentFromCommentReplies(@NonNull FragmentManager fm,
                                                  boolean popBackStack) {
    // obtain the name of the fragment under the replies fragment that's going to be popped
    @Nullable String fragmentUnderEntryName;
    if (fm.getBackStackEntryCount() < 2) {
        fragmentUnderEntryName = null;
    } else {
        fragmentUnderEntryName = fm.getBackStackEntryAt(fm.getBackStackEntryCount() - 2).getName();
    }

    // the root comment is the comment for which the user opened the replies page
    @Nullable CommentRepliesFragment repliesFragment =
            (CommentRepliesFragment) fm.findFragmentByTag(CommentRepliesFragment.TAG);
    @Nullable CommentsInfoItem rootComment =
            repliesFragment == null ? null : repliesFragment.getCommentsInfoItem();

    // sometimes this function pops the backstack, other times it's handled by the system
    if (popBackStack) {
        fm.popBackStackImmediate();
    }

    // only expand the bottom sheet back if there are no more nested comment replies fragments
    // stacked under the one that is currently being popped
    if (CommentRepliesFragment.TAG.equals(fragmentUnderEntryName)) {
        return;
    }

    BottomSheetBehavior<FragmentContainerView> behavior =
            BottomSheetBehavior.from(mainBinding.fragmentPlayerHolder);
    // do not return to the comment if the details fragment was closed
    if (behavior.getState() == BottomSheetBehavior.STATE_HIDDEN) {
        return;
    }

    // scroll to the root comment once the bottom sheet expansion animation is finished
    behavior.addBottomSheetCallback(new BottomSheetBehavior.BottomSheetCallback() {
        @Override
        public void onStateChanged(@NonNull View bottomSheet, int newState) {
            if (newState == BottomSheetBehavior.STATE_EXPANDED) {
                Fragment detailFragment = fm.findFragmentById(R.id.fragment_player_holder);
                if (detailFragment instanceof VideoDetailFragment && rootComment != null) {
                    // should always be the case
                    ((VideoDetailFragment) detailFragment).scrollToComment(rootComment);
                }
                behavior.removeBottomSheetCallback(this);
            }
        }

        @Override
        public void onSlide(@NonNull View bottomSheet, float slideOffset) {
            // not needed, listener is removed once the sheet is expanded
        }
    });

    behavior.setState(BottomSheetBehavior.STATE_EXPANDED);
}

private void buildCaptionMenu(@NonNull List<String> availableLanguages) {
    if (captionPopupMenu == null) {
        return;
    }
    captionPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_CAPTION);

    captionPopupMenu.setOnDismissListener(this);

    // Add option for turning off caption
    MenuItem captionOffItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION,
            0, Menu.NONE, R.string.caption_none);
    captionOffItem.setOnMenuItemClickListener(menuItem -> {
        int textRendererIndex = player.getCaptionRendererIndex();
        if (textRendererIndex != RENDERER_UNAVAILABLE) {
            player.getTrackSelector().setParameters(player.getTrackSelector()
                    .buildUponParameters().setRendererDisabled(textRendererIndex, true));
        }
        player.getPrefs().edit()
                .remove(context.getString(R.string.caption_user_set_key)).apply();
        return true;
    });

    // Add all available captions
    for (int i = 0; i < availableLanguages.size(); i++) {
        String captionLanguage = availableLanguages.get(i);
        MenuItem captionItem = captionPopupMenu.getMenu().add(POPUP_MENU_ID_CAPTION,
                i + 1, Menu.NONE, captionLanguage);
        captionItem.setOnMenuItemClickListener(menuItem -> {
            int textRendererIndex = player.getCaptionRendererIndex();
            if (textRendererIndex != RENDERER_UNAVAILABLE) {
                // DefaultTrackSelector will select for text tracks in the following order.
                // When multiple tracks share the same rank, a random track will be chosen.
                // 1. ANY track exactly matching preferred language name
                // 2. ANY track exactly matching preferred language stem
                // 3. ROLE_FLAG_CAPTION track matching preferred language stem
                // 4. ROLE_FLAG_DESCRIBES_MUSIC_AND_SOUND track matching preferred language stem
                // This means if a caption track of preferred language is not available,
                // then an auto-generated track of that language will be chosen automatically.
                player.getTrackSelector().setParameters(player.getTrackSelector()
                        .buildUponParameters()
                        .setPreferredTextLanguages(captionLanguage,
                                PlayerHelper.captionLanguageStemOf(captionLanguage))
                        .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
                        .setRendererDisabled(textRendererIndex, false));
                player.getPrefs().edit()
                        .putString(context.getString(R.string.caption_user_set_key), captionLanguage)
                        .apply();
            }
            return true;
        });
    }
    captionPopupMenu.setOnDismissListener(this);

    // apply caption language from previous user preference
    int textRendererIndex = player.getCaptionRendererIndex();
    if (textRendererIndex == RENDERER_UNAVAILABLE) {
        return;
    }

    // If user prefers to show no caption, then disable the renderer.
    // Otherwise, DefaultTrackSelector may automatically find an available caption
    // and display that.
    String userPreferredLanguage =
            player.getPrefs().getString(context.getString(R.string.caption_user_set_key), null);
    if (userPreferredLanguage == null) {
        player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters()
                .setRendererDisabled(textRendererIndex, true));
        return;
    }

    // Only set preferred language if it does not match the user preference,
    // otherwise there might be an infinite cycle at onTextTracksChanged.
    List<String> selectedPreferredLanguages =
            player.getTrackSelector().getParameters().preferredTextLanguages;
    if (!selectedPreferredLanguages.contains(userPreferredLanguage)) {
        player.getTrackSelector().setParameters(player.getTrackSelector().buildUponParameters()
                .setPreferredTextLanguages(userPreferredLanguage,
                        PlayerHelper.captionLanguageStemOf(userPreferredLanguage))
                .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
                .setRendererDisabled(textRendererIndex, false));
    }
}

private void onItemSelectedSetFileName() {
    String fileName = FilenameUtils.createFilename(getContext(), currentInfo.getName());
    String prevFileName = Optional.ofNullable(dialogBinding.fileName.getText())
            .map(Object::toString)
            .orElse("");

    if (prevFileName.isEmpty()
            || prevFileName.equals(fileName)
            || prevFileName.startsWith(getString(R.string.caption_file_name, fileName, ""))) {
        // only update the file name field if it was not edited by the user

        switch (dialogBinding.videoAudioGroup.getCheckedRadioButtonId()) {
            case R.id.audio_button:
            case R.id.video_button:
                if (!prevFileName.equals(fileName)) {
                    // since the user might have switched between audio and video, the correct
                    // text might already be in place, so avoid resetting the cursor position
                    dialogBinding.fileName.setText(fileName);
                }
                break;

            case R.id.subtitle_button:
                String setSubtitleLanguageCode =
                        subtitleStreamsAdapter.getItem(selectedSubtitleIndex).getLanguageTag();
                // this will reset the cursor position, which is bad UX, but it can't be avoided
                dialogBinding.fileName.setText(
                        getString(R.string.caption_file_name, fileName, setSubtitleLanguageCode));
                break;
        }
    }
}

