private void process() {
    do {
        Object reply = authenticator.client.getUnflushedObject();

        if (reply instanceof List) {
            List<Object> listReply = (List<Object>) reply;
            final Object firstObj = listReply.get(0);
            if (!(firstObj instanceof byte[])) {
                throw new JedisException("Unknown message type: " + firstObj);
            }

            final byte[] resp = (byte[]) firstObj;
            if (Arrays.equals(SUBSCRIBE.getRaw(), resp)) {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bchannel = (byte[]) listReply.get(1);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                onSubscribe(enchannel, subscribedChannels);

            } else if (Arrays.equals(UNSUBSCRIBE.getRaw(), resp)) {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bchannel = (byte[]) listReply.get(1);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                onUnsubscribe(enchannel, subscribedChannels);

            } else if (Arrays.equals(MESSAGE.getRaw(), resp)) {
                final byte[] bchannel = (byte[]) listReply.get(1);
                final Object mesg = listReply.get(2);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                if (mesg instanceof List) {
                    ((List<byte[]>) mesg).forEach(bmesg -> onMessage(enchannel, encode(bmesg)));
                } else {
                    onMessage(enchannel, (mesg == null) ? null : encode((byte[]) mesg));
                }

            } else if (Arrays.equals(PMESSAGE.getRaw(), resp)) {
                final byte[] bpattern = (byte[]) listReply.get(1);
                final byte[] bchannel = (byte[]) listReply.get(2);
                final byte[] bmesg = (byte[]) listReply.get(3);
                final T enpattern = (bpattern == null) ? null : encode(bpattern);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                final T enmesg = (bmesg == null) ? null : encode(bmesg);
                onPMessage(enpattern, enchannel, enmesg);

            } else if (Arrays.equals(PSUBSCRIBE.getRaw(), resp)) {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bpattern = (byte[]) listReply.get(1);
                final T enpattern = (bpattern == null) ? null : encode(bpattern);
                onPSubscribe(enpattern, subscribedChannels);

            } else if (Arrays.equals(PUNSUBSCRIBE.getRaw(), resp)) {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bpattern = (byte[]) listReply.get(1);
                final T enpattern = (bpattern == null) ? null : encode(bpattern);
                onPUnsubscribe(enpattern, subscribedChannels);

            } else if (Arrays.equals(PONG.getRaw(), resp)) {
                final byte[] bpattern = (byte[]) listReply.get(1);
                final T enpattern = (bpattern == null) ? null : encode(bpattern);
                onPong(enpattern);

            } else {
                throw new JedisException("Unknown message type: " + firstObj);
            }

        } else if (reply instanceof byte[]) {
            Consumer<Object> resultHandler = authenticator.resultHandler.poll();
            if (resultHandler == null) {
                throw new JedisException("Unexpected message : " + SafeEncoder.encode((byte[]) reply));
            }
            resultHandler.accept(reply);

        } else {
            throw new JedisException("Unknown message type: " + reply);
        }
    } while (!Thread.currentThread().isInterrupted() && isSubscribed());

    // /* Invalidate instance since this thread is no longer listening */
    // this.client = null;
}

@Override
public final <T> T broadcastCommand(CommandObject<T> commandObject) {
    Map<String, ConnectionPool> connectionMap = provider.getConnectionMap();

    boolean isErrored = false;
    T reply = null;
    JedisBroadcastException bcastError = new JedisBroadcastException();

    for (Map.Entry<String, ConnectionPool> entry : connectionMap.entrySet()) {
        HostAndPort node = HostAndPort.from(entry.getKey());
        ConnectionPool pool = entry.getValue();

        try (Connection connection = pool.getResource()) {
            T aReply = execute(connection, commandObject);
            bcastError.addReply(node, aReply);

            if (isErrored) {
                // already errored
            } else if (reply == null) {
                reply = aReply; // ok
            } else if (reply.equals(aReply)) {
                // ok
            } else {
                isErrored = true;
                reply = null;
            }
        } catch (Exception anError) {
            bcastError.addReply(node, anError);
            isErrored = true;
        }
    }

    if (isErrored) {
        throw bcastError;
    }
    return reply;
}

@Override
public Map<String, Object> build(Object data) {
    if (data == null) {
        return null;
    }
    final List<Object> list = (List<Object>) data;
    if (list.isEmpty()) {
        return Collections.emptyMap();
    }

    if (list.get(0) instanceof KeyValue) {
        final Map<String, Object> map = new HashMap<>(list.size(), 1f);
        final Iterator iterator = list.iterator();
        while (iterator.hasNext()) {
            KeyValue kv = (KeyValue) iterator.next();
            map.put(STRING.build(kv.getKey()), ENCODED_OBJECT.build(kv.getValue()));
        }
        return map;
    } else {
        final Map<String, Object> map = new HashMap<>(list.size() / 2, 1f);
        final Iterator iterator = list.iterator();
        while (iterator.hasNext()) {
            map.put(STRING.build(iterator.next()), ENCODED_OBJECT.build(iterator.next()));
        }
        return map;
    }
}

@Override
public boolean isCacheable(ProtocolCommand command, List<Object> keys) {
    if (allowCommands != null && !allowCommands.contains(command)) {
        return false;
    }
    if (denyCommands != null && denyCommands.contains(command)) {
        return false;
    }

    for (Object key : keys) {
        if (!(key instanceof String)) {
            return false;
        }
        if (allowKeys != null && !allowKeys.contains((String) key)) {
            return false;
        }
        if (denyKeys != null && denyKeys.contains((String) key)) {
            return false;
        }
    }

    return DefaultCacheable.isDefaultCacheableCommand(command);
}

@Override
public <T> T executeCommand(final CommandObject<T> commandObject) {
    final CacheKey cacheKey = new CacheKey(commandObject);
    if (!cache.isCacheable(cacheKey)) {
        cache.getStats().nonCacheable();
        return super.executeCommand(commandObject);
    }

    CacheEntry<T> cacheEntry = cache.get(cacheKey);
    if (cacheEntry != null) { // (probable) CACHE HIT !!
        cacheEntry = validateEntry(cacheEntry);
        if (cacheEntry != null) {
            // CACHE HIT confirmed !!!
            cache.getStats().hit();
            return cacheEntry.getValue();
        }
    }

    // CACHE MISS !!
    cache.getStats().miss();
    T value = super.executeCommand(commandObject);
    cacheEntry = new CacheEntry<>(cacheKey, value, this);
    cache.set(cacheKey, cacheEntry);
    // this line actually provides a deep copy of cached object instance
    value = cacheEntry.getValue();
    return value;
}

@Override
public String apply(String input) {
    StringBuilder output = new StringBuilder();
    String[] lines = input.split("\n");
    int continueCount = 0;
    int lastStartPos = 0;
    int lastContinueLineNum = -1;
    int matchCount = 0;

    for (int lineNum = 0; lineNum < lines.length; ) {
        String line = this.trimEnd ? StringUtils.stripEnd(lines[lineNum], null) : lines[lineNum];
        lineNum++;

        final boolean match;
        if (pattern == null) {
            match = (ignoreCase ? line.toLowerCase() : line).contains(keyword);
        } else {
            match = pattern.matcher(line).find();
        }

        if (invertMatch != match) {
            matchCount++;

            if (beforeLines > continueCount) {
                int n = (lastContinueLineNum == -1)
                        ? (beforeLines >= lineNum ? 1 : lineNum - beforeLines)
                        : lineNum - beforeLines - continueCount;

                if (n >= lastContinueLineNum || lastContinueLineNum == -1) {
                    StringBuilder beforeSb = new StringBuilder();
                    for (int i = n; i < lineNum; i++) {
                        appendLine(beforeSb, i, lines[i - 1]);
                    }
                    output.insert(lastStartPos, beforeSb);
                }
            }

            lastStartPos = output.length();
            appendLine(output, lineNum, line);

            if (afterLines > continueCount) {
                int last = lineNum + afterLines - continueCount;
                if (last > lines.length) {
                    last = lines.length;
                }
                for (int i = lineNum; i < last; i++) {
                    appendLine(output, i + 1, lines[i]);
                    lineNum++;
                    continueCount++;
                    lastStartPos = output.length();
                }
            }

            continueCount++;
            if (maxCount > 0 && matchCount >= maxCount) {
                break;
            }
        } else {
            if (continueCount > 0) {
                lastContinueLineNum = lineNum - 1;
                continueCount = 0;
            }
        }
    }

    return output.toString();
}

private static List<String> readLines(File batchFile) {
    List<String> list = new ArrayList<>();
    BufferedReader br = null;
    try {
        br = new BufferedReader(new FileReader(batchFile));
        String line = br.readLine();
        while (line != null) {
            list.add(line);
            line = br.readLine();
        }
    } catch (IOException e) {
        e.printStackTrace();
    } finally {
        if (br != null) {
            try {
                br.close();
            } catch (IOException e) {
                // ignore
            }
        }
    }
    return list;
}

private void renderObject(Object obj, int deep, int expand, final StringBuilder buf) throws ObjectTooLargeException {

    if (obj == null) {
        appendStringBuilder(buf, "null");
        return;
    }

    final Class<?> clazz = obj.getClass();
    final String className = clazz.getSimpleName();

    // 7,@[]
    if (Integer.class.isInstance(obj)
            || Long.class.isInstance(obj)
            || Float.class.isInstance(obj)
            || Double.class.isInstance(obj)
            // || Character.class.isInstance(obj)
            || Short.class.isInstance(obj)
            || Byte.class.isInstance(obj)
            || Boolean.class.isInstance(obj)) {
        appendStringBuilder(buf, format("@%s[%s]", className, obj));
    }

    // Char,
    else if (Character.class.isInstance(obj)) {
        final Character c = (Character) obj;

        // ASCII
        if (c >= 32 && c <= 126) {
            appendStringBuilder(buf, format("@%s[%s]", className, c));
        }

        // ASCII
        else if (ASCII_MAP.containsKey((byte) c.charValue())) {
            appendStringBuilder(buf, format("@%s[%s]", className, ASCII_MAP.get((byte) c.charValue())));
        }

        // ASCII
        else {
            appendStringBuilder(buf, format("@%s[%s]", className, c));
        }
    }

    // 
    else if (String.class.isInstance(obj)) {
        appendStringBuilder(buf, "@");
        appendStringBuilder(buf, className);
        appendStringBuilder(buf, "[");
        for (Character c : ((String) obj).toCharArray()) {
            switch (c) {
                case '\n':
                    appendStringBuilder(buf, "\\n");
                    break;
                case '\r':
                    appendStringBuilder(buf, "\\r");
                    break;
                default:
                    appendStringBuilder(buf, c.toString());
            }
        }
        appendStringBuilder(buf, "]");
    }

    // 
    else if (Collection.class.isInstance(obj)) {

        @SuppressWarnings("unchecked")
        final Collection<Object> collection = (Collection<Object>) obj;

        // 
        if (!isExpand(deep, expand) || collection.isEmpty()) {
            appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", className, collection.isEmpty(), collection.size()));
        }

        // 
        else {
            appendStringBuilder(buf, format("@%s[", className));
            for (Object e : collection) {
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep + 1; i++) {
                    appendStringBuilder(buf, TAB);
                }
                renderObject(e, deep + 1, expand, buf);
                appendStringBuilder(buf, ",");
            }
            appendStringBuilder(buf, "\n");
            for (int i = 0; i < deep; i++) {
                appendStringBuilder(buf, TAB);
            }
            appendStringBuilder(buf, "]");
        }
    }

    // Map
    else if (Map.class.isInstance(obj)) {
        @SuppressWarnings("unchecked")
        final Map<Object, Object> map = (Map<Object, Object>) obj;

        // 
        if (!isExpand(deep, expand) || map.isEmpty()) {
            appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", className, map.isEmpty(), map.size()));
        } else {
            appendStringBuilder(buf, format("@%s[", className));
            for (Map.Entry<Object, Object> entry : map.entrySet()) {
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep + 1; i++) {
                    appendStringBuilder(buf, TAB);
                }
                renderObject(entry.getKey(), deep + 1, expand, buf);
                appendStringBuilder(buf, ":");
                renderObject(entry.getValue(), deep + 1, expand, buf);
                appendStringBuilder(buf, ",");
            }
            appendStringBuilder(buf, "\n");
            for (int i = 0; i < deep; i++) {
                appendStringBuilder(buf, TAB);
            }
            appendStringBuilder(buf, "]");
        }
    }

    // 
    else if (obj.getClass().isArray()) {

        final String typeName = obj.getClass().getSimpleName();

        // int[]
        if (typeName.equals("int[]")) {
            final int[] arrays = (int[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (int e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // long[]
        else if (typeName.equals("long[]")) {
            final long[] arrays = (long[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (long e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // short[]
        else if (typeName.equals("short[]")) {
            final short[] arrays = (short[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (short e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // float[]
        else if (typeName.equals("float[]")) {
            final float[] arrays = (float[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (float e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // double[]
        else if (typeName.equals("double[]")) {
            final double[] arrays = (double[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (double e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // boolean[]
        else if (typeName.equals("boolean[]")) {
            final boolean[] arrays = (boolean[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (boolean e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // char[]
        else if (typeName.equals("char[]")) {
            final char[] arrays = (char[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (char e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // byte[]
        else if (typeName.equals("byte[]")) {
            final byte[] arrays = (byte[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
                appendStringBuilder(buf, format("@%s[", className));
                for (byte e : arrays) {
                    appendStringBuilder(buf, "\n");
                    for (int i = 0; i < deep + 1; i++) {
                        appendStringBuilder(buf, TAB);
                    }
                    renderObject(e, deep + 1, expand, buf);
                    appendStringBuilder(buf, ",");
                }
                appendStringBuilder(buf, "\n");
                for (int i = 0; i < deep; i++) {
                    appendStringBuilder(buf, TAB);
                }
                appendStringBuilder(buf, "]");
            }
        }

        // Object[]
        else {
            final Object[] arrays = (Object[]) obj;

            if (!isExpand(deep, expand) || arrays.length == 0) {
                appendStringBuilder(buf, format("@%s[isEmpty=%s;size=%d]", typeName, arrays.length == 0, arrays.length));
            } else {
