public boolean isAvailable() {
    if (!isConnected()) {
        return false;
    }
    try {
        if (_socket_.getInetAddress() == null) {
            return false;
        }
        if (_socket_.getPort() == 0) {
            return false;
        }
        if (_socket_.getRemoteSocketAddress() == null) {
            return false;
        }
        if (_socket_.isClosed()) {
            return false;
        }
        // These aren't exact checks (a Socket can be half-open), but since we usually
        // require two-way data transfer, we check these here too:
        if (_socket_.isInputShutdown()) {
            return false;
        }
        if (_socket_.isOutputShutdown()) {
            return false;
        }
        // ignore the result, catch exceptions:
        _socket_.getInputStream();
        _socket_.getOutputStream();
        return true;
    } catch (IOException ioex) {
        return false;
    }
}

public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
    // :
    table.row("IS-RETURN", "" + false);
    table.row("IS-EXCEPTION", "" + true);

    // :
    Throwable t = (Throwable) throwableVO.getObject();
    Throwable cause = (t instanceof InvocationTargetException) ? t.getCause() : t;

    if (throwableVO.needExpand()) {
        table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
    } else {
        StringWriter stringWriter = new StringWriter();
        PrintWriter printWriter = new PrintWriter(stringWriter);
        try {
            cause.printStackTrace(printWriter);
            table.row("THROW-EXCEPTION", stringWriter.toString());
        } finally {
            printWriter.close();
        }
    }
}

@Override
public void process(final CommandProcess process) {

    Session session = process.session();
    timer = new Timer("Timer-for-arthas-dashboard-" + session.getSessionId(), true);

    // ctrl-C support
    process.interruptHandler(new DashboardInterruptHandler(process, timer));

    // handlesuspendendtimerresumetimer
    Handler<Void> stopHandler =
            new Handler<Void>() {
                @Override
                public void handle(Void event) {
                    stop();
                }
            };

    Handler<Void> restartHandler =
            new Handler<Void>() {
                @Override
                public void handle(Void event) {
                    restart(process);
                }
            };

    process.suspendHandler(stopHandler);
    process.resumeHandler(restartHandler);
    process.endHandler(stopHandler);

    // q exit support
    process.stdinHandler(new QExitHandler(process));

    // start the timer
    timer.scheduleAtFixedRate(new DashboardTimerTask(process), 0, getInterval());
}

private String drawField() {

    final StringBuilder fieldSB = new StringBuilder();

    final Field[] fields = clazz.getDeclaredFields();
    if (fields.length > 0) {
        for (Field field : fields) {
            final KVView kvView =
                    new KVView(
                                    new TableView.ColumnDefine(TableView.Align.RIGHT),
                                    new TableView.ColumnDefine(50, false, TableView.Align.LEFT))
                            .add("modifier", StringUtils.modifier(field.getModifiers(), ','))
                            .add("type", StringUtils.classname(field.getType()))
                            .add("name", field.getName());

            final StringBuilder annotationSB = new StringBuilder();
            final Annotation[] annotationArray = field.getAnnotations();
            if (annotationArray != null && annotationArray.length > 0) {
                for (Annotation annotation : annotationArray) {
                    annotationSB.append(StringUtils.classname(annotation.annotationType())).append(",");
                }
                if (annotationSB.length() > 0) {
                    annotationSB.deleteCharAt(annotationSB.length() - 1);
                }
                kvView.add("annotation", annotationSB);
            }

            if (Modifier.isStatic(field.getModifiers())) {
                final boolean isAccessible = field.isAccessible();
                try {
                    field.setAccessible(true);
                    kvView.add("value", StringUtils.objectToString(field.get(null)));
                } catch (IllegalAccessException e) {
                    // ignore
                } finally {
                    field.setAccessible(isAccessible);
                }
            }

            fieldSB.append(kvView.draw()).append("\n");
        }
    }

    return fieldSB.toString();
}

private static String normalizeArch(String value) {
    value = normalize(value);

    if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
        return "x86_64";
    }
    if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
        return "x86_32";
    }
    if (value.matches("^(ia64w?|itanium64)$")) {
        return "itanium_64";
    }
    if ("ia64n".equals(value)) {
        return "itanium_32";
    }
    if (value.matches("^(sparc|sparc32)$")) {
        return "sparc_32";
    }
    if (value.matches("^(sparcv9|sparc64)$")) {
        return "sparc_64";
    }
    if (value.matches("^(arm|arm32)$")) {
        return "arm_32";
    }
    if ("aarch64".equals(value)) {
        return "aarch_64";
    }
    if (value.matches("^(mips|mips32)$")) {
        return "mips_32";
    }
    if (value.matches("^(mipsel|mips32el)$")) {
        return "mipsel_32";
    }
    if ("mips64".equals(value)) {
        return "mips_64";
    }
    if ("mips64el".equals(value)) {
        return "mipsel_64";
    }
    if (value.matches("^(ppc|ppc32)$")) {
        return "ppc_32";
    }
    if (value.matches("^(ppcle|ppc32le)$")) {
        return "ppcle_32";
    }
    if ("ppc64".equals(value)) {
        return "ppc_64";
    }
    if ("ppc64le".equals(value)) {
        return "ppcle_64";
    }
    if ("s390".equals(value)) {
        return "s390_32";
    }
    if ("s390x".equals(value)) {
        return "s390_64";
    }
    return value;
}

public void start() throws Exception {
    // Configure SSL.
    final SslContext sslCtx;
    if (ssl) {
        SelfSignedCertificate ssc = new SelfSignedCertificate();
        sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
    } else {
        sslCtx = null;
    }

    ServerBootstrap b = new ServerBootstrap();
    b.group(bossGroup, workerGroup)
            .channel(NioServerSocketChannel.class)
            .handler(new LoggingHandler(LogLevel.INFO))
            .childHandler(new TunnelSocketServerInitializer(this, sslCtx));

    if (StringUtils.isBlank(host)) {
        channel = b.bind(port).sync().channel();
    } else {
        channel = b.bind(host, port).sync().channel();
    }

    logger.info("Tunnel server listen at {}:{}", host, port);

    workerGroup.scheduleWithFixedDelay(
            new Runnable() {
                @Override
                public void run() {
                    agentInfoMap.entrySet()
                            .removeIf(e -> !e.getValue().getChannelHandlerContext().channel().isActive());
                    clientConnectionInfoMap.entrySet()
                            .removeIf(e -> !e.getValue().getChannelHandlerContext().channel().isActive());

                    // key
                    if (tunnelClusterStore != null && clientConnectHost != null) {
                        try {
                            for (Entry<String, AgentInfo> entry : agentInfoMap.entrySet()) {
                                tunnelClusterStore.addAgent(
                                        entry.getKey(),
                                        new AgentClusterInfo(entry.getValue(), clientConnectHost, port),
                                        60 * 60,
                                        TimeUnit.SECONDS);
                            }
                        } catch (Throwable t) {
                            logger.error("update tunnel info error", t);
                        }
                    }
                }
            },
            60,
            60,
            TimeUnit.SECONDS);
}

public static String nullSafeToString(byte[] array) {
    if (array == null) {
        return "null";
    }
    int length = array.length;
    if (length == 0) {
        return "{}";
    }

    StringBuilder sb = new StringBuilder("{");
    for (int i = 0; i < length; i++) {
        if (i > 0) {
            sb.append(", ");
        }
        sb.append(array[i]);
    }
    sb.append("}");
    return sb.toString();
}

