@Override
public final <T> T broadcastCommand(CommandObject<T> commandObject)
{
    Map<String, ConnectionPool> connectionMap = provider.getConnectionMap();

    boolean isErrored = false;
    T reply = null;
    JedisBroadcastException bcastError = new JedisBroadcastException();
    for (Map.Entry<String, ConnectionPool> entry : connectionMap.entrySet())
    {
        HostAndPort node = HostAndPort.from(entry.getKey());
        ConnectionPool pool = entry.getValue();
        try (Connection connection = pool.getResource())
        {
            T aReply = execute(connection, commandObject);
            bcastError.addReply(node, aReply);
            if (isErrored)
            {
                // already errored
            }
            else if (reply == null)
            {
                reply = aReply; // ok
            }
            else if (reply.equals(aReply))
            {
                // ok
            }
            else
            {
                isErrored = true;
                reply = null;
            }
        }
        catch (Exception anError)
        {
            bcastError.addReply(node, anError);
            isErrored = true;
        }
    }
    if (isErrored)
    {
        throw bcastError;
    }
    return reply;
}

@Override
public final <T> T executeCommand(CommandObject<T> commandObject)
{

    Instant deadline = Instant.now().plus(maxTotalRetriesDuration);

    int consecutiveConnectionFailures = 0;
    JedisException lastException = null;
    for (int attemptsLeft = this.maxAttempts; attemptsLeft > 0; attemptsLeft--)
    {
        Connection connection = null;
        try
        {
            connection = provider.getConnection(commandObject.getArguments());

            return execute(connection, commandObject);
        }
        catch (JedisConnectionException jce)
        {
            lastException = jce;
            ++consecutiveConnectionFailures;
            log.debug("Failed connecting to Redis: {}", connection, jce);
            // "- 1" because we just did one, but the attemptsLeft counter hasn't been decremented yet
            boolean reset = handleConnectionProblem(attemptsLeft - 1, consecutiveConnectionFailures, deadline);
            if (reset)
            {
                consecutiveConnectionFailures = 0;
            }
        }
        finally
        {
            if (connection != null)
            {
                connection.close();
            }
        }
        if (Instant.now().isAfter(deadline))
        {
            throw new JedisException("Retry deadline exceeded.");
        }
    }

    JedisException maxAttemptsException = new JedisException("No more attempts left.");
    maxAttemptsException.addSuppressed(lastException);
    throw maxAttemptsException;
}

@Override
public Map<String, Map<String, Double>> build(Object data)
{
    List rawDataList = (List) data;
    if (rawDataList.isEmpty())
    {
        return Collections.emptyMap();
    }

    if (rawDataList.get(0) instanceof KeyValue)
    {
        KeyValue rawData = (KeyValue) rawDataList.get(0);
        String header = STRING.build(rawData.getKey());
        if (!RESULTS.equals(header))
        {
            throw new IllegalStateException("Unrecognized header: " + header);
        }

        return ((List<KeyValue>) rawData.getValue()).stream().collect(Collectors.toMap(
            rawTerm -> STRING.build(rawTerm.getKey()),
            rawTerm -> ((List<List<KeyValue>>) rawTerm.getValue()).stream()
                .collect(Collectors.toMap(entry -> STRING.build(entry.get(0).getKey()),
                    entry -> BuilderFactory.DOUBLE.build(entry.get(0).getValue()))),
            (x, y) -> x, LinkedHashMap::new));
    }

    Map<String, Map<String, Double>> returnTerms = new LinkedHashMap<>(rawDataList.size());

    for (Object rawData : rawDataList)
    {
        List<Object> rawElements = (List<Object>) rawData;

        String header = STRING.build(rawElements.get(0));
        if (!TERM.equals(header))
        {
            throw new IllegalStateException("Unrecognized header: " + header);
        }
        String term = STRING.build(rawElements.get(1));

        List<List<Object>> list = (List<List<Object>>) rawElements.get(2);
        Map<String, Double> entries = new LinkedHashMap<>(list.size());
        list.forEach(entry -> entries.put(STRING.build(entry.get(1)), BuilderFactory.DOUBLE.build(entry.get(0))));

        returnTerms.put(term, entries);
    }
    return returnTerms;
}

@Override
public final List<Object> exec()
{
    if (!inMulti)
    {
        throw new IllegalStateException("EXEC without MULTI");
    }

    try (Connection connection = failoverProvider.getConnection())
    {

        commands.forEach((command) -> connection.sendCommand(command.getKey()));
        // following connection.getMany(int) flushes anyway, so no flush here.

        // ignore QUEUED (or ERROR)
        connection.getMany(commands.size());

        // remove extra response builders
        for (int idx = 0; idx < extraCommandCount.get(); ++idx)
        {
            commands.poll();
        }

        connection.sendCommand(EXEC);

        List<Object> unformatted = connection.getObjectMultiBulkReply();
        if (unformatted == null)
        {
            commands.clear();
            return null;
        }

        List<Object> formatted = new ArrayList<>(unformatted.size() - extraCommandCount.get());
        for (Object rawReply : unformatted)
        {
            try
            {
                Response<?> response = commands.poll().getValue();
                response.set(rawReply);
                formatted.add(response.get());
            }
            catch (JedisDataException e)
            {
                formatted.add(e);
            }
        }
        return formatted;

    }
    finally
    {
        inMulti = false;
        inWatch = false;
    }
}

private void process()
{

    do
    {
        Object reply = authenticator.client.getUnflushedObject();

        if (reply instanceof List)
        {
            List<Object> listReply = (List<Object>) reply;
            final Object firstObj = listReply.get(0);
            if (!(firstObj instanceof byte[]))
            {
                throw new JedisException("Unknown message type: " + firstObj);
            }
            final byte[] resp = (byte[]) firstObj;
            if (Arrays.equals(SSUBSCRIBE.getRaw(), resp))
            {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bchannel = (byte[]) listReply.get(1);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                onSSubscribe(enchannel, subscribedChannels);
            }
            else if (Arrays.equals(SUNSUBSCRIBE.getRaw(), resp))
            {
                subscribedChannels = ((Long) listReply.get(2)).intValue();
                final byte[] bchannel = (byte[]) listReply.get(1);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                onSUnsubscribe(enchannel, subscribedChannels);
            }
            else if (Arrays.equals(SMESSAGE.getRaw(), resp))
            {
                final byte[] bchannel = (byte[]) listReply.get(1);
                final byte[] bmesg = (byte[]) listReply.get(2);
                final T enchannel = (bchannel == null) ? null : encode(bchannel);
                final T enmesg = (bmesg == null) ? null : encode(bmesg);
                onSMessage(enchannel, enmesg);
            }
            else
            {
                throw new JedisException("Unknown message type: " + firstObj);
            }
        }
        else if (reply instanceof byte[])
        {
            Consumer<Object> resultHandler = authenticator.resultHandler.poll();
            if (resultHandler == null)
            {
                throw new JedisException("Unexpected message : " + SafeEncoder.encode((byte[]) reply));
            }
            resultHandler.accept(reply);
        }
        else
        {
            throw new JedisException("Unknown message type: " + reply);
        }
    }
    while (!Thread.currentThread().isInterrupted() && isSubscribed());

//    /* Invalidate instance since this thread is no longer listening */
//    this.client = null;
}

@Override
public boolean equals(Object o)
{
    if (o == this)
    {
        return true;
    }
    if (!(o instanceof CommandObject))
    {
        return false;
    }

    Iterator<Rawable> e1 = arguments.iterator();
    Iterator<Rawable> e2 = ((CommandObject) o).arguments.iterator();
    while (e1.hasNext() && e2.hasNext())
    {
        Rawable o1 = e1.next();
        Rawable o2 = e2.next();
        if (!(o1 == null ? o2 == null : o1.equals(o2)))
        {
            return false;
        }
    }
    if (e1.hasNext() || e2.hasNext())
    {
        return false;
    }

    return builder == ((CommandObject) o).builder;
}

public CommandArguments add(Object arg)
{
    if (arg == null)
    {
        throw new IllegalArgumentException("null is not a valid argument.");
    }
    else if (arg instanceof Rawable)
    {
        args.add((Rawable) arg);
    }
    else if (arg instanceof byte[])
    {
        args.add(RawableFactory.from((byte[]) arg));
    }
    else if (arg instanceof Boolean)
    {
        args.add(RawableFactory.from((Boolean) arg));
    }
    else if (arg instanceof Integer)
    {
        args.add(RawableFactory.from((Integer) arg));
    }
    else if (arg instanceof Long)
    {
        args.add(RawableFactory.from((Long) arg));
    }
    else if (arg instanceof Double)
    {
        args.add(RawableFactory.from((Double) arg));
    }
    else if (arg instanceof float[])
    {
        args.add(RawableFactory.from(RediSearchUtil.toByteArray((float[]) arg)));
    }
    else if (arg instanceof String)
    {
        args.add(RawableFactory.from((String) arg));
    }
    else if (arg instanceof GeoCoordinate)
    {
        GeoCoordinate geo = (GeoCoordinate) arg;
        args.add(RawableFactory.from(geo.getLongitude() + "," + geo.getLatitude()));
    }
    else
    {
        args.add(RawableFactory.from(String.valueOf(arg)));
    }
    return this;
}

private void initMaster(HostAndPort master)
{
    initPoolLock.lock();

    try
    {
        if (!master.equals(currentMaster))
        {
            currentMaster = master;

            ConnectionPool newPool = createNodePool(currentMaster);

            ConnectionPool existingPool = pool;
            pool = newPool;
            LOG.info("Created connection pool to master at {}.", master);
            if (clientSideCache != null)
            {
                clientSideCache.flush();
            }

            if (existingPool != null)
            {
                // although we clear the pool, we still have to check the returned object in getResource,
                // this call only clears idle instances, not borrowed instances
                // existingPool.clear(); // necessary??
                existingPool.close();
            }
        }
    }
    finally
    {
        initPoolLock.unlock();
    }
}

private static void processPartAdvanced(Object part, EmailContent content, EmlToPdfRequest request) {
	try {
		if (!isValidJakartaMailPart(part)) {
			log.warn("Invalid Jakarta Mail part type: {}", part.getClass().getName());
			return;
		}
		Class<?> partClass = part.getClass();
		Method isMimeType = partClass.getMethod("isMimeType", String.class);
		Method getContent = partClass.getMethod("getContent");
		Method getDisposition = partClass.getMethod("getDisposition");
		Method getFileName = partClass.getMethod("getFileName");
		Method getContentType = partClass.getMethod("getContentType");
		Method getHeader = partClass.getMethod("getHeader", String.class);

		Object disposition = getDisposition.invoke(part);
		String filename = (String) getFileName.invoke(part);
		String contentType = (String) getContentType.invoke(part);

		if ((Boolean) isMimeType.invoke(part, "text/plain") && disposition == null) {
			content.setTextBody((String) getContent.invoke(part));
		} else if ((Boolean) isMimeType.invoke(part, "text/html") && disposition == null) {
			content.setHtmlBody((String) getContent.invoke(part));
		} else if ("attachment".equalsIgnoreCase((String) disposition)
				|| (filename != null && !filename.trim().isEmpty())) {
			content.setAttachmentCount(content.getAttachmentCount() + 1);

			// Always extract basic attachment metadata for display
			if (filename != null && !filename.trim().isEmpty()) {
				// Create attachment with metadata only
				EmailAttachment attachment = new EmailAttachment();
				// Apply MIME decoding to filename to handle encoded attachment names
				attachment.setFilename(safeMimeDecode(filename));
				attachment.setContentType(contentType);

				// Check if it's an embedded image
				String[] contentIdHeaders = (String[]) getHeader.invoke(part, "Content-ID");
				if (contentIdHeaders != null && contentIdHeaders.length > 0) {
					attachment.setEmbedded(true);
					// Store the Content-ID, removing angle brackets if present
					String contentId = contentIdHeaders[0];
					if (contentId.startsWith("<") && contentId.endsWith(">")) {
						contentId = contentId.substring(1, contentId.length() - 1);
					}
					attachment.setContentId(contentId);
				}

				// Extract attachment data if attachments should be included OR if it's an embedded image
				if ((request != null && request.isIncludeAttachments()) || attachment.isEmbedded()) {
					try {
						Object attachmentContent = getContent.invoke(part);
						byte[] attachmentData = null;

						if (attachmentContent instanceof java.io.InputStream inputStream) {
							try {
								attachmentData = inputStream.readAllBytes();
							} catch (IOException e) {
								log.warn("Failed to read InputStream attachment: {}", e.getMessage());
							}
						} else if (attachmentContent instanceof byte[] byteArray) {
							attachmentData = byteArray;
						} else if (attachmentContent instanceof String stringContent) {
							attachmentData = stringContent.getBytes(StandardCharsets.UTF_8);
						}

						if (attachmentData != null) {
							// Check size limit (use default 10MB if request is null)
							long maxSizeMB = request != null ? request.getMaxAttachmentSizeMB() : 10L;
							long maxSizeBytes = maxSizeMB * 1024 * 1024;

							if (attachmentData.length <= maxSizeBytes) {
								attachment.setData(attachmentData);
								attachment.setSizeBytes(attachmentData.length);
							} else {
								// For embedded images, always include data regardless of size
								// to ensure inline display works
								if (attachment.isEmbedded()) {
									attachment.setData(attachmentData);
									attachment.setSizeBytes(attachmentData.length);
								} else {
									// Still show attachment info even if too large
									attachment.setSizeBytes(attachmentData.length);
								}
							}
						}
					} catch (Exception e) {
						log.warn("Error extracting attachment data: {}", e.getMessage());
					}
				}

				// Add attachment to the list for display (with or without data)
				content.getAttachments().add(attachment);
			}
		} else if ((Boolean) isMimeType.invoke(part, "multipart/*")) {
			// Handle nested multipart content
			try {
				Object multipartContent = getContent.invoke(part);
				Class<?> multipartClass = Class.forName("jakarta.mail.Multipart");
				if (multipartClass.isInstance(multipartContent)) {
					processMultipartAdvanced(multipartContent, content, request);
				}
			} catch (Exception e) {
				log.warn("Error processing multipart content: {}", e.getMessage());
			}
		}
	} catch (Exception e) {
		log.warn("Error processing multipart part: {}", e.getMessage());
	}
}

private static EmailContent extractEmailContentAdvanced(byte[] emlBytes, EmlToPdfRequest request) {
	try {
		// Use Jakarta Mail for processing
		Class<?> sessionClass = Class.forName("jakarta.mail.Session");
		Class<?> mimeMessageClass = Class.forName("jakarta.mail.internet.MimeMessage");

		Method getDefaultInstance = sessionClass.getMethod("getDefaultInstance", Properties.class);
		Object session = getDefaultInstance.invoke(null, new Properties());

		// Cast the session object to the proper type for the constructor
		Class<?>[] constructorArgs = new Class<?>[] { sessionClass, InputStream.class };
		Constructor<?> mimeMessageConstructor = mimeMessageClass.getConstructor(constructorArgs);
		Object message = mimeMessageConstructor.newInstance(session, new ByteArrayInputStream(emlBytes));

		return extractEmailContentAdvanced(message, request);
	} catch (ReflectiveOperationException e) {
		// Create basic EmailContent from basic processing
		EmailContent content = new EmailContent();
		content.setHtmlBody(convertEmlToHtmlBasic(emlBytes, request));
		return content;
	}
}

